INSERT INTO `tbl_news` (`id`, `cat_id`, `user`, `name`, `title`, `slug`, `img`, `detail`, `content`, `hits`, `status`, `hot`, `description`, `desc_size`, `keywords`, `robot`, `date`, `date_up`) VALUES
(33, 11, 'admin', 'Những lý do khách hàng muốn sở hữu ngay căn hộ tại Eurowindow Nghệ An.', 'Những lý do khách hàng muốn sở hữu ngay căn hộ tại Eurowindow Nghệ An', 'nhung-ly-do-khach-hang-muon-so-huu-ngay-can-ho-tai-eurowindow-nghe-an-', '/public/upload/images/eurowindow/euro-window-nghe-an.jpg', 'Sở hữu vị trí đắc địa ngay giữa trung tâm thành phố Vinh, Eurowindow Nghệ An là tòa tháp căn hộ cao cấp kết hợp nhà ở thương mại được trang bị hệ thống', '<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Sở hữu vị tr&iacute; đắc địa ngay giữa trung t&acirc;m th&agrave;nh phố Vinh, <a href=\"http://www.bdsnghetinh.net/du-an-bds/toa-thap-eurowindow-nghe-an/50.html\"><strong>Eurowindow Nghệ An</strong></a> l&agrave; t&ograve;a th&aacute;p căn hộ cao cấp kết hợp nh&agrave; ở thương mại được trang bị hệ thống tiện &iacute;ch đa dạng kh&ocirc;ng chỉ thỏa m&atilde;n mong muốn của người d&acirc;n về một m&aacute;i ấm an cư chất lượng, tiện nghi đầy đủ m&agrave; c&ograve;n l&agrave; một k&ecirc;nh đầu tư an to&agrave;n, hiệu quả với khả năng sinh lời cao. Điều n&agrave;y cũng l&yacute; giải v&igrave; sao ngay từ khi ra mắt thị trường, Eurowindow Nghệ An đ&atilde; tạo được sức h&uacute;t đặc biệt, trở th&agrave;nh sản phẩm &ldquo;Hot nhất&rdquo; được đ&ocirc;ng đảo kh&aacute;ch h&agrave;ng v&agrave; nh&agrave; đầu tư quan t&acirc;m, săn l&ugrave;ng trong thời gian gần đ&acirc;y.</span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/eurowindow/tong-quan-eurowindown-nghe-an.png\" /></span></p>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\">Địa thế sinh ph&uacute; qu&yacute;</span></u></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Tọa lạc tại vị tr&iacute; đặc biệt nhất th&agrave;nh phố Vinh, với 4 mặt tiếp gi&aacute;p c&aacute;c trục đường lớn v&agrave; l&agrave; nơi quy tụ của h&agrave;ng trăm cửa h&agrave;ng, cửa hiệu kinh doanh sầm uất. Từ t&ograve;a th&aacute;p Eurowindow Nghệ An c&aacute;c cư d&acirc;n c&oacute; thể kết n&ocirc;i thuận tiện tới c&aacute;c tiện &iacute;ch xung quanh như&nbsp; s&acirc;n bay quốc tế Vinh, b&ecirc;n xe Vinh, trường đại học Vinh, chợ Vinh, si&ecirc;u thị BigC, c&ocirc;ng vi&ecirc;n trung t&acirc;m,&nbsp; rạp chiếu phim&hellip;</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;B&ecirc;n cạnh đ&oacute;, Eurowindow Nghệ An c&ograve;n l&agrave; một trong những dự &aacute;n bất động sản ti&ecirc;n phong tại th&agrave;nh phố Vinh khi nắm bắt kịp xu hướng ph&aacute;t triển m&ocirc; h&igrave;nh nh&agrave; ở thương mại với 19 căn shophouse được bố tr&iacute; từ tầng 1 đến tầng 5 của dự &aacute;n, kh&ocirc;ng chỉ gi&uacute;p chủ nh&acirc;n &ldquo;một bước xuống phố&rdquo; m&agrave; c&ograve;n&nbsp; mang lại gi&aacute; trị sử dụng linh hoạt v&agrave; khả năng đầu tư sinh lời cao.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Theo đ&aacute;nh gi&aacute; của giới chuy&ecirc;n m&ocirc;n, thời gian tới độ &ldquo;n&oacute;ng&rdquo; của dự &aacute;n sẽ kh&ocirc;ng hề giảm nhiệt m&agrave; c&oacute; xu hướng ng&agrave;y c&agrave;ng tăng nhanh c&ugrave;ng với những dự đo&aacute;n đầy lạc quan về tương lai ph&aacute;t triển vượt bậc của th&agrave;nh phố Vinh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\">Kh&ocirc;ng gian sống tiện nghi &ndash; một bước xuống phố</span></u></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;L&agrave; dự &aacute;n được ph&aacute;t triển bởi chủ đầu tư uy t&iacute;n C&ocirc;ng ty Cổ phần Đầu tư Trung t&acirc;m Thương mại Vinh (thuộc Tập đo&agrave;n Eurowindow) v&agrave; đơn vị gi&aacute;m s&aacute;t Beacons, đơn vị thi c&ocirc;ng Th&agrave;nh An (thuộc BQP ), t&ograve;a th&aacute;p Eurowindow Nghệ An được đ&aacute;nh gi&aacute; c&oacute; chất lượng v&agrave; thiết kế tổng thể đẹp nhất th&agrave;nh phố Vinh. Sống tại đ&acirc;y, c&aacute;c cư d&acirc;n ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m về vấn đề an ninh, an to&agrave;n đồng thời lại được tận hưởng đầy đủ c&aacute;c dịch vụ tiện nghi ho&agrave;n hảo ngay tại nơi sinh sống m&agrave; kh&ocirc;ng cần phải di chuyển xa.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;C&aacute;c căn hộ tại Eurowindow Nghệ An được thiết kế hiện đại với diện t&iacute;ch hợp l&yacute; từ 45-91m2 , tối ưu h&oacute;a về c&ocirc;ng năng sử dụng, đảm bảo kh&ocirc;ng gian mở đ&oacute;n nắng đ&oacute;n gi&oacute; tự nhi&ecirc;n, gi&uacute;p điều h&ograve;a kh&ocirc;ng kh&iacute;, tạo cảm gi&aacute;c thư th&aacute;i, dễ chịu cho gia chủ.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Hệ thống cửa đồng bộ, hiện đại, an to&agrave;n v&agrave; tiết kiệm điện với cửa đi ch&iacute;nh l&agrave; cửa an to&agrave;n chống trộm, hệ thống cửa sổ l&agrave; cửa UPVC chống ồn do Eurowindow sản xuất, 3 thang m&aacute;y tốc độ cao, di chuyển &ecirc;m v&agrave; nhanh ch&oacute;ng. Ngo&agrave;i ra, c&ograve;n c&oacute; hệ thống điện dự ph&ograve;ng, gi&uacute;p đảm bảo việc di chuyển trong t&ograve;a nh&agrave; kh&ocirc;ng bị gi&aacute;n đoạn.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\">Cơ hội đầu tư sinh lời vượt trội</span></u></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Theo th&ocirc;ng tin quy hoạch mới, th&agrave;nh phố Vinh &ndash; Nghệ An sẽ trở th&agrave;nh khu đ&ocirc; thị đa cực với quy m&ocirc; 250km2, theo đ&oacute; diện t&iacute;ch đất, khu c&ocirc;ng nghiệp v&agrave; d&acirc;n số sẽ gia tăng nhanh ch&oacute;ng, k&eacute;o theo tốc độ tăng trưởng của nhu cầu nh&agrave; ở rất lớn.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với lợi thế nằm ngay ng&atilde; tư chợ Vinh, tiếp gi&aacute;p&nbsp; 3 tuyến phố kinh doanh sầm uất l&agrave; đường Trần Ph&uacute;, đường Cao Thắng v&agrave; đường l&ecirc; Hu&acirc;n, c&ugrave;ng hệ thống tiện &iacute;ch nội khu, ngoại khu đa dạng, Eurowindow Nghệ An kh&ocirc;ng chỉ đ&aacute;p ứng thỏa m&atilde;n nhu cầu sinh hoạt tiện nghi của c&aacute;c gia đ&igrave;nh m&agrave; c&ograve;n c&oacute; thể sử dụng để đầu tư kinh doanh hoặc cho thu&ecirc;, thu lợi nhuận đều đặn h&agrave;ng th&aacute;ng.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Đặc biệt với c&aacute;c gia đ&igrave;nh đang phải thu&ecirc; cửa h&agrave;ng để kinh doanh th&igrave; việc lựa chọn một căn nh&agrave; phố thương mại tại t&ograve;a th&aacute;p Eurowindow Nghệ An được xem l&agrave; lựa chọn kh&ocirc;n ngoan mang lại lợi &iacute;ch ba trong một, vừa để ở - vừa kết hợp kinh doanh - vừa kh&ocirc;ng mất chi ph&iacute; thu&ecirc; mặt bằng, trong khi gi&aacute; trị căn hộ kh&ocirc;ng ngừng gia tăng theo thời gian.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/files/z825297503329_0fc5162f37ab99cb8bae18776683c6f4.jpg\" /></p>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\">Ch&iacute;nh s&aacute;ch ưu đ&atilde;i hấp dẫn</span></u></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Trước thực tế ở nh&agrave; đất c&oacute; nhiều bất tiện, gi&aacute; cả đắt đỏ v&agrave; ng&agrave;y c&agrave;ng chật chội v&igrave; quỹ đất dần bị thu hẹp th&igrave; việc sở hữu một căn hộ chung cư đ&atilde; trở th&agrave;nh lựa chọn của nhiều gia đ&igrave;nh trẻ hiện nay. Hơn nữa, một căn hộ c&oacute; vị tr&iacute; tốt, gi&aacute; vừa phải chỉ từ 650 triệu/căn, kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng, m&ocirc;i trường sống trong l&agrave;nh, tiện nghi như t&ograve;a th&aacute;p Eurowindow Nghệ An lu&ocirc;n l&agrave; điều mong ước của c&aacute;c gia đ&igrave;nh.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Đặc biệt, tại thời điểm n&agrave;y qu&yacute; kh&aacute;ch h&agrave;ng đặt mua căn hộ c&ograve;n được tặng ngay 10 triệu lộc may mắn v&agrave; được hỗ trợ vay vốn từ ng&acirc;n h&agrave;ng Vietcombank, Techcombank với l&atilde;i suất 0% đến khi nhận b&agrave;n giao nh&agrave;.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Như vậy, với ch&iacute;nh s&aacute;ch gi&aacute; ưu đ&atilde;i, c&ugrave;ng tiến độ thanh to&aacute;n linh hoạt 7 đợt, kh&aacute;ch h&agrave;ng chỉ cần thanh to&aacute;n gần 200 triệu đồng đ&atilde; c&oacute; thể sở hữu ngay một căn hộ cao cấp 2 ph&ograve;ng ngủ giữa trung t&acirc;m th&agrave;nh phố Vinh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>--------------------------------------------------------------------------</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Xin cảm ơn quý khách!</strong></span></p>', 1193, 1, 1, 'Sở hữu vị trí đắc địa ngay giữa trung tâm thành phố Vinh, Eurowindow Nghệ An là tòa tháp căn hộ cao cấp kết hợp nhà ở thương mại được trang bị hệ', 5, NULL, '0', '4-8-2017', '30-11-2017'),
(34, 11, 'admin', 'Đô thị xanh - Xu hướng nhà ở đô thị Việt Nam trong 20 năm tới.', NULL, 'do-thi-xanh-xu-huong-nha-o-do-thi-viet-nam-trong-20-nam-toi-', '/public/upload/images/cua-tien-home/khong-gian-xanh-khu-do-thi-cua-tien-home-tp-vinh.jpg', NULL, '<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Kh&ocirc;ng gian sống xanh lu&ocirc;n l&agrave; một trong những xu hướng của mọi thời đại bất kể sự thay đổi nhanh ch&oacute;ng của c&aacute;c tr&agrave;o lưu thượng t&ocirc;n phong c&aacute;ch kiến tr&uacute;c cổ điển, t&acirc;n cổ điển hay hiện đại.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/khong-gian-xanh-khu-do-thi-cua-tien-home-tp-vinh.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><em>Đ&ocirc; thị xanh - Xu hướng nh&agrave; ở đ&ocirc; thị Việt Nam trong 20 năm tới</em></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"color:#000000;\"><span style=\"font-size:16px;\"><em>Khu đ&ocirc; thị Cửa Tiền Home - Dự &aacute;n nổi bật quy hoach đồng bộ, kh&ocirc;ng gian xanh nhất tại TP Vinh, Nghệ An.</em></span></span></u></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Bắt kịp xu hướng c&ugrave;ng mục ti&ecirc;u ph&aacute;t triển bền vững, <strong>CTCP Danatol </strong>lu&ocirc;n đặt kh&ocirc;ng gian sống xanh l&agrave;m trọng t&acirc;m ph&aacute;t triển trong c&aacute;c dự &aacute;n bất động sản tại Việt Nam. Trong đ&oacute;, dự &aacute;n <strong>Khu đ&ocirc; thị Cửa Tiền Home</strong> ch&iacute;nh l&agrave; h&igrave;nh mẫu đ&ocirc; thị ti&ecirc;u chuẩn trong quy hoạch khu đ&ocirc; thị tương lai.</span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/cua-tien-home.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><strong>Đ&ocirc; thị xanh &ndash; Xu thế ph&aacute;t triển đem lại gi&aacute; trị bền vững</strong></span></u></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Nhiều nghi&ecirc;n cứu đ&atilde; chỉ ra rằng m&ocirc; h&igrave;nh &ldquo;rừng trong phố&rdquo; tại c&aacute;c đ&ocirc; thị lớn đang l&agrave; tr&agrave;o lưu v&agrave; kh&ocirc;ng ngừng ph&aacute;t triển trong nhiều năm trở lại đ&acirc;y. Kh&aacute;i niệm &ldquo;Rừng trong phố&rdquo; được Tổ chức N&ocirc;ng lương Li&ecirc;n Hợp Quốc (FAO) đưa ra để m&ocirc; tả hệ thống bao phủ bởi c&acirc;y xanh ở c&aacute;c khu đ&ocirc; thị.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;M&ocirc; h&igrave;nh n&agrave;y từng được khảo s&aacute;t v&agrave; chứng minh l&agrave; c&oacute; t&aacute;c dụng gi&uacute;p con người thoải m&aacute;i v&agrave; hạnh ph&uacute;c hơn tại Singapore, New York v&agrave; Paris. Năm 2016, Hội đồng th&agrave;nh phố Brisbane, Australia đ&atilde; đầu tư 90 triệu USD để x&acirc;y dựng c&aacute;c h&agrave;nh lang xanh tr&ecirc;n khắp th&agrave;nh phố. Sydney v&agrave; Vancouver cũng l&agrave; 2 th&agrave;nh phố xanh đang thực hiện hiệu quả m&ocirc; h&igrave;nh &ldquo;vườn trong phố&rdquo; để &ldquo;xanh h&oacute;a&rdquo; đ&ocirc; thị.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Theo Cục Hạ tầng Kỹ thuật &ndash; Bộ X&acirc;y dựng, ph&aacute;t triển đ&ocirc; thị xanh l&agrave; giải ph&aacute;p tổng thể bảo vệ m&ocirc;i trường v&agrave; n&acirc;ng cao chất lượng cuộc sống của cư d&acirc;n đ&ocirc; thị. Trong c&aacute;c ti&ecirc;u ch&iacute; về đ&ocirc; thị xanh, diện t&iacute;ch c&acirc;y xanh đ&ocirc; thị l&agrave; chỉ ti&ecirc;u c&oacute; hiệu quả cao nhất.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Theo b&aacute;o c&aacute;o qu&yacute; I năm 2017 của Colliers International, d&acirc;n số đ&ocirc; thị tại Việt Nam sẽ đạt mức 45% trong v&ograve;ng 3 năm tới. Bối cảnh chuyển h&oacute;a đ&ocirc; thị tr&ecirc;n diện rộng như vậy sẽ khiến những th&agrave;nh phố lớn như H&agrave; Nội, Hồ Ch&iacute; Minh, Đ&agrave; Nẵng, Hải Ph&ograve;ng, Hạ Long, TP Vinh, ... chứng kiến sự b&ugrave;ng nổ về số lượng nh&agrave; ở. Bởi vậy, phong c&aacute;ch sống xanh c&agrave;ng n&ecirc;n được đặt l&agrave;m trọng t&acirc;m trong định hướng ph&aacute;t triển đ&ocirc; thị tại Việt Nam.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Theo nghi&ecirc;n cứu của <strong><span style=\"color:#0000cd;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ A</span>n</strong>, thực tế, người mua kh&ocirc;ng chỉ lựa chọn căn nh&agrave; hay chỉ quan t&acirc;m đến việc phủ xanh c&ocirc;ng tr&igrave;nh. Họ c&ograve;n chọn cả kh&ocirc;ng gian sống xung quanh cũng như cơ sở vật chất hay hệ thống giao th&ocirc;ng, tiện &iacute;ch kết nối để đảm bảo cho cuộc sống tiện nghi nhất v&agrave; sự h&agrave;i ho&agrave; của kh&ocirc;ng gian xanh trong c&ocirc;ng tr&igrave;nh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/sacircn%20tennis_zpsoklr105r.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/Cua%20tien%20home%20TTTM.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/Cua%20tien%20home%20The%20thao.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ch&iacute;nh v&igrave; thế, c&ugrave;ng với sự ph&aacute;t triển của TP Vinh, ph&aacute;t triển dự &aacute;n khu đ&ocirc; thị Cửa Tiền Home mang theo triết l&yacute; về x&acirc;y dựng đ&ocirc; thị xanh, sẵn s&agrave;ng đầu tư h&agrave;ng triệu USD để mang tới một kh&ocirc;ng gian sống xanh cho tất cả mọi người nơi cư d&acirc;n kh&ocirc;ng những được h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n m&agrave; c&ograve;n tận hưởng c&aacute;c tiện &iacute;ch hiện đại kh&aacute;c.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\"><u><span style=\"color:#0000cd;\">Cửa Tiền Home - H&igrave;nh mẫu ti&ecirc;u chuẩn của đ&ocirc; thị sinh th&aacute;i</span></u></span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ở TP Vinh - Nghệ An, Cửa Tiền Home l&agrave; một c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu thể hiện triết l&yacute; ph&aacute;t triển bền vững, c&acirc;n bằng giữa kh&ocirc;ng gian xanh v&agrave; tiện &iacute;ch cộng đồng. Tổng diện t&iacute;ch d&agrave;nh cho khu đ&ocirc; thị l&agrave; 22 Hecta, đồng bộ tiện &iacute;ch chung cư với khu&ocirc;n vi&ecirc;n c&acirc;y xanh. Đặc biệt, c&aacute;c kiến tr&uacute;c sư h&agrave;ng đầu đ&atilde; kh&eacute;o l&eacute;o thiết kế c&aacute;c khu nh&agrave; đều c&oacute; hướng nh&igrave;n ra c&ocirc;ng vi&ecirc;n hoặc hồ cảnh quan, cho ph&eacute;p cư d&acirc;n tận hưởng &aacute;nh s&aacute;ng tự nhi&ecirc;n v&agrave; kh&ocirc;ng kh&iacute; trong l&agrave;nh ngay cả khi ở nh&agrave;&rdquo;.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/h%20ca%20nam_zpskxxdl4m0.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cua-tien-home/goc-nhin-cua-tien-home.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Danatol l&agrave; nh&agrave; đầu tư v&agrave; ph&aacute;t triển khu đ&ocirc; thị trụ sở ch&iacute;nh l&agrave; ở Đ&agrave; Nẵng. Đến thời điểm đầu tư về Nghệ An nổi bật với dự &aacute;n Cửa Tiền Home với khu&ocirc;n vi&ecirc;n rộng nhất TP Vinh, tạo n&ecirc;n một l&agrave;n s&oacute;ng mới tại TP Vinh, Nghệ An.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">--------------------------------------------------------------------------</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">Xin cảm ơn quý khách!</span></strong></p>', 1125, 1, 1, NULL, 150, NULL, '0', '16-8-2017', '30-11-2017'),
(35, 11, 'admin', '5 Lý do nên chọn mua chung cư trên tầng cao.', '5 Lý do nên chọn mua chung cư trên tầng cao', '5-ly-do-nen-chon-mua-chung-cu-tren-tang-cao-', '/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg', 'Việt Nam nằm trong vùng có khí hậu nhiệt đới ẩm ướt nên khi có gió nồm, sàn nhà thường bị ẩm mốc. Tuy nhiên, với các căn hộ trên tầng cao, không khí thông thoáng...', '<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Trước đ&acirc;y, người Việt Nam thường c&oacute; t&acirc;m l&yacute; sở hữu một ng&ocirc;i nh&agrave; đất nền thấp tầng. Tuy nhi&ecirc;n, gần đ&acirc;y, xu hướng đ&oacute; đang dần được thay thế bởi loại h&igrave;nh căn hộ chung cư, đặc biệt l&agrave; c&aacute;c t&ograve;a chung cư cao tầng. Kh&ocirc;ng phải ngẫu nhi&ecirc;n m&agrave; c&aacute;c căn hộ tr&ecirc;n tầng cao tuy c&oacute; gi&aacute; đắt hơn so với c&aacute;c căn hộ tầng thấp nhưng vẫn được c&aacute;c nh&agrave; đầu tư săn l&ugrave;ng. Sau đ&acirc;y l&agrave; những l&yacute; do m&agrave; ch&uacute;ng t&ocirc;i đưa ra.</span></p>\r\n<ul>\r\n	<li>\r\n		<u><strong><span style=\"font-size:16px;\"><a href=\"http://www.bdsnghetinh.net/do-thi-xanh-xu-huong-nha-o-do-thi-viet-nam-trong-20-nam-toi/34.html\"><span style=\"color:#000000;\">Đ&ocirc; thị xanh - Xu hướng nh&agrave; ở đ&ocirc; thị Việt Nam trong 20 năm tới</span></a></span></strong></u></li>\r\n	<li>\r\n		<u><strong><span style=\"font-size:16px;\"><a href=\"http://www.bdsnghetinh.net/nhung-ly-do-khach-hang-muon-so-huu-ngay-can-ho-tai-eurowindow-nghe-an/33.html\"><span style=\"color:#000000;\">Những l&yacute; do kh&aacute;ch h&agrave;ng muốn sở hữu ngay căn hộ tại Eurowindow Nghệ An</span></a></span></strong></u></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<u><strong><span style=\"font-size:16px;\">1. Tầm nh&igrave;n rộng, tho&aacute;ng đ&atilde;ng, kh&ocirc;ng bị giới hạn.</span></strong></u></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Một trong những ưu điểm của căn hộ chung cư cao tầng l&agrave; sở hữu tầm nh&igrave;n th&ocirc;ng tho&aacute;ng, kh&ocirc;ng gian kh&ocirc;ng bị giới hạn. C&agrave;ng nằm tr&ecirc;n tầng cao th&igrave; tầm nh&igrave;n c&agrave;ng rộng, view nh&igrave;n ra kh&ocirc;ng gian b&ecirc;n ngo&agrave;i c&agrave;ng đẹp.&nbsp; Đ&acirc;y cũng ch&iacute;nh l&agrave; l&yacute; do v&igrave; sao c&aacute;c căn hộ lu&ocirc;n nằm tr&ecirc;n tầng cao nhất của t&ograve;a nh&agrave;, thường được định vị l&agrave; căn hộ đẳng cấp v&agrave; l&agrave; loại h&igrave;nh sản phẩm y&ecirc;u th&iacute;ch của c&aacute;c hộ gia đ&igrave;nh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><u><strong>2. Tr&aacute;nh sự ẩm ướt</strong></u></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Việt Nam nằm trong v&ugrave;ng c&oacute; kh&iacute; hậu nhiệt đới ẩm ướt n&ecirc;n khi c&oacute; gi&oacute; nồm, s&agrave;n nh&agrave; thường bị ẩm mốc. Tuy nhi&ecirc;n, với c&aacute;c căn hộ tr&ecirc;n tầng cao, kh&ocirc;ng kh&iacute; th&ocirc;ng tho&aacute;ng hơn, lưu th&ocirc;ng gi&oacute; tốt hơn&hellip;n&ecirc;n sẽ tr&aacute;nh được sự ẩm mốc.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><u><strong>3. Tr&aacute;nh &ocirc; nhiễm tiếng ồn v&agrave; kh&oacute;i bụi</strong></u></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;C&aacute;c căn hộ tr&ecirc;n tầng cao sẽ hạn chế tối đa được những nhược điểm khi sống tại c&aacute;c th&agrave;nh phố như: kh&oacute;i bụi, tiếng ồn, m&ugrave;i ẩm mốc&hellip;Tr&aacute;nh được &ocirc; nhiễm tiếng ồn v&agrave; kh&oacute;i bụi, sống trong bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh, y&ecirc;n tĩnh, sẽ mang đến giấc ngủ s&acirc;u hơn cho chủ nh&acirc;n căn hộ. Từ đ&oacute; m&agrave; sức khỏe được cải thiện, hạn chế được c&aacute;c bệnh về h&ocirc; hấp.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><u><strong>4. Hạn chế c&ocirc;n tr&ugrave;ng</strong></u></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Nếu việc đối mặt với ruồi, muỗi, gi&aacute;n, chuột&hellip;l&agrave; nỗi &aacute;m ảnh của những căn hộ đất nền th&igrave; đối với c&aacute;c căn hộ chung cư tầng cao, chủ sở hữu ho&agrave;n to&agrave;n tr&aacute;nh được c&aacute;c bệnh truyền nhiễm như sốt r&eacute;t, sốt xuất huyết&hellip;</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<u><strong><span style=\"font-size:16px;\">5. Tận dụng được nắng v&agrave; gi&oacute;</span></strong></u></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Do tận dụng được nắng v&agrave; gi&oacute; tự nhi&ecirc;n n&ecirc;n chủ sở hữu cũng sẽ tiết kiệm được c&aacute;c chi ph&iacute; về điện, năng lượng&hellip; gi&uacute;p nh&agrave; cửa trở n&ecirc;n th&ocirc;ng tho&aacute;ng hơn.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/ban-cong-dep.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với những lợi &iacute;ch tr&ecirc;n, căn hộ <a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-bao-son-complex/48.html\">chung cư Bảo Sơn Complex tại TP Vinh</a> được đ&aacute;nh gi&aacute; l&agrave; một trong những dự &aacute;n đẹp nhất th&agrave;nh Vinh bởi vị tr&iacute; đắc địa, th&ocirc;ng tho&aacute;ng v&agrave; được mệnh danh l&agrave; &ldquo; Căn hộ trong mơ&rdquo; đối với nhiều người do những gi&aacute; trị về mặt tinh thần m&agrave; n&oacute; mang lại cho chủ sở hữu.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>--------------------------------------------------------------------------</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Xin cảm ơn quý khách!</strong></span></p>', 911, 1, 1, 'Việt Nam nằm trong vùng có khí hậu nhiệt đới ẩm ướt nên khi có gió nồm, sàn nhà thường bị ẩm mốc. Tuy nhiên, với các căn hộ trên tầng cao, sẽ tránh..', 1, NULL, '0', '16-8-2017', '5-12-2017'),
(36, 11, 'admin', 'Quà tặng đặc biệt trong tháng ngâu khi mua căn hộ T&T Victoria.', 'Quà tặng đặc biệt trong tháng ngâu khi mua căn hộ T&T Victoria', 'qua-tang-dac-biet-trong-thang-ngau-khi-mua-can-ho-t-t-victoria-', '/public/upload/images/T-T/Tong-quan-t-t-Vinh.jpg', 'Ngày 24/8/2017, tập đoàn T&T vừa công bố chính sách ưu đãi đặc biệt, áp dụng duy nhất cho 30 khách hàng đầu tiên đặt mua căn hộ T&T Victoria...', '<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ng&agrave;y 24/8/2017, tập đo&agrave;n T&amp;T vừa c&ocirc;ng bố ch&iacute;nh s&aacute;ch ưu đ&atilde;i đặc biệt, &aacute;p dụng <em><strong>duy nhất cho 30 kh&aacute;ch h&agrave;ng đầu ti&ecirc;n</strong></em> đặt mua căn hộ T&amp;T Victoria trong th&aacute;ng Ng&acirc;u với th&ocirc;ng điệp &ldquo;Sống tiện &iacute;ch &ndash; Hưởng tiện nghi&rdquo; &ndash; <em><strong>Tặng ngay TỦ LẠNH Panasonic 238L hoặc M&Aacute;Y GIẶT Panasonic 7KG c&ugrave;ng nhiều quyền lợi kh&aacute;c cho kh&aacute;ch h&agrave;ng</strong></em>, khi mua căn hộ tại khu chung cư cao cấp T&amp;T Victoria (Số 1, Quang Trung, th&agrave;nh phố Vinh, Nghệ An).</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/T-T/ANH-BIA.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/khuyen-mai-thang-ngau-600.jpg\" /></span></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\">Với ch&iacute;nh s&aacute;ch qu&agrave; tặng đặc biệt trong th&aacute;ng Ng&acirc;u, kh&aacute;ch h&agrave;ng c&oacute; cơ hội nhận được nhiều ưu đ&atilde;i, bao gồm:</span></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Được chủ đầu tư hỗ trợ vay vốn ng&acirc;n h&agrave;ng l&ecirc;n tới 70% gi&aacute; trị nh&agrave;, thời gian vay l&ecirc;n tới 25 năm gi&uacute;p giảm &aacute;p lực thanh to&aacute;n, trong đ&oacute; l&atilde;i suất ở mức 0% trong 6 th&aacute;ng đầu ti&ecirc;n.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Được tặng ngay tủ lạnh hoặc m&aacute;y giặt khi đặt mua căn hộ.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Chiết khấu 1% gi&aacute; trị căn hộ cho kh&aacute;ch h&agrave;ng thanh to&aacute;n bằng vốn tự c&oacute;.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Tọa lạc tại số 1 Quang Trung, TP. Vinh, Nghệ An, T&amp;T Victoria sở hữu vị tr&iacute; v&agrave;ng của th&agrave;nh phố với 4 mặt tiếp gi&aacute;p c&aacute;c trục đường sầm uất như: Phan Đ&igrave;nh Ph&ugrave;ng; Quang Trung, Đặng Th&aacute;i Th&acirc;n v&agrave; Nguyễn Cảnh Ch&acirc;n. Từ đ&acirc;y, cư d&acirc;n c&oacute; thể dễ d&agrave;ng di chuyển tới c&aacute;c tiện &iacute;ch trong th&agrave;nh phố v&agrave; tới c&aacute;c địa phương kh&aacute;c qua hệ thống hạ tầng giao th&ocirc;ng đồng bộ: chợ Vinh; Bệnh viện Đa khoa th&agrave;nh phố; bến xe Vinh; c&ocirc;ng vi&ecirc;n trung t&acirc;m; ga Vinh; quảng trường HCM; &hellip;</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/Tong-quan-t-t-Vinh.jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Hơn nữa, T&amp;T Victoria l&agrave; dự &aacute;n căn hộ chung cư đầu ti&ecirc;n tại Vinh mang tới cho người d&acirc;n cơ hội sống đẳng cấp v&agrave; trải nghiệm đỉnh cao với 45 tiện &iacute;ch v&agrave; dịch vụ nội khu: trung t&acirc;m thương mại, khu sinh hoạt cộng đồng, khu ẩm thực, khu vui chơi trẻ em, khu đọc s&aacute;ch, ng&acirc;n h&agrave;ng, si&ecirc;u thị, chuỗi cửa h&agrave;ng thời trang cao cấp, đường dạo bộ, vườn sỏi thiền, khu tập thể thao ngo&agrave;i trời, vườn nghệ thuật chụp ảnh c&ugrave;ng hệ thống dịch vụ bảo vệ 24/7, quản l&yacute; căn hộ, chăm s&oacute;c c&acirc;y xanh, giặt l&agrave;, vệ sinh trong căn hộ&hellip;</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/T-T/t-t-victoria-sky-garden.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<em style=\"font-size: 14px;\">Phối cảnh s&acirc;n vườn tr&ecirc;n cao &ndash; Tận hưởng đặc quyền d&agrave;nh ri&ecirc;ng cho gia chủ</em><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/t-t-victoria-sky-garden.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;C&aacute;c căn hộ tại T&amp;T Victoria c&oacute; diện t&iacute;ch linh hoạt từ 47-100m2, mỗi căn 1 đến 3 ph&ograve;ng ngủ ph&ugrave; hợp nhu cầu đa dạng của kh&aacute;ch h&agrave;ng. Chất lượng x&acirc;y dựng vượt trội, bố tr&iacute; kh&ocirc;ng gian sinh hoạt hiện đại, th&ocirc;ng minh, thiết kế th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n sẽ khiến cho căn hộ đẳng cấp của bạn tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n v&agrave; gi&oacute; trời. T&amp;T Victoria &ndash; Nơi chắp c&aacute;nh hạnh ph&uacute;c v&agrave; hiện thực h&oacute;a giấc mơ về tổ ấm an cứ l&yacute; tưởng cho gia đ&igrave;nh bạn.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/T-T/phoi-canh-can-ho%20-t-t-3.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<em style=\"font-size: 14px;\">Phối cảnh minh họa nội thất ph&ograve;ng kh&aacute;ch tại căn hộ T&amp;T Victoria</em><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/phoi-canh-phong-khach.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với những ưu điểm vượt trội, ngay từ khi mở b&aacute;n ch&iacute;nh thức, dự &aacute;n đ&atilde; trở th&agrave;nh một t&acirc;m điểm bất động sản nhận được đ&ocirc;ng đảo người d&acirc;n Nghệ An v&agrave; c&aacute;c địa phương l&acirc;n cận quan t&acirc;m, ch&agrave;o đ&oacute;n. Đặc biệt, từ khi khu căn hộ mẫu v&agrave; văn ph&ograve;ng b&aacute;n h&agrave;ng dự &aacute;n khai trương, ước t&iacute;nh trung b&igrave;nh mỗi ng&agrave;y c&oacute; h&agrave;ng chục lượt kh&aacute;ch tới tham quan khu nh&agrave; mẫu v&agrave; thực hiện giao dịch.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#008080;\"><em><strong>T &amp; T Victoria TP Vinh - &quot;Trải nghiệm đỉnh cao, sống s&agrave;nh đ&uacute;ng điệu&quot;</strong></em></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>--------------------------------------------------------------------------</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Xin cảm ơn quý khách!</strong></span></p>', 859, 1, 1, 'T & T Victoria Vinh dự án HOT bậc nhất thành Vinh, nổi trội và sang trọng đỉnh cao của cuộc sống. Quà tặng đặc biệt tháng ngâu', 24, NULL, '0', '1-9-2017', '5-12-2017'),
(37, 11, 'admin', '8 lý do khiến chung cư đang cho nhà mặt đất ra rìa.', '8 lý do khiến chung cư đang cho nhà mặt đất ra rìa', '8-ly-do-khien-chung-cu-dang-cho-nha-mat-dat-ra-ria-', '/public/upload/images/T-T/t-va-t-vinh-cao-28-tang.jpg', 'Nhà chung cư đang trở thành xu hướng của cuộc sống hiện đại. Vậy đâu là lý do khiến nhà chung cư \"đánh bật\" nhà đất thổ cư trong lựa chọn của giới trẻ...', '<p>\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; &nbsp;Nh&agrave; chung cư đang trở th&agrave;nh xu hướng của cuộc sống hiện đại. Vậy đ&acirc;u l&agrave; l&yacute; do khiến nh&agrave; chung cư &quot;đ&aacute;nh bật&quot; nh&agrave; đất thổ cư trong lựa chọn của giới trẻ hiện nay.</strong></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Điều đầu ti&ecirc;n, chung cư hiện nay sở hữu c&aacute;c tiện &iacute;ch ngay trong khu d&acirc;n cư vượt trội so với những nh&agrave; phố</strong></em>. Cư d&acirc;n sống tại đ&acirc;y c&oacute; thể thừa hưởng c&aacute;c tiện &iacute;ch như hồ bơi, si&ecirc;u thị, c&aacute;c dịch vụ ăn uống, thể thao, l&agrave;m đẹp&hellip;ngay tại khu TTTM. Nhiều khu chung cư hiện đại c&ograve;n được đầu tư c&aacute;c khu vui chơi v&agrave; s&acirc;n chơi cho trẻ nhỏ v&ocirc; c&ugrave;ng tiện &iacute;ch.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Thứ hai, sống ở chung cư bạn sẽ c&oacute; kh&ocirc;ng gian y&ecirc;n tĩnh v&agrave; ri&ecirc;ng tư</strong></em>. Bạn kh&ocirc;ng bị l&agrave;m ồn hay quấy rầy bởi những người h&agrave;ng x&oacute;m của m&igrave;nh. Việc kh&oacute;a k&iacute;n cửa v&agrave; bật m&aacute;y điều h&ograve;a đ&atilde; l&agrave;m giảm đến 90% tiếng ồn từ b&ecirc;n ngo&agrave;i.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Ưu điểm thứ ba, đ&oacute; l&agrave; kh&acirc;u vệ sinh c&ocirc;ng cộng, ở c&aacute;c khu chung cư lớn ng&agrave;y ng&agrave;y nay thường rất sạch sẽ v&agrave; trong l&agrave;nh.</strong></em> C&ocirc;ng t&aacute;c vệ sinh lu&ocirc;n được thực hiện li&ecirc;n tục trong khu vực như lau dọn vệ sinh tất cả c&aacute;c khu vực c&ocirc;ng cộng, cắt tỉa, chăm s&oacute;c c&acirc;y cối, ph&ograve;ng ngừa c&aacute;c loại c&ocirc;n tr&ugrave;ng&hellip;l&agrave; ưu điểm thứ 3 m&agrave; nhiều người bị thuyết phục khi quyết định chọn nh&agrave; chung cư l&agrave;m tổ ấm.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/phoi-canh-can-ho%20-t-t-3.jpg\" style=\"width: 600px; height: 396px;\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/files/t-t-victoria.jpg\" style=\"width: 1348px; height: 431px;\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Thứ tư, c&aacute;c căn hộ chung cư đều c&oacute; nơi bỏ r&aacute;c c&ocirc;ng cộng ở mỗi tầng. </strong></em>Sau đ&oacute; r&aacute;c sẽ được xử l&iacute; tại hầm đổ r&aacute;c của t&ograve;a nh&agrave;. Bạn sẽ kh&ocirc;ng c&ograve;n thấy cảnh những th&ugrave;ng r&aacute;c đầy vương v&atilde;i tại lối đi, trước cửa ch&iacute;nh của c&aacute;c hộ gia đ&igrave;nh như ở nh&agrave; phố.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Thứ năm, an ninh v&agrave; an to&agrave;n:</strong></em> Dường như ở tất cả c&aacute;c dự &aacute;n chung cư, căn hộ được camera an ninh quan s&aacute;t 24/24, đội ngũ bảo vệ được huấn luyện với kĩ năng d&agrave;y dặn&hellip; Cho n&ecirc;n trường hợp bị trộm cướp l&agrave; rất h&atilde;n hữu. Ngược lại nếu ở nh&agrave; phố, việc đi chơi xa nhiều ng&agrave;y th&igrave; đ&oacute; c&oacute; thể l&agrave; một rủi ro rất lớn cho ng&ocirc;i nh&agrave; của bạn.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/t-t-phoi-canh-phong-ngu.jpg\" style=\"width: 600px; height: 374px;\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Lợi &iacute;ch thứ 6 khi sống trong khu c&aacute;c khu chung cư l&agrave; bạn ho&agrave;n to&agrave;n tho&aacute;t khỏi cảnh sống chung với bụi v&agrave; m&ugrave;i xăng xe độc hại trong nh&agrave;.</strong></em> Nếu bạn mua nh&agrave; phố th&igrave; hầu hết đều phải lấy diện t&iacute;ch ph&ograve;ng kh&aacute;ch l&agrave;m nơi để xe. Đặc biệt ở những con hẻm nhỏ, th&igrave; hầu như bạn phải để xe trong nh&agrave; li&ecirc;n tục.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Thứ 7, kh&ocirc;ng gian sống tho&aacute;ng đ&atilde;ng:</strong></em> Nếu sống trong c&aacute;c căn nh&agrave; phố, đặc biệt l&agrave; c&aacute;c nh&agrave; trong ng&otilde; th&igrave; hầu như nh&agrave; bạn chỉ c&oacute; một mặt tho&aacute;ng. Tuy nhi&ecirc;n, khi sống ở chung cư th&igrave; kh&aacute;c, m&ocirc;i trường sống th&ocirc;ng tho&aacute;ng hơn v&agrave; lu&ocirc;n tr&agrave;n ngập &aacute;nh s&aacute;ng v&agrave; kh&iacute; trời. Ở tr&ecirc;n cao kh&ocirc;ng kh&iacute; cũng đỡ &ocirc; nhiễm hơn v&agrave; luồng gi&oacute; sẽ len lỏi khắp ng&ocirc;i nh&agrave;. Tạo thuận lợi khi bạn muốn c&oacute; một ch&uacute;t kh&ocirc;ng kh&iacute; trong l&agrave;nh. Đ&acirc;y ch&iacute;nh l&agrave; lợi &iacute;ch nữa của chung cư m&agrave; bạn sẽ kh&ocirc;ng c&oacute; được khi sống ở nh&agrave; mặt đất.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><em><strong>&nbsp; &nbsp;Cuối c&ugrave;ng, gi&aacute; tiền của chung cư:</strong></em> Điều quan trọng nhất l&agrave; nếu so chi ph&iacute; bỏ tiền để mua một căn hộ chung cư v&agrave; một ng&ocirc;i nh&agrave; phố c&ugrave;ng vị tr&iacute; th&igrave; bạn n&ecirc;n chọn mua chung cư l&agrave; tốt nhất bởi th&ocirc;ng thường với c&ugrave;ng mức tiền mua một căn nh&agrave; trong ng&otilde; trong khu vực nội th&agrave;nh th&igrave; bạn cũng c&oacute; thể mua được một căn chung cư hạng trung với nội thất ho&agrave;n thiện v&agrave; tiện nghi.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; &nbsp;Ch&iacute;nh v&igrave; những lợi &iacute;ch thiết thực tr&ecirc;n m&agrave; chung cư ng&agrave;y nay đang trở th&agrave;nh lựa chọn số một của giới trẻ. Sống ở chung cư với đầy đủ tiện &iacute;ch, được hưởng kh&ocirc;ng gian trong l&agrave;nh, m&ocirc;i trường sống hiện đại.... nhất định cuộc sống của bạn sẽ trở n&ecirc;n th&uacute; vị v&agrave; vi&ecirc;n m&atilde;n hơn rất nhiều.</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">--------------------------------------------------------------------------</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Xin cảm ơn quý khách!</span></strong></p>', 714, 1, 1, 'Nhà chung cư đang trở thành xu hướng của cuộc sống hiện đại. Vậy đâu là lý do khiến nhà chung cư', 0, NULL, '0', '26-9-2017', '5-12-2017');
INSERT INTO `tbl_news` (`id`, `cat_id`, `user`, `name`, `title`, `slug`, `img`, `detail`, `content`, `hits`, `status`, `hot`, `description`, `desc_size`, `keywords`, `robot`, `date`, `date_up`) VALUES
(38, 11, 'admin', 'Kinh nghiệm và những lưu ý khi mua căn hộ chung cư.', 'Kinh nghiệm và những lưu ý khi mua căn hộ chung cư', 'kinh-nghiem-va-nhung-luu-y-khi-mua-can-ho-chung-cu-', '/public/upload/images/T-T/phoi-canh-can-ho%20-t-t-3.jpg', 'Kinh nghiệm thực tế dành cho người chuẩn bị mua căn hộ chung cư và những lưu ý cần phải biết trước khi quyết định mua căn hộ chung cư, cách chọn...', '<p>\r\n	<em><strong><span style=\"font-size:14px;\">&nbsp; &nbsp;Kinh nghiệm thực tế d&agrave;nh cho người chuẩn bị mua căn hộ chung cư v&agrave; những lưu &yacute; cần phải biết trước khi quyết định mua căn hộ chung cư, c&aacute;ch chọn mua căn hộ chung cư, những rủi ro khi mua căn hộ c&aacute;ch ph&ograve;ng tr&aacute;nh những rủi ro kh&ocirc;ng đ&aacute;ng c&oacute;.</span></strong></em></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Xu hướng hiện nay nhiều hộ gia đ&igrave;nh trẻ lựa chọn căn hộ chung cư thay v&igrave; mua đất l&agrave;m nh&agrave;, lợi thế của căn hộ chung cư l&agrave; cơ sở hạ tầng sẵn c&oacute;, chỉ cần dọn về ở ngay trong khi gi&aacute; cả lại thấp hơn so với việc mua nh&agrave; đất. Nhưng kh&ocirc;ng phải ai cũng biết v&agrave; lựa chọn được một căn hộ chung cư ưng &yacute;. <strong>Dưới đ&acirc;y m&igrave;nh sẽ tổng hợp những kinh nghiệm thực tế từ những người đ&atilde; từng mua căn hộ chung cư.</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/eurowindow/tong-quan-eurowindown-nghe-an.png\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Tr&ecirc;n thị trường hiện nay c&oacute; kh&aacute; nhiều dự &aacute;n căn hộ chung cư đ&atilde; v&agrave; đang triển khai. Điều n&agrave;y cho ph&eacute;p kh&aacute;ch h&agrave;ng c&oacute; th&ecirc;m nhiều cơ hội lựa chọn nhưng mặt kh&aacute;c cũng khiến họ bối rồi v&igrave; kh&ocirc;ng biết dự &aacute;n n&agrave;o ph&ugrave; hợp nhất với khả năng t&agrave;i ch&iacute;nh của m&igrave;nh trong khi vẫn cung cấp đầy đủ c&aacute;c tiện &iacute;ch thiết yếu. Kh&ocirc;ng phải ai cũng c&oacute; kinh nghiệm mua nh&agrave; như chuy&ecirc;n gia, v&igrave; vậy nếu bạn mới lần đầu ti&ecirc;n đi mua nh&agrave; th&igrave; cần dựa theo những ti&ecirc;u chuẩn như sau:</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><u><span style=\"font-size:16px;\"><strong>I. Lựa chọn căn hộ c&oacute; vị tr&iacute; đẹp.</strong></span></u><br />\r\n	&nbsp; &nbsp;Như một nh&agrave; đầu tư bất động sản nổi tiếng đ&atilde; từng tiết lộ: <span style=\"color:#ff0000;\"><em><strong>&ldquo;Ba yếu tố quan trọng nhất của một bất động sản đ&oacute; ch&iacute;nh l&agrave; &ldquo; vị tr&iacute;, vị tr&iacute; v&agrave; vị tr&iacute; &rdquo;</strong></em></span>.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">1. Khoảng c&aacute;ch v&agrave; thời gian.</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Cụ thể l&agrave; khoảng c&aacute;ch từ dự &aacute;n đến trung t&acirc;m th&agrave;nh phố, c&aacute;c dự &aacute;n c&aacute;ch trung t&acirc;m dưới 15 ph&uacute;t chạy xe thường được ưa th&iacute;ch nhất v&agrave; b&aacute;n chạy nhất so với tất cả c&aacute;c dự &aacute;n kh&aacute;c. Tuy nhi&ecirc;n, khoảng c&aacute;ch kh&ocirc;ng phải l&agrave; tất cả. Nếu dự &aacute;n ở v&ugrave;ng ngoại &ocirc;, nhưng c&oacute; hạ tầng tốt, giao th&ocirc;ng thuận tiện, thiết kế đẹp th&igrave; cũng vẫn c&oacute; thể h&uacute;t kh&aacute;ch.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">2. Phong thuỷ</span></strong></p>\r\n<p>\r\n	<span style=\"font-size: 14px;\">&nbsp; &nbsp;Phong thủy tốt l&agrave; những nơi c&oacute; địa thế &ldquo; tiền thủy, hậu sơn &rdquo;, hoặc đơn giản hơn l&agrave; &ldquo; gần s&ocirc;ng, hồ &rdquo;. Ch&iacute;nh v&igrave; lẽ đ&oacute;, c&oacute; kh&ocirc;ng &iacute;t chủ đầu tư như Vingroup, Ph&uacute; Mỹ Hưng&hellip; đ&atilde; mạnh tay đầu tư trong khu đ&ocirc; thị của m&igrave;nh những d&ograve;ng s&ocirc;ng, ngọn n&uacute;i nh&acirc;n tạo. Sự đầu tư n&agrave;y thực sự kh&ocirc;ng uổng ph&iacute; v&agrave; đ&atilde; l&agrave;m gia tăng gi&aacute; trị cho dự &aacute;n l&ecirc;n bội phần. Nếu ng&acirc;n s&aacute;ch kh&ocirc;ng cho ph&eacute;p bạn mơ mộng, th&igrave; với kinh nghiệm mua nh&agrave; đất n&agrave;y bạn ho&agrave;n to&agrave;n c&oacute; thể tự lựa chọn một dự &aacute;n c&oacute; địa thế đẹp với một mức gi&aacute; hợp l&yacute;.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">3. M&ocirc;i trường</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;V&igrave; căn nh&agrave; bạn sắp lựa chọn c&oacute; thể l&agrave; nơi bạn d&agrave;nh cả phần đời của m&igrave;nh v&agrave; thậm ch&iacute; của cả con ch&aacute;u ở đ&oacute;, n&ecirc;n cần phải d&agrave;nh thời gian nghi&ecirc;n cứu thật kỹ m&ocirc;i trường sống ở đ&oacute;. Những nơi c&oacute; m&ocirc;i trường k&eacute;m như: &ocirc; nhiễm tiếng ồn, r&aacute;c thải, mật độ d&acirc;n cư đ&ocirc;ng&hellip; sẽ ảnh hưởng nghi&ecirc;m trọng đến chất lượng sống của bạn v&agrave; gia đ&igrave;nh, c&oacute; khi sẽ khiến bạn &ldquo;bỏ của chạy lấy người&rdquo;. Đ&acirc;y l&agrave; những yếu tố c&oacute; ảnh hưởng l&acirc;u d&agrave;i n&ecirc;n bạn nhất định phải t&igrave;m hiểu v&agrave; c&acirc;n nhắc kỹ lưỡng v&agrave; l&agrave; kinh nghiệm mua nh&agrave; đất m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">4. Hạ tầng x&atilde; hội</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ch&uacute;ng ta cần một ng&ocirc;i nh&agrave; để ở. Nhưng hơn thế, ch&uacute;ng ta cần một m&ocirc;i trường tốt để sinh sống. Ở nơi đ&oacute; c&oacute; đầy đủ mọi tiện &iacute;ch để phục vụ cho đời sống của gia đ&igrave;nh bạn. Đ&oacute; l&agrave; trường học, si&ecirc;u thị, bệnh viện, c&ocirc;ng vi&ecirc;n&hellip; C&aacute;c dự &aacute;n lớn thường được đầu tư đồng bộ n&ecirc;n cư d&acirc;n ở đ&oacute; c&oacute; thể t&igrave;m thấy mọi dịch vụ quanh nơi m&igrave;nh sinh sống. C&aacute;c dự &aacute;n nhỏ bị hạn chế ở mặt n&agrave;y.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Tuy nhi&ecirc;n, theo kinh nghiệm của những người mua nh&agrave; chia sẻ th&igrave; với t&igrave;nh h&igrave;nh thị trường cạnh tranh như ng&agrave;y nay, ở c&aacute;c dự &aacute;n nhỏ, bạn vẫn c&oacute; thể thấy sự hiện diện của trường mầm non, bể bơi, trung t&acirc;m thể thao, thậm ch&iacute; l&agrave; ph&ograve;ng kh&aacute;m, rạp chiếu phim&hellip; ngay b&ecirc;n trong t&ograve;a nh&agrave; chung cư.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">5. Hạ tầng kỹ thuật</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Trong tất cả c&aacute;c yếu tố về hạ tầng kỹ thuật b&ecirc;n ngo&agrave;i, yếu tố về đường giao th&ocirc;ng l&agrave; quan trọng nhất. Những dự &aacute;n c&oacute; vị tr&iacute; đắc địa, giao th&ocirc;ng thuận tiện thường c&oacute; gi&aacute; cao hơn v&agrave; thanh khoản tốt hơn so với c&aacute;c dự &aacute;n kh&aacute;c.</span></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/T%20%26%20T/phoi-canh-phong-khach.jpg\" style=\"width: 600px; height: 396px;\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/T-T/ANH-BIA.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><u><span style=\"font-size:16px;\"><strong>II. 5 điểm cần ch&uacute; &yacute; khi chọn mua chung cư.</strong></span></u><br />\r\n	&nbsp; &nbsp;Với chất lượng sống ng&agrave;y c&agrave;ng n&acirc;ng cao, nh&agrave; cửa kh&ocirc;ng c&ograve;n l&agrave; nơi để tr&uacute; ngụ, đ&oacute; c&ograve;n l&agrave; nơi gi&uacute;p gia đ&igrave;nh bạn t&igrave;m lại những gi&acirc;y ph&uacute;t b&igrave;nh y&ecirc;n sau một ng&agrave;y l&agrave;m việc mệt nhọc. Mang trong m&igrave;nh đầy đủ c&aacute;c tiện &iacute;ch cần thiết, căn hộ chung cư thực sự l&agrave; lựa chọn hấp dẫn đặc biệt với c&aacute;c cặp vợ chồng trẻ. Khi lựa chọn một căn hộ chung cư, bạn cần c&acirc;n nhắc những yếu tố sau đ&acirc;y:</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">1. Hệ thống hạ tầng</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Hệ thống hạ tầng kỹ thuật trong khu chung cư bao gồm: đường, h&egrave;, s&acirc;n vườn, cấp tho&aacute;t nước, điện chiếu s&aacute;ng&hellip; To&agrave;n bộ hệ thống n&agrave;y cần được đầu tư đồng bộ v&agrave; ho&agrave;n chỉnh v&igrave; đ&acirc;y l&agrave; y&ecirc;u cầu tối thiểu với bất kỳ dự &aacute;n chung cư n&agrave;o.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">2. Chỗ đỗ xe</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;B&aacute;o ch&iacute; đ&atilde; từng đưa tin về rất nhiều tranh chấp giữa kh&aacute;ch h&agrave;ng v&agrave; chủ đầu tư xung quanh c&acirc;u chuyện ph&iacute; gửi xe. Để tr&aacute;nh đi v&agrave;o vết xe đổ n&agrave;y, bạn cần d&agrave;nh thời gian nghi&ecirc;n cứu kỹ hợp đồng v&agrave; thảo luận với chủ đầu tư về chủ đề n&agrave;y. B&ecirc;n cạnh ph&iacute; gửi xe, bạn cũng n&ecirc;n quan t&acirc;m đến ti&ecirc;u chuẩn gửi xe ( mỗi căn hộ được gửi tối đa bao nhi&ecirc;u xe m&aacute;y, bao nhi&ecirc;u &ocirc; t&ocirc;&hellip;). Tất cả những th&ocirc;ng tin n&agrave;y cần được l&agrave;m r&otilde; trong hợp đồng mua b&aacute;n. Kinh nghiệm mua chung cư n&agrave;y sẽ gi&uacute;p bạn tr&aacute;nh được những rắc rối kh&ocirc;ng đ&aacute;ng c&oacute;, đồng thời l&agrave; một yếu tố m&agrave; bạn c&oacute; thể mang ra đ&agrave;m ph&aacute;n với chủ đầu tư trong qu&aacute; tr&igrave;nh thương thảo hợp đồng.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">3. Hệ thống b&aacute;o ch&aacute;y v&agrave; an ninh</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Một trong những kh&iacute;a cạnh m&agrave; mọi người thường c&acirc;n nhắc trước khi chọn mua một căn hộ chung cư l&agrave; vấn đề ph&ograve;ng ch&aacute;y chữa ch&aacute;y. Tuy kh&ocirc;ng nhiều nhưng cũng đ&atilde; từng c&oacute; những vụ hỏa hoạn ở chung cư cao tầng g&acirc;y khiếp sợ cho cư d&acirc;n. Kh&ocirc;ng ai c&oacute; thể lường hết được mọi bất trắc, v&igrave; vậy ch&uacute;ng ta cần c&oacute; sự ph&ograve;ng bị chu đ&aacute;o. Bạn cần hỏi r&otilde; về hệ thống ph&ograve;ng ch&aacute;y chữa ch&aacute;y ở trong t&ograve;a nh&agrave; v&agrave; tự t&igrave;m hiểu xem đ&oacute; c&oacute; phải l&agrave; hệ thống chất lượng kh&ocirc;ng?</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp;B&ecirc;n cạn &nbsp;h đ&oacute;, để đảm bảo an ninh cho t&ograve;a nh&agrave;, một số chủ đầu tư c&ograve;n trang bị th&ecirc;m hệ thống camera quan s&aacute;t an ninh, kiểm tra ra v&agrave;o trong khu nh&agrave;. Tất nhi&ecirc;n, những chi ph&iacute; n&agrave;y sẽ được t&iacute;nh v&agrave;o gi&aacute; mua nh&agrave;.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">4. C&ocirc;ng tr&igrave;nh tiện &iacute;ch c&ocirc;ng cộng</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Căn hộ chung cư m&agrave; bạn mua sẽ l&agrave; nơi bạn sẽ gắn b&oacute; cả cuộc đời v&igrave; vậy cần c&acirc;n nhắc v&agrave; chọn lựa kỹ c&agrave;ng. Kinh nghiệm cho thấy: Một nh&agrave; chung cư cao tầng th&ocirc;ng thường sẽ c&oacute; những tiện &iacute;ch sau đ&acirc;y: nh&agrave; sinh hoạt cộng đồng, nh&agrave; tập thể thao, bể bơi, s&acirc;n chơi trẻ em, c&ocirc;ng vi&ecirc;n v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh tiện &iacute;ch phục vụ nhu cầu sinh hoạt văn ho&aacute; thể thao kh&aacute;c.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với c&aacute;c dự &aacute;n nh&agrave; ở thu nhập thấp th&igrave; ngo&agrave;i khu vực ở, c&aacute;c cư d&acirc;n cũng được hưởng hệ thống tiện &iacute;ch hạ tầng x&atilde; hội được đầu tư đồng bộ từ trường mầm non, nh&agrave; trẻ, khu thể thao, c&acirc;y xanh cảnh quan, b&atilde;i đỗ xe đến c&aacute;c dịch vụ an ninh trật tự, vệ sinh m&ocirc;i trường, ph&ograve;ng chống ch&aacute;y nổ&hellip;</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">5. Dịch vụ đi k&egrave;m</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Trong c&aacute;c khu chung cư, bạn cũng sẽ t&igrave;m thấy c&aacute;c dịch vụ đi k&egrave;m như dịch vụ ăn uống, mua sắm, chăm s&oacute;c sức khỏe, l&agrave;m đẹp, thể dục thể thao&hellip; C&aacute;c dịch vụ n&agrave;y gi&uacute;p mang lại tiện nghi cho cuộc sống cư d&acirc;n v&agrave; l&agrave;m tăng gi&aacute; trị cho dự &aacute;n.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/Tong-quan-t-t-Vinh.jpg\" style=\"width: 600px; height: 367px;\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><u><span style=\"font-size:16px;\"><strong>III. Ti&ecirc;u chuẩn căn hộ chung cư.</strong></span></u><br />\r\n	&nbsp; &nbsp;N&oacute;i về kinh nghiệm mua căn hộ chung cư: tr&ecirc;n thị trường hiện nay ch&uacute;ng ta c&oacute; nh&agrave; ở cho người thu nhập thấp ( dưới 10 triệu/m2 ), nh&agrave; ở gi&aacute; rẻ ( 13-15 triệu/m2), nh&agrave; ở thương mại&nbsp; ( dưới 20 triệu/m2 ) v&agrave; nh&agrave; ở cao cấp ( tr&ecirc;n 20 triệu/m2 ). Ngo&agrave;i gi&aacute; cả, ti&ecirc;u chuẩn một căn hộ c&ograve;n được x&eacute;t đến dựa tr&ecirc;n c&aacute;c yếu tố sau đ&acirc;y:</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">1. Thiết kế b&ecirc;n trong.&nbsp;</span></strong><span style=\"font-size: 14px;\">Căn hộ c&oacute; mấy ph&ograve;ng ngủ, ph&ograve;ng vệ sinh, c&oacute; cửa sổ v&agrave; ban c&ocirc;ng kh&ocirc;ng ?</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>2. Hệ thống kỹ thuật trong căn hộ c&oacute; đầy đủ v&agrave; hợp l&yacute; kh&ocirc;ng?</strong> Trong căn hộ c&oacute; những thiết bị g&igrave;? C&aacute;c điểm chờ để đấu nối c&aacute;c thiết bị kh&aacute;c c&oacute; đầy đủ kh&ocirc;ng?</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>3. H&agrave;nh lang b&ecirc;n ngo&agrave;i c&oacute; rộng kh&ocirc;ng?</strong> Cầu thang bộ v&agrave; thang m&aacute;y c&oacute; thuận tiện cho người sử dụng kh&ocirc;ng?</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Về phong thủy, do đặc điểm nh&agrave; chung cư thuộc loại nh&agrave; đa sở hữu, việc xem hướng nh&agrave; được giới hạn trong phạm vi từng căn hộ ri&ecirc;ng lẻ. Khi đ&oacute;, hướng nh&agrave; được x&aacute;c định l&agrave; hướng cửa ra v&agrave;o của căn hộ, chứ kh&ocirc;ng phải l&agrave; hướng cổng của cả t&ograve;a nh&agrave;. Tổng hợp kinh nghiệm mua nh&agrave; chung cư th&igrave; c&aacute;c căn hộ c&oacute; hướng ban c&ocirc;ng l&agrave; Đ&ocirc;ng Nam thường được b&aacute;n hết trước ti&ecirc;n mặc d&ugrave; c&oacute; gi&aacute; cao hơn. Đ&oacute; l&agrave; do những căn n&agrave;y được hưởng nắng ấm v&agrave;o buổi s&aacute;ng, tr&aacute;nh nắng gắt của buổi chiều, được đ&oacute;n gi&oacute; m&aacute;t trong m&ugrave;a h&egrave; trong khi vẫn tr&aacute;nh được c&aacute;i r&eacute;t của m&ugrave;a đ&ocirc;ng. ( xem b&agrave;i 5 quy tắc v&agrave;ng chọn căn hộ theo phong thủy ).</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/phoi-canh-can-ho%20-t-t-4.jpg\" style=\"width: 600px; height: 442px;\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><u><span style=\"font-size:16px;\"><strong>IV. Hợp đồng mua b&aacute;n căn hộ chung cư.</strong></span></u><br />\r\n	&nbsp; &nbsp;Th&ocirc;ng thường, hợp đồng mua b&aacute;n thường do b&ecirc;n b&aacute;n đề ra n&ecirc;n sẽ c&oacute; lợi nhiều cho b&ecirc;n b&aacute;n. B&ecirc;n mua thường c&oacute; nhiều tr&aacute;ch nhiệm v&agrave; nghĩa vụ trong khi quyền lợi lại &iacute;t hơn đ&aacute;ng kể. L&agrave; người mua, bạn cần ch&uacute; &yacute; đến thực tế n&agrave;y v&agrave; d&agrave;nh sự quan t&acirc;m đ&aacute;ng kể cho n&oacute; trước khi đặt b&uacute;t k&yacute; hợp đồng. Cụ thể bạn cần lưu &yacute; đến những kinh nghiệm mua chung cư dưới đ&acirc;y</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">1. Gi&aacute; cả v&agrave; điều kiện thanh to&aacute;n</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Đối với những dự &aacute;n đ&atilde; ho&agrave;n th&agrave;nh, gi&aacute; b&aacute;n c&oacute; thể x&aacute;c định rất dễ d&agrave;ng dựa tr&ecirc;n chất lượng căn hộ. Với c&aacute;c dự &aacute;n c&ograve;n dở dang, bạn cần y&ecirc;u cầu chủ đầu tư cam kết chất lượng c&ocirc;ng tr&igrave;nh v&agrave; thời điểm b&agrave;n giao, phụ lục đ&iacute;nh k&egrave;m cần m&ocirc; tả đầy đủ mức độ ho&agrave;n thiện của căn hộ v&agrave; c&aacute;c tiện &iacute;ch đi k&egrave;m.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">2. Quy định quyền lợi v&agrave; nghĩa vụ</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Trong hợp đồng mua b&aacute;n phải c&oacute; điều khoản qui định cụ thể về quyền lợi v&agrave; nghĩa vụ của b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n. Trong đ&oacute;, đặc biệt ch&uacute; &yacute; đến c&aacute;c qui định về quyền lợi v&agrave; nghĩa vụ của b&ecirc;n mua. Việc sửa đổi hợp đồng hiếm khi xảy ra, nhưng bạn cần y&ecirc;u cầu chủ đầu tư l&agrave;m r&otilde; những chỗ n&agrave;o chưa hiểu để trong trường hợp bạn kh&ocirc;ng chấp nhận theo y&ecirc;u cầu của họ th&igrave; bạn vẫn c&oacute; thể tạm bỏ qua dự &aacute;n n&agrave;y v&agrave; chờ một dự &aacute;n ph&ugrave; hợp hơn.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Hiện tại thị trường c&ograve;n tồn kho một số lượng căn hộ tương đối lớn n&ecirc;n nếu được trang bị kinh nghiệm mua nh&agrave; đầy đủ bạn ho&agrave;n to&agrave;n c&oacute; thể tự tin chọn cho m&igrave;nh một căn nh&agrave; đẹp v&agrave; hợp t&uacute;i tiền.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ngo&agrave;i ra,v&igrave; l&agrave; chủ một căn hộ trong dự &aacute;n, bạn c&oacute; những quyền nhất định v&agrave; những quyền n&agrave;y cần được ghi r&otilde; trong hợp đồng. Đ&oacute; l&agrave;: được sử dụng chỗ để xe, những tiện &iacute;ch c&ocirc;ng cộng, được lựa chọn nh&agrave; cung cấp dịch vụ về điện thoại, truyền h&igrave;nh, internet với mức ph&iacute; thỏa thuận; được y&ecirc;u cầu bảo h&agrave;nh, bảo tr&igrave; c&ocirc;ng tr&igrave;nh; được tự thay đổi, sửa chữa ho&agrave;n thiện căn hộ v&agrave; c&aacute;c quyền kh&aacute;c (nếu c&oacute;).</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/T%20%26%20T/phoi-canh-can-ho%20-t-t-3.jpg\" style=\"width: 600px; height: 396px;\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><u><span style=\"font-size:16px;\"><strong>V. Ph&iacute; quản l&yacute; chung cư.</strong></span></u></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ph&iacute; quản l&yacute; l&agrave; khoản chi ph&iacute; cho c&aacute;c hoạt động quản l&yacute;, vận h&agrave;nh nh&agrave; chung cư. T&ugrave;y v&agrave;o ti&ecirc;u chuẩn cửa dự &aacute;n ( cao cấp, th&ocirc;ng thường hay gi&aacute; rẻ thương giao động từ 4 000/2 - 8 000/m2), mỗi dự &aacute;n sẽ c&oacute; một mức thu kh&aacute;c nhau nhưng kh&ocirc;ng được vượt trần của nh&agrave; nước ( ngoại trừ trường hợp c&oacute; thỏa thuận ri&ecirc;ng giữa chủ đầu tư v&agrave; kh&aacute;ch h&agrave;ng ). Thực tế, cho thấy đ&atilde; c&oacute; rất nhiều vụ tranh chấp giữa c&aacute;c chủ đầu tư v&agrave; kh&aacute;ch h&agrave;ng xung quanh loại ph&iacute; n&agrave;y. Để tr&aacute;nh rơi v&agrave;o trường hợp n&agrave;y, bạn cần lưu &yacute; những điểm kinh nghiệm mua nh&agrave; dưới đ&acirc;y:</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">1. Điều khoản về quản l&yacute; v&agrave; sử dụng khu chung cư</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Bản quy định về quản l&yacute; v&agrave; sử dụng khu chung cư do chủ đầu tư x&aacute;c lập theo c&aacute;c quy định chung của Nh&agrave; nước v&agrave; theo y&ecirc;u cầu quản l&yacute; của từng loại chung cư. Nội dung của bản quy định n&agrave;y li&ecirc;n quan trực tiếp đến quyền lợi v&agrave; nghĩa vụ của người mua. Vậy n&ecirc;n, người mua cần phải y&ecirc;u cầu b&ecirc;n b&aacute;n cung cấp văn bản quy định về quản l&yacute; v&agrave; sử dụng khu chung cư trước khi k&yacute; kết hợp đồng để c&oacute; đầy đủ th&ocirc;ng tin v&agrave; l&agrave;m cơ sở quyết định việc mua căn hộ.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">2. Mức ph&iacute; quản l&yacute;</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;N&oacute;i chung mức ph&iacute; quản l&yacute; tỷ lệ thuận với gi&aacute; b&aacute;n căn hộ, với căn hộ cao cấp thường c&oacute; mức ph&iacute; quản l&yacute; cao hơn nhiều so với căn hộ b&igrave;nh d&acirc;n cho c&ugrave;ng một loại diện t&iacute;ch. Người mua nh&agrave; cần hỏi r&otilde; mức ph&iacute; quản l&yacute; trước khi đặt b&uacute;t k&yacute; hợp đồng để tr&aacute;nh những tranh chấp đ&aacute;ng tiếc sau n&agrave;y.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">3. Đơn vị quản l&yacute;</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Đơn vị quản l&yacute; chuy&ecirc;n nghiệp sẽ gi&uacute;p cuộc sống cư d&acirc;n an nh&agrave;n v&agrave; dễ chịu hơn. Bạn c&oacute; thể đ&aacute;nh gi&aacute; dễ d&agrave;ng chất lượng quản l&yacute; của một đơn vị cụ thể bằng c&aacute;ch t&igrave;m hiểu xem hiện giờ họ đang quản l&yacute; những dự &aacute;n n&agrave;o v&agrave; &yacute; kiến của cư d&acirc;n ở đ&oacute;. Kinh nghiệm mua chung cư cho thấy bạn kh&ocirc;ng n&ecirc;n đặt niềm tin v&agrave;o những đơn vị quản l&yacute; mới v&igrave; họ c&oacute; thể mắc phải sai lầm m&agrave; những đơn vị kh&aacute;c đ&atilde; từng trải qua. V&agrave; người chịu thiệt th&ograve;i kh&ocirc;ng ai kh&aacute;ch ch&iacute;nh l&agrave; c&aacute;c kh&aacute;ch h&agrave;ng.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Gi&aacute; trị chất lượng của một khu chung cư gắn liền với gi&aacute; trị chất lượng quản l&yacute; của đơn vị quản l&yacute; v&agrave; đ&oacute; cũng ch&iacute;nh l&agrave; một trong những ti&ecirc;u ch&iacute; quan trọng để người ti&ecirc;u d&ugrave;ng quyết định lựa chọn mua căn hộ chung cư.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size: 14px;\">--------------------------------------------------------------------------</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Xin cảm ơn quý khách!</span></strong></p>', 966, 1, 1, 'Kinh nghiệm và những lưu ý khi mua căn hộ chung cư, để chuẩn bị kĩ lưỡng tất cả những thông tin trước khi mua chung cư, tránh những rủi ro không đáng.', 0, NULL, '0', '26-9-2017', '5-12-2017'),
(48, 11, 'admin', 'Chung cư Cửa Tiền Home', NULL, 'chung-cu-cua-tien-home', '/public/upload/images/cua-tien-home/cua-tien-home.jpg', NULL, '<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">&nbsp; &nbsp;<strong>Dự &aacute;n Chung cư Cửa Tiền Home</strong> (<strong> KHU Đ&Ocirc; THỊ MỚI</strong> &nbsp;của TP.Vinh) tọa lạc ngay trong trung t&acirc;m th&agrave;nh phố, được thừa hưởng vẻ đẹp thơ mộng, quyến rũ, bầu kh&ocirc;ng kh&iacute; m&aacute;t l&agrave;nh, từ d&ograve;ng s&ocirc;ng Cửa Tiền. Đ&acirc;y cũng l&agrave; khu chung cư đầu ti&ecirc;n tại Nghệ An được đầu tư, thiết kế x&acirc;y dựng theo kiến tr&uacute;c c&aacute;c t&ograve;a nh&agrave; tại Singapore, mang lại sự phấn&nbsp; khởi, khơi dậy mong muốn kh&aacute;m ph&aacute; đến tất cả kh&aacute;ch h&agrave;ng với dự &aacute;n n&agrave;y.</span></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">&nbsp; &nbsp;Li&ecirc;n hệ ngay h&ocirc;m nay đ&ecirc;̉ được h&ocirc;̃ trợ chọn căn t&acirc;̀ng đẹp nh&acirc;́t và chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/CUATIENHOME%20OP1(1).jpg\" style=\"width: 800px; height: 256px;\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">&quot;Khu đ&ocirc; thị CỬA TIỀN HOME - Kh&ocirc;ng đơn thuần chỉ l&agrave; nơi ở.&quot;</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>GI&Aacute; CHỈ TỪ 565 triệu.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Tặng ngay 01 bếp từ v&agrave; bộ nồi cao cấp trị gi&aacute; #13,5 triệu đồng.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Dự &aacute;n đ&atilde; cất n&oacute;c &amp; Bàn giao nhà quý IV/ 2017.</strong></span></span></li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-size: 14px; color: rgb(0, 0, 0);\">--------------------------------</span></p>\r\n<p>\r\n	<strong><span style=\"color:#000000;\"><span style=\"font-size:14px;\">DIỆN T&Iacute;CH CĂN HỘ ĐA DẠNG:</span></span></strong></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>2 Ph&ograve;ng Ngủ+ 1WC &ndash; DIỆN T&Iacute;CH: 49M2.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>2 Ph&ograve;ng Ngủ + 2WC &ndash; DIỆN T&Iacute;CH: 57M2, 58M2, 63.5M2, 69,5M2.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>3 Ph&ograve;ng Ngủ + 2WC &ndash; DIỆN T&Iacute;CH: 73M2, 74M2.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Tầng 1 &ndash; 2, 18 &ndash; 19, thiết kế c&aacute;c căn hộ th&ocirc;ng tầng, Penhouse.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Tầng 3 &ndash; 17, c&aacute;c căn hộ tự nhi&ecirc;n.</strong></span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/hinh%205.png\" style=\"width: 800px; height: 564px;\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">--------------------------------</span></span></p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><strong><span style=\"color:#000000;\">ƯU ĐIỂM NỔI BẬT CỦA DỰ &Aacute;N:</span></strong></span></u></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>TIỆN &Iacute;CH tối ưu - DỊCH VỤ tối đa:</strong></span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Khoảng kh&ocirc;ng rộng giữa 2 t&ograve;a nh&agrave; được thiết kế v&ograve;i phun nước, c&acirc;y xanh, khu vui chơi cho trẻ em.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Sử dụng hệ thống thẻ từ cho c&aacute;c căn hộ.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Khu biệt thự, nh&agrave; liền kề, căn hộ th&ocirc;ng tầng gắn với nhiều loại dịch vụ: nh&agrave; trẻ, GYM, trung t&acirc;m thương mại, s&acirc;n b&oacute;ng đ&aacute;, bể bơi...</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Hệ thống s&acirc;n vườn, c&acirc;y xanh h&agrave;i h&ograve;a kết hợp với c&aacute;c tiện &iacute;ch dịch vụ kh&aacute;c nhau tạo cho cư d&acirc;n nơi đ&acirc;y c&oacute; một m&ocirc;i trường sống l&yacute; tưởng nhất, xanh nhất.</span></span></li>\r\n</ul>\r\n<p>\r\n	<strong><span style=\"color:#000000;\"><span style=\"font-size:14px;\">&nbsp; &nbsp;Dự &aacute;n nằm b&ecirc;n bờ s&ocirc;ng Cửa Tiền kh&ocirc;ng chỉ mang đến cho cư d&acirc;n sinh sống tại đ&acirc;y bầu kh&ocirc;ng kh&iacute; m&aacute;t mẻ, mang đến gi&aacute; trị thẩm mỹ cao, m&agrave; theo c&aacute;c chuy&ecirc;n gia phong thủy, vị tr&iacute; n&agrave;y c&ograve;n mang đến sự ph&aacute;t triển thịnh vượng về kinh tế, t&agrave;i ch&iacute;nh, sự nghiệp, c&ocirc;ng việc cho cư d&acirc;n sinh sống tại đ&acirc;y.</span></span></strong></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z632652838358_1ff4b52218381b8466dddc7577ec174d.jpg\" style=\"width: 800px; height: 534px;\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">--------------------------------</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u><span style=\"color:#000000;\">TỔNG QUAN DỰ &Aacute;N CỬA TIỀN HOME:</span></u></strong></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Vị trí đắc địa: Ngay trung t&acirc;m th&agrave;nh phố Vinh. B&ecirc;n cạnh cầu cửa Tiền 2.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Dự &aacute;n được thiết kế gồm 2 t&ograve;a nh&agrave;, mỗi t&ograve;a cao 19 tầng. Mỗi s&agrave;n gồm 19 căn hộ với diện t&iacute;ch v&agrave; thiết kế kh&aacute;c nhau.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Hướng cửa, hướng ban c&ocirc;ng thiết kế th&ocirc;ng minh để đ&oacute;n &aacute;nh s&aacute;ng, gi&oacute; m&aacute;t tự nhi&ecirc;n.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Thiết kế theo phong c&aacute;ch hiện đại của SINGAPORE.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Hệ thống 4 thang m&aacute;y hiện đại, 2 thang bộ v&agrave; tho&aacute;t hiểm, hệ thống b&aacute;o v&agrave; chữa ch&aacute;y tự động.</span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/TuanLam1.jpg\" style=\"width: 800px; height: 800px;\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">--------------------------------</span></span></p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><strong><span style=\"color:#000000;\">B&Agrave;N GIAO CĂN HỘ:</span></strong></span></u></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Cửa ch&iacute;nh v&agrave; cửa ph&ograve;ng ngủ l&agrave;m bằng gỗ c&ocirc;ng nghiệp chất lượng cao.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Nội thất nh&agrave; bếp: b&agrave;n đ&aacute; bếp.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Cửa nh&agrave; vệ sinh: L&agrave;m bằng gỗ c&ocirc;ng nghiệp hoặc bằng nh&ocirc;m k&iacute;nh đạt chuẩn.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Nội thất nh&agrave; vệ sinh: Bồn tắm, v&ograve;i hoa sen, chậu rửa&hellip;</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\">S&agrave;n gạch.</span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">--------------------------------</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u><span style=\"color:#000000;\">HỖ TRỢ:</span></u></strong></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Ng&acirc;n h&agrave;ng hỗ trợ vốn: TPBank, OceanBank, Seabank ( HỖ TRỢ VAY L&Ecirc;N ĐẾN 70% ).</strong></span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Thời gian vay tối đa: 15 năm.</strong></span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">--------------------------------</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u><span style=\"color:#000000;\">BQL Dự &aacute;n:</span></u></strong></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Chủ đầu tư uy tín:</strong> C&ocirc;ng ty cổ phần DATANOL.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Đơn vị thi c&ocirc;ng hàng đ&acirc;̀u: </strong>C&ocirc;ng ty Cổ Phần đầu tư &amp; TM Dầu Kh&iacute;.</span></span></li>\r\n	<li>\r\n		<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>Đơn vị ph&acirc;n phối ch&iacute;nh thức: </strong>C&ocirc;ng ty cổ phần dịch vụ v&agrave; địa ốc Đất Xanh Miền Bắc.</span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">--------------------------------</span></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></span></p>\r\n<p>\r\n	<strong><span style=\"color:#000000;\"><span style=\"font-size:14px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></span></strong></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>ĐĂNG K&Yacute; NHẬN BẢNG GI&Aacute; CH&Iacute;NH THỨC: </strong></span><a href=\"https://goo.gl/s1kkdf\"><span style=\"color:#000000;\">https://goo.gl/s1kkdf</span></a><span style=\"color:#000000;\">.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266</strong><br />\r\n	<strong>Email:</strong> bds37.datxanhnghean@gmail.com<br />\r\n	<strong>Website: </strong></span><a href=\"http://www.bdsnghetinh.net/\"><span style=\"color:#000000;\">http://www.bdsnghetinh.net/</span></a></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\">Xin cảm ơn quý khách!</span></span></p>', 775, 1, 0, NULL, 150, NULL, '0', '9-12-2017', NULL),
(41, 15, 'admin', 'Những nguyên tắc vàng trong phong thủy cho ngôi nhà.', 'Những nguyên tắc vàng trong phong thủy cho ngôi nhà.', 'nhung-nguyen-tac-vang-trong-phong-thuy-cho-ngoi-nha-', '/public/upload/images/T-T/phoi-canh-can-ho%20-t-t-3.jpg', 'Cách sắp xếp và bài trí nhà ở sao cho phù hợp với Phong Thủy là cả một một nghệ thuật. Tuy nhiên, nó có những quy tắc cơ bản mà bạn phải tuân theo để tránh những sai lầm ...', '<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;C&aacute;ch sắp xếp v&agrave; b&agrave;i tr&iacute; nh&agrave; ở sao cho ph&ugrave; hợp với Phong Thủy l&agrave; cả một một nghệ thuật. Tuy nhi&ecirc;n, n&oacute; c&oacute; những quy tắc cơ bản m&agrave; bạn phải tu&acirc;n theo để tr&aacute;nh những sai lầm c&oacute; thể ph&aacute; hỏng kh&ocirc;ng gian sống của bạn.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Dưới đ&acirc;y l&agrave; những nguy&ecirc;n tắc ngắn gọn, cơ bản nhưng cực kỳ quan trọng trong thiết kế nh&agrave; ở ph&ugrave; hợp với nguy&ecirc;n tắc Phong Thủy m&agrave; bạn c&oacute; thể tham khảo để mang đến cho gia đ&igrave;nh bạn một cuộc sống tốt đẹp hơn.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cs/2ccd0f69fccf109149de.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/4b35861e74b898e6c1a9.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">M&agrave;u sắc của trần nh&agrave; phải được sử dụng m&agrave;u nhạt hơn so với bốn bức tường xung quanh.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Nền nh&agrave; nhất thiết phải bằng phẳng, kh&ocirc;ng được lồi l&otilde;m ở mọi chỗ như ph&ograve;ng kh&aacute;ch, ph&ograve;ng tắm, gian bếp, lối đi&hellip;</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Ph&ograve;ng vệ sinh kh&ocirc;ng n&ecirc;n đặt ở vị tr&iacute; liền kề hoặc đối diện với bếp.</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cs/6baadb622bc4c79a9ed5.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Cửa sổ trong ph&ograve;ng nhất thiết phải cao hơn cửa ra v&agrave;o.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Cửa ph&ograve;ng ngủ kh&ocirc;ng n&ecirc;n đặt đối diện với ph&ograve;ng tắm.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">S&agrave;n ph&ograve;ng vệ sinh v&agrave; ph&ograve;ng tắm kh&ocirc;ng n&ecirc;n thiết kế cao hơn s&agrave;n ph&ograve;ng ngủ.</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/9db1cdd03c76d0288967.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Ph&ograve;ng ngủ cần phải đảm bảo yếu tố &ldquo;tĩnh&rdquo; do đ&oacute; kh&ocirc;ng thiết kể cửa sổ ph&ograve;ng ngủ h&igrave;nh tr&ograve;n, b&agrave;n đặt trong ph&ograve;ng ngủ cũng n&ecirc;n c&oacute; dạng h&igrave;nh vu&ocirc;ng hoặc chữ nhật.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Trong ph&ograve;ng ngủ, gương tr&ecirc;n b&agrave;n trang điểm kh&ocirc;ng n&ecirc;n đối diện với giường nằm.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Hai b&ecirc;n lối đi v&agrave;o nh&agrave; kh&ocirc;ng n&ecirc;n b&agrave;y c&aacute;c chậu c&acirc;y cảnh cao, to khiến người ta c&oacute; cảm gi&aacute;c bị trấn &aacute;p, thiếu thoải m&aacute;i.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Kh&ocirc;ng n&ecirc;n đặt b&igrave;nh phong che chắn lối đi v&agrave;o cửa ch&iacute;nh.</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/c9002d80de2632786b37.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">N&ecirc;n bố tr&iacute; ph&ograve;ng bếp ở nửa ph&iacute;a sau của ng&ocirc;i nh&agrave;, đồng nghĩa với việc ph&iacute;a sau ph&ograve;ng bếp kh&ocirc;ng n&ecirc;n c&oacute; ph&ograve;ng kh&aacute;c.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Ph&ograve;ng bếp tối kị được bố tr&iacute; ph&iacute;a trước cửa nh&agrave; hoặc ph&iacute;a trước ph&ograve;ng kh&aacute;ch.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Bếp đun n&ecirc;n đặt c&ugrave;ng ph&iacute;a với ống dẫn nước, kị đối diện với hướng cổng hoặc cửa lớn.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Ph&ograve;ng kh&aacute;ch v&agrave; ph&ograve;ng bếp kh&ocirc;ng n&ecirc;n được đặt qu&aacute; gần nhau v&agrave; ph&ograve;ng ăn kh&ocirc;ng n&ecirc;n ở qu&aacute; xa ph&ograve;ng bếp.</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size: 14px;\">--------------------------------------------------------------------------</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Xin cảm ơn quý khách!</span></strong></p>', 574, 1, 1, 'Những nguyên tắc vàng trong phong thủy cho ngôi nhà.', 98, NULL, '0', '28-11-2017', '5-12-2017'),
(40, 21, 'admin', 'Mở bán đợt 1 dự án cao cấp EUROWINDOW.', 'Mở bán đợt 1 dự án cao cấp EUROWINDOW.', 'mo-ban-dot-1-du-an-cao-cap-eurowindow-', '/public/upload/images/eurowindow/khoi-de-toa-thap-eurowindow-nghe-an-600.jpg', 'Công trình Tòa tháp Eurowindow Tower cao 20 tầng nổi và 1 tầng hầm, trong đó tầng 1 đến tầng 5 được bố trí 19 căn hộ nhà ở kết hợp thương mại, tầng 6 đến tầng 20 là chung cư với 186 căn hộ cao cấp có diện tích từ 45 - 90 m2.', '<p>\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; &nbsp;C&ocirc;ng tr&igrave;nh T&ograve;a th&aacute;p Eurowindow Tower cao 20 tầng nổi v&agrave; 1 tầng hầm, trong đ&oacute; tầng 1 đến tầng 5 được bố tr&iacute; 19 căn hộ nh&agrave; ở kết hợp thương mại, tầng 6 đến tầng 20 l&agrave; chung cư với 186 căn hộ cao cấp c&oacute; diện t&iacute;ch từ 45 - 90 m2.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ng&agrave;y 20/5,&nbsp; tại TP Vinh, C&ocirc;ng ty CP Đầu tư Trung t&acirc;m Thương mại Vinh ( ĐT TTTM) kết hợp với Đơn vị ph&acirc;n phối ch&iacute;nh thức ĐẤT XANH MIỀN BẮC tổ chức giới thiệu v&agrave; mở b&aacute;n dự &aacute;n T&ograve;a th&aacute;p Eurowindow Nghệ An tại số 2 đường Trần Ph&uacute; - TP Vinh.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z825320762201_63e0fed3198e47d219bc8c88cd5bf84b.jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"Chương trình diễn ra hấp dẫn và thu hút đông đảo khách hàng.\" src=\"/public/upload/files/z818887642405_2b533fd1394b15252ec56004845cdf77.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Dự lễ c&oacute; &ocirc;ng Hồ Văn B&agrave;i - Tổng Gi&aacute;m đốc C&ocirc;ng ty CP ĐT TTTM Vinh (Vicentra), đại diện c&aacute;c cấp, ng&agrave;nh li&ecirc;n quan, c&aacute;c đối t&aacute;c v&agrave; đ&ocirc;ng đảo kh&aacute;ch h&agrave;ng.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Dự &aacute;n được x&acirc;y dựng tr&ecirc;n khu đất với diện t&iacute;ch hơn 2.817 m2, trong đ&oacute; diện t&iacute;ch x&acirc;y dựng gần 2.000 m2, l&agrave; tổ hợp hiện đại nằm tại vị tr&iacute; trung t&acirc;m TP Vinh (nằm tr&ecirc;n trục đường Cao Thắng - Trần Ph&uacute;).</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z818887516423_561b03e1c3b824076a4848ef1c8eb35b.jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;C&ocirc;ng tr&igrave;nh T&ograve;a th&aacute;p Eurowindow Tower cao 20 tầng nổi v&agrave; 1 tầng hầm, trong đ&oacute; tầng 1 đến tầng 5 được bố tr&iacute; 19 căn hộ nh&agrave; ở kết hợp thương mại, tầng 6 đến tầng 20 l&agrave; chung cư với 186 căn hộ cao cấp c&oacute; diện t&iacute;ch từ 45 - 90 m2. C&ocirc;ng tr&igrave;nh được x&acirc;y dựng bằng c&ocirc;ng nghệ hiện đại, trang bị hệ thống cửa đồng bộ, an to&agrave;n chống trộm, chống ch&aacute;y, hệ thống cửa sổ do Eurowindow cung cấp...</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"Dự án hiện tại đã gần cất nóc. Chất lượng công trình đi đầu - khách hàng nhìn từ bên ngoài vào cũng cảm nhận được.\" src=\"/public/upload/files/z825297503329_0fc5162f37ab99cb8bae18776683c6f4.jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Để tạo điều kiện thuận lợi cho kh&aacute;ch h&agrave;ng, chủ đầu tư đ&atilde; phối hợp với ng&acirc;n h&agrave;ng hỗ trợ l&atilde;i suất 0% đến khi nhận nh&agrave;, ưu đ&atilde;i gi&aacute; tới 4,5%, ng&acirc;n h&agrave;ng t&agrave;i trợ 70%...</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Tại buổi giới thiệu v&agrave; mở b&aacute;n T&ograve;a th&aacute;p Eurowindow Tower, chủ đầu tư đ&atilde; c&oacute; nhiều ch&iacute;nh s&aacute;ch ưu đ&atilde;i b&aacute;n h&agrave;ng linh hoạt như: Bốc thăm tr&uacute;ng nhiều giải thưởng c&oacute; gi&aacute; trị lớn (xe SH, ti vi Samsung, l&ograve; vi s&oacute;ng...) v&agrave; kh&aacute;ch h&agrave;ng nộp đủ tiền đặt cọc được ưu đ&atilde;i 5 triệu đồng...</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với lợi thế l&agrave; một trong những dự &aacute;n bất động sản c&oacute; vị tr&iacute; đặc địa tại TP Vinh v&agrave; những ch&iacute;nh s&aacute;ch ưu đ&atilde;i b&aacute;n h&agrave;ng linh hoạt, dự &aacute;n Eurowindow Tower đ&atilde; thu h&uacute;t rất đ&ocirc;ng kh&aacute;ch h&agrave;ng./.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>--------------------------------------------------------------------------</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Xin cảm ơn quý khách!</strong></span></p>', 563, 1, 1, 'Mở bán đợt 1 dự án cao cấp EUROWINDOW. Vị trí đắc địa ngay Ngã tư chợ Vinh. Dự án mang lại nhiều trải nghiệm cho khách hàng.', 26, NULL, '0', '16-11-2017', '28-11-2017');
INSERT INTO `tbl_news` (`id`, `cat_id`, `user`, `name`, `title`, `slug`, `img`, `detail`, `content`, `hits`, `status`, `hot`, `description`, `desc_size`, `keywords`, `robot`, `date`, `date_up`) VALUES
(42, 15, 'admin', 'Tại sao cần phải tư vấn thiết kế nhà trước khi xây dựng.', 'TẠI SAO CẦN PHẢI TƯ VẤN THIẾT KẾ NHÀ TRƯỚC KHI XÂY DỰNG', 'tai-sao-can-phai-tu-van-thiet-ke-nha-truoc-khi-xay-dung-', '/public/upload/images/cs/720c73a9820f6e51371e.jpg', 'Ngôi nhà là ước mơ là khao khát của mỗi người, đó là nơi sinh hoạt, nghỉ ngơi của gia đình mình. Chính vì vậy việc xây dựng ngôi nhà theo đúng sở thích và công năng của gia chủ là điều rất quan trọng.', '<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Ng&ocirc;i nh&agrave; l&agrave; ước mơ l&agrave; khao kh&aacute;t của mỗi người, đ&oacute; l&agrave; nơi sinh hoạt, nghỉ ngơi của gia đ&igrave;nh m&igrave;nh. Ch&iacute;nh v&igrave; vậy việc x&acirc;y dựng ng&ocirc;i nh&agrave; theo đ&uacute;ng sở th&iacute;ch v&agrave; c&ocirc;ng năng của gia chủ l&agrave; điều rất quan trọng. Để thỏa m&atilde;n được nhu cầu n&agrave;y của gia chủ, ch&uacute;ng ta cần sự tư vấn v&agrave; thiết kế của Kỹ Sư v&agrave; Kiến Tr&uacute;c Sư x&acirc;y dựng.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/720c73a9820f6e51371e.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;<strong>KIẾN TR&Uacute;C SƯ X&Acirc;Y DỰNG</strong> sẽ tư vấn v&agrave; dựng n&ecirc;n m&ocirc; h&igrave;nh,mặt đứng, mặt bằng, bố tr&iacute; c&aacute;c ph&ograve;ng theo c&ocirc;ng năng v&agrave; theosở th&iacute;ch của gia chủ cho hợp l&yacute; đảm bảo yếu tố Thẩm Mỹ h&agrave;i h&ograve;a với khu&ocirc;n đất với phong thủy của gia chủ.<br />\r\n	&nbsp; &nbsp;<strong>KỸ SƯ X&Acirc;Y DỰNG </strong>sẽ tư vấn v&agrave; thiết kế cho bạn về mặt Kết Cấu để x&acirc;y được ng&ocirc;i nh&agrave; như kiến tr&uacute;c sư đ&atilde; dựng đảm bảo ng&ocirc;i nh&agrave; của gia chủ Bền Vững v&agrave; Ổn Định theo thời gian sử dụng.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/f4b37eea8c4c6012395d.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; BẠN BĂN KHOĂN V&Igrave; PHẢI D&Ugrave;NG MỘT KHOẢN TIỀN V&Agrave;O VIỆC THU&Ecirc; THIẾT KẾ.</strong><br />\r\n	&nbsp; Nếu bạn l&agrave;m một b&agrave;i to&aacute;n kinh tế :&nbsp;Bạn kh&ocirc;ng cần thiết kế m&agrave; đội thợ bạn thu&ecirc; vẫn dựng n&ecirc;n được ng&ocirc;i nh&agrave;, bạn đ&atilde; tiết kiệm được một khoản tiền thiết kế nhưng bạn sẽ phải trả th&ecirc;m tiền cho những việc m&agrave; khi c&oacute; bản vẽ thiết kế th&igrave; bạn sẽ kh&ocirc;ng mất, đ&oacute; l&agrave; những chi ph&iacute; sau:</span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Bạn sẽ kh&ocirc;ng biết trước được h&igrave;nh dạng kiến tr&uacute;c ng&ocirc;i nh&agrave; của m&igrave;nh sau khi ho&agrave;n thiện c&oacute; hợp l&yacute; hay kh&ocirc;ng, diện t&iacute;ch c&aacute;c ph&ograve;ng, hướng lấy &aacute;nh s&aacute;ng, c&oacute; đầy đủ c&aacute;c ph&ograve;ng chưa ?</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Sau khi ho&agrave;n thiện bạn muốn thay đổi n&oacute; th&igrave; chi ph&iacute; sửa chữa l&agrave; bao nhi&ecirc;u ?</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Nếu bạn xử l&yacute; nền m&oacute;ng kh&ocirc;ng vững chắc th&igrave; ng&ocirc;i nh&agrave; của bạn sẽ bị l&uacute;n v&agrave; nứt, khi đ&oacute; chi ph&iacute; sửa chữa sẽ l&agrave; rất lớn so với số tiền bạn trả cho đơn vị thiết kế. Nếu bạn xử l&yacute; qu&aacute; nhiều ( nhiều cọc ) th&igrave; rất l&atilde;ng ph&iacute;.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Thợ thi c&ocirc;ng d&ugrave;ng vật liệu do bạn mua về họ c&oacute; tiết kiệm d&ugrave;ng vừa v&agrave; đủ cho kết cấu nh&agrave; của bạn kh&ocirc;ng ( Th&eacute;p dầm +Th&eacute;p s&agrave;n + Th&eacute;p cột + xi măng ) hay l&agrave; đặt th&eacute;p một c&aacute;ch l&atilde;ng ph&iacute;, d&ugrave;ng xi măng kh&ocirc;ng đ&uacute;ng tỉ lệ &hellip;Vậy số tiền l&atilde;ng ph&iacute;vật liệu của bạn l&agrave; bao nhi&ecirc;u ?</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/8a4e910f63a98ff7d6b8.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">** Khi bạn d&ugrave;ng dịch vụ <strong>Tư Vấn Thiết Kế</strong> th&igrave; bạn sẽ giải quyết được tất cả những b&agrave;i to&aacute;n tr&ecirc;n m&agrave; tổng kinh ph&iacute; x&acirc;y dựng ng&ocirc;i nh&agrave; của bạn thậm ch&iacute; sẽ giảm đi một phần so với biện ph&aacute;p m&agrave; bạn kh&ocirc;ng c&oacute; hồ sơ bản vẽ thiết kế. Vậy tại sao bạn lại kh&ocirc;ng d&ugrave;ng dịch vụ Tư Vấn Thiết Kế X&acirc;y Dựng.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size: 14px;\">--------------------------------------------------------------------------</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Xin cảm ơn quý khách!</span></strong></p>', 1379, 1, 1, 'TẠI SAO CẦN PHẢI TƯ VẤN THIẾT KẾ NHÀ TRƯỚC KHI XÂY DỰNG', 95, NULL, '0', '28-11-2017', '5-12-2017'),
(43, 15, 'admin', 'Thiết kế căn hộ Bảo Sơn Complex - Nguyễn Sỹ Sách - Thiết kế tối giản.', 'Thiết kế căn hộ Bảo Sơn Complex.', 'thiet-ke-can-ho-bao-son-complex-nguyen-sy-sach-thiet-ke-toi-gian-', '/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg', 'Bảo Sơn Complex 126 Nguyễn Sỹ Sách sở hữu vị trí vô cùng đắc địa khi tọa lạc tại ngã tư HecMan – Nguyễn Sỹ Sách, Tp. Vinh, Nghệ An.\r\n\r\n   Với vị trí rất gần trung tâm thành phố...', '<p>\r\n	<strong><span style=\"font-size:14px;\">&nbsp; &nbsp;Bảo Sơn Complex 126 Nguyễn Sỹ S&aacute;ch sở hữu vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa khi tọa lạc tại ng&atilde; tư HecMan &ndash; Nguyễn Sỹ S&aacute;ch, Tp. Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp;&nbsp; Với vị tr&iacute; rất gần trung t&acirc;m th&agrave;nh phố, c&ocirc;ng tr&igrave;nh tương lai sẽ c&oacute; nhiều ưu điểm về khả năng tiếp cận dễ d&agrave;ng tới c&aacute;c địa điểm quan trọng của th&agrave;nh phố. B&ecirc;n cạnh đ&oacute;, khu đất c&ograve;n c&oacute; những vị tr&iacute; thuận lợi về giao th&ocirc;ng v&agrave; cơ sở hạ tầng tốt. V&igrave; vậy, c&ocirc;ng tr&igrave;nh được thiết kế sẽ ph&aacute;t huy tối đa những lợi thế vốn c&oacute; để đ&aacute;p ứng cho nhu cầu tiếp cận ng&agrave;y c&agrave;ng cao của của khu vực trung t&acirc;m n&agrave;y.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">&nbsp;&nbsp; Sau đ&acirc;y l&agrave; mẫu thiết kế căn hộ 3 ph&ograve;ng ngủ của dự &aacute;n Bảo Sơn Complex, tầng 12, ph&ograve;ng 11. Do đơn vị CS HOME thiết kế theo phong c&aacute;ch tối giản:</span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/720c73a9820f6e51371e.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/c158b0b9411fad41f40e.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/e8ce4041b1e75db904f6.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/577102e5f3431f1d4652.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/52e55519a4bf48e111ae.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/48af53a5a3034f5d1612.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/xe-Mazda-3-gia-bao-nhieu.JPG\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/c5e32557d4f138af61e0.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/9db1cdd03c76d0288967.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/a6da0c00fda611f848b7.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/cs/628318bfe91905475c08.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">--------------------------------------------------------------------------</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Địa chỉ: Tầng 18, Th&aacute;p A T&ograve;a nh&agrave; Dầu Kh&iacute;, số 7 Quang Trung, TP Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">☎ Hotline tư v&acirc;́n mi&ecirc;̃n phí: 094.220.6262 - 0986.098.266<br />\r\n	Email: bds37.datxanhnghean@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></strong></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Xin cảm ơn quý khách!</span></strong></p>', 936, 1, 1, 'Thiết kế căn hộ Bảo Sơn Complex.', 118, NULL, '0', '28-11-2017', '1-12-2017'),
(44, 15, 'admin', 'Thiết kế căn hộ Bảo Sơn Complex - Nguyễn Sỹ Sách - Thiết kế hiện đại.', 'Thiết kế căn hộ Bảo Sơn Complex - Nguyễn Sỹ Sách.', 'thiet-ke-can-ho-bao-son-complex-nguyen-sy-sach-thiet-ke-hien-dai-', '/public/upload/images/cs/2ccd0f69fccf109149de.jpg', 'Bảo Sơn Complex 126 Nguyễn Sỹ Sách sở hữu vị trí vô cùng đắc địa khi tọa lạc tại ngã tư HecMan – Nguyễn Sỹ Sách, Tp. Vinh, Nghệ An.\r\n\r\n   Với vị trí rất gần trung tâm thành phố...', '<p>\r\n	<strong><span style=\"font-size:14px;\">&nbsp; &nbsp;Bảo Sơn Complex 126 Nguyễn Sỹ S&aacute;ch sở hữu vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa khi tọa lạc tại ng&atilde; tư HecMan &ndash; Nguyễn Sỹ S&aacute;ch, Tp. Vinh, Nghệ An.</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp;&nbsp; Với vị tr&iacute; rất gần trung t&acirc;m th&agrave;nh phố, c&ocirc;ng tr&igrave;nh tương lai sẽ c&oacute; nhiều ưu điểm về khả năng tiếp cận dễ d&agrave;ng tới c&aacute;c địa điểm quan trọng của th&agrave;nh phố. B&ecirc;n cạnh đ&oacute;, khu đất c&ograve;n c&oacute; những vị tr&iacute; thuận lợi về giao th&ocirc;ng v&agrave; cơ sở hạ tầng tốt. V&igrave; vậy, c&ocirc;ng tr&igrave;nh được thiết kế sẽ ph&aacute;t huy tối đa những lợi thế vốn c&oacute; để đ&aacute;p ứng cho nhu cầu tiếp cận ng&agrave;y c&agrave;ng cao của của khu vực trung t&acirc;m n&agrave;y.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">&nbsp; &nbsp;Sau đ&acirc;y l&agrave; mẫu thiết kế căn hộ 3 ph&ograve;ng ngủ của dự &aacute;n Bảo Sơn Complex, tầng 12, ph&ograve;ng 11. Do đơn vị CS HOME thiết kế theo phong c&aacute;ch hiện đại:</span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/cs/2ccd0f69fccf109149de.jpg\" /></span></strong></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center