  `name` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `address` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `investor` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `complete` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `acreage` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `hotline` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `hits` int(20) DEFAULT 50,
  `img` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `price` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `old_price` int(20) DEFAULT NULL,
  `hot` int(11) DEFAULT NULL,
  `status` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `detail` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content1` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content2` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content3` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content4` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content5` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `content6` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading1` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading2` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading3` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading4` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading5` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `heading6` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `date` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `date_up` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `slug` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `desc_size` int(11) DEFAULT NULL,
  `keywords` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `robot` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

