INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(48, 26, 7, NULL, 'CHUNG CƯ BẢO SƠN COMPLEX.', '126 NGUYỄN SỸ SÁCH', 'Tập đoàn Xây dựng đầu tư & Du lịch Bảo Sơn', 'cuối năm 2017', NULL, NULL, 2376, '/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg', '12 TRIỆU/M2.', NULL, 0, '1', NULL, '<p>\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; &nbsp;Bảo Sơn Complex 126 Nguyễn Sỹ S&aacute;ch sở hữu vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa khi tọa lạc tại ng&atilde; tư HecMan &ndash; Nguyễn Sỹ S&aacute;ch, Tp. Vinh, Nghệ An.&nbsp;</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với vị tr&iacute; rất gần trung t&acirc;m th&agrave;nh phố, c&ocirc;ng tr&igrave;nh tương lai sẽ c&oacute; nhiều ưu điểm về khả năng tiếp cận dễ d&agrave;ng tới c&aacute;c địa điểm quan trọng của th&agrave;nh phố. B&ecirc;n cạnh đ&oacute;, khu đất c&ograve;n c&oacute; những vị tr&iacute; thuận lợi về giao th&ocirc;ng v&agrave; cơ sở hạ tầng tốt. V&igrave; vậy, c&ocirc;ng tr&igrave;nh được thiết kế sẽ ph&aacute;t huy tối đa những lợi thế vốn c&oacute; để đ&aacute;p ứng cho nhu cầu tiếp cận ng&agrave;y c&agrave;ng cao của của khu vực trung t&acirc;m n&agrave;y.</span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"Chung cư Bảo Sơn Complex\" src=\"/public/upload/images/bao-son-complex/tong-quan-du-an-bao-son-complex.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/BAOSONCOMPLEX%20OP1.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u>ƯU ĐI&Ecirc;̉M N&Ocirc;̉I B&Acirc;̣T DỰ &Aacute;N BẢO SƠN COMPLEX:</u></strong></span></p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><strong>Vị trí đẹp:</strong> Chung Cư Bảo Sơn Complex nằm ngay Ngã tư Nguy&ecirc;̃n Sỹ Sách &ndash; Hecman thu&ocirc;̣c phường Hưng Phúc &ndash; Đ&acirc;y là khu vực có d&acirc;n trí cao, xung quanh dự án đ&ecirc;̀u là cán b&ocirc;̣ c&ocirc;ng nh&acirc;n vi&ecirc;n chức, cơ sở hạ t&acirc;̀ng đ&ocirc;̀ng b&ocirc;̣ : trường học, chợ búa,&hellip; kh&ocirc;ng gian s&ocirc;́ng y&ecirc;n tĩnh.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><strong>Chủ đ&acirc;̀u tư uy tín:</strong> T&acirc;̣p đoàn đ&acirc;̀u tư x&acirc;y dựng và du lịch Bảo Sơn là t&acirc;̣p đoàn có thương hi&ecirc;̣u l&acirc;u năm, đơn vị thi c&ocirc;ng là C&ocirc;ng ty Cổ Phần x&acirc;y dựng Bảo Tàng H&ocirc;̀ Chí Minh được&nbsp; giám sát bởi đơn vị giám sát hàng đ&acirc;̀u hi&ecirc;̣n nay là CONINCO ch&acirc;́t lượng x&acirc;y dựng đảm bảo.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><strong>Thi&ecirc;́t k&ecirc;́ hi&ecirc;̣n đại t&ocirc;́i ưu c&ocirc;ng năng sử dụng:</strong> Các căn h&ocirc;̣ tại Chung Cư Bảo Sơn 100% đ&ecirc;̀u có ánh sáng tự nhi&ecirc;n, khu vực phòng khách , b&ecirc;́p được b&ocirc;́ trí hợp lý.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><strong>Pháp lý đảm bảo:</strong> Chung Cư Bảo Sơn Complex có đ&acirc;̀y đủ gi&acirc;́y tờ pháp lý theo quy định của Nhà nước : Gi&acirc;́y phép x&acirc;y dựng , Gi&acirc;́y chứng nh&acirc;̣n quy&ecirc;̀n sử dụng đ&acirc;́t, Gi&acirc;́y được phép bán căn h&ocirc;̣ hình thành trong tương lai &hellip;</span></li>\r\n</ol>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u>TỔNG QUAN DỰ &Aacute;N BẢO SƠN COMPLEX:</u></strong></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">T&ecirc;n dự &aacute;n: <strong>Chung cư cao cấp Bảo Sơn Complex.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Chủ đầu tư: <strong>Tập đo&agrave;n đầu tư v&agrave; x&acirc;y dựng Bảo Sơn.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Đơn vị thi c&ocirc;ng: <strong>C&ocirc;ng ty Cổ Phần x&acirc;y dựng nh&agrave; H&agrave; Nội 10.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Gi&aacute;m s&aacute;t thi c&ocirc;ng: <strong>C&ocirc;ng ty CP tư vấn c&ocirc;ng nghệ, thiết bị v&agrave; kiểm định x&acirc;y dựng CONINCO.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Đơn vị ph&acirc;n phối ch&iacute;nh thức: <strong>C&ocirc;ng ty CP &amp; DV Địa ốc Đất Xanh Miền Bắc &ndash; Chi nh&aacute;nh Nghệ An.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Ng&acirc;n hàng h&ocirc;̃ trợ tài chính : <strong>BIDV, OceanBank. LS 0% đến khi b&agrave;n giao nh&agrave;.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Tầng cao dự &aacute;n: <strong>31 tầng , 2 t&acirc;̀ng h&acirc;̀m:</strong></span></li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Tầng 1-2: Trung t&acirc;m thương mại.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Tầng 3-31: Khu căn hộ cao cấp.</strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z634347431625_5c08dd2b3bf70fc1b077951d839cd597.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u>TI&Ecirc;̣N ÍCH HOÀN HẢO:</u></strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">Chung Cư Bảo Sơn Complex có đ&acirc;̀y đủ ti&ecirc;̣n ích cao c&acirc;́p phục vụ cu&ocirc;̣c s&ocirc;́ng của cư d&acirc;n với :</span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">2 tầng hầm để xe r&ocirc;̣ng rãi, giám sát an ninh 24/24</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Tổ hợp si&ecirc;u thị thương mại dịch vụ: spa, fitness, thẩm mỹ, yoga, nh&agrave; h&agrave;ng, khu mua sắm&hellip;.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">4 Thang m&aacute;y tốc độ cao nhập khẩu.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Hệ thống điện, nước dự ph&ograve;ng đảm bảo.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Cửa sổ k&iacute;nh cường lực, an to&agrave;n d&aacute;n 2 lớp phản quang v&agrave; c&aacute;ch nhiệt hiện đại bậc nhất.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Hệ thống b&aacute;o ch&aacute;y, ph&ograve;ng ch&aacute;y chữa ch&aacute;y đạt ti&ecirc;u chuẩn.</span></li>\r\n</ul>\r\n<p>\r\n	<em><span style=\"font-size:14px;\">&nbsp; &nbsp;Tại Bảo Sơn Complex, b&ecirc;n cạnh trải nghiệm một cuộc sống chất lượng cao trong căn hộ đẳng cấp, cư d&acirc;n nơi đ&acirc;y ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m về một cuộc sống hiện đại, tiện nghi với đầy đủ c&aacute;c dịch vụ tiện &iacute;ch tại tầng 1 v&agrave; 2 của dự &aacute;n. Ngo&agrave;i ra, với vị tr&iacute; gần kề trung t&acirc;m v&agrave; nằm trong khu vực c&oacute; hạ tầng đồng bộ, c&aacute;c cư d&acirc;n cũng dễ d&agrave;ng kết nối với c&aacute;c dịch vụ tiện &iacute;ch ngoại khu như trường học, bệnh viện,&hellip;</span></em></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><u><strong>THI&Ecirc;́T K&Ecirc;́ HI&Ecirc;̣N ĐẠI:</strong></u></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">Thi&ecirc;́t k&ecirc;́ xẻ khe 100% căn h&ocirc;̣ tại Bảo Sơn Complex đ&ecirc;̀u có ánh sáng tự nhi&ecirc;n.</span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\">Diện t&iacute;ch: Từ 67 &ndash; 91 m2/ căn (2,3 ph&ograve;ng ngủ / căn).</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">B&agrave;n giao n&ocirc;̣i th&acirc;́t cao c&acirc;́p.</span></li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z634349469624_914e0ab58acf357b77e0f32a11e63006.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><u>TI&Ecirc;́N Đ&Ocirc;̣ VÀ CH&Acirc;́T LƯỢNG HÀNG Đ&Acirc;̀U:</u></strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Chung Cư Bảo Sơn Complex được khởi c&ocirc;ng x&acirc;y dựng từ tháng 3/2016 qua hơn 1 năm thi c&ocirc;ng với t&ocirc;́c đ&ocirc;̣ vượt tr&ocirc;̣i hi&ecirc;̣n tại đến th&aacute;ng 7/2017 dự án đã c&acirc;́t nóc và đang hoàn thi&ecirc;̣n đ&ecirc;́n t&acirc;̀ng 21.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với ch&acirc;́t lượng tuy&ecirc;̣t vời và ti&ecirc;́n đ&ocirc;̣ được đánh giá nhanh s&ocirc;́ 1 hi&ecirc;̣n nay tại thành ph&ocirc;́ Vinh. Dự án sẽ bàn giao cho khách hàng vào tháng 10/2017 theo đúng như cam k&ecirc;́t của chủ đ&acirc;̀u tư trong hợp đ&ocirc;̀ng mua bán.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><u><span style=\"font-size:16px;\">GIÁ BÁN VÀ TI&Ecirc;́N Đ&Ocirc;̣ THANH TOÁN:</span></u></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Với mức giá chỉ từ <strong>11,9 tri&ecirc;̣u/m2 </strong>đơn giá th&ocirc;ng thủy đã bao g&ocirc;̀m VAT và n&ocirc;̣i th&acirc;́t. Gi&aacute; chỉ từ 816 triệu/ căn bạn đ&atilde; c&oacute; cơ hội sở hữu căn hộ tại đ&acirc;y với tiến độ thanh to&aacute;n linh hoạt.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 1:</strong> K&yacute; HĐMB kh&aacute;ch h&agrave;ng thanh to&aacute;n 30% Gi&aacute; trị căn hộ.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 2:</strong> Đổ s&agrave;n tầng 15 kh&aacute;ch h&agrave;ng thanh to&aacute;n 10% Gi&aacute; trị căn hộ.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 3:</strong> Đổ s&agrave;n tầng 20 kh&aacute;ch h&agrave;ng thanh to&aacute;n 10% Gi&aacute; trị căn hộ.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 4:</strong> Đổ s&agrave;n tầng 25 kh&aacute;ch h&agrave;ng thanh to&aacute;n 10% Gi&aacute; trị căn hộ.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 5:</strong> Cất n&oacute;c kh&aacute;ch h&agrave;ng thanh to&aacute;n 10% Gi&aacute; trị căn hộ.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 6: </strong>B&agrave;n giao nh&agrave; 25%.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>&ndash; Đợt 7:</strong> Ngay khi b&agrave;n giao giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất thanh to&aacute;n 5% gi&aacute; trị c&ograve;n lại.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z634347556718_84bd4177ff0a46c0c45f779abf8928b0.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/z634347620949_2ff340159e791554aae041abf2a9a936.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	--------------------------------------------------------------------------</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Xin cảm ơn quý khách!</strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Chung cư Bảo Sơn Complex', '1-8-2017', '4-12-2018', 'chung-cu-bao-son-complex-', NULL, 150, 'chung cư bảo sơn, bao son complex, chung cư giá rẻ, chung cư cao cấp.', '0'),
(50, 26, 7, NULL, 'TÒA THÁP EUROWINDOW NGHỆ AN.', 'SỐ 2 TRẦN PHÚ', 'Công ty Cổ phần Đầu tư Trung tâm Thương mại Vinh', 'Quý I/2018', '47m2', '0941.441.991.', 5918, '/public/upload/images/eurowindow/tong-quan-eurowindown-nghe-an.png', '13 TRIỆU/M2.', NULL, 0, '1', NULL, '<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>   Tòa tháp Eurowindow Nghệ A</strong>n là một trong hai dự án </span><a href=\"http://www.bdsnghetinh.net/\"><span style=\"color:#000000;\"><strong>chung cư TP Vinh</strong></span></a><span style=\"color:#000000;\"> thuộc phân khúc cao cấp bậc nhất đang triển khai trong thời điểm hiện tại. Eurowindow <strong>VỊ TRÍ VÀNG</strong> nằm ngay giữa ngã tư chợ Vinh tiếp giáp 3 tuyến phố chính: <strong>Trần Phú, Cao Thắng, Lê Huân</strong> với sứ mệnh mang lại một không gian sống văn minh hiện đại, đáng sống cho cư dân tương lai.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">   Vị trí đắc địa thuận lợi cho sinh sống, làm việc hoặc đầu tư giá chỉ <strong>tăng - không bao giờ giảm</strong>, chung cư cao cấp Eurowindow Nghệ An tạo nên cơn sốt ngay từ khi bắt đầu triển khai dự án.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/files/EUROWINDOW%20OP1.jpg\" style=\"width: 800px; height: 256px;\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>KHUYẾN MÃI KHỦNG NHẤT DỊP LỄ CẤT NÓC TÒA THÁP EUROWINDOW NGHỆ AN THÁNG 10/2017:</strong></span></span></u></p>\r\n<p>\r\n	 </p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Tặng ngay Voucher</strong> mua hàng tại Mê Linh Plaza trị giá <strong>25.000.000 VNĐ</strong>. Nếu khách hàng không lựa chọn Voucer sẽ được trừ <strong>20.000.000 VNĐ</strong> vào giá bán căn hộ.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Có cơ hội bốc thăm trúng thưởng lớn:</strong></span></span></li>\r\n</ol>\r\n<ul>\r\n	<li>\r\n		<strong style=\"color: rgb(0, 0, 0); font-size: 14px;\">- Giải nhất:</strong><span style=\"color: rgb(0, 0, 0); font-size: 14px;\"> 01 Xe máy SH Mode.</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Giải nhì:</strong> 02 Xe máy Vison.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Giải khuyến khích:</strong> 15 Nồi cơm điện SHARP.</span></span></li>\r\n</ul>\r\n<p>\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Tổng quan dự án EUROWINDOW.\" src=\"/public/upload/images/eurowindow/chuong-trinh-cat-noc-eurowindow-nghe-an.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><u><span style=\"color:#000000;\">I. TỔNG QUAN VỀ TÒA THÁP EUROWINDOW NGHỆ AN:</span></u></span></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Tên dự án:</strong> Tòa tháp Eurowindow Nghệ An.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Địa chỉ:</strong> Số 2 Trần Phú, thành phố Vinh, Nghệ An</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Chủ đầu tư:</strong> Công ty Cổ phần Đầu tư Trung tâm Thương mại Vinh</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Đơn vị thi công:</strong> Tổng công ty Thành An – Bộ Quốc Phòng</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Tầng cao dự án:</strong> 20 tầng</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Tầng 1-5: </strong>19 căn nhà ở thương mại</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Tầng 6-20:</strong> Khu căn hộ cao cấp</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Diện tích:</strong> Từ 45 m2 – 91 m2/căn (2 đến 3 phòng ngủ).</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Bàn giao nhà: </strong>Quý I/ 2018</span></span></li>\r\n</ul>\r\n<p>\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/images/eurowindow/khoi-de-toa-thap-eurowindow-nghe-an-600.jpg\" /></span></span><br />\r\n	 </p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Mô hình kiến trúc hiện đại, sang trọng tổ hợp Căn hộ – Khu nhà phố – Trung tâm thương mại phong cách Châu Âu chắc chắn sẽ làm hài lòng Quý khách hàng.</span></span></p>\r\n<p>\r\n	 </p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><u><span style=\"color:#000000;\">II. TIỆN ÍCH CHUNG CƯ EUROWINDOW NGHỆ AN ĐỒNG BỘ:</span></u></span></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Tầng Hầm để xe rộng rãi giám sát anh ninh 24/7</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">3 Thang máy tốc độ cao nhập khẩu thương hiệu Mishubishi.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Hệ thống điện, nước dự phòng đảm bảo, tiêu chuẩn.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Hệ thống báo cháy, phòng cháy chữa cháy đạt tiêu chuẩn.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Cửa sổ kính cường lực, an toàn dán 2 lớp phản quang và cách nhiệt hiện đại bậc nhất.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Thiết kế theo phong cách hiện đại của Châu Âu.</span></span></li>\r\n</ul>\r\n<p>\r\n	 </p>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\"><span style=\"color:#000000;\">III. CHÍNH SÁCH TỐT NHẤT TỪ CHỦ ĐẦU TƯ:</span></span></u></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Chung cư Eurowindow Nghệ An Giá chỉ từ 608 triệu.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Ngân hàng hỗ trợ vốn: VPbank. (Hỗ trợ vay lên đến 70%, LS 0% đến khi nhận nhà).</strong></span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Thời gian vay tối đa: 15 năm.</strong></span></span></li>\r\n	<li>\r\n		<h3>\r\n			<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Tiến độ thanh toán chung cư Eurowindow Nghệ An:</span></span></h3>\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>              + Đợt 1:</strong> Ký HĐMB khách hàng đóng 25% giá trị căn hộ.<br />\r\n	<strong>              + Đợt 2: </strong>Đổ sàn tầng 5 Khách hàng đóng 10% giá trị căn hộ.<br />\r\n	<strong>              + Đợt 3: </strong>Đổ sàn tầng 10 Khách hàng đóng 10% giá trị căn hộ.<br />\r\n	<strong>              + Đợt 4: </strong>Đổ sàn tầng 15 Khách hàng đóng 10% giá trị căn hộ.<br />\r\n	<strong>              + Đợt 5:</strong> Cất nóc - khách hàng đóng 15% giá trị căn hộ.<br />\r\n	<strong>              + Đợt 6:</strong> Bàn giao nhà - khách hàng đóng 25% giá trị căn hộ.<br />\r\n	<strong>              + Đợt 7:</strong> Sau khi nhận sổ hồng - khách hàng đóng 5% giá trị căn hộ.</span></span></p>\r\n<p>\r\n	 </p>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\"><span style=\"color:#000000;\">IV. CHỦ ĐẦU TƯ VÀ CÁC ĐƠN VỊ HỢP TÁC UY TÍN:</span></span></u></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Chủ đầu tư uy tín:</strong> Tập đoàn Eurowindow.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Đơn vị thi công hàng đầu: </strong>Tổng công ty Thành An - Bộ Quốc Phòng.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Giám sát thi công đạt chuẩn: </strong>BEACONS.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Đơn vị phân phối chính thức:</strong> Công ty cổ phần dịch vụ và địa ốc Đất Xanh Miền Bắc.</span></span></li>\r\n</ul>\r\n<p>\r\n	 </p>\r\n<p style=\"margin-left: 40px; text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><u>MỌI THÔNG TIN CHI TIẾT VỀ DỰ ÁN XIN VUI LÒNG LIÊN HỆ:</u></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><u>Công ty CPDV & Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. Lâm )</u></span></strong></span></p>\r\n<p style=\"margin-left: 40px; text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><u>Email: lamtlt.nghean@dxmb.vn</u></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><u>Website: http://www.bdsnghetinh.net/</u></span></strong></span></p>\r\n<p style=\"margin-left: 40px; text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">ĐĂNG KÝ NHẬN BẢNG GIÁ CHÍNH THỨC: https://goo.gl/HOjPbt</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><em>Xin cảm ơn quý khách!</em></span></strong></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Một số dự án tiêu biểu khác:</span></p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><strong><span style=\"color:#000000;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-cao-cap-t-t-victoria/53.html\">Chung cư cao cấp T & T Victoria Nghệ An</a></span></strong></span></li>\r\n	<li>\r\n		<a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-bao-son-complex-/48.html\"><strong style=\"font-size: 14px;\"><span style=\"color:#000000;\">Chung cư Bảo Sơn Complex 126 Nguyễn Sỹ sách</span></strong></a></li>\r\n</ol>\r\n<p style=\"text-align: center;\">\r\n	 </p>\r\n<p>\r\n	 </p>', NULL, NULL, NULL, NULL, NULL, '25 Triệu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Tòa tháp Eurowindow Nghệ An khẳng định đẳng cấp CĂN HỘ CAO CẤP và sang trọng bậc nhất', '4-8-2017', '4-12-2018', 'toa-thap-eurowindow-nghe-an-', 'Tòa tháp Eurowindow Nghệ An đẳng cấp, Vị trí đắc địa thuận lợi sinh sống và làm việc hoặc đầu tư. Chung cư ở đây giá chỉ tăng không bao giờ giảm.', 5, 'Eurowindow Nghệ An, Eurowindow Ngã tư chợ Vinh, Chung cu cao cap TP Vinh, chung cư vinh,', '0'),
(51, 26, 7, NULL, 'CHUNG CƯ BẢO SƠN GREEN PEARL.', '72 LÊ LỢI', 'Công ty Cổ Phần Tập Đoàn Đầu Tư Và Du Lịch Bảo Sơn', 'Qúy II/ 2018', '64 - 98m2', '0942.206.262', 7048, '/public/upload/images/Bao-son-green-pearl/bao-son-green-pearl-bdsnghetinh.jpg', '11,3 TRIỆU/M2.', NULL, 0, '1', NULL, '<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;Chung cư Bảo Sơn Green Pearl </strong></span>tọa lạc tại vị trí đắc địa, trung t&acirc;m thành ph&ocirc;́ Vinh, nằm ngay tại mặt đường L&ecirc; Lợi, b&ecirc;n cạnh khách sạn Hữu Nghị.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Đ&acirc;y là nút giao th&ocirc;ng chính, l&agrave; tuyến đường huyết mạch của thành ph&ocirc;́ Vinh, từ đ&acirc;y có th&ecirc;̉ thu&acirc;̣n lợi di chuy&ecirc;̉n tới các địa đi&ecirc;̉m lớn như B&ecirc;́n xe Vinh, ga Vinh,s&acirc;n bay Vinh, t&ocirc;̉ hợp giải trí VRC, ng&acirc;n hàng, c&ocirc;ng vi&ecirc;n trung t&acirc;m, bệnh viện lớn ...</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/images/Bao-son-green-pearl/bao-son-green-pearl-tp-vinh.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/files/151111-VIEW-03-1.jpg\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	<u style=\"font-size: 16px;\"><span style=\"color:#000000;\">ƯU ĐI&Ecirc;̉M N&Ocirc;̉I B&Acirc;̣T CỦA DỰ ÁN BẢO SƠN GREEN PEARL 72 L&Ecirc; LỢI:</span></u></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>&nbsp;Chủ đ&acirc;̀u tư uy tín:</strong> T&acirc;̣p đoàn đ&acirc;̀u tư x&acirc;y dựng và du lịch Bảo Sơn là t&acirc;̣p đoàn có thương hi&ecirc;̣u l&acirc;u năm, đơn vị thi c&ocirc;ng là C&ocirc;ng ty Cổ Phần x&acirc;y dựng Bảo Tàng H&ocirc;̀ Chí Minh được giám sát bởi đơn vị giám sát hàng đ&acirc;̀u hi&ecirc;̣n nay là CONINCO ch&acirc;́t lượng x&acirc;y dựng đảm bảo.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Thi&ecirc;́t k&ecirc;́ hi&ecirc;̣n đại t&ocirc;́i ưu c&ocirc;ng năng sử dụng:</strong> Các căn h&ocirc;̣ tại Chung Cư Bảo Sơn 100% đ&ecirc;̀u có ánh sáng tự nhi&ecirc;n, khu vực phòng khách , b&ecirc;́p được b&ocirc;́ trí hợp lý</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Ti&ecirc;̣n ích hoàn hảo: </strong>Trung t&acirc;m thương mại dịch vụ, GYM, Spa ... H&ecirc;̣ th&ocirc;́ng 6 thang máy cao c&acirc;́p Misubishi, 6000m2 t&acirc;̀ng h&acirc;̀m và t&acirc;̀ng lửng đ&ecirc;̉ xe r&ocirc;̣ng rãi.( Dự án có khu vực đ&ecirc;̉ xe r&ocirc;̣ng nh&acirc;́t tại tp Vinh).</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Pháp lý đảm bảo:</strong> Chung Cư Bảo Sơn Green Pearl có đ&acirc;̀y đủ gi&acirc;́y tờ pháp lý theo quy định của Nhà nước: Gi&acirc;́y phép x&acirc;y dựng, Gi&acirc;́y chứng nh&acirc;̣n quy&ecirc;̀n sử dụng đ&acirc;́t, Gi&acirc;́y được phép bán căn h&ocirc;̣ hình thành trong tương lai &hellip;</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Giá h&acirc;́p d&acirc;̃n chỉ</strong> <strong>từ 679 tri&ecirc;̣u/ căn 2PN 60m2.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Ng&acirc;n h&ocirc;̃ trợ 70% giá trị căn h&ocirc;̣ với lãi su&acirc;́t 0% đ&ecirc;́n lúc nh&acirc;̣n nhà.</strong></span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">---------------------------------------------------------------</span></span></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><u><span style=\"color:#000000;\">I. T&Ocirc;̉NG QUAN DỰ ÁN CHUNG CƯ CAO CẤP BẢO SƠN GREEN PEARL 72 L&Ecirc; LỢI:</span></u></span></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>T&ecirc;n dự &aacute;n: </strong>Chung cư Bảo Sơn Green Pearl:</span></span></li>\r\n</ul>\r\n<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-size: 14px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Chung Cư Bảo Sơn cao 31 tầng 2 tầng thương mại.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + 2 tầng hầm kết hợp tầng lửng gửi xe diện t&iacute;ch 6.000 m2 v&agrave; 522 căn hộ cao cấp từ tầng 3 đến tầng 31.</span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Vị tr&iacute;:</strong> Số 72 L&ecirc; Lợi, TP Vinh, Nghệ An -&nbsp; Ngay ngã tư ga.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Chủ đầu tư uy tín:</strong> C&ocirc;ng ty Cổ Phần Tập Đo&agrave;n Đầu Tư V&agrave; Du Lịch Bảo Sơn.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Đơn vị thi c&ocirc;ng hàng đ&acirc;̀u:</strong> C&ocirc;ng ty Cổ Phần x&acirc;y dựng nh&agrave; H&agrave; Nội.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Gi&aacute;m s&aacute;t thi c&ocirc;ng s&ocirc;́ 1 Vi&ecirc;̣t Nam:</strong> C&ocirc;ng ty CP tư vấn c&ocirc;ng nghệ, thiết bị v&agrave; kiểm định x&acirc;y dựng CONINCO.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Ng&acirc;n hàng h&ocirc;̃ trợ tài chính:</strong> BIDV, OCEANBANK.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Ph&acirc;n ph&ocirc;́i đ&ocirc;̣c quy&ecirc;̀n:</strong> CTCP Dịch vụ v&agrave; Địa ốc Đất Xanh Miền Bắc - Chi nh&aacute;nh Nghệ An.</span></span></li>\r\n</ul>\r\n<h3>\r\n	&nbsp;</h3>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><u><span style=\"color:#000000;\">II. TI&Ecirc;̣N ÍCH CHUNG CƯ BẢO SƠN GREEN PEARL:</span></u></span></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&ndash; 2 tầng hầm để xe r&ocirc;̣ng rãi, giám sát an ninh 24/24.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Tổ hợp si&ecirc;u thị thương mại dịch vụ: spa, fitness, thẩm mỹ, yoga, nh&agrave; h&agrave;ng, khu mua sắm&hellip;.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">H&ecirc;̣ th&ocirc;́ng 6 thang máy cao c&acirc;́p t&ocirc;́c đ&ocirc;̣ cao 3m/s , h&ecirc;̣ th&ocirc;́ng phòng cháy chữa cháy, thoát nước được thi&ecirc;́t k&ecirc;́ hi&ecirc;̣n đại đảm bảo ti&ecirc;u chu&acirc;̉n cao c&acirc;́p.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Hệ thống điện, nước dự ph&ograve;ng đảm bảo.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Cửa sổ k&iacute;nh cường lực, an to&agrave;n d&aacute;n 2 lớp phản quang v&agrave; c&aacute;ch nhiệt hiện đại bậc nhất.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Hệ thống b&aacute;o ch&aacute;y, ph&ograve;ng ch&aacute;y chữa ch&aacute;y đạt ti&ecirc;u chuẩn.</span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><u><span style=\"color:#000000;\">III. THI&Ecirc;́T K&Ecirc;́ CĂN H&Ocirc;̣ CHUNG CƯ BẢO SƠN GREEN PEARL:</span></u></span></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Được thi&ecirc;́t k&ecirc;́ theo phong cách xẻ khe hi&ecirc;̣n đại 100% căn h&ocirc;̣ tại Chung Cư Bảo Sơn Green Pearl đ&ecirc;̀u có ánh sáng tự nhi&ecirc;n , khu vực b&ecirc;́p, v&ecirc;̣ sinh được b&ocirc;́ trí hợp lý t&ocirc;́i ưu c&ocirc;ng năng sử dụng.<br />\r\n	&nbsp; &nbsp;Di&ecirc;̣n tích căn h&ocirc;̣ từ 64 &ndash; 98m2 từ 2- 3PN phù hợp với mọi nhu c&acirc;̀u của khách hàng. H&ecirc;̣ th&ocirc;́ng hành lang và khu vực chờ thang máy được thi&ecirc;́t k&ecirc;́ r&ocirc;̣ng rãi th&ocirc;ng gió tự nhi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Căn h&ocirc;̣ 92,6m2 - 3PN, 2WC&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Căn hộ 92,6m2 - 3PN, 2WC \" src=\"/public/upload/images/Bao-son-green-pearl/can-ho-01-92_6m2-3pn-2wc.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Căn h&ocirc;̣ 75,5m2 - 3PN, 2WC&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Căn hộ 75,5m2 - 3PN, 2WC \" src=\"/public/upload/images/Bao-son-green-pearl/can-ho-09--75-5-3pn-2wc-1.jpg\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Căn h&ocirc;̣ 60,1m2 - 2PN, 2WC&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Căn hộ 60,1m2 - 2PN, 2WC \" src=\"/public/upload/images/Bao-son-green-pearl/can-ho-06-60-1.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Căn h&ocirc;̣ 68,6m2 - 2PN, 2WC</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Căn hộ 68,6m2 - 2PN, 2WC\" src=\"/public/upload/images/Bao-son-green-pearl/can-ho-11-12-13-68-7m2-2pn-2wc-1.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\">IV. N&Ocirc;̣I TH&Acirc;́T BÀN GIAO CHUNG CƯ BẢO SƠN GREEN PEARL:</span></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/images/Bao-son-green-pearl/phu-luc-ban-giao-du-an-bao-son-tp-vinh.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:16px;\"><span style=\"color:#000000;\">V. GIÁ BÁN VÀ TI&Ecirc;́N Đ&Ocirc;̣ THANH TOÁN CHUNG CƯ BẢO SƠN GREEN PEARL :</span></span></u></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Với mức giá chỉ từ 11.3/m2 đã bao g&ocirc;̀m VAT và n&ocirc;̣i th&acirc;́t cơ bản.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Đợt 1 chỉ c&acirc;̀n thanh toán 30%&nbsp; ký hợp đ&ocirc;̀ng mua bán.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Chỉ từ 679 Triệu quý khách đã sở hữu căn h&ocirc;̣ cao c&acirc;́p ngay tại m&ocirc;̣t trong những vị trí đắc địa nh&acirc;́t của thành ph&ocirc;́ Vinh.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">Và đặc bi&ecirc;̣t ng&acirc;n hàng BIDV, OCEANBANK h&ocirc;̃ trợ cho vay<strong> 70% giá trị căn h&ocirc;̣ với lãi su&acirc;́t 0% đ&ecirc;́n khi nh&acirc;̣n nhà.</strong></span></span></li>\r\n</ul>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">-------------------------------------------------</span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 094.220.6262 ( Ms. Tr&acirc;m )</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Email: tramnt.nghean@dxmb.vn</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>Tham khảo th&ecirc;m một số dự &aacute;n kh&aacute;c:</strong></span></span></p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><strong><a href=\"http://www.bdsnghetinh.net/du-an-bds/lien-ke-72-le-loi/52.html\"><span style=\"color:#000000;\">Liền kề Bảo Sơn Green Pearl 72 L&ecirc; Lợi</span></a></strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-bao-son-complex/48.html\"><span style=\"color:#000000;\"><b>Chung cư Bảo Sơn Compex 126 Nguyễn Sỹ S&aacute;ch</b></span></a></span></li>\r\n</ol>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Chung cư Bảo Sơn Green Pearl - Mặt đường Lê Lợi Trả góp 0%', '14-8-2017', '11-9-2019', 'chung-cu-bao-son-green-pearl-', 'Chung cư Bảo Sơn Green Pearl tọa lạc tại vị trí đắc địa, trung tâm thành phố Vinh, nằm ngay tại mặt đường Lê Lợi, thuận tiên giao thông', 1, 'Chung cư Bảo Sơn, chung cư Bảo Sơn 72 Lê Lợi, Bảo Sơn Green Pearl, chung cư TP Vinh, chung cư cao cấp Vinh,', '0'),
(52, 26, 7, NULL, 'LIỀN KỀ 72 LÊ LỢI.', '72 LÊ LỢI', 'Công ty CP Tập đoàn Đầu tư Xây dựng và Du lịch Bảo Sơn', 'Qúy 1/ 2018', '75 - 101 m2', '0941.441.991.', 1590, '/public/upload/images/Bao-son-green-pearl/lien-ke-72-le-loi.jpg', 'LIÊN HỆ.', NULL, 1, '1', NULL, '<p>\r\n	<span style=\"font-size:24px;\"><strong><span style=\"color:#000000;\">Liền kề 72 L&ecirc; Lợi, TP Vinh, Nghệ An.</span></strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><strong><span style=\"color:#000000;\">I. TỔNG QUAN:</span></strong></span></u></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Diện t&iacute;ch đất:</strong> 75m2- 101m2</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Vị trí đắc địa: </strong>Mặt đường số 72 L&ecirc; Lợi TP Vinh, Nghệ An.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Mặt sau đất liền kề: </strong>Chung cư cao cấp Bảo Sơn Green Pearl 31 tầng:</span></span></li>\r\n</ul>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;T&acirc;̀ng 1-2: Nhà ph&ocirc;́ thương mại, trung t&acirc;m dịch vụ mua sắm, nhà hàng, gym, spa &hellip;.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;T&acirc;̀ng 3-31: Khu căn h&ocirc;̣ cao c&acirc;́p.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;2 tầng hầm để xe, 4 Thang m&aacute;y tốc độ cao, c&oacute; ph&ograve;ng sinh hoạt cộng đồng, sảnh đ&oacute;n tiếp trang trọng, lịch sự.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Hệ thống điện, nước dự ph&ograve;ng đảm bảo, ti&ecirc;u chuẩn.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Hệ thống b&aacute;o ch&aacute;y, ph&ograve;ng ch&aacute;y chữa ch&aacute;y đạt ti&ecirc;u chuẩn.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Cửa sổ k&iacute;nh cường lực, an to&agrave;n d&aacute;n 2 lớp phản quang v&agrave; c&aacute;ch nhiệt hiện đại bậc nhất.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Thiết kế theo phong c&aacute;ch hiện đại của Ch&acirc;u &Acirc;u.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Bàn giao qu&yacute; II/ 2018.</span></span></li>\r\n</ol>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/images/Bao-son-green-pearl/lien-ke-72-le-loi.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/files/151111-VIEW-03-1.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><u><span style=\"color:#000000;\"><strong>&nbsp; &nbsp; Đầu tư mua đất x&acirc;y dựng căn hộ ở vị tr&iacute; n&agrave;y vừa tận dụng được lượng lớn kh&aacute;ch h&agrave;ng từ chung cư 31 tầng ph&iacute;a sau, vừa l&agrave; đất mặt đường lưu th&ocirc;ng đ&ocirc;ng đ&uacute;c rất thuận lợi cho ph&aacute;t triển kinh doanh v&agrave; đầu tư.</strong></span></u></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>II. TIẾN ĐỘ THANH TO&Aacute;N:</strong></span></span></u></p>\r\n<ul>\r\n	<li>\r\n		<p>\r\n			<span style=\"font-size:16px;\"><strong style=\"color: rgb(0, 0, 0);\">Đợt 1 : </strong><strong style=\"color: rgb(0, 0, 0);\">Ký thoả thu&acirc;̣n đặt mua : Khách hàng thanh toán 50% giá trị l&ocirc; đ&acirc;́</strong><strong style=\"color: rgb(0, 0, 0);\">t</strong></span></p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Đợt 2 : Thanh toán 45% sau 2 tháng ký thoả thu&acirc;̣n đặt mua.</strong></span></span></p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			<span style=\"font-size:16px;\"><strong style=\"color: rgb(0, 0, 0); font-size: 14px;\">Đợt 3 : Thanh toán 5% còn lại, khi CĐT làm thủ tục c&acirc;́p s&ocirc;̉ h&ocirc;̀ng.</strong></span></p>\r\n	</li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><strong><span style=\"color:#000000;\">III. ƯU Đ&Atilde;I LỚN NHẤT TỪ TRƯỚC ĐẾN NAY:</span></strong></span></u></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Tặng ngay xe TOYOTA ALTIS 1.8MT cho kh&aacute;ch h&agrave;ng mua l&ocirc; liền kề c&oacute; diện t&iacute;ch 75m2.</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Tặng ngay xe TOYOTA FORTUNER 2.4G4x2MT cho kh&aacute;ch h&agrave;ng mua l&ocirc; liền kề c&oacute; diện t&iacute;ch từ 96m2.</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Hoặc CHIẾT KHẤU NGAY 10 triệu VNĐ/ 1 m2.</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>Thời gian &aacute;p dung chương tr&igrave;nh: từ 1/8 - 31/12/2017.</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>-------------------------------------------------</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><em>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư.</em></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">Email: lamtlt.nghean@dxmb.vn</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">Website: http://www.bdsnghetinh.net/</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\"><em>Xin cảm ơn quý khách!</em></span></strong></span></p>', NULL, NULL, NULL, NULL, NULL, '750.000.000 - 1.010.000.000', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Mở bán Liền kề Lê Lợi cách BigC Vinh 1,5 Km tặng ngay Oto Toyota', '16-8-2017', '30-11-2018', 'lien-ke-72-le-loi-', 'Mua Liền kề Lê Lợi cách BigC Vinh 1,5 Km tặng ngay OTO TOYOTA hoặc chiết khấu 10 TRIỆU VNĐ/ 1m2', 55, 'liền kề tại vinh, đất nền tại vinh, nhà liền kề giá rẻ, đất mặt đường Quốc lộ Vinh,', '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(53, 26, 7, NULL, 'CHUNG CƯ CAO CẤP T&T VICTORIA.', 'SỐ 1 QUANG TRUNG', 'CTCP Tập đoàn T & T', 'Quý III/ 2018', 'Từ 45m2.', '094.220.6262', 8888, '/public/upload/images/T-T/ANH-BIA.jpg', '701 TRIỆU.', NULL, 1, '1', NULL, '<p>\r\n	<span style=\"color:#000000;\"><strong><span style=\"font-size:16px;\"><span style=\"background-color:#ffd700;\">Cập nhật Ưu đ&atilde;i mới nhất: Thời gian từ 01/04/2018 đến 30/4/2018.&nbsp;</span></span></strong></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:16px;\">- Kh&aacute;ch H&agrave;ng được tặng 1 Phiếu Giảm Gi&aacute; trị gi&aacute; 20.000.000 VND khi KH đ&atilde; đ&oacute;ng đủ cọc 50.000.000 VND/căn v&agrave; k&yacute; TTĐC/HĐMB từ ng&agrave;y ch&iacute;nh s&aacute;ch n&agrave;y c&oacute; hiệu lực đến hết ng&agrave;y 31/03/2018. Phiếu Giảm Gi&aacute; 20.000.000 VND n&agrave;y chỉ &aacute;p dụng cho tối đa 15 căn đầu ti&ecirc;n ti&ecirc;n k&yacute; TTĐC/ HĐMB theo quy định.<br />\r\n	- Tất cả c&aacute;c Kh&aacute;ch mời tham gia sự kiện (dự kiến tổ chức ng&agrave;y 08/04/2018) sẽ được nhận phiếu bốc thăm tr&uacute;ng thưởng Phiếu Giảm Gi&aacute; trị gi&aacute; 15.000.000 VND (Số lượng: 15 Phiếu Giảm Gi&aacute;). &Aacute;p dụng cho KH k&yacute; TTĐC/ HĐMB kh&ocirc;ng muộn hơn 22 ng&agrave;y từ ng&agrave;y tổ chức sự kiện (ng&agrave;y +22 dự kiến l&agrave; ng&agrave;y 30/04/2018).<br />\r\n	- Tất cả c&aacute;c loại Phiếu Giảm Gi&aacute; được Chiết khấu v&agrave;o Gi&aacute; B&aacute;n chưa VAT, KPBT tại thời điểm k&yacute; HĐMB (Kh&aacute;ch H&agrave;ng vui l&ograve;ng xuất tr&igrave;nh Phiếu Giảm Gi&aacute; tại thời điểm k&yacute; HĐMB).</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\"><strong>&nbsp; &nbsp;T &amp; T Victoria Vinh</strong>&nbsp;đang tạo n&ecirc;n cơn sốt thực sự đối với thị trường Bất động sản Nghệ An n&oacute;i chung,</span><strong><span style=\"color:#000000;\"> </span><a href=\"http://www.bdsnghetinh.net/\"><span style=\"color:#000000;\">chung cư TP Vinh</span></a></strong><span style=\"color:#000000;\"> n&oacute;i ri&ecirc;ng. Với tầm nh&igrave;n chiến lược v&agrave; ph&aacute;t triển bền vững, dự &aacute;n </span><a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-cao-cap-t-t-victoria/53.html\"><span style=\"color:#000000;\"><strong>chung cư T&amp;T Victoria Vinh</strong></span></a><span style=\"color:#000000;\"> được tọa lạc tại Số 01 Quang Trung (đối diện Big C) l&agrave; vị tr&iacute; trung t&acirc;m th&agrave;nh phố Vinh.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Dự &aacute;n hội tụ những gi&aacute; trị vượt trội: <em>Sản phẩm nh&agrave; ở chất lượng cao; Dịch vụ chuy&ecirc;n nghiệp; M&ocirc;i trường sống xanh, sạch; Văn h&oacute;a cộng đồng nh&acirc;n văn; An ninh &ndash; An to&agrave;n được đảm bảo; v&agrave; Tiện &iacute;ch đầy đủ.</em></span></span></p>\r\n<p>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:14px;\"><strong>1. CHƯƠNG TR&Igrave;NH LỘC XU&Acirc;N</strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:14px;\"><strong>1.1 Chương tr&igrave;nh Lộc Xu&acirc;n</strong><br />\r\n	Chiết khấu ngay 01% Gi&aacute; B&aacute;n chưa VAT, KPBT. &Aacute;p dụng đối với Kh&aacute;ch H&agrave;ng mua từ 02<br />\r\n	Căn Hộ trở l&ecirc;n, c&ugrave;ng tầng, cạnh nhau thuộc Phụ lục 01.</span></span></p>\r\n<p>\r\n	<strong style=\"font-size: 14px; color: rgb(255, 0, 0);\">1.2 Kh&aacute;ch H&agrave;ng v&agrave;/hoặc người th&acirc;n mua từ Căn Hộ thứ hai trở l&ecirc;n:</strong></p>\r\n<p>\r\n	<em style=\"color: rgb(0, 0, 0); font-size: 14px;\">(&aacute;p dụng tối đa 50 căn)*:</em></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Kh&aacute;ch h&agrave;ng hoặc người th&acirc;n mua từ Căn hộ thứ <strong>02 tại dự &aacute;n T&amp;T Victoria sẽ được chiết khấu 1% cho mỗi căn mua mới v&agrave;o gi&aacute; b&aacute;n Căn hộ trước VAT v&agrave; kinh ph&iacute; bảo tr&igrave; (&ldquo;KPBT&rdquo;).&nbsp;</strong><br />\r\n	Người th&acirc;n của KH thuộc c&aacute;c mối quan hệ trong phạm vi sau: <strong>vợ với chồng; cha/mẹ đẻ với con đẻ; cha/mẹ nu&ocirc;i với con nu&ocirc;i; cha/mẹ chồng với con d&acirc;u; cha/mẹ vợ với con rể; &ocirc;ng/b&agrave; nội với ch&aacute;u nội; &ocirc;ng/b&agrave; ngoại với ch&aacute;u ngoại; anh, chị, em ruột với nhau.</strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:14px;\"><strong>2. ĐẶC BIỆT Chương tr&igrave;nh hỗ trợ của Chủ đầu tư:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>i. Hỗ trợ l&atilde;i suất của Chủ đầu tư:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Đợt 1 thanh to&aacute;n 25% K&yacute; Hợp đồng mua b&aacute;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Ng&acirc;n h&agrave;ng SHB, Vietcombank, TPBank hỗ trợ cho vay l&ecirc;n đến 75% Gi&aacute; trị căn hộ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Chủ đầu tư hỗ trợ l&atilde;i suất 0% v&agrave; &acirc;n hạn nợ gốc đến khi b&agrave;n giao nh&agrave;.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>ii. Chiết khấu kh&ocirc;ng tham gia chương tr&igrave;nh Hỗ trợ l&atilde;i suất:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">- KH thanh to&aacute;n tiền mua căn hộ bằng vốn tự c&oacute; được chiết khấu 1,5% v&agrave;o gi&aacute; b&aacute;n căn hộ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">- Chiết khấu thanh to&aacute;n sớm bằng vốn tự c&oacute; KH c&oacute; thể lựa chọn một trong 2 h&igrave;nh thức thanh to&aacute;n sớm như sau:<br />\r\n	(1) Thanh to&aacute;n sớm 95% gi&aacute; b&aacute;n căn hộ&nbsp;trong v&ograve;ng 10 ng&agrave;y kể từ ng&agrave;y k&yacute; HĐMB, được hưởng l&atilde;i suất chiết khấu 8,5%/năm tr&ecirc;n khoản tiền v&agrave; số ng&agrave;y thanh to&aacute;n trước hạn.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">(2) KH thanh to&aacute;n trước hạn từng phần bằng vốn tự c&oacute; (&iacute;t nhất trước 07 ng&agrave;y kể từ ng&agrave;y đến hạn thanh to&aacute;n v&agrave; số tiền thanh to&aacute;n sớm của mỗi đợt thanh to&aacute;n tối thiểu l&agrave; 50 triệu đồng) được hưởng l&atilde;i suất chiết khấu 7,5%/năm tr&ecirc;n khoản tiền thanh to&aacute;n trước hạn theo lịch thanh to&aacute;n chuẩn.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>TIẾN ĐỘ THANH TO&Aacute;N</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Tiến độ thanh toán T&amp;T Victoria Tháng 4/ 2018\" src=\"/public/upload/images/T-T/tien-do-thanh-toan-thang-4.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp; Sở hữu chuỗi 45 tiện &iacute;ch dịch vụ nội khu v&agrave; cảnh quan, dự &aacute;n chung cư cao cấp T&amp;T Victoria kh&ocirc;ng chỉ đơn thuần l&agrave; cuộc sống sung t&uacute;c được đ&aacute;p ứng đầy đủ c&aacute;c nhu cầu tiện nghi m&agrave; c&ograve;n trở th&agrave;nh thi&ecirc;n đường của sự hưởng thụ, của những trải nghiệm sống sang trọng ngay giữa đời thực. Với hệ thống nh&agrave; h&agrave;ng, si&ecirc;u thị, khu&ocirc;n vi&ecirc;n dạo bộ, tập thể dục, trường học,&hellip; T&amp;T Victoria Vinh chắc chắn sẽ mang tới qu&yacute; cư d&acirc;n tương lai của m&igrave;nh một cuộc sống hiện đại v&agrave; tuyệt vời nhất.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/images/T-T/t-t-victoria-vinh.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:14px;\"><span style=\"color:#000000;\">ƯU ĐIỂM NỔI BẬT CỦA DỰ &Aacute;N T &amp; T VICTORIA TP. VINH:</span></span></u></h2>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">1. Chủ đầu tư uy t&iacute;n, tầm nh&igrave;n chiến lược:</span></span></h3>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Tập đo&agrave;n T&amp;T &ndash; một tập đo&agrave;n kinh tế hoạt động đa ng&agrave;nh nghề tr&ecirc;n nhiều lĩnh vực: c&ocirc;ng nghiệp, t&agrave;i ch&iacute;nh, bất động sản, xuất nhập khẩu&hellip; Một tập đo&agrave;n kh&eacute;t tiếng trong thị trường BĐS với rất nhiều dự &aacute;n lớn trong những năm qua, nhận được sự t&iacute;n nhiệm v&agrave; tin tưởng của kh&aacute;ch h&agrave;ng v&agrave; đặc biệt tập đo&agrave;n c&oacute; tiềm lực mạnh về t&agrave;i ch&iacute;nh để đảm bảo tốt nhất tiến độ của dự &aacute;n m&agrave; kh&ocirc;ng lo gi&aacute;n đoạn về vấn đề vốn.<br />\r\n	Mục ti&ecirc;u lớn của tập đo&agrave;n l&agrave; ph&aacute;t triển các dự &aacute;n bất động sản lớn trong đ&oacute; c&oacute; chung cư T &amp; T Victoria TP Vinh. Tập đo&agrave;n c&ograve;n cố gắng đa dạng c&aacute;c sản phẩm như: nh&agrave; ở để b&aacute;n, trung t&acirc;m thương mại, văn ph&ograve;ng cho thu&ecirc;, biệt thự nghỉ dưỡng, nh&agrave; mặt phố&hellip; đ&acirc;y cũng l&agrave; một thế mạnh đặc biệt của tập đo&agrave;n.</span></span></p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">2. Vị tr&iacute; quyết định sự th&agrave;nh c&ocirc;ng của chung cư T &amp; T Victoria Tp Vinh.</span></span></h3>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Yếu tố VỊ TR&Iacute;, VỊ TR&Iacute; v&agrave; VỊ TR&Iacute; l&agrave; yếu tố ti&ecirc;n phong để việc đầu tư bất động sản c&oacute; quyết định đầu tư hay kh&ocirc;ng. Người mua nh&agrave; cũng thế, để chọn một nơi để ở thuận tiện cho việc đi lại sẽ chọn trung t&acirc;m h&agrave;nh ch&iacute;nh v&igrave; cơ sở hạ tầng ho&agrave;n thiện, tập trung nhiều c&ocirc;ng ty, cửa h&agrave;ng mua sắm, trường học, bệnh viện&hellip;</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>&nbsp; &nbsp;Dự &aacute;n nằm ngay ng&atilde; tư chợ Vinh - vị tr&iacute; KIM CƯƠNG của Tp. Vinh</strong>. Nắm trong tay ưu thế vượt trội Chung cư T &amp; T Victoria Vinh ch&iacute;nh l&agrave; quyết định h&agrave;ng đầu của người mua nh&agrave; trong năm nay.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"\" src=\"/public/upload/images/T-T/Tong-quan-t-t-Vinh.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">3. Thiết kế hiện đại, tối ưu c&ocirc;ng năng sử dụng</span></span></h3>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Với mục ti&ecirc;u tạo ra những gi&aacute; trị sống đ&iacute;ch thực, với thiết kế hiện đại, tho&aacute;ng m&aacute;t v&agrave; rất thu h&uacute;t &aacute;nh nh&igrave;n với mật độ thang m&aacute;y kh&aacute; hợp l&yacute;. Dự &aacute;n được nằm trong khu vực c&oacute; tiềm năng đầu tư rất tốt. Điểm nhấn độc đ&aacute;o trong thiết kế của căn hộ T &amp; T Nghệ An đ&oacute; l&agrave; t&ograve;a căn hộ được thiết kế theo phong c&aacute;ch hiện đại, kết hợp với kh&ocirc;ng gian mở, tạo n&ecirc;n một sản phẩm kh&ocirc;ng chỉ hiện đại, đẳng cấp m&agrave; c&ograve;n h&ograve;a hợp với thi&ecirc;n nhi&ecirc;n.<br />\r\n	X&eacute;t ri&ecirc;ng về c&aacute;c căn hộ tại dự &aacute;n T&amp;T Tp Vinh đều c&oacute; view cực tho&aacute;ng khi đều hướng v&agrave;o th&agrave;nh phố v&agrave; c&oacute; thể nh&igrave;n thẳng ra Quảng Trường v&agrave; Cầu Cửa Nam chỉ c&aacute;ch 500m.</span></span></p>\r\n<h3>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">4. Tiện &iacute;ch ho&agrave;n hảo</span></span></h3>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Cư d&acirc;n T&amp;T Victoria sẽ được hưởng trọn vẹn chuỗi 45 tiện &iacute;ch như: Khu sinh hoạt cộng đồng, khu vui chơi d&agrave;nh ri&ecirc;ng cho trẻ em, khu b&oacute;ng b&agrave;n, khu đọc s&aacute;ch, ph&ograve;ng đa năng, c&ocirc;ng vi&ecirc;n, trung t&acirc;m thương mại, khu thể thao, spa, gym, ... được trang bị những thiết bị hiện đại, t&acirc;n tiến nhất, th&aacute;i độ phục vụ của nh&acirc;n vi&ecirc;n cũng lu&ocirc;n thể hiện tốt nhất, đưa gi&aacute; trị cuộc sống l&ecirc;n một tầm cao mới trong giới thượng lưu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Dịch vụ tiện ích T &amp; T Victoria Vinh\" src=\"/public/upload/images/T-T/dich-vu-tien-ich-t-t-victoria-vinh.jpg\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:14px;\"><span style=\"color:#000000;\">TỔNG QUAN DỰ &Aacute;N T &amp; T VICTORIA TP VINH:</span></span></u></h2>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- T&ecirc;n dự &aacute;n: T&amp;T Victoria.</strong></span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Địa chỉ dự &aacute;n: </strong>Số 1 Quang Trung, Th&agrave;nh phố Vinh, Tỉnh Nghệ An.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Đơn vị thi c&ocirc;ng hàng đ&acirc;̀u: </strong>C&ocirc;ng ty Cổ Phần X&acirc;y dựng số 1 H&agrave; Nội HACC1.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Tổng diện t&iacute;ch to&agrave;n dự &aacute;n: </strong>7.383,8 m2.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Dự &aacute;n gồm 25 tầng nổi v&agrave; 2 tầng hầm trong đ&oacute; </strong>:</span></span></li>\r\n</ul>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Tầng hầm 1: Cao 3,2m, diện t&iacute;ch 4320m2.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Tầng hầm 2: Cao 3m, diện t&iacute;ch 4320m2.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Tầng 1 &ndash; 5 được quy hoạch th&agrave;nh khu TTTM &amp; Dịch vụ (GYM, Si&ecirc;u thị, trường mầm non, khu ẩm thực v&agrave; c&aacute;c cửa h&agrave;ng dịch vụ,&hellip;)</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Tầng 6 &ndash; 25 d&agrave;nh cho 483 căn hộ chất lượng</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp;Khu căn hộ T&amp;T Victoria sở hữu chuỗi 45 dịch vụ tiện &iacute;ch đa dạng, lần đầu xuất hiện tại Vinh: Khu sinh hoạt cộng đồng, khu vui chơi trẻ em, cảnh quan c&acirc;y xanh, đường dạo bộ, dịch vụ an ninh 24/7, dịch vụ vệ sinh chung, dịch vụ tr&ocirc;ng giữ xe v&agrave; c&aacute;c dịch vụ theo y&ecirc;u cầu.</span></span></li>\r\n</ol>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Loại căn hộ</strong>: Được chia th&agrave;nh c&aacute;c căn 1-3 PN từ khoảng 47m2 &ndash; 100m2</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Ng&agrave;y khởi c&ocirc;ng</strong> : Qu&yacute; I/2016</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Dự kiến b&agrave;n giao</strong>: Qu&yacute; III/2018</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong>- Sở hữu</strong>: Sổ đỏ l&acirc;u d&agrave;i.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><span style=\"color:#000000;\">-&nbsp;</span></span><strong style=\"color: rgb(0, 0, 0); font-size: 14px;\">Đơn vị ph&acirc;n phối ch&iacute;nh thức:</strong><span style=\"color: rgb(0, 0, 0); font-size: 14px;\"> C&ocirc;ng ty cổ phần dịch vụ v&agrave; địa ốc Đất Xanh Miền Bắc.</span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:14px;\"><span style=\"color:#000000;\">THIẾT KẾ CĂN HỘ CHUNG CƯ T &amp; T VICTORIA VINH:</span></span></u></h2>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Được chia th&agrave;nh c&aacute;c căn 1-3 PN từ khoảng 47m2 &ndash; 100m2, thiết kế tối ưu c&ocirc;ng năng sử dụng, 100% đều tiếp x&uacute;c &aacute;nh s&aacute;ng tự nhi&ecirc;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">&nbsp; &nbsp;Phối cảnh căn hộ:</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Phối cảnh căn hộ T &amp; T Victoria Vinh 1\" src=\"/public/upload/images/T-T/phoi-canh-can-ho%20-t-t-6.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Phối cảnh căn hộ T &amp; T Vinh 2\" src=\"/public/upload/images/T-T/phoi-canh-can-ho%20-t-t-2.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"T &amp; T Vinh phối cảnh phòng ngủ\" src=\"/public/upload/images/T-T/t-t-phoi-canh-phong-ngu.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	<u><span style=\"font-size:14px;\"><span style=\"color:#000000;\">CH&Iacute;NH S&Aacute;CH B&Aacute;N H&Agrave;NG CHUNG CƯ T&amp; T VICTORIA VINH:</span></span></u></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">- Chỉ từ <strong>676 Triệu</strong> Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; ngay cơ hội sở hữu căn hộ vị tr&iacute; trung t&acirc;m đẳng cấp nhất th&agrave;nh Vinh.</span></span></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><strong><span style=\"background-color:#ffd700;\">- Đặc biệt: </span></strong></span></span></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Đợt 1 thanh to&aacute;n 25% K&yacute; Hợp đồng mua b&aacute;n</span></span></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Ng&acirc;n h&agrave;ng SHB, Vietcombank, TPBank hỗ trợ cho vay l&ecirc;n đến 75% Gi&aacute; trị căn hộ</span></span></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Chủ đầu tư hỗ trợ l&atilde;i suất 0% v&agrave; &acirc;n hạn nợ gốc đến khi b&agrave;n giao nh&agrave;</span></span></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">+ Chiết khấu d&agrave;nh cho kh&aacute;ch h&agrave;ng kh&ocirc;ng vay ng&acirc;n h&agrave;ng</span></span></h2>\r\n<h2>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\">- Gi&aacute; cả v&agrave; chất lượng chưa bao giờ tốt hơn, b&agrave;n giao nội thất cao cấp, sang trọng.</span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Tiêu chuẩn bàn giao căn hộ T &amp; T Nghệ An\" src=\"/public/upload/images/T-T/tieu-chuan-ban-giao.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><img alt=\"Tiêu chuẩn bàn giao căn hộ T &amp; T Nghệ An\" src=\"/public/upload/images/T-T/tieu-chuan-ban-giao-2.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<h2>\r\n	&nbsp;</h2>\r\n<h2>\r\n	-------------------------------------------------------------</h2>\r\n<h2 style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư.</strong></span></span></h2>\r\n<h2 style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Mọi chi ti&ecirc;́t vui lòng li&ecirc;n h&ecirc;̣:</strong></span></span></h2>\r\n<h2 style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></h2>\r\n<h2>\r\n	&nbsp;</h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>C&ocirc;ng ty CP Dịch vụ v&agrave; Đầu tư T&acirc;n Long<br />\r\n	Hotline: 094.220.6262 ( Ms. Tr&acirc;m)</strong></span></span></p>\r\n<h2 style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Email: tramnt.bdstanlongnghean@gmail.com</strong></span></span></h2>\r\n<h2>\r\n	&nbsp;</h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><em>Xin cảm ơn quý khách!</em></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#000000;\"><em>Tham khảo th&ecirc;m một số dự &aacute;n kh&aacute;c:</em></span></span></p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/trung-duc-tower/56.html\"><strong>Chung cư cao cấp Trung Đức số 2 L&ecirc; Lợi TP Vinh</strong></a></span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-bao-son-green-pearl/51.html\"><span style=\"color:#000000;\"><strong>Chung cư Bảo Sơn mặt đường L&ecirc; Lợi Vinh</strong></span></a></span></li>\r\n</ol>\r\n<p>\r\n	&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 'Lãi suất CK 8,5%', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Chung cư cao cấp T & T Victoria Vinh LS CK Khủng 8,5% LH 0942206262', '20-8-2017', '21-8-2020', 'chung-cu-cao-cap-t-t-victoria-', 'Mua chung cư cao cấp T & T Victoria TP Vinh Cơ hội sở hữu xe MAZDA trị giá 650 triệu đồng. (Đầu tiên và Duy nhất mua nhà tặng XE HƠI tại Nghệ An)', 5, 'chung cư tp vinh, victoria vinh, chung cư t&t victoria vinh, chung cư t&t nghệ an, chung cư victoria vinh, t&t quang trung vinh, dự án t&t victoria t&t vinh', '0'),
(59, 27, 7, NULL, 'BIỆT THỰ VEN BIỂN - CUALO GOLF RESORT', 'Đường Bình Minh', 'Công ty Cổ phần Gofl biển Cửa Lò.', NULL, NULL, '094.220.6262', 3488, '/public/upload/files/Cualo-Golf-Resort5.jpg', NULL, NULL, 0, '1', NULL, '<p>\r\n	<span style=\"font-size:18px;\"><span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>1. T&ecirc;n dự &aacute;n:</strong></span></span> TỔ HỢP S&Acirc;N GOLF KH&Aacute;CH SẠN, BIỆT THỰ, NGHỈ DƯỠNG.</span></p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>2. Chủ đầu tư dự &aacute;n:</strong></span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:18px;\"><strong>T&ecirc;n giao dịch:</strong> C&ocirc;ng ty CP Golf Biển Cửa L&ograve; (Cua Lo Golf Resort).</span></li>\r\n	<li>\r\n		<span style=\"font-size:18px;\"><strong>Địa chỉ: </strong>Đường B&igrave;nh Minh, X&atilde; Nghi Hương, Thị x&atilde; Cửa L&ograve;, Tỉnh Nghệ An.</span></li>\r\n	<li>\r\n		<span style=\"font-size:18px;\">L&agrave; c&ocirc;ng ty chuy&ecirc;n kinh doanh tổ hợp s&acirc;n golf - du lịch - kh&aacute;ch sạn - nh&agrave; h&agrave;ng, đầu tư c&aacute;c dự &aacute;n theo phương thức x&acirc;y dựng - kinh doanh - chuyển giao, x&acirc;y dựng v&agrave; kinh doanh cơ sở hạ tầng cầu dường - khu c&ocirc;ng nghiệp - khu d&acirc;n cư - khu đ&ocirc; thị...</span></li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>3. Vị tr&iacute; dự &aacute;n trong quy hoạch v&ugrave;ng:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp; &nbsp;Cửa l&ograve; l&agrave; một địa điểm du lịch trọng điểm v&agrave; nổi tiếng nhất của Nghệ An c&aacute;ch Th&agrave;nh phố Vinh 18km. Với bờ biển d&agrave;i v&agrave; đẹp tự nhi&ecirc;n Biển Cửa l&ograve; lu&ocirc;n thu h&uacute;t lượng du kh&aacute;ch đ&ocirc;ng đ&uacute;c đặc biệt l&agrave; v&agrave;o m&ugrave;a h&egrave;. Thời Ph&aacute;p thuộc người Ph&aacute;p&nbsp; nhận thấy Cửa L&ograve; l&agrave; v&ugrave;ng biển nghỉ m&aacute;t tốt n&ecirc;n đ&atilde; x&acirc;y dựng một khu biệt thự d&agrave;nh ri&ecirc;ng cho người Ph&aacute;p để nghi ngơi v&agrave; thư giản.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><img alt=\"\" src=\"/public/upload/images/1%20RS/29.jpg\" /><br />\r\n	&nbsp; &nbsp;Dự &aacute;n Tổ Hợp S&acirc;n Golf Kh&aacute;ch sạn, biệt thự, nghỉ dưỡng nằm dọc theo bờ biển Cửa L&ograve;. L&agrave; một vị tr&iacute; v&ocirc; c&ugrave;ng&nbsp; thuận lợi, tạo cho gi&aacute; trị của dự &aacute;n tăng l&ecirc;n gấp bội.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><img alt=\"\" src=\"/public/upload/files/Cualo-Golf-Resort5(1).jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:18px;\">- Khu biệt thự cao cấp Cualo Golf Resort được thừa hưởng sự ưu đ&atilde;i tuyệt vời của thi&ecirc;n nhi&ecirc;n. Ph&iacute;a trước dự &aacute;n l&agrave; biển Cửa L&ograve;, sau lưng l&agrave; kh&ocirc;ng gian xanh tho&aacute;ng đ&atilde;ng của s&acirc;n golf 18 lỗ. Với giải ph&aacute;p quy hoạch kiến tr&uacute;c th&ocirc;ng minh, khu biệt thự được bố tr&iacute; d&atilde;y so le nhau với những khoảng c&aacute;ch hợp l&yacute; mang lại vẻ sinh động cho mặt tiền chung v&agrave; tho&aacute;ng đ&atilde;ng đến từng căn biệt thự. Mỗi căn sẽ được hưởng tầm nh&igrave;n của cả hai hướng: hướng từ biển v&agrave; hướng từ s&acirc;n golf.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/phoi-canh1.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/tien-ich-cua-lo-1.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/san-golf-cua-lo-golf3%20(1).jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp; &nbsp;Cửa L&ograve; Golf Resort được quy hoạch tổng thể l&agrave; sự kết hợp giữa thể thao, tắm biển nghỉ dưỡng v&agrave; du lịch sinh th&aacute;i sẽ l&agrave; nơi dừng ch&acirc;n l&yacute; tưởng cho những ai y&ecirc;u th&iacute;ch bộ m&ocirc;n thể thao qu&yacute; tộc n&agrave;y v&agrave; tận hưởng cuộc sống thượng lưu ho&agrave;n hảo.&nbsp;Những biệt thự được thiết kế độc đ&aacute;o, hiện đại v&agrave; sang trọng sẽ l&agrave; lựa tuyệt vời cho những đại gia muốn c&oacute; một ng&ocirc;i nh&agrave; thứ hai với mục đ&iacute;ch thư gi&atilde;n sau những ng&agrave;y l&agrave;m việc căng thẳng v&agrave; nghỉ dưỡng - giải tr&iacute; trong những dịp đặc biệt c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave;.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/Masterplan%20in%20so%20thua.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-size:22px;\"><span style=\"color:#ff0000;\"><strong>Lợi thế của dự &aacute;n:</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:18px;\">- Nằm trong v&ugrave;ng t&acirc;m điểm du lịch của Bắc trung bộ v&agrave; miền Bắc.<br />\r\n	- Dự &aacute;n nằm dọc theo bờ biển đẹp của Cửa L&ograve;.<br />\r\n	- L&agrave; s&acirc;n Golf duy nhất trong v&ugrave;ng v&agrave; được thiết kế bởi IMG nh&agrave; thiết kế h&agrave;ng đầu thế giới về s&acirc;n Golf.<br />\r\n	- Nằm gần c&aacute;c dự &aacute;n lớn của c&aacute;c tập đo&agrave;n nước ngo&agrave;i (Refinery Project, Formosa steel mill project&hellip;) v&igrave; vậy sẽ c&oacute; sẵn một lượng kh&aacute;ch tiềm năng đ&aacute;ng kể.<br />\r\n	- Ngo&agrave;i s&acirc;n golf c&ograve;n c&oacute; khu phức hợp giải tr&iacute; lớn ven biển sẽ thu h&uacute;t kh&aacute;ch du lịch từ H&agrave; Nội v&agrave; c&aacute;c tỉnh th&agrave;nh l&acirc;n cận.<br />\r\n	- C&aacute;c biệt thự c&oacute; thiết kế hiện đại, độc đ&aacute;o v&agrave; được bố tr&iacute; trong khu&ocirc;n vi&ecirc;n s&acirc;n để tận hưởng c&aacute;c kh&ocirc;ng gian thi&ecirc;n nhi&ecirc;n của s&acirc;n golf v&agrave; b&atilde;i biển. L&agrave; ng&ocirc;i nh&agrave; thứ 2 l&yacute; tưởng d&agrave;nh cho giới thượng lưu từ H&agrave; Nội v&agrave; c&aacute;c tỉnh kế cận.<br />\r\n	- Được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i của tỉnh nhằm ph&aacute;t triển du lịch v&ugrave;ng. Tỉnh cũng đ&atilde; c&oacute; c&aacute;c ch&iacute;nh s&aacute;ch cụ thể để thu h&uacute;t kh&aacute;ch du lịch trong v&agrave; ngo&agrave;i nước.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>H&Igrave;NH ẢNH THỰC TẾ</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20RS/23.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20RS/24.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20RS/25.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20RS/26.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20RS/27.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><em>H&atilde;y gọi ngay cho ch&uacute;ng t&ocirc;i để&nbsp; được tư vấn v&agrave; nhận ch&iacute;nh s&aacute;ch tốt nhất từ chủ đầu tư.</em></span></span><br />\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, 'CHIẾT KHẤU 7% CHO KHÁCH HÀNG ĐẶT CỌC TỪ NGÀY 7/11/2018.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'GOLF RESORT BIỆT THỰ BIỂN CỬA LÒ - Sở hữu BĐS Quý tộc - Đầu tư BĐS đẳng cấp', '4-5-2018', '30-11-2018', 'biet-thu-ven-bien-cualo-golf-resort', 'Cửa Lò Golf Resort được quy hoạch tổng thể là sự kết hợp giữa thể thao, tắm biển nghỉ dưỡng và du lịch sinh thái sẽ là nơi dừng chân lý tưởng', 9, 'CỬA LÒ GOLF RESORT, GOLF RESORT CỬA LÒ, golf của lò, đất nền golf resort cửa lò', '0'),
(55, 27, 7, NULL, 'SAIGON SKY', '26 Nguyễn Thái Học', 'Công ty cổ phần Đầu tư và Phát triển Miền Trung', NULL, NULL, NULL, 721, '/public/upload/images/SG%20SKy/saigon-sky-tpvinh-2.jpg', NULL, NULL, 0, '0', NULL, '<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">Email: lamtlt.nghean@dxmb.vn</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">Website: http://www.bdsnghetinh.net/</span></strong></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '12-1-2018', '30-11-2018', 'saigon-sky', NULL, 150, NULL, '0'),
(56, 26, 7, NULL, 'TRUNG ĐỨC TOWER', 'SỐ 2 LÊ LỢI', 'Công ty cổ phần xây dựng Trung Đức', '18/12/2017', NULL, '0942206262', 3302, '/public/upload/images/trung-duc-tower/anh-thuc-te-trung-duc-tower.jpg', NULL, NULL, 0, '1', NULL, '<p>\r\n	<span style=\"font-size:16px;\"><strong>Cập nhật Ưu đ&atilde;i mới nhất: Thời gian từ 01/02/2018 đến 28/02/2018.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>&nbsp;&nbsp;&nbsp; Chương tr&igrave;nh &ldquo;Mừng t&acirc;n gia du xu&acirc;n thả ga&rdquo;</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">KH khi mua căn hộ dự &aacute;n Trung Đức Tower trong thời gian khuyến mại sẽ được nhận 01 tour du lịch Singapore d&agrave;nh cho 02 người trị gi&aacute; 20 triệu.</span></p>\r\n<p>\r\n	<em><span style=\"font-size:14px;\">Trường hợp kh&aacute;ch h&agrave;ng kh&ocirc;ng nhận chuyến du lịch sẽ được khấu trừ 20 triệu v&agrave;o gi&aacute; b&aacute;n căn hộ chưa gồm VAT v&agrave; KPBT.</span></em></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/trung-duc-tower-mung-xuan-2018-qua%20tang.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;Chung cư cao cấp Trung Đức &ndash; số 2 L&ecirc; Lợi, TP Vinh</strong> nằm ở vị tr&iacute; đắc địa bậc nhất th&agrave;nh phố. Ph&iacute;a t&acirc;y tiếp gi&aacute;p quốc lộ 1A mặt tiền đường L&ecirc; Lợi, ph&iacute;a bắc tiếp gi&aacute;p đường L&yacute; Thường Kiệt k&eacute;o d&agrave;i, ph&iacute;a nam nh&igrave;n ra hồ Thủy Tạ với đ&agrave;i phun nước lung linh cho ta một kh&ocirc;ng gian sống l&iacute; tưởng nhất.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">&nbsp; &nbsp;Đơn vị thiết kế l&agrave; c&ocirc;ng ty Li&ecirc;n Doanh tư vấn thiết kế Việt Nam &ndash; Singapore đ&atilde; mang đến cho Chung cư cao cấp Trung Đức một thiết kế h&igrave;nh chiếc thuyền độc đ&aacute;o mang đẳng cấp Singapore.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/trung-duc-tower-tpvinh.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>Tổng quan dự &aacute;n Chung cư cao cấp Trung Đức Tower TP Vinh</strong></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>Địa chỉ: </strong>số 2 L&ecirc; Lợi, phường Hưng b&igrave;nh, TP Vinh, Nghệ An<br />\r\n	<strong>Chủ đầu tư: </strong>C&ocirc;ng ty cổ phần x&acirc;y dựng Trung Đức<br />\r\n	<strong>Đơn vị thi c&ocirc;ng:</strong> C&ocirc;ng ty TNHH x&acirc;y dựng hạ tầng Bảo Tiến Ph&aacute;t<br />\r\n	<strong>Đơn vị thiết kế:</strong> Li&ecirc;n doanh tư vấn thiết kế Việt Nam - Singapore<br />\r\n	<strong>Khởi c&ocirc;ng:</strong> 18/08/2016<br />\r\n	<strong>Ho&agrave;n th&agrave;nh:</strong> 18/12/2017</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/trung-duc-tower-tpvinh-2.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>Vị Tr&iacute; dự &aacute;n Trung Đức Tower TP Vinh</strong></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\">&nbsp; &nbsp;Chung cư Trung Đức TP Vinh c&oacute; vị tr&iacute; si&ecirc;u đắc địa nằm ngay tr&ecirc;n trục đường quốc lộ 1A đoạn L&ecirc; Lợi, Đối diện bến xe Vinh, khu kinh doanh sầm uất bậc nhất, giao thương to&agrave;n th&agrave;nh phố.</span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Ph&iacute;a t&acirc;y tiếp gi&aacute;p mặt đường L&ecirc; Lợi</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Ph&iacute;a bắc tiếp gi&aacute;p mặt đường L&yacute; Thường Kiệt k&eacute;o d&agrave;i</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Ph&iacute;a Nam l&agrave; hồ Thủy Tạ diện t&iacute;ch 3400 m2, với c&ocirc;ng tr&igrave;nh đ&agrave;i phun nước hiện đại</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Ph&iacute;a Đ&ocirc;ng gi&aacute;p đường quy hoạch rộng 5 m&eacute;t</strong></span></li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/chung-cu-trung-duc-tp-vinh-2.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>Thiết kế chung cư Trung Đức:</strong></span></span></h2>\r\n<h3>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000cd;\"><strong>Chung cư cao cấp Trung Đức TP Vinh thiết kế gồm 22 tầng:</strong></span></span></h3>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Tầng hầm để xe diện t&iacute;ch:</strong> 3500M2</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Tầng 1 - 4 Trung t&acirc;m thương mại:</strong> Diện t&iacute;ch mỗi s&agrave;n 2000m2</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Tầng 5 - 19 căn hộ cao cấp:</strong> Diện t&iacute;ch lớn từ 83 -100m2. Tất cả đều c&oacute; 2 ph&ograve;ng ngủ v&agrave; 2 ph&ograve;ng vệ sinh mang đến c&oacute; một kh&ocirc;ng gian rộng r&atilde;i tho&aacute;i m&aacute;i.</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Tầng 20 - 21:</strong> căn hộ Penhouse thiết kế đặc biệt, đẳng cấp.</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Tầng 22:</strong> Biệt thự s&acirc;n vườn tr&ecirc;n kh&ocirc;ng.</span></li>\r\n</ol>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>Tiện &iacute;ch dự &aacute;n Chung cư Trung Đức TP Vinh</strong></span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Tầng hầm để xe rộng r&atilde;i với diện t&iacute;ch lớn</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>4 tầng thương mại sầm uất với đầy đủ những thương hiệu nổi tiếng nhất</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Thiết kế căn hộ sang trọng, rộng r&atilde;i dễ d&agrave;ng bố tr&iacute; nội thất.</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Khu vui chơi trẻ em rộng r&atilde;i, tho&aacute;ng m&aacute;t</strong></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>khu&ocirc;n vi&ecirc;n c&ocirc;ng cộng: 2.448 m2/ Tổng diện t&iacute;ch dự &aacute;n 4.886 m2&nbsp;</strong></span></li>\r\n</ul>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:16px;\"><em>Ngo&agrave;i ra dự &aacute;n được sử dụng kh&ocirc;ng gian hai vỉa h&egrave; tr&ecirc;n hai tuyến đường ch&iacute;nh với h&agrave;ng c&acirc;y cổ thụ, ghế đ&aacute; đan xen bồn hoa tạo n&ecirc;n kh&ocirc;ng gian m&aacute;t mẻ v&agrave; l&agrave; điểm dừng ch&acirc;n cho kh&aacute;ch h&agrave;ng tham quan mua sắm tại khu thương mại, ph&iacute;a mặt hồ Thủy Tạ được cải tạo v&agrave; lắp đ&agrave;i phun nước kết hợp đ&egrave;n m&agrave;u tạo điểm nhấn cho t&ograve;a nh&agrave; v&agrave; kh&ocirc;ng gian của Trung t&acirc;m thương mại.</em></span></p>\r\n</blockquote>\r\n<p>\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>H&igrave;nh ảnh thực tế Chung cư cao cấp Trung Đức Tower:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/anh-thuc-te-trung-duc-tower.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/trung-duc-tower-TTTM.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/Khuon-vien-trung-duc-tower.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/S%E1%BA%A3nh%20Trung%20%C4%90%E1%BB%A9c%20Tower.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c/trung-duc-tower-anh-thuc-te-1.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\"><strong>Tiến độ:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">Khởi c&ocirc;ng ng&agrave;y 18/8/2016<br />\r\n	B&agrave;n giao 18/12/2017<br />\r\n	<strong>Hiện tại đang b&agrave;n giao nh&agrave;.</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong style=\"font-size: 14px;\">-------------------------------------------------------------</strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><em>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư.</em><br />\r\n	<strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#ff0000;\"><em><strong style=\"font-size: 14px;\">Tham khảo th&ecirc;m một số dự &aacute;n kh&aacute;c:</strong></em></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-bao-son-green-pearl-/51.html\"><strong style=\"font-size: 14px;\">Chung cư Bảo Sơn mặt đường L&ecirc; Lợi Vinh</strong></a></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-cao-cap-t-t-victoria-/53.html\"><strong style=\"font-size: 14px;\">Chung cư cao cấp T&amp;T Victoria TP Vinh</strong></a></span></p>\r\n<p>\r\n	&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, '20000000', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Trung Đức Tower - Chung cư bậc nhất TP Vinh', '12-1-2018', '4-12-2018', 'trung-duc-tower', 'Chung cư Trung Đức TP Vinh -nơi an cư lý tưởng - cơ hội sinh lời cao. Vị trí vàng, kiến trúc singapore hình con thuyền bên bờ hồ Thủy Tạ LH 0942206262', 0, 'chung cư thành phố vinh, chung cu tp vinh, chung cu vinh nghe an, chung cư ở vinh, mua chung cư trả góp ở vinh, bán chung cư thành phố vinh, cần mua căn hộ chung cư', '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(54, 27, 7, NULL, 'KHU ĐÔ THỊ GOLF VILLAGE CỬA LÒ', 'NGUYỄN HUỆ, THỊ XÃ CỬA LÒ', 'Công ty Cổ phần Gofl biển Cửa Lò.', NULL, NULL, '0941.441.991.', 5769, '/public/upload/files/2.jpg', 'LIÊN HỆ.', NULL, 1, '1', NULL, '<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<br />\r\n	<img alt=\"\" src=\"/public/upload/files/1.jpg\" /><br />\r\n	<br />\r\n	&nbsp;</p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Khu Đ&ocirc; Thị biển đẳng cấp Nguyễn Sinh Cung tọa lạc ngay tr&aacute;i tim của trung t&acirc;m h&agrave;nh ch&iacute;nh chiến lược thị x&atilde; Cửa L&ograve;, kết nối dễ d&agrave;ng tới c&aacute;c c&ocirc;ng tr&igrave;nh trọng điểm: s&acirc;n bay Vinh, ủy ban nh&acirc;n d&acirc;n thị x&atilde; Cửa L&ograve;, chợ Nghi Hương, đảo Lan Ch&acirc;u, Quốc Lộ 1A, b&atilde;i biển, quảng trường B&igrave;nh Minh, c&ocirc;ng an thị x&atilde; Cửa l&ograve;&hellip; Đặc biệt chỉ cần 5 ph&uacute;t đi bộ từ Khu Đ&ocirc; Thị l&agrave; c&oacute; thể tận hưởng hương vị của biển, thỏa th&iacute;ch v&ugrave;ng vẫy s&oacute;ng nước Cửa L&ograve;.</span><br />\r\n	<br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/files/tong%20mat%20bang-%20co%20cay%20xanh.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:18px;\"><strong>1.Giao th&ocirc;ng v&ocirc; cùng thu&acirc;̣n lợi:</strong></span><br />\r\n	<br />\r\n	&nbsp; &nbsp;Khu đ&ocirc; thị nằm tr&ecirc;n 2 trục đướng chính của Cửa Lò: Đường Nguy&ecirc;̃n Sinh Cung r&ocirc;̣ng 47m và đường s&ocirc;́ 3 Nguy&ecirc;̃n Hu&ecirc;̣ r&ocirc;̣ng 36m. Từ Khu đ&ocirc; thị có th&ecirc;̉ di chuy&ecirc;̉n d&ecirc;̃ dàng tới b&acirc;́t kỳ đ&acirc;u ở Cửa Lò và sang các khu vực l&acirc;n c&acirc;̣n.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Đặc bi&ecirc;̣t tuy&ecirc;́n đường đại l&ocirc;̣ Vinh &ndash; Cửa Lò r&ocirc;̣ng 72m đang thi c&ocirc;ng sẽ rút ngắn khoảng cách từ Vinh &ndash; Cửa Lò chỉ còn 10km để sự di chuyển qu&aacute; dễ d&agrave;ng trong tầm tay.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp;&nbsp;</span><span style=\"font-size: 14px;\">Đặc biệt, hiện đang khởi c&ocirc;ng c&ocirc;ng tr&igrave;nh cầu Cửa Hội bắc qua S&ocirc;ng Lam, kết nối thị x&atilde; Cửa L&ograve; (tỉnh Nghệ An) v&agrave; huyện Nghi Xu&acirc;n (tỉnh H&agrave; Tĩnh) nằm tr&ecirc;n tuyến đường bộ ven biển Việt Nam đang triển khai v&agrave; ho&agrave;n th&agrave;nh v&agrave;o năm 2020, c&ocirc;ng tr&igrave;nh cầu Cửa Hội ho&agrave;n th&agrave;nh sẽ mở ra nhiều hướng đi mới trong ph&aacute;t triển kinh tế &ndash; x&atilde; hội của c&aacute;c địa phương, g&oacute;p phần tăng sức hấp dẫn v&agrave; cải thiện m&ocirc;i trường đầu tư, th&uacute;c đẩy ph&aacute;t triển kinh tế &ndash; x&atilde; hội, củng cố an ninh &ndash; quốc ph&ograve;ng tr&ecirc;n địa b&agrave;n hai tỉnh Nghệ An &ndash; H&agrave; Tĩnh v&agrave; v&ugrave;ng Bắc Trung Bộ.</span><span style=\"font-size: 14px;\">&nbsp;&nbsp;</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/files/4---khu-trung-tam-thuong-mai-dv-do-thi_25879014042_o(1).jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Giao th&ocirc;ng n&ocirc;̣i khu : V&ocirc; cùng ti&ecirc;̣n lợi các con đường trong KĐT được quy hoạch đ&ocirc;̀ng b&ocirc;̣ rộng 12m, bài bản đã hình thành với hàng loạt c&acirc;y xanh ven đường mang lại kh&ocirc;ng kh&iacute; trong l&agrave;nh, vẻ đẹp cảnh quan v&agrave; kh&ocirc;ng gian sống xanh cho gia đ&igrave;nh.</span></p>\r\n<p>\r\n	<br />\r\n	<br />\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:18px;\"><strong>2. Ti&ecirc;̣n ích, ti&ecirc;u chu&acirc;̉n s&ocirc;́ng vượt tr&ocirc;̣i:</strong></span><br />\r\n	<br />\r\n	&nbsp; &nbsp;Đ&acirc;y là Khu đ&ocirc; thị trung t&acirc;m, duy nh&acirc;́t đ&ocirc;̀ng b&ocirc;̣ theo ti&ecirc;u chu&acirc;̉n của m&ocirc;̣t Khu đ&ocirc; thị cao c&acirc;́p mang đ&ecirc;́n những giá trị s&ocirc;́ng đích thực th&ocirc;ng qua h&ecirc;̣ th&ocirc;́ng ti&ecirc;̣n ích đ&acirc;̀y đủ, ch&acirc;́t lượng cao như: Si&ecirc;u thị, Khu vui chơi giải tr&iacute;, B&ecirc;̉ bơi, S&acirc;n Tennis, Nh&agrave; trẻ, Trung t&acirc;m Y tế, Nh&agrave; sinh hoạt văn h&oacute;a cộng đồng, Khu c&ocirc;ng vi&ecirc;n c&acirc;y xanh, H&ocirc;̀ nước&hellip; Tất cả sẽ mang lại cho gia đ&igrave;nh một m&ocirc;i trường sống l&yacute; tưởng bậc nhất giữa l&ograve;ng thi&ecirc;n nhi&ecirc;n.<br />\r\n	<br />\r\n	&nbsp; &nbsp;Cư d&acirc;n của KDT đ&ecirc;̀u là những người thành đạt, có địa vị trong xã h&ocirc;̣i, c&ugrave;ng với gia đ&igrave;nh m&igrave;nh sẽ tạo n&ecirc;n một KĐT tri thức, văn minh với tr&igrave;nh độ d&acirc;n tr&iacute; cao v&agrave; an ninh bậc nhất Cửa L&ograve;.</span><br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/files/3---khu-chung-cu_25367097764_o.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:18px;\"><strong>3. Ti&ecirc;̀m năng và cơ h&ocirc;̣i đ&acirc;̀u tư an toàn, sinh lời cao nh&acirc;́t:&nbsp;</strong></span><br />\r\n	<br />\r\n	&nbsp; Cửa Lò hi&ecirc;̣n đang là m&ocirc;̣t trong những bãi bi&ecirc;̉n đẹp nh&acirc;́t Vi&ecirc;̣t Nam với lượng khách du lịch hàng năm l&ecirc;n đ&ecirc;́n g&acirc;̀n 3 tri&ecirc;̣u lượt. Hi&ecirc;̣n Tỉnh Ngh&ecirc;̣ An cũng như UBND TX. Cửa Lò đang có những ch&ecirc;́ đ&ocirc;̣ ưu đãi đặc bi&ecirc;̣t đ&ecirc;̉ Cửa Lò trở thành m&ocirc;̣t trong những khu đ&ocirc; thị bi&ecirc;̉n hi&ecirc;̣n đại, phát tri&ecirc;̉n mạnh mẽ và trở thành Khu đ&ocirc; thị loại 2, là cực tăng trưởng kinh t&ecirc;́ quan trọng của Tỉnh Ngh&ecirc;̣ An. ( Chi tiết:&nbsp;</span><span style=\"font-size:14px;\"><strong><a href=\"http://www.bdsnghetinh.net/tap-doan-flc-hemaraj-rot-hon-chuc-nghin-ty-dong-dau-tu-vao-nghe-an-/52.html\">TẬP ĐO&Agrave;N FLC, VIN, ... R&Oacute;T H&Agrave;NG CHỤC TỈ ĐỒNG V&Agrave;O NGHỆ AN</a> </strong>)</span><br />\r\n	<br />\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;Đ&acirc;̀u tư vào B&acirc;́t đ&ocirc;̣ng sản ven bi&ecirc;̉n đang là xu hướng và k&ecirc;nh đ&acirc;̀u tư an toàn, sinh lời t&ocirc;́t nh&acirc;́t hi&ecirc;̣n nay khi mà các k&ecirc;nh đ&acirc;̀u tư như Vàng, Chứng khoán&hellip;v..v..b&acirc;́p b&ecirc;nh, rủi ro và m&acirc;́t nhi&ecirc;̀u thời gian. Đặc bi&ecirc;̣t, theo dự báo của các chuy&ecirc;n gia và chi&ecirc;́n lược của NHNN thì lãi su&acirc;́t NH hi&ecirc;̣n đang là th&acirc;́p nh&acirc;́t và v&acirc;̃n sẽ giữ vững ở mức hi&ecirc;̣n tại trong vòng những năm tới. Khi mà lạm phát v&acirc;̃n tăng trưởng ở mức 2 con s&ocirc;́ thì với nhi&ecirc;̀u người d&acirc;n, gửi ti&ecirc;́t ki&ecirc;̣m tức là m&acirc;́t ti&ecirc;̀n.<br />\r\n	<br />\r\n	&nbsp; &nbsp;Nắm bắt được xu th&ecirc;́ cũng như cơ h&ocirc;̣i, các &ocirc;ng lớn trong lĩnh vực BĐS như T&acirc;̣p Đoàn VinGroup, FLC, SUN, KCN HEMAJAI đang tri&ecirc;̉n khai c&aacute;c dự án c&aacute;c dự &aacute;n nghỉ dưỡng, vui chơi,... tại khu vực Cửa H&ocirc;̣i, thị xã Cửa Lò. Ngoài ra còn các dự án Resort, khách sạn 5*, khu đ&ocirc; thị Nghi Hoà, khu du lịch sinh thái Nghi Thu&hellip;đang được tri&ecirc;̉n khai, góp ph&acirc;̀n tạo đi&ecirc;̉m nh&acirc;́n cho du lịch Cửa Lò.</span><br />\r\n	<br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/files/2.jpg\" /></span><br />\r\n	<br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/19--mau-chung-cu_25367078694_o.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/20---mau-khach-san_25699224980_o.jpg\" /><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/24---trung-tam-thuong-mai_25699221870_o.jpg\" /><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/Mau%20BT%20Golf%20-mau%205%20view%202.jpg\" /><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/15--mau-biet-thu-song-lap_25367083564_o.jpg\" /><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/22---mau-truong-mam-non_25973860106_o.jpg\" /><img alt=\"\" src=\"/public/upload/images/GOLF%20CUA%20LO/golf%20villa/29--bai-xe_25999772455_o.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\"><span style=\"font-size:18px;\"><strong>4. Ưu đ&atilde;i hấp dẫn d&agrave;nh cho Kh&aacute;ch h&agrave;ng:</strong></span><br />\r\n	<br />\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;- Hiện tại c&aacute;c l&ocirc; đất đ&atilde; c&oacute; sổ đỏ vĩnh viễn.</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;- Thủ tục nhanh gọn, được hỗ trợ về giấy tờ v&agrave; thủ tục ph&aacute;p l&yacute;.</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;- Kh&aacute;ch h&agrave;ng c&oacute; thể nhận đất v&agrave; x&acirc;y nh&agrave; ngay với gi&aacute; chỉ từ 4,1tr/m2- 7.5tr/m2 với di&ecirc;̣n tích từ 160m2 &ndash; 350m2.</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;- Hỗ trợ vay ng&acirc;n h&agrave;ng tối đa 70% gi&aacute; trị l&ocirc; đất.</strong></span></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong style=\"font-size: 14px;\">-------------------------------------------------------------</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư.</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:24px;\"><strong>Xin cảm ơn quý khách!</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;</span></p>\r\n<p>\r\n	&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'KHU ĐÔ THỊ GOLF VILLAGE CỬA LÒ', '6-12-2017', '30-11-2018', 'khu-do-thi-golf-village-cua-lo', 'KHU ĐÔ THỊ GOLF Village CỬA LÒ,  Khu Đô Thị biển đẳng cấp Nguyễn Sinh Cung tọa lạc ngay trái tim của trung tâm hành chính chiến lược thị xã Cửa Lò...', 1, 'KHU ĐÔ THỊ CỬA LÒ, KHU ĐÔ THỊ GOLF VILLAGE CỬA LÒ, golf village cửa lò,', '0'),
(57, 26, 7, NULL, 'Shophouse T&T Victoria TP Vinh', 'Số 1 Quang Trung', 'Công ty CP Tập đoàn T&T', NULL, NULL, '094.220.6262', 1500, '/public/upload/images/T-T/Shophouse-t-t-victoria-nghe-an.jpg', NULL, NULL, 0, '1', NULL, '<p>\r\n	<strong><span style=\"color:#696969;\"><span style=\"font-size:16px;\">Trong thời gian gần đ&acirc;y, m&ocirc; h&igrave;nh nh&agrave; phố thương mại (shophouse) xuất hiện ng&agrave;y c&agrave;ng phổ biến hơn tr&ecirc;n thị trường bất động sản (BĐS) Việt Nam n&oacute;i chung cũng như thị trường BĐS TP Vinh n&oacute;i ri&ecirc;ng. Với ưu điểm c&oacute; thể kết hợp để ở v&agrave; kinh doanh, nh&agrave; phố thương mại trở th&agrave;nh xu hướng mới, nhận được sự quan t&acirc;m của nhiều đối tượng kh&aacute;ch h&agrave;ng. H&atilde;y c&ugrave;ng t&igrave;m hiểu </span></span><span style=\"color:#008080;\"><span style=\"font-size:16px;\">Shophouse T&amp;T Viectoria TP Vinh, Nghệ An </span></span><span style=\"color:#696969;\"><span style=\"font-size:16px;\">- một trong những dự &aacute;n điển h&igrave;nh v&agrave; g&acirc;y sốt nhất trong thời gian vừa qua để hiểu r&otilde; hơn về m&ocirc; h&igrave;nh n&agrave;y nh&eacute;.&nbsp;</span></span></strong></p>\r\n<p>\r\n	<strong><span style=\"color:#696969;\"><span style=\"font-size:16px;\"><img alt=\"Shophouse T&amp;T Victoria Nghệ An\" src=\"/public/upload/images/T-T/Shophouse-t-t-victoria-nghe-an.jpg\" /></span></span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"font-size:16px;\">Shophouse T&amp;T Victoria Nghệ An - Mua một căn nh&agrave;, tận hưởng hai gi&aacute; trị</span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\">C&ugrave;ng với sự ph&aacute;t triển b&ugrave;ng nổ của nền kinh tế, việc mở rộng giao thương với c&aacute;c nền văn h&oacute;a kh&aacute;c nhau đ&atilde; k&eacute;o theo sự thay đổi về xu hướng, phong c&aacute;ch an cư của người Việt. B&ecirc;n cạnh c&aacute;c loại h&igrave;nh nh&agrave; ở truyền thống, m&ocirc; h&igrave;nh nh&agrave; phố thương mại - shophouse đ&atilde; v&agrave; đang được nhiều kh&aacute;ch h&agrave;ng ưa chuộng bởi gi&aacute; b&aacute;n hợp l&yacute;, đ&aacute;p ứng được c&ugrave;ng l&uacute;c cả nhu cầu để ở v&agrave; đầu tư kinh doanh.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">So với nh&agrave; phố truyền thống, m&ocirc; h&igrave;nh nh&agrave; ở shophouse khắc phục được nhiều điểm hạn chế như: hạ tầng được quy hoạch tổng thể, hiện đại; thiết kế kh&ocirc;ng gian linh hoạt; tạo gi&aacute; trị lợi nhuận cao cho chủ sở hữu,&hellip; <strong>Nh&agrave; phố thương mại shophouse, liền kề T&amp;T Victoria Vinh</strong> được thiết kế th&ocirc;ng minh theo hướng đa chức năng, c&oacute; diện t&iacute;ch lớn, kh&ocirc;ng gian bố tr&iacute; linh hoạt. Diện t&iacute;ch kinh doanh v&agrave; diện t&iacute;ch nh&agrave; ở được t&aacute;ch biệt, v&igrave; vậy rất thuận tiện cho việc kinh doanh hoặc cho thu&ecirc; văn ph&ograve;ng.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">Tr&ecirc;n thế giới, shophouse đ&atilde; c&oacute; lịch sử ph&aacute;t triển l&acirc;u d&agrave;i, đem lại nhiều gi&aacute; trị lợi &iacute;ch v&agrave; được nhiều người lựa chọn. Tại một số nước ch&acirc;u &Aacute;, c&aacute;c d&atilde;y phố nổi tiếng được x&acirc;y dựng v&agrave; ph&aacute;t triển theo m&ocirc; h&igrave;nh shophouse c&oacute; thể kể đến như khu mua sắm Geylang (Singapore); khu shophouse Penang, Malacca (Malaysia), phố Cổ H&agrave; Nội&hellip; Thực tế cho thấy, hầu hết c&aacute;c căn shophouse đều c&oacute; vị tr&iacute; nổi bật, c&oacute; từ một đến hai mặt tiền. Đặc biệt, khi sở hữu c&aacute;c căn shophouse tại c&aacute;c vị tr&iacute; trung t&acirc;m sẽ k&eacute;o theo gi&aacute; trị thương mại của shophouse ng&agrave;y c&agrave;ng tăng so với c&aacute;c căn hộ chung cư. Đ&acirc;y cũng l&agrave; yếu tố đảm bảo cho việc kinh doanh hoặc cho thu&ecirc; mặt bằng với khả năng sinh lời.</span></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\">Shophouse T&amp;T Victoria Vinh: cơ hội đầu tư hấp dẫn, gi&aacute; trị tối đa</span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\">Bắt kịp xu hướng ph&aacute;t triển, C&ocirc;ng ty CP Tập đo&agrave;n T&amp;T đ&atilde; d&agrave;nh rất nhiều t&acirc;m huyết để x&acirc;y dựng c&aacute;c căn nh&agrave; phố thương mại tại dự &aacute;n Shophouse T&amp;T Victoria Vinh, Nghệ An.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">Dự &aacute;n Shophouse T&amp;T Victoria Vinh nằm ở vị tr&iacute; đắc địa Ng&atilde; tư Chợ Vinh, đối diện BigC Vinh &ndash; khu vực đ&ocirc;ng d&acirc;n cư sinh sống, giao thương tấp nập. T&amp;T Victoria Vinh sở hữu 4 mặt tiền huyết mạch: Quang Trung, Phan Đ&igrave;nh Ph&ugrave;ng, Đặng Th&aacute;i Th&acirc;n, Nguyễn Cảnh Ch&acirc;n. Từ T&amp;T Victoria, mọi nhu cầu di chuyển tới c&aacute;c địa điểm trong TP v&agrave; c&aacute;c địa phương l&acirc;n cận đều đ&aacute;p ứng dễ d&agrave;ng:<br />\r\n	- 100m tới Chợ Vinh<br />\r\n	- 1Km tới bệnh viện Đa khoa<br />\r\n	- 1Km bến xe Vinh<br />\r\n	- 1Km tới c&ocirc;ng vi&ecirc;n trung t&acirc;m<br />\r\n	- 1,5Km tới Ga Vinh<br />\r\n	- 2Km tới Quảng trường HCM<br />\r\n	- 3Km tới ĐH Vinh<br />\r\n	Những li&ecirc;n kết v&ugrave;ng ho&agrave;n hảo n&agrave;y sẽ mang lại cơ hội đầu tư v&agrave; lợi nhuận kinh doanh trong thời gian ngắn. C&ugrave;ng với đ&oacute; l&agrave; lượng cư d&acirc;n đ&ocirc;ng đảo từ 420 căn hộ chung cư trong tổ hợp chung cư &amp; nh&agrave; phố dự &aacute;n T&amp;T Victoria sẽ l&agrave; nguồn kh&aacute;ch h&agrave;ng đ&ocirc;ng đảo tại chỗ cho c&aacute;c đơn vị ph&aacute;t triển kinh doanh ở đ&acirc;y.</span></p>\r\n<p>\r\n	<em><span style=\"font-size:16px;\">Đặc biệt, chức năng &ldquo;k&eacute;p&rdquo; &ndash; vừa kinh doanh, vừa sinh hoạt thuận tiện &ldquo;một bước ra phố&rdquo; l&agrave; ưu điểm nổi trội của loại h&igrave;nh BĐS n&agrave;y. B&ecirc;n cạnh đ&oacute;, quy hoạch b&agrave;i bản v&agrave; lối kiến tr&uacute;c hiện đại sang trọng mang lại c&aacute;c gi&aacute; trị gia tăng, tạo sức h&uacute;t cho dự &aacute;n.</span></em></p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Dự &aacute;n: </strong>Shophouse T&amp;T Victoria Nghệ An</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Chủ đầu tư: </strong>C&ocirc;ng ty CP tập đo&agrave;n T&amp;T</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Đơn vị ph&acirc;n phối độc quyền:</strong> C&ocirc;ng ty CP Dịch vụ &amp; Địa ốc Đất Xanh Nghệ An.</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Hotline:</strong> <span style=\"color:#ff0000;\"><strong>094.220.6262 &ndash; 0986098266</strong></span></span></li>\r\n</ol>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><span style=\"color:#000000;\">Xem th&ecirc;m:&nbsp;</span></strong></span></p>\r\n<ul>\r\n	<li>\r\n		<a href=\"http://www.bdsnghetinh.net/du-an-bds/chung-cu-cao-cap-t-t-victoria-/53.html\"><span style=\"color:#0000cd;\"><span style=\"font-size:16px;\"><strong>Chung cư cao cấp T&amp;T Victoria Vinh</strong></span></span></a></li>\r\n	<li>\r\n		<a href=\"http://www.bdsnghetinh.net/du-an-bds/lien-ke-72-le-loi-/52.html\"><span style=\"color:#0000cd;\"><span style=\"font-size:16px;\"><strong>Liền kề 72 L&ecirc; Lợi</strong></span></span></a><span style=\"color:#0000cd;\"><strong style=\"font-size: 16px;\">&nbsp;</strong></span></li>\r\n	<li>\r\n		<a href=\"http://www.bdsnghetinh.net/du-an-bds/trung-duc-tower/56.html\"><span style=\"color:#0000cd;\"><strong style=\"font-size: 16px;\">Chung cư cao cấp Trung Đức Tower</strong></span></a></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"color: rgb(0, 0, 205); font-size: 16px;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"font-size: 16px;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"font-size: 16px;\">Email: lamtlt.nghean@dxmb.vn</strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"font-size: 16px;\">Website: http://www.bdsnghetinh.net/</strong></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Shophouse T&T Victoria TP Vinh – Xu hướng sống mới, đầu tư sinh lời cao', '8-2-2018', '4-12-2018', 'shophouse-t-t-victoria-tp-vinh', 'Nhu cầu đầu nền, đất dự án hay căn hộ shophouse ở Vinh cực cao. Và Shophouse T&T victoria Vinh mở bán càng làm khuynh đảo thị trường BĐS Nghệ An', 6, 'Liền kề T&T Victoria Vinh, shophouse T&T Nghệ An, đất đẹp trung tâm vinh, đất vinh,', '0'),
(58, 1, 7, NULL, 'MỞ BÁN CĂN HỘ HANDICO 30.', 'Đại lộ Lê Nin,xóm 20, xã Nghi Phú.', 'Công ty CP Đầu Tư và Phát Triển Nhà Hà Nội Số 30 (Handico 30).', '12/2018', '49 - 67m2', '0941.441.991', 1132, '/public/upload/images/HANDICO/tong-quan-handico-30.jpg', NULL, NULL, 0, '1', NULL, '<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:18px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\">I. Th&ocirc;ng Tin Tổng Quan Dự &Aacute;n</span></strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/tong-quan-handico-30.jpg\" /></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tổng Quan Dự &Aacute;n Handico 30.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><strong>Chủ đầu tư:</strong> C&ocirc;ng ty CP Đầu Tư v&agrave; Ph&aacute;t Triển Nh&agrave; H&agrave; Nội Số 30 (Handico 30).</span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Địa điểm:</strong> X&oacute;m 20, x&atilde; Nghi Ph&uacute;, Tp Vinh, Nghệ An.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Diện t&iacute;ch x&acirc;y dựng: </strong>710 m2.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Diện t&iacute;ch s&agrave;n x&acirc;y dựng:</strong> 9929,6 m2.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Quy m&ocirc; t&ograve;a nh&agrave;:</strong> T&ograve;a nh&agrave; gồm 01 tầng hầm v&agrave; 14 tầng nổi. Tầng hầm l&agrave; nơi để xe &ocirc; t&ocirc;, khu vực kỹ thuật t&ograve;a nh&agrave;. Tầng 1 l&agrave; khu vực sinh hoạt cộng đồng, khu kinh doanh thương mại. Tầng 2 tới tầng 14 l&agrave; tầng căn hộ.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>C&aacute;c loại diện t&iacute;ch:</strong> 49, 54, 57, 64, 67 m2.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Thời gian ho&agrave;n th&agrave;nh:</strong> Th&aacute;ng 12/2018.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Hỗ trợ vay ng&acirc;n h&agrave;ng:</strong> 70% gi&aacute; trị căn hộ.</span></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:18px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\">II. Vị Tr&iacute; Dự &Aacute;n.</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/vi-tri-handico-30.jpg\" /></span></strong></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">&nbsp; &nbsp;Dự &aacute;n Handico nằm ngay ng&atilde; tư Phạm Đ&igrave;nh To&aacute;i v&agrave; Đại Lộ L&ecirc; Nin. Đ&acirc;y l&agrave; vị tr&iacute; c&oacute; kết nối giao th&ocirc;ng v&agrave; hạ tầng đồng bộ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">&nbsp; &nbsp;Kết nối giao th&ocirc;ng: Trục đường 32 l&agrave; trục đường lớn nhất tại TP Vinh. Trong khi đ&oacute;, trục đường Phạm Đ&igrave;nh To&aacute;i nằm trong quy hoạch k&eacute;o d&agrave;i của th&agrave;nh phố Vinh. V&igrave; vậy, việc kết nối giao th&ocirc;ng từ dự &aacute;n của cư d&acirc;n sẽ rất dễ d&agrave;ng.</span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">&nbsp; &nbsp;Kết nối hạ tầng cao cấp: Trục đường 32 l&agrave; nơi tập hợp của c&aacute;c Sở, Ban, Ng&agrave;nh lớn nhất tại Vinh. B&ecirc;n cạnh đ&oacute;, quanh dự &aacute;n l&agrave; rất nhiều địa điểm quan trọng nổi tiếng. C&oacute; thể kể đến như: Bệnh Viện Ba Lan mới, Bệnh Viện Quốc Tế, Cục thuế tỉnh Nghệ An&hellip;</span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">&nbsp; &nbsp;Sở hữu vị tr&iacute; trung t&acirc;m cực kỳ quan trọng, Handico 30 l&agrave; một sản phẩm chất lượng m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ lỡ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:18px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt Bằng Điển H&igrave;nh Căn Hộ</span></span></strong></p>\r\n<p style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:18px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/mat-bang-dien-hinh-handico30.jpg\" /></span></span></strong></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt Bằng Điển H&igrave;nh Handico30</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/mat-bang-dien-hinh-can-a.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt bằng điển h&igrave;nh căn A</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/mat-bang-dien-hinh-can-b.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt Bằng Điển H&igrave;nh Căn B</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/mat-bang-dien-hinh-can-c.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt Bằng Điển H&igrave;nh Căn C</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/mat-bang-dien-hinh-can-d.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt Bằng Điển H&igrave;nh Căn D</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><img alt=\"\" src=\"/public/upload/images/HANDICO/mat-bang-dien-hinh-can-e.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mặt Bằng Điển H&igrave;nh Căn E</span></span><br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<u><span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size:22px;\"><strong>Tiến Độ Thanh To&aacute;n Dự &Aacute;n.</strong></span></span></span></u></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:arial,helvetica,sans-serif;\"><strong>Đợt 1: </strong>Th&aacute;ng 1/2018. Xong s&agrave;n tầng 3 (Thu 20% GTCH).<br />\r\n	<strong>Đợt 2:</strong> Th&aacute;ng 3/2018. Xong s&agrave;n tầng 7 (Thanh to&aacute;n 15% GTCH).<br />\r\n	<strong>Đợt 3:</strong> Th&aacute;ng 5/2018. Xong s&agrave;n tầng 10 (Thanh to&aacute;n 15% GTCH).<br />\r\n	<strong>Đợt 4: </strong>Th&aacute;ng 7/2018. X&acirc;y th&ocirc; ho&agrave;n thiện (Thanh to&aacute;n 10% GTCH).<br />\r\n	<strong>Đợt 5: </strong>Th&aacute;ng 9/2018: Tr&aacute;t xong t&ograve;a nh&agrave; (Thanh to&aacute;n 10% GTCH).<br />\r\n	<strong>Đợt 6: </strong>Th&aacute;ng 12/2018: Ho&agrave;n thiện t&ograve;a nh&agrave; (Thanh to&aacute;n 30% GTCH v&agrave; 2% KPBT).</span></span><br />\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-family: arial, helvetica, sans-serif; font-size: 16px;\">-------------------------------------------------------------</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Hãy gọi ngay chúng t&ocirc;i h&ocirc;m nay đ&ecirc;̉ được tư vấn và hưởng chính sách t&ocirc;́t nh&acirc;́t từ chủ đ&acirc;̀u tư.</span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">Mọi chi ti&ecirc;́t vui lòng li&ecirc;n h&ecirc;̣:</span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\">Email: lamtlt.nghean@dxmb.vn</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:arial,helvetica,sans-serif;\">Website: http://www.bdsnghetinh.net/</span></strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'MỞ BÁN CĂN HỘ HANDICO 30.', '30-3-2018', '4-12-2018', 'mo-ban-can-ho-handico-30-', 'MỞ BÁN CĂN HỘ HANDICO 30.', 125, 'chung cư, handico 30, chung cư 32, chung cư lenin', '0'),
(60, 27, 9, NULL, 'KĐT HOÀNG MAI CENTER POINT', 'Khối 5, Phường Quỳnh Thiện', 'Công ty TNHH Kỹ Nghệ Đại Bách Khoa', NULL, NULL, '094.220.6262', 3137, '/public/upload/files/001%20HM.PNG', NULL, NULL, 0, '1', NULL, '<h2>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\"><strong>Ch&iacute;nh thức ra mắt SI&Ecirc;U dự &aacute;n tại TX. Ho&agrave;ng Mai: HO&Agrave;NG MAI CENTER POINT&nbsp;</strong></span></span></h2>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Li&ecirc;n hệ ngay: 0941.441.991 (Mr. L&acirc;m) - 094.220.6262 (Ms. Tr&acirc;m)</span></strong></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#0000ff;\"><strong>&ndash; VỊ TR&Iacute; TRUNG T&Acirc;M, N&Acirc;NG TẦM ĐẲNG CẤP</strong></span><br />\r\n	+ Nằm trong khu vực khu c&ocirc;ng nghiệp đầu tư lớn nhất của Nam Thanh &ndash; Bắc Nghệ<br />\r\n	+ Hạ tầng giao th&ocirc;ng kết nối v&ocirc; c&ugrave;ng thuận tiện. B&aacute;m đường Quốc lộ 1A huyết mạch của cả nước, Gần đường 36 nối liền Tx Th&aacute;i Ho&agrave;, Nghĩa Đ&agrave;n v&agrave; khu c&ocirc;ng nghiệp Nghi Sơn.</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/upload/images/hoang-mai-center-point/Bien%20Van%20Phong%20Du%20An-06%206262-min.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/Ban-Do-Vi-Tri-DA%20Ho%C3%A0ng%20Mai.png\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><span style=\"font-size: 14px;\">+ Dự &aacute;n rất gần với c&aacute;c khu dịch vụ mua sắm, giải trí, trường học, b&ecirc;̣nh vi&ecirc;̣n..v...v... hệ thống giao th&ocirc;ng đồng bộ hiện đại thuận tiện cho cư d&acirc;n sinh sống nơi đ&acirc;y:</span></strong></p>\r\n<p>\r\n	<em><span style=\"font-size:14px;\">&gt;&gt; Trường M&acirc;̀m non, Ti&ecirc;̉u học, THCS, THPH Hoàng Mai.<br />\r\n	&gt;&gt; Bệnh viện Đa khoa Hoàng Mai, B&ecirc;̣nh vi&ecirc;̣n Phong, Da li&ecirc;̃u TW 1,2...<br />\r\n	&gt;&gt; Si&ecirc;u thị, trung t&acirc;m mua sắm Mường Thanh Grand<br />\r\n	&gt;&gt; UBND , Trung t&acirc;m hành chính mới ...</span></em></p>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;\">Đ&oacute;n đầu nhu cầu nh&agrave; ở từ định hướng ph&aacute;t triển mạnh nhiều Khu c&ocirc;ng nghiệp tại địa b&agrave;n. Dự ki&ecirc;́n d&acirc;n s&ocirc;́ sẽ tăng 1,5 l&acirc;̀n trong giai đoạn 2018 &ndash; 2022. Được x&acirc;y dựng với m&ocirc; h&igrave;nh khu đ&ocirc; thị nh&agrave; ở với kh&ocirc;ng gian khoáng đạt, thi&ecirc;́t k&ecirc;́ hi&ecirc;̣n đại, quy hoạch đ&ocirc;̀ng b&ocirc;̣, cộng đ&ocirc;̀ng văn minh kiến tạo &ldquo;h&agrave;nh tr&igrave;nh hưởng thụ cuộc sống&rdquo; cho cư d&acirc;n nơi đ&acirc;y.</span></p>\r\n</blockquote>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\"><strong>TỔNG QUAN DỰ &Aacute;N KĐT HO&Agrave;NG MAI CENTER POINT</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>T&ecirc;n dự &aacute;n: </strong>Khu nh&agrave; ở Đ&ocirc; thị Hoang Mai Center Point<br />\r\n	<strong>Chủ đầu tư: </strong>C&ocirc;ng ty TNHH Kỹ Ngh&ecirc;̣ Đại Bách Khoa</span></p>\r\n<p>\r\n	<span style=\"font-size: 14px;\"><strong>Nh&agrave; ph&aacute;t triển Dự &aacute;n:</strong> CTCP Dịch vụ &amp; Địa ốc Đất Xanh Nghệ An</span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><strong>Vị tr&iacute;: </strong>Kh&ocirc;́i 5, Phường Quỳnh Thi&ecirc;̣n, TX. Hoàng Mai, Ngh&ecirc;̣ An<br />\r\n	<strong>Diện t&iacute;ch đất sử dụng:</strong> 4,03 ha<br />\r\n	<strong>Tư vấn thiết kế Hạ tầng kỹ thuật: </strong>C&ocirc;ng ty CP XD Hoàng Thái<br />\r\n	<strong>Quy m&ocirc;: </strong>Khu nh&agrave; ở đ&ocirc; thị g&ocirc;̀m 183 căn bao gồm:<br />\r\n	<strong>Nhà li&ecirc;̀n k&ecirc;̀: </strong>123 căn, cao 3,5 tầng, diện t&iacute;ch từ 101,35m2 &ndash; 110m2<br />\r\n	<strong>Nh&agrave; vườn: </strong>60 căn, cao 3 t&acirc;̀ng, di&ecirc;̣n tích từ 166,5m2 &ndash; 178m2<br />\r\n	<strong>Khu c&acirc;y xanh, TDTT:</strong> 3,345m2<br />\r\n	<strong>M&acirc;̣t đ&ocirc;̣ XD: </strong>46,46%<br />\r\n	<strong>Sổ đỏ vĩnh vi&ecirc;̃n</strong></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\">---------------------------------------------------------------</span></p>\r\n<p>\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:14px;\">7 L&Yacute; DO TẠI SAO CHỌN MUA HOANG MAI CENTER POINT:</span></strong></span></p>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;\">1.&nbsp;TX. Hoàng Mai l&agrave; một trong 3 thị xã ph&aacute;t triển bậc nhất tại Ngh&ecirc;̣ An.</span></p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">2.&nbsp;Hạ tầng giao th&ocirc;ng kết nối v&ocirc; c&ugrave;ng thuận tiện, nằm tr&ecirc;n Qu&ocirc;́c l&ocirc;̣ 1A kéo dài từ Bắc vào Nam</span></p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">3.&nbsp;Đ&oacute;n đầu nhu cầu nh&agrave; ở từ định hướng ph&aacute;t triển mạnh nhiều Khu c&ocirc;ng nghiệp tại địa b&agrave;n.Dự ki&ecirc;́n d&acirc;n s&ocirc;́ sẽ tăng 1,5 l&acirc;̀n trong giai đoạn 2018 - 2022</span></p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">4.&nbsp;Nằm tại vị tr&iacute; đắc địa, trung t&acirc;m nh&acirc;́t TX. Hoàng Mai  phấn đấu trở th&agrave;nh đ&ocirc; thị loại III năm 2020  cơ hội gia tăng gi&aacute; trị BĐS r&acirc;́t nhanh</span></p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">5.&nbsp;Được x&acirc;y dựng với m&ocirc; h&igrave;nh khu đ&ocirc; thị nh&agrave; ở với kh&ocirc;ng gian khoáng đạt, thi&ecirc;́t k&ecirc;́ hi&ecirc;̣n đại, quy hoạch đ&ocirc;̀ng b&ocirc;̣, c&ocirc;ng đ&ocirc;̀ng văn minh kiến tạo &ldquo;h&agrave;nh tr&igrave;nh hưởng thụ cuộc sống&rdquo; cho cư d&acirc;n nơi đ&acirc;y</span></p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">6.&nbsp;M&ocirc; h&igrave;nh nh&agrave; ở, có th&ecirc;̉ kết hợp kinh doanh ngay trong nội khu  Tạo giá trị gia tăng theo thời gian cho BĐS</span></p>\r\n	<p>\r\n		<span style=\"font-size:14px;\">7.&nbsp;Gi&aacute; hợp lý, cơ h&ocirc;̣i đ&acirc;̀u tư sinh lời kh&ocirc;ng giới hạn. Ph&aacute;p l&yacute; đầy đủ, minh bạch, sở hữu vĩnh vi&ecirc;̃n.</span></p>\r\n	<p>\r\n		&nbsp;</p>\r\n</blockquote>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Siêu dự án- KĐT Hoàng Mai Center Point', '21-5-2018', '4-12-2018', 'kdt-hoang-mai-center-point', 'Cơ hội vàng cho các nhà đầu tư đất tại TX.Hoàng Mai, Dự án Đang Siêu Sốt ”HOÀNG MAI CENTER POINT”. Nhanh tay liên hệ để chọn ngay lô đẹp 0941.441.991', 0, 'Hoàng Mai Center Point, đất nền tx hoàng mai,', '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(61, 27, 7, NULL, 'KHU ĐÔ THỊ NAM LÊ LỢI', 'Phường Lê Lợi', NULL, NULL, NULL, '0962452245', 2689, '/public/upload/images/nam%20l%C3%AA%20l%C6%A1i/event%20Nam%20Le%20Loi.jpg', NULL, NULL, 1, '1', NULL, '<h2 style=\"text-align: justify;\">\r\n	<font color=\"#ff0000\"><span style=\"font-size: 20px;\">&nbsp;</span></font></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/14.jpg\" /></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp; &nbsp;</h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Dự &aacute;n <a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-nam-le-loi/61.html\">khu đ&ocirc; thị Nam L&ecirc; Lợi</a> tọa lạc ngay trung t&acirc;m th&agrave;nh phố Vinh với nhiều tiện &iacute;ch kh&eacute;p k&iacute;n phục vụ cư d&acirc;n, l&agrave; một điểm nhấn về nh&agrave; ở đ&aacute;ng ch&uacute; &yacute; nhất hiện nay.</span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Ngo&agrave;i đời sống sinh hoạt tiện nghi, khu đ&ocirc; thị c&ograve;n ch&uacute; trọng x&acirc;y dựng kh&ocirc;ng gian sống xanh, l&agrave; nơi giao thoa giữa th&agrave;nh phố hiện đại v&agrave; thi&ecirc;n nhi&ecirc;n trong l&agrave;nh, chốn an cư l&yacute; tưởng cho mọi gia đ&igrave;nh.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><span style=\"font-size:24px;\">TH&Ocirc;NG TIN TỔNG QUAN</span></span></span></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- T&ecirc;n dự &aacute;n: <a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-nam-le-loi/61.html\">Khu đ&ocirc; thị Nam L&ecirc; Lợi.</a></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- Chủ đầu tư: C&ocirc;ng ty Cổ Phần x&acirc;y dựng địa ốc Bến Th&agrave;nh.</span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- Đơn vị ph&acirc;n phối: C&ocirc;ng ty CP Dịch Vụ &amp; Địa Ốc Đất Xanh Nghệ An.</span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- Vị tr&iacute;: Chu Văn An, P.L&ecirc; Lợi, TP Vinh.</span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- Diện t&iacute;ch: 9.26 ha.</span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- Quy m&ocirc;: Khu đ&ocirc; thị kết hợp gồm 376 căn bao gồm:</span></p>\r\n<ul>\r\n	<li>\r\n		<h2 style=\"text-align: justify;\">\r\n			<span style=\"font-size:20px;\">Nh&agrave; liền kề: 356 căn cao 3 tầng, diện t&iacute;ch từ 82 &ndash; 205 m2.</span></h2>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<strong><span style=\"font-size:20px;\">Nh&agrave; biệt thự: gồm 20 căn, cao 3 tầng, diện t&iacute;ch từ 364 &ndash; 466 m2.</span></strong></li>\r\n</ul>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">- Ph&aacute;p l&yacute;: Sổ hồng vĩnh viễn.</span></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp;</h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:26px;\"><strong><span style=\"color:#0000ff;\">VỊ TR&Iacute; DỰ &Aacute;N</span></strong></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Dự &aacute;n tọa lạc tr&ecirc;n trục đường Chu Văn An, Phường L&ecirc; Lợi, Tp. Vinh, Tỉnh Nghệ An. Đ&acirc;y l&agrave; tuyến đường khang trang, rộng r&atilde;i nhưng vẫn giữ nguy&ecirc;n kh&ocirc;ng kh&iacute; y&ecirc;n b&igrave;nh của hai h&agrave;ng c&acirc;y xanh trải d&agrave;i t&iacute;t tắp. Từ đường Chu Văn An c&oacute; thể dễ d&agrave;ng tiếp cận với c&aacute;c con đường huyết mạch của th&agrave;nh phố như quốc lộ 1A, Trường Chinh, L&yacute; Thường Kiệt&hellip;</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<img alt=\"\" src=\"/public/upload/images/nam%20l%C3%AA%20l%C6%A1i/Mat%20bang%20tong%20the-02-02.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Tập trung xung quanh khu đ&ocirc; thị Nam L&ecirc; Lợi l&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh sinh hoạt trọng điểm của th&agrave;nh phố như S&acirc;n bay Vinh, Trung t&acirc;m giải tr&iacute; VRC, Big C, Bệnh viện Th&agrave;nh An, Trường mầm non ABC, Trường mầm non Blue Sky, Trường Tiểu học v&agrave; Trung học cơ sở L&ecirc; Lợi,&hellip; Do đ&oacute; cư d&acirc;n c&oacute; thể sử dụng c&aacute;c dịch vụ kh&aacute;m chữa bệnh, vui chơi v&agrave; học tập ngay gần nh&agrave;.</span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp;</h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><span style=\"font-size:24px;\">TIỆN &Iacute;CH</span></span></span></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Với quy m&ocirc; 9,26ha, khu đ&ocirc; thị quy hoạch 376 căn nh&agrave; c&ugrave;ng nhiều tiện &iacute;ch đ&aacute;ng ch&uacute; &yacute;. Nam L&ecirc; Lợi l&agrave; khu đ&ocirc; thị cao cấp duy nhất tr&ecirc;n địa b&agrave;n th&agrave;nh phố Vinh phục vụ đầy đủ c&aacute;c tiện &iacute;ch cần thiết cho cuộc sống gia đ&igrave;nh ngay trong khu&ocirc;n vi&ecirc;n, bao gồm: nh&agrave; trẻ, trường mầm non, trung t&acirc;m thể dục thể thao, trung t&acirc;m y tế, khu&ocirc;n vi&ecirc;n c&acirc;y xanh.</span></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Hạ tầng đường s&aacute;, đ&egrave;n điện, c&ocirc;ng tr&igrave;nh tho&aacute;t nước được x&acirc;y dựng đồng bộ trải rộng trong nội khu, gi&uacute;p c&aacute;c gia đ&igrave;nh trong cộng đồng dễ d&agrave;ng di chuyển giữa c&aacute;c khu vực để kết nối với nhau.</span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Cảnh quan được bố tr&iacute; hợp l&yacute; với 40% diện t&iacute;ch dự &aacute;n d&agrave;nh cho c&acirc;y xanh. Tại đ&acirc;y, cư d&acirc;n c&oacute; thể h&iacute;t thở bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t mẻ mỗi ng&agrave;y, h&ograve;a quyện c&ugrave;ng thi&ecirc;n nhi&ecirc;n với s&acirc;n vườn, h&agrave;ng c&acirc;y trong nội khu, c&ocirc;ng vi&ecirc;n v&agrave; vỉa h&egrave; ngập tr&agrave;n b&oacute;ng m&aacute;t tr&ecirc;n đường Chu Văn An.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:20px;\"><img alt=\"\" src=\"/public/upload/images/nam%20l%C3%AA%20l%C6%A1i/vi-tri-du-an.jpg\" /></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:20px;\"><img alt=\"\" src=\"/public/upload/images/nam%20l%C3%AA%20l%C6%A1i/Mat%20bang%20tong%20the-02-01.jpg\" /></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:24px;\"><strong>CH&Iacute;NH S&Aacute;CH B&Aacute;N H&Agrave;NG</strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"#ff0000\"><span style=\"font-size: 20px;\"><b>&nbsp;</b></span></font></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/2ff5b5baf72e17704e3f.jpg\" /></p>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp;</h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><span style=\"font-size:24px;\">TIẾN ĐỘ THI C&Ocirc;NG V&Agrave; THANH TO&Aacute;N</span></span></span></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\">&nbsp; &nbsp;Dự &aacute;n <a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-nam-le-loi/61.html\">khu đ&ocirc; thị Nam L&ecirc; Lợi</a> đ&atilde; ho&agrave;n thiện xong hạng c&aacute;c mục cơ sở hạ tầng: đ&egrave;n điện chiếu s&aacute;ng, c&ocirc;ng tr&igrave;nh tho&aacute;t nước, hệ thống đường nội bộ rộng 12 &ndash; 18m. Trung t&acirc;m thể thao đ&atilde; đi v&agrave;o hoạt động từ năm 2018. Trường mầm non d&agrave;nh ri&ecirc;ng cho c&aacute;c cư d&acirc;n khu đ&ocirc; thị vừa được kh&aacute;nh th&agrave;nh v&agrave;o cuối th&aacute;ng 7/2018. Hiện tại đ&atilde; c&oacute; một số gia đ&igrave;nh bắt đầu x&acirc;y dựng nh&agrave; ở tr&ecirc;n khu đất dự &aacute;n.</span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"font-size:20px;\">&nbsp; &nbsp;Thời gian thanh to&aacute;n: Hiện tại đ&atilde; c&oacute; sổ từng l&ocirc;, kh&aacute;ch h&agrave;ng c&oacute; thể thanh to&aacute;n 100% để nhận sổ c&aacute; nh&acirc;n.</span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:24px;\"><strong>H&Igrave;NH ẢNH THỰC TẾ</strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/10.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/14be00946900895ed011.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/11.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/12.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/9.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20NLL/13.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<br />\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">----------------------------------------------------------</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#ff0000;\"><strong><span style=\"font-size:26px;\">MỌI TH&Ocirc;NG TIN CHI TI</span></strong><strong><span style=\"font-size: 26px;\">ẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:<br />\r\n	C&ocirc;ng ty Cổ Phần BĐS GO - Vland<br />\r\n	Hotline: 096.245.2.245 ( Mr. L&acirc;m )<br />\r\n	Email: lamtrinhgovland@gmail.com</span></strong><br />\r\n	<strong><span style=\"font-size:26px;\">Website: http://www.bdsnghetinh.net/</span></strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, 'CHIẾT KHẤU NGAY 10 TRIỆU ĐỒNG CHO 5 KHÁCH HÀNG ĐẦU TIÊN ĐẶT CỌC TỪ NGÀY 7/11/2018.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'TIN CỰC HOT CUỐI NĂM - TRI ÂN KHÁCH HÀNG KĐT NAM LÊ LỢI 23/12/2018', '26-5-2018', '27-1-2021', 'khu-do-thi-nam-le-loi', 'KĐT NAM LÊ LỢI - SỐNG ĐẲNG CẤP GIỮA LÒNG THÀNH VINH.', 98, NULL, '0'),
(62, 27, 8, NULL, 'KHU ĐÔ THỊ CỬA HỘI SEASIDE', 'Xuân Hội', NULL, NULL, NULL, '094.220.6262', 1996, '/public/upload/files/cua-hoi-seaside.jpg', NULL, NULL, 0, '1', NULL, '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Năm 2013, bất động sản H&agrave; Tĩnh bắt đầu &ldquo;dậy s&oacute;ng&rdquo; sau khi b&atilde;i biển Thi&ecirc;n Cầm được ph&ecirc; duyệt trong quy hoạch ph&aacute;t triển khu du lịch quốc gia. Sau Thi&ecirc;n Cầm, H&agrave; Tĩnh đang chuẩn bị cho sự chuyển m&igrave;nh của c&aacute;c b&atilde;i biển đầy tiềm năng kh&aacute;c như Xu&acirc;n Hội, Xu&acirc;n Trường, Thạch Hải, Kỳ Ninh, Mũi Đao. Nhờ lợi thế sẵn c&oacute; về kinh tế v&agrave; văn h&oacute;a, H&agrave; Tĩnh được dự đo&aacute;n sẽ l&agrave; mảnh đất m&agrave;u mỡ mới cho thị trường du lịch nghỉ dưỡng.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/cua-hoi-seaside.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-mat-bien-cua-hoi-seaside/62.html\"><em><strong>&nbsp; &nbsp;</strong></em></a><em><strong><a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-mat-bien-cua-hoi-seaside/62.html\">Dự &aacute;n Cửa Hội Seaside </a></strong></em>tọa lạc tr&ecirc;n tuyến quốc lộ 8B đi qua thị trấn Nghi Xu&acirc;n, H&agrave; Tĩnh. Đ&acirc;y cũng l&agrave; tuyến đường tiếp nhận điểm cuối của cầu Cửa Hội kết nối giữa hai tỉnh H&agrave; Tĩnh v&agrave; Nghệ An, dự kiến được đưa v&agrave;o sử dụng v&agrave;o năm 2019. Sau khi ho&agrave;n th&agrave;nh, đ&acirc;y sẽ l&agrave; c&acirc;y cầu đưa du kh&aacute;ch thập phương đến trải nghiệm kh&ocirc;ng gian nghỉ dưỡng tr&ecirc;n c&aacute;c b&atilde;i biển tự nhi&ecirc;n đẹp m&ecirc; hồn tại H&agrave; Tĩnh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:26px;\"><span style=\"color:#0000cd;\"><strong><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N CỬA HỘI SEASIDE</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- <strong>T&ecirc;n dự &aacute;n:</strong> <a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-mat-bien-cua-hoi-seaside/62.html\">CỬA HỘI SEASIDE.</a></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- <strong>Chủ đầu tư:</strong> C&ocirc;ng ty TNHH XNK Ch&acirc;u Tuấn.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- <strong>Vị tr&iacute;:</strong> Xu&acirc;n Hội, Nghi Xu&acirc;n, H&agrave; Tĩnh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- <strong>Quy m&ocirc;:</strong> 4,9ha.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- <strong>Bao gồm:</strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; 191 l&ocirc; đất liền kề v&agrave; biệt thự.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; khu vui chơi TDTT.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; khu c&ocirc;ng vi&ecirc;n c&acirc;y xanh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Nh&agrave; văn h&oacute;a.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Diện t&iacute;ch từ: 140 &ndash; 246m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Đường trải nhựa: rộng 12m, 18m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Sổ hồng vĩnh viễn.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:24px;\"><span style=\"color:#0000cd;\"><strong><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N CỬA HỘI SEASIDE</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<strong style=\"color: rgb(0, 0, 205); font-size: 24px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp;</span></strong><strong style=\"color: rgb(0, 0, 205); font-size: 24px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp;</span></strong><strong style=\"color: rgb(0, 0, 205); font-size: 24px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp;</span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Nghi Xu&acirc;n với vị tr&iacute; l&agrave; cửa ng&otilde; giao thương giữa Nghệ An &ndash; H&agrave; Tĩnh, c&ugrave;ng với sự ưu đ&atilde;i của thi&ecirc;n nhi&ecirc;n v&agrave; c&aacute;c địa điểm tham quan, du lịch phong ph&uacute;, sẽ g&oacute;p phần th&uacute;c đẩy kinh tế, văn h&oacute;a của địa phương ph&aacute;t triển vượt bậc trong những năm tiếp theo.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/vi-tri-cua-hoi-seaside.PNG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Xu&acirc;n Hội l&agrave; cực Bắc của tỉnh H&agrave; Tĩnh, với hệ thống giao th&ocirc;ng đường thủy ph&aacute;t triển c&ugrave;ng hệ sinh th&aacute;i du lịch dọc ven biển.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Cửa Hội Seaside c&oacute; vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa, tọa lạc ngay cạnh đường quốc lộ 8B:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; C&aacute;ch cảng Xu&acirc;n Hội 300m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; C&aacute;ch b&atilde;i biển Xu&acirc;n Hội 1.5km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; C&aacute;ch b&atilde;i biển Xu&acirc;n Trường 4 km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; C&aacute;ch b&atilde;i biển Cửa L&ograve; 5km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; C&aacute;ch Vinpearl Cửa hội 2 km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; C&aacute;ch cầu Cửa Hội 800m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Ngay cạnh Khu du lịch sinh th&aacute;i biển Xu&acirc;n Hội.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:24px;\"><span style=\"color:#0000cd;\"><strong><span style=\"font-family:times new roman,times,serif;\">LI&Ecirc;N KẾT V&Ugrave;NG THUẬN TIỆN</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Cầu Cửa Hội được ch&iacute;nh phủ v&agrave; c&aacute;c ban ng&agrave;nh Trung ương chấp thuận chủ trương đầu tư c&ocirc;ng, với kinh ph&iacute; l&agrave; 950 tỉ đồng.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/bot_cua_hoi(1).jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Cầu cửa hội được thiết kế với phần cầu vượt s&ocirc;ng c&oacute; chiều d&agrave;i khoảng 1,7km, chiều rộng cầu ch&iacute;nh 19m, bề rộng cầu dẫn rộng 16m, phần đường hai đầu cầu thiết kế theo ti&ecirc;u chuẩn cấp III đồng bằng, tốc độ thiết kế 80km/h.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự kiến cầu Cửa Hội sẽ tiến h&agrave;nh thi c&ocirc;ng v&agrave;o ng&agrave;y 02/9/2018 v&agrave; ho&agrave;n thiện, đưa v&agrave;o sử dụng v&agrave;o ng&agrave;y 02/9/2019.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Cầu Cửa Hội được x&acirc;y dựng sẽ gi&uacute;p kết nối TX.Cửa L&ograve;, Nghệ An v&agrave; QL8B tại huyện Nghi Xu&acirc;n, H&agrave; Tĩnh, tạo giao th&ocirc;ng thuận lợi trong khu vực. Th&uacute;c đẩy giao thương giữa hai tỉnh Nghệ An &ndash; H&agrave; Tĩnh ph&aacute;t triển đột ph&aacute;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự &aacute;n nằm ngay cạnh quốc lộ 8B d&agrave;i 25 km. Đ&acirc;y l&agrave; tuyến đường nối thị x&atilde; Hồng Lĩnh với huyện Nghi Xu&acirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch quốc lộ ven biển 800m, l&agrave; một tuyến giao th&ocirc;ng đường bộ đang được thi c&ocirc;ng, d&agrave;i khoảng 3.041 km v&agrave; cũng l&agrave; tuyến đường bộ thứ 3 chạy từ Bắc v&agrave;o Nam sau quốc lộ 1A v&agrave; đường Hồ Ch&iacute; Minh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Đường bộ ven biển Việt Nam đi qua tất cả c&aacute;c tỉnh ven biển, đ&acirc;y c&oacute; thể coi l&agrave; tuyến đường bộ d&agrave;i nhất Việt Nam. Tổng chiều d&agrave;i c&aacute;c đoạn tuyến đ&atilde; c&oacute; đường năm 2010 l&agrave; 1.800,86 km, chiếm 59,21%.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/ketnoi.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:24px;\"><span style=\"color:#0000cd;\"><span style=\"font-family:times new roman,times,serif;\"><strong>TIỆN &Iacute;CH DỰ &Aacute;N</strong></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự &aacute;n được đầu tư đồng bộ hệ thống cơ sở hạ tầng với đường trải nhựa 18m v&agrave; 12m, hệ thống c&acirc;y xanh, điện chiếu s&aacute;ng c&ocirc;ng cộng, cấp tho&aacute;t nước dọc theo c&aacute;c trục đường trong khu d&acirc;n cư. Nh&agrave; văn h&oacute;a, s&acirc;n thể thao, l&agrave; nơi phục vụ mục đ&iacute;ch vui chơi, giải tr&iacute;, thể thao của cộng đồng cư d&acirc;n.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/tienich.JPG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hệ thống c&acirc;y xanh của dự &aacute;n được đảm bảo quy hoạch v&agrave; trồng tr&ecirc;n c&aacute;c trục đường, đảm bảo &ldquo;xanh h&oacute;a&rdquo; khu d&acirc;n cư. Đồng thời, bố tr&iacute; một v&ugrave;ng c&acirc;y xanh tập trung với giống c&acirc;y được chọn lựa lỹ c&agrave;ng. Với kh&ocirc;ng gian sống xanh, m&ocirc;i trường sống trong l&agrave;nh như vậy, kh&aacute;ch h&agrave;ng sẽ ho&agrave;n to&agrave;n thư gi&atilde;n sau một ng&agrave;y l&agrave;m việc.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Kh&aacute;ch h&agrave;ng &ndash; những cư d&acirc;n tương lai của cụm d&acirc;n cư Hội Thủy ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m với chất lượng của dự &aacute;n bởi c&aacute;c hạng mục cơ sở hạ tầng đều được quy hoạch b&agrave;i bản, chất lượng nhằm mang đến cuộc sống tốt nhất cho cư d&acirc;n nơi đ&acirc;y.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">7 L&Yacute; DO ĐỂ ĐẦU TƯ NGAY DỰ &Aacute;N CỬA HỘI SEASIDE</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/7lydo.JPG\" /></span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"color: rgb(0, 0, 205);\"><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"color: rgb(0, 0, 205);\"><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"color: rgb(0, 0, 205);\"><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">Email: lamtlt.nghean@dxmb.vn</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong style=\"color: rgb(0, 0, 205);\"><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">Website: http://www.bdsnghetinh.net/</span></span></strong></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Khu Đô Thị Mặt Biển Cửa Hội Seaside', '18-7-2018', '30-11-2018', 'khu-do-thi-cua-hoi-seaside', 'Dự án Cửa Hội Seaside tọa lạc trên tuyến quốc lộ 8B đi qua thị trấn Nghi Xuân, Hà Tĩnh. Đây cũng là tuyến đường tiếp nhận điểm cuối của cầu Cửa Hội kế', 0, NULL, '0'),
(63, 27, 7, NULL, 'KHU ĐÔ THỊ ĐẠI THÀNH TRUNG ĐÔ', 'Khối 3, P.Trung Đô', NULL, NULL, NULL, '0941.441.991', 2481, '/public/upload/images/slide/baner-daithanh-trung-do.jpg', NULL, NULL, 0, '1', NULL, '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-trung-do/63.html\"><strong>&nbsp; &nbsp;</strong></a><strong><a href=\"http://datxanhnghean.com.vn/du-an/12/khu-do-thi-dai-thanh-trung-do\">Khu đ&ocirc; thị Đại Th&agrave;nh Trung Đ&ocirc;</a></strong> &ndash; Khu d&acirc;n cư cao cấp được x&acirc;y dựng v&agrave; ph&aacute;t triển với định hướng trở th&agrave;nh một trong những khu vực đ&aacute;ng sống nhất TP Vinh. Nằm tr&ecirc;n mảnh đất &ldquo;Địa linh nh&acirc;n kiệt&rdquo; Phượng Ho&agrave;ng &ndash; Trung Đ&ocirc; &ndash; nơi Vua Quang Trung từng lựa chọn l&agrave;m Thủ đ&ocirc; đất Việt bởi lợi thế thi&ecirc;n thời &ndash; địa lợi &ndash; nh&acirc;n h&ograve;a. Phong thủy vượng kh&iacute;: Mảnh đất l&agrave;nh được Vua Quang Trung che chở, gi&uacute;p con ch&aacute;u đời sau ph&aacute;t vượng. Nơi c&oacute; thế đất Rồng tọa sơn hướng thủy (tựa n&uacute;i liền s&ocirc;ng) mang đến sự ph&aacute;t đạt, thịnh vượng v&agrave; may mắn cho gia chủ.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/Picture2.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N ĐẠI TH&Agrave;NH TRUNG Đ&Ocirc;</span></span></strong></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- T&ecirc;n dự &aacute;n:</strong> Khu đ&ocirc; thị Đại Th&agrave;nh &ndash; Trung Đ&ocirc;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Chủ đầu tư: </strong>C&ocirc;ng ty CP Đầu tư v&agrave; X&acirc;y dựng Đại Th&agrave;nh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Đơn vị ph&acirc;n phối ch&iacute;nh thức:</strong> C&ocirc;ng ty CP Dịch Vụ &amp; Địa Ốc <a href=\"http://datxanhnghean.com.vn\">Đất Xanh Nghệ An</a>.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Vị tr&iacute;:</strong> Khối 3, P. Trung Đ&ocirc;, TP. Vinh, Nghệ An.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Diện t&iacute;ch:</strong> 49.191 m&sup2;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Tư vấn thiết kế hạ tầng kỹ thuật: </strong>C&ocirc;ng ty CP Kiến tr&uacute;c x&acirc;y dựng v&agrave; Thương mại ATC.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Quy m&ocirc;:</strong> Khu đ&ocirc; thị kết hợp gồm:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Khu c&acirc;y xanh thể dục thể thao: 1.646m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Khu nh&agrave; trẻ mẫu gi&aacute;o: 3.835m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Khu nh&agrave; văn h&oacute;a: 305m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Khu nh&agrave; thấp tầng : 216 l&ocirc; đất liền kề diện t&iacute;ch rộng từ 83 &ndash; 270m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Đường rộng từ: 9 &ndash; 36m2.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N ĐẠI TH&Agrave;NH TRUNG Đ&Ocirc;</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-trung-do/63.html\"><strong>Khu Đ&ocirc; Thị&nbsp; Đại Th&agrave;nh </strong></a>&ndash; <a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-trung-do/63.html\"><strong>Trung Đ&ocirc;</strong></a>, Nằm trong khu quy hoạch tổng thể ph&aacute;t triển đ&ocirc; thị năng động của th&agrave;nh phố, với cảnh quan thi&ecirc;n nhi&ecirc;n h&agrave;i h&ograve;a v&agrave; hệ thống giao th&ocirc;ng thuận tiện - Khu đ&ocirc; thị v&agrave; Dịch vụ Thương mại Đại Th&agrave;nh tại Khối 3 - phường Trung Đ&ocirc; &ndash; Tp Vinh - Nghệ An do C&ocirc;ng ty Cổ phần Đầu tư v&agrave; X&acirc;y dựng Đại Th&agrave;nh l&agrave;m chủ đầu tư sẽ l&agrave; chốn an cư l&yacute; tưởng cho mọi gia đ&igrave;nh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/vi-tri-dai-thanh-trung-do.PNG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Từ vị tr&iacute; dự &aacute;n c&oacute; thể dễ d&agrave;ng di chuyển tới:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Đại Học Vinh: 500m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Bảo t&agrave;ng qu&acirc;n khu 4: 400m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Chợ bến thủy: 400m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Quảng trường HCM: 1,5km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Bệnh viện đa khoa th&agrave;nh phố Vinh: 2km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Đền thờ Quang Trung &ndash; N&uacute;i Quyết: 300m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Cầu bến thủy: 1km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Trường tiểu học, trung học cơ sở Trung Đ&ocirc;: 300m.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-trung-do/63.html\">Khu đ&ocirc; thị Đại Th&agrave;nh &ndash; Trung Đ&ocirc;</a></strong><a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-trung-do/63.html\"> </a>được quy hoạch 216 l&ocirc; đất liền kề diện t&iacute;ch rộng từ 83 &ndash; 270m2, gi&uacute;p kh&aacute;ch h&agrave;ng lựa chọn l&ocirc; đất với khả năng t&agrave;i ch&iacute;nh v&agrave; nhu cầu sử dụng đa dạng.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Trở th&agrave;nh cư d&acirc;n Khu đ&ocirc; thị Đại Th&agrave;nh &ndash; Trung Đ&ocirc;, cư d&acirc;n được thừa hưởng c&aacute;c tiện &iacute;ch kh&eacute;p k&iacute;n: nh&agrave; trẻ, nh&agrave; văn h&oacute;a, c&ocirc;ng vi&ecirc;n c&acirc;y xanh, kiến tạo n&ecirc;n cộng đồng văn minh v&agrave; ph&aacute;t triển.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/tien-ich.PNG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Cơ sở hạ tầng được x&acirc;y dựng đồng bộ gồm mương tho&aacute;t nước, trạm điện, đường nội khu rộng từ 9 &ndash; 32m thuận lợi cho c&ocirc;ng việc kinh doanh quy m&ocirc; lớn, mang đến cơ hội sinh lời l&yacute; tưởng cho gia chủ.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Từ đ&acirc;y, cư d&acirc;n dễ d&agrave;ng tiếp cận c&aacute;c khu vực trọng điểm của th&agrave;nh phố Vinh: c&aacute;ch Đại Học Vinh 500m, c&aacute;ch Bảo t&agrave;ng qu&acirc;n khu Bốn 400m, c&aacute;ch Quảng trường Hồ Ch&iacute; Minh 1,5km, c&aacute;ch Bệnh viện đa khoa th&agrave;nh phố Vinh 2km. Tập trung xung quanh l&agrave; c&aacute;c tiện &iacute;ch phục vụ cuộc sống: Chợ Bến Thủy (chợ Đại học), Trường tiểu học v&agrave; Trung học cơ sở Trung Đ&ocirc;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Đ&acirc;y cũng được xem l&agrave; mảnh đất &ldquo;địa linh nh&acirc;n kiệt&rdquo; bởi tựa ch&acirc;n n&uacute;i Quyết, hướng l&ecirc;n Đền thờ Quang Trung, đ&oacute;n nhận vượng kh&iacute; v&agrave; an l&agrave;nh, t&agrave;i lộc từ sự che chở của Vua.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:times new roman,times,serif;\">PHONG THỦY ĐỘC T&Ocirc;N - ĐỊA LINH NH&Acirc;N KIỆT</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-trung-do/63.html\">Khu đ&ocirc; thị Đại Th&agrave;nh Trung Đ&ocirc;</a> nằm tr&ecirc;n mảnh đất &ldquo;Địa linh nh&acirc;n kiệt&rdquo; Phượng Ho&agrave;ng &ndash; Trung Đ&ocirc; &ndash; nơi Vua Quang Trung từng lựa chọn l&agrave;m Thủ đ&ocirc; đất Việt bởi lợi thế thi&ecirc;n thời &ndash; địa lợi &ndash; nh&acirc;n h&ograve;a. Phong thủy vượng kh&iacute;: Mảnh đất l&agrave;nh được Vua Quang Trung che chở, gi&uacute;p con ch&aacute;u đời sau ph&aacute;t vượng.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/Picture6.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Nơi c&oacute; thế đất Rồng tọa sơn hướng thủy (tựa n&uacute;i liền s&ocirc;ng) mang đến sự ph&aacute;t đạt, thịnh vượng v&agrave; may mắn cho gia chủ.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:times new roman,times,serif;\">SƠ ĐỒ PH&Acirc;N L&Ocirc; DỰ &Aacute;N</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/so-do-phan-lo.jpg\" /></span></span><br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>CH&Iacute;NH S&Aacute;CH B&Aacute;N H&Agrave;NG</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">Tặng ngay 10 triệu đồng cho qu&yacute; kh&aacute;ch h&agrave;ng đặt cọc trong thời gian từ 8-11-2018.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/42.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>H&Igrave;NH ẢNH THỰC T&Eacute;</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/3.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/2.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/1.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/4.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/5.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/dai-thanh-trung-do/7.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:22px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:22px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:22px;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:22px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></span></span></p>', NULL, NULL, NULL, NULL, NULL, 'KHUYẾN MÃI NGAY 10 TRIỆU ĐỒNG CHO 5 KHÁCH HÀNG ĐẶT CỌC ĐẦU TIÊN TỪ NGÀY 7/11/2018.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Khu Đô Thị Đại Thành Trung Đô', '17-8-2018', '4-12-2018', 'khu-do-thi-dai-thanh-trung-do', 'Khu đô thị Đại Thành Trung Đô – Khu dân cư cao cấp được xây dựng và phát triển với định hướng trở thành một trong những khu vực đáng sống nhất TP Vinh', 0, 'Đại thành trung đô, Khu đô thị đại thành trung đô', '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(64, 27, 7, NULL, 'KHU ĐÔ THỊ ĐẠI THÀNH NGHI KIM', 'Xã Nghi Kim', NULL, NULL, NULL, '094.220.6262', 1986, '/public/upload/images/dai-thanh-nghi-kim/gioi-thieu-kdt-dai-thanh-nghi-kim.jpg', NULL, NULL, 0, '1', NULL, '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-nghi-kim-tp-vinh/64.html\">Khu Đ&ocirc; Thị&nbsp; Đại Th&agrave;nh &ndash; Nghi Kim</a></strong> tọa lạc tại cửa ng&otilde; ph&iacute;a Bắc của TP. Vinh, chạy dọc theo QL1A, gần bến xe Vinh mới, v&ugrave;ng đất được định hướng ph&aacute;t triển trở th&agrave;nh khu vực trung t&acirc;m mới của th&agrave;nh phố Vinh trong tương lai gần.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-nghi-kim/phoi-canh-du-an.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Với hạ tầng ho&agrave;n thiện, c&ugrave;ng hệ thống giao th&ocirc;ng ph&aacute;t triển khi nằm gần ngay bến xe mới Vinh, Đất nền tại Khu đ&ocirc; thị Đại Th&agrave;nh Nghi Kim được kh&aacute;ch h&agrave;ng săn l&ugrave;ng ngay tại thời điểm mở b&aacute;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-size:24px;\"><span style=\"color:#0000ff;\"><strong><span style=\"font-family:times new roman,times,serif;\">TH&Ocirc;NG TIN TỔNG QUAN DỰ &Aacute;N</span></strong></span></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- T&ecirc;n dự &aacute;n:</strong> Khu đ&ocirc; thị Đại Th&agrave;nh &ndash; Nghi Kim.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Chủ đầu tư:</strong> C&ocirc;ng ty CP Đầu tư v&agrave; X&acirc;y dựng Đại Th&agrave;nh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Vị tr&iacute;:</strong> Ng&atilde; tư Qu&aacute;n B&aacute;nh, X&atilde; Nghi Kim, TP. Vinh, Nghệ An.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Diện t&iacute;ch:</strong> 89.322 m&sup2;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Tư vấn thiết kế hạ tầng kỹ thuật:</strong> C&ocirc;ng ty CP Kiến tr&uacute;c x&acirc;y dựng v&agrave; Thương mại ATC.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Quy m&ocirc;:</strong> Khu đ&ocirc; thị kết hợp gồm:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Khu nh&agrave; cao tầng: 6.966m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Khu c&acirc;y xanh thể dục thể thao: 2.900m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Khu nh&agrave; trẻ mẫu gi&aacute;o: 2.000m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Khu hội qu&aacute;n: 705m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Khu văn ph&ograve;ng: 2.083m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Khu nh&agrave; thấp tầng 319 căn bao gồm:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Nh&agrave; liền kề: 202 căn, cao 3 tầng, diện t&iacute;ch từ 96m2 &ndash; 176m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">+ Nh&agrave; phố thương mại: 117 căn, cao 3 tầng, diện t&iacute;ch từ 102m2 &ndash; 256m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N ĐẠI TH&Agrave;NH NGHI KIM</span></span></strong></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-dai-thanh-nghi-kim-tp-vinh/64.html\">KĐT Đại Th&agrave;nh &ndash; Nghi Kim</a></strong> tọa lạc tại cửa ng&otilde; ph&iacute;a Bắc của TP. Vinh, chạy dọc theo QL1A, gần bến xe Vinh mới. Từ đ&acirc;y cư d&acirc;n chỉ mất 10 ph&uacute;t di chuyển qua tuyến đại lộ Thăng Long &ndash; Nguyễn Tr&atilde;i &ndash; H&agrave; Huy Tập để tiếp cận trung t&acirc;m th&agrave;nh phố.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-nghi-kim/vi-tri-du-an.JPG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;KĐT Đại Th&agrave;nh &ndash; Nghi Kim được định hướng trở th&agrave;nh khu đ&ocirc; thị tập trung tất cả c&aacute;c tiện &iacute;ch về sinh hoạt, gi&aacute;o dục, giải tr&iacute; v&agrave; việc l&agrave;m, x&acirc;y dựng cuộc sống tiện nghi giữa kh&ocirc;ng gian thanh b&igrave;nh ở ngoại &ocirc; th&agrave;nh phố nhưng vẫn dễ d&agrave;ng kết nối với v&ugrave;ng trung t&acirc;m khi cần thiết.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Từ dự &aacute;n, cư d&acirc;n dễ d&agrave;ng tiếp cận đến c&aacute;c tiện &iacute;ch c&ocirc;ng cộng:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Đi bộ ra bến xe Vinh mới, c&aacute;ch S&acirc;n bay Vinh 800m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;c c&ocirc;ng tr&igrave;nh gi&aacute;o dục trong x&atilde;: Trường mầm non Nghi Kim, Trường Tiểu học v&agrave; THCS Nghi Kim.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch B&atilde;i biển Cửa L&ograve; 10km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch KCN Bắc Vinh v&agrave; Nh&agrave; m&aacute;y bao b&igrave; Sabeco 1,5km, KCN VSIP Nghệ An 3,7km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Xung quanh dự &aacute;n c&oacute; đầy đủ chợ, ng&acirc;n h&agrave;ng, khu vui chơi giải tr&iacute; đ&aacute;p ứng nhu cầu của cư d&acirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<strong><span style=\"color:#0000ff;\"><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N</span></span></span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Trở th&agrave;nh cư d&acirc;n KĐT Đại Th&agrave;nh &ndash; Nghi Kim, cha mẹ kh&ocirc;ng phải lo lắng lựa chọn nh&agrave; trẻ hoặc trường học cho con. Sau khi ho&agrave;n th&agrave;nh bậc Đại học, con c&aacute;i c&oacute; thể trở về l&agrave;m việc ngay tại những c&ocirc;ng ty tầm cỡ trong khu đ&ocirc; thị để tận hưởng cuộc sống gia đ&igrave;nh gần gũi m&agrave; vẫn đủ điều kiện ph&aacute;t triển tương lai.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-nghi-kim/tien-ich.JPG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Khu đ&ocirc; thị Đại Th&agrave;nh Nghi Kim d&agrave;nh 40% diện t&iacute;ch cho vui chơi v&agrave; tận hưởng cuộc sống giao h&ograve;a với thi&ecirc;n nhi&ecirc;n. Tại đ&acirc;y, c&aacute;c th&agrave;nh vi&ecirc;n c&oacute; thể c&ugrave;ng nhau trải nghiệm những tr&ograve; chơi thả diều, đ&aacute; b&oacute;ng, c&ugrave;ng nhau l&agrave;m vườn hoặc chăm s&oacute;c th&uacute; cưng. Người gi&agrave; được vui vầy b&ecirc;n con ch&aacute;u, trẻ em sống t&igrave;nh cảm v&agrave; gắn kết với gia đ&igrave;nh. KĐT Đại Th&agrave;nh &ndash; Nghi Kim l&agrave; chốn nghỉ ngơi đ&uacute;ng nghĩa, nơi an cư l&yacute; tưởng cho mọi gia đ&igrave;nh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:24px;\"><strong><span style=\"font-family:times new roman,times,serif;\">SƠ ĐỒ PH&Acirc;N L&Ocirc; DỰ &Aacute;N</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:24px;\"><strong><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/dai-thanh-nghi-kim/so-do.jpg\" /></span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:24px;\"><strong>MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:24px;\"><strong>C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:24px;\"><strong>Email: lamtlt.nghean@dxmb.vn</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:24px;\"><strong>Website: http://www.bdsnghetinh.net/</strong></span></span></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Khu Đô Thị Đại Thành Nghi Kim - TP Vinh', '17-8-2018', '30-11-2018', 'khu-do-thi-dai-thanh-nghi-kim', 'Phân Phối Độc Quyền Đất Nền dự án Khu Đô Thị Đại Thành Nghi Kim. Vị trí siêu đẹp, giá rẻ nhất thị trường, sổ đỏ chính chủ...', 26, 'đại thành nghi kim, khu đô thị đại thành nghi kim', '0'),
(65, 27, 10, NULL, 'KHU ĐÔ THỊ KỲ ANH CENTRAL PARK', NULL, NULL, NULL, NULL, NULL, 2170, '/public/upload/images/kyanhcentralpark/phoi-canh-ky-anh-center-park-logo.jpg', NULL, NULL, 1, '1', NULL, '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-ky-anh-central-park/65.html\"><strong>&nbsp; &nbsp;Kỳ Anh Central Park</strong></a> (Khu đ&ocirc; thị v&agrave; C&ocirc;ng Vi&ecirc;n Hồ R&agrave;ng R&agrave;ng) - Si&ecirc;u đ&ocirc; thị hiện đại đồng bộ bậc nhất tại Thị X&atilde; Kỳ Anh tọa lạc tại vị tr&iacute; chiến lược, nằm ngay trung t&acirc;m của khu kinh tế trọng điểm tỉnh H&agrave; Tĩnh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/kyanhcentralpark/phoi-canh-ky-anh-center-park-logo.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Với quy m&ocirc; l&ecirc;n đến 116ha được x&acirc;y dựng &amp; thiết kế trở th&agrave;nh một khu đ&ocirc; thị kiểu mẫu đ&aacute;ng sống bậc nhất, Kỳ Anh Central Park đang l&agrave;m thay đổi ho&agrave;n to&agrave;n bộ mặt của đ&ocirc; thị Kỳ Anh v&agrave; trở th&agrave;nh nơi an cư cho cư d&acirc;n tại đ&acirc;y.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000cd;\"><span style=\"font-size:22px;\"><strong>TỔNG QUAN DỰ &Aacute;N</strong></span></span></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- T&ecirc;n dự &aacute;n:</strong> <a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-ky-anh-central-park/65.html\">Kỳ Anh Central Park (Khu đ&ocirc; thị &amp; c&ocirc;ng vi&ecirc;n Hồ R&agrave;ng R&agrave;ng)</a></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Chủ đầu tư:</strong> C&ocirc;ng ty Cổ phần SPT.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Vị tr&iacute;:</strong> Khu phố Hưng Thịnh, Thị x&atilde; Kỳ Anh, Tỉnh H&agrave; Tĩnh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Quy m&ocirc;:</strong> 116 ha.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Tư vấn thiết kế Hạ tầng kỹ thuật: </strong>PACE Architecture.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>- Quy hoạch:</strong> Khu đ&ocirc; thị kết hợp gồm:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Nh&agrave; ở, biệt thự, shophouse diện t&iacute;ch từ: 139 &ndash; 427m2.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Chợ trung t&acirc;m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Nh&agrave; văn h&oacute;a, nh&agrave; trẻ.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Quảng trường, hồ nước v&agrave; c&ocirc;ng vi&ecirc;n c&acirc;y xanh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Khu vui chơi giải tr&iacute; v&agrave; thể dục thể thao.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; + Đường nội bộ rộng 12 - 30m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- M&acirc;̣t đ&ocirc;̣ x&acirc;y dựng: 45,3%.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><span style=\"font-size:24px;\"><strong><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N KỲ ANH CENTRAL PARK</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><a href=\"http://www.bdsnghetinh.net/du-an-bds/khu-do-thi-ky-anh-central-park/65.html\"><strong>&nbsp; &nbsp;Kỳ Anh Central Park</strong></a> tọa lạc tại vị tr&iacute; chiến lược, nằm ngay trung t&acirc;m của khu kinh tế trọng điểm tỉnh H&agrave; Tĩnh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự &aacute;n thừa hưởng c&aacute;c c&ocirc;ng tr&igrave;nh giao th&ocirc;ng, văn h&oacute;a v&agrave; kinh tế - x&atilde; hội của thị x&atilde; Kỳ Anh, tạo điều kiện cho c&aacute;c hoạt động giao thương diễn ra thuận lợi, x&acirc;y dựng m&ocirc;i trường kinh doanh l&yacute; tưởng cho c&aacute;c nh&agrave; đầu tư.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/kyanhcentralpark/vi-tri-center-park.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/15.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Với lợi thế nằm tiếp gi&aacute;p mặt đường Quốc lộ 1A, gần trung t&acirc;m h&agrave;nh ch&iacute;nh của thị x&atilde; Kỳ Anh, bao quanh l&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh bệnh viện, trường học, chợ, Khu c&ocirc;ng nghiệp, Kỳ Anh Central Park xứng tầm l&agrave; Si&ecirc;u dự &aacute;n trung t&acirc;m đ&ocirc; thị sầm uất bậc nhất H&agrave; Tĩnh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/kyanhcentralpark/lien-ket-vung-ky-anh.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Li&ecirc;n kết v&ugrave;ng dự &aacute;n:</span></strong></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Tiếp gi&aacute;p Quốc lộ 1A.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch trung t&acirc;m h&agrave;nh ch&iacute;nh mới 800m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch bệnh viện Đa khoa 600m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch sở c&ocirc;ng an Huyện Kỳ Anh 600m.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch Trường mầm non Quốc tế, Trường Cấp I &ndash; III b&aacute;n k&iacute;nh 1km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch KCN Vũng &Aacute;ng 6 km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- C&aacute;ch KCN nh&agrave; m&aacute;y Formosa 12km.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><span style=\"font-size:24px;\"><strong><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Với quy m&ocirc; 116 ha, Kỳ Anh Central Park x&acirc;y dựng những tiện &iacute;ch ti&ecirc;u chuẩn phục vụ cuộc sống gồm khu c&ocirc;ng vi&ecirc;n c&acirc;y xanh, hồ sinh th&aacute;i, nh&agrave; văn h&oacute;a, trung t&acirc;m thể dục thể thao, kiến tạo m&ocirc;i trường sống hiện đại, an l&agrave;nh v&agrave; văn minh cho cư d&acirc;n đ&ocirc; thị.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/kyanhcentralpark/tienich.JPG\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Nh&agrave; ở v&agrave; biệt thự với thiết kế thượng lưu, diện t&iacute;ch linh hoạt, ph&ugrave; hợp với nhu cầu sinh sống v&agrave; kinh doanh đa dạng, l&agrave; tuyệt t&aacute;c chưa từng c&oacute; tr&ecirc;n thị trường bất động sản Kỳ Anh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Khu tổ hợp thương mại dịch vụ v&agrave; nh&agrave; ở cao tầng:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Được tổ chức ở vị tr&iacute; c&oacute; tầm nh&igrave;n v&agrave; kh&ocirc;ng gian thuận lợi nhất trong khu đ&ocirc; thị.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Nằm b&ecirc;n trục đường quốc lộ 1A, đối trọng với khu chợ trung t&acirc;m v&agrave; khu c&ocirc;ng vi&ecirc;n đ&ocirc; thị.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Thiết kế bao gồm c&aacute;c chức năng: 3 tầng dưới tổ chức th&agrave;nh trung t&acirc;m thương mại dịch vụ, si&ecirc;u thị b&aacute;n lẻ, trung t&acirc;m tổ chức sự kiện (hội nghị, hội thảo v&agrave; tiệc cưới...)</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Tầng 4 đến tầng 15 tổ chức th&agrave;nh c&aacute;c căn hộ cho thu&ecirc; hoặc b&aacute;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Khu c&ocirc;ng vi&ecirc;n với thiết kế đa dạng:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Khu cao v&agrave; trung tuổi: s&acirc;n rộng để người cao tuổi c&oacute; kh&ocirc;ng gian tập luyện y&ecirc;n tĩnh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Khu trẻ em c&oacute; c&aacute;c tr&ograve; chơi ngo&agrave;i trời được bố tr&iacute; một c&aacute;ch linh hoạt.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Khu quảng trường rộng lớn c&oacute; kh&ocirc;ng gian rộng để sử dụng cho c&aacute;c dịp lễ hội.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Khu picnic c&oacute; b&atilde;i cỏ tự nhi&ecirc;n để cắm trại v&agrave; tổ chức c&aacute;c hoạt động ngo&agrave;i trời.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; - Nh&agrave; h&agrave;ng thủy tạ v&agrave; bến thuyền để phục vụ nhu cầu của cư d&acirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Khu c&acirc;y xanh:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Trồng xen kẽ c&aacute;c loại c&acirc;y v&agrave; hoa nhiều m&agrave;u sắc với quy hoạch b&agrave;i bản, kết hợp với hệ thống đ&egrave;n chiếu s&aacute;ng, tiện &iacute;ch đ&ocirc; thị&nbsp; tạo n&ecirc;n chốn giao lưu, gặp gỡ l&yacute; tưởng cho cư d&acirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Khu vực n&agrave;y được bố tr&iacute; đường dạo v&agrave; trở th&agrave;nh khu c&ocirc;ng vi&ecirc;n khu vực thị x&atilde; Kỳ Anh v&agrave; c&aacute;c v&ugrave;ng phụ cận.</span></span></p>\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Hạ Tầng Kỹ Thuật Đạt Chuẩn.</span></span></li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Tất cả c&aacute;c tuyến đường giao th&ocirc;ng đều trồng c&acirc;y xanh.</span></span></li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Mạng lưới tho&aacute;t nước được tổ chức dưới l&ograve;ng c&aacute;c tuyến đường.</span></span></li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Sử dụng c&aacute;c c&ocirc;ng nghệ mới đảm bảo cho việc x&acirc;y dựng đường giao th&ocirc;ng cũng như c&ocirc;ng tr&igrave;nh đạt độ an to&agrave;n cao nhất.</span></span></li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">X&acirc;y k&egrave; đập gia cố với kết cấu nền cứng, l&agrave;m giảm &aacute;p lực v&agrave; vận tốc của nước tại c&aacute;c vị tr&iacute; khe suối tụ thủy để đấu nối v&agrave;o hệ thống tho&aacute;t nước của khu vực được an to&agrave;n.</span></span></li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Nước thải sinh hoạt đ&ocirc; thị được xử l&yacute; theo d&acirc;y chuyền c&ocirc;ng nghệ sạch sinh học nh&acirc;n tạo qua bể Aeroten, d&ugrave;ng b&ugrave;n hoạt t&iacute;nh để giảm thiểu chỉ ti&ecirc;u &ocirc; nhiễm BOD, COD, SS... Nước thải sinh hoạt sau khi xử l&yacute; đạt ti&ecirc;u chuẩn B theo TCVN 7222-2002.</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:24px;\"><span style=\"color:#0000cd;\"><strong><span style=\"font-family:times new roman,times,serif;\">ƯU Đ&Atilde;I D&Agrave;NH CHO KH&Aacute;CH H&Agrave;NG</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/kyanhcentralpark/chinh1sach.JPG\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>H&Igrave;NH ẢNH THỰC TẾ</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/16.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/17.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/18.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/19.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/20.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/21.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/1%20KY%20ANH/22.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0941.441.991 ( Mr. L&acirc;m )</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\">Email: lamtlt.nghean@dxmb.vn</span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\">Website: http://www.bdsnghetinh.net/</span></strong></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '4-9-2018', '5-12-2018', 'khu-do-thi-ky-anh-central-park', 'Kỳ Anh Central Park (Khu đô thị và Công Viên Hồ Ràng Ràng) - Siêu đô thị hiện đại đồng bộ bậc nhất tại Thị Xã Kỳ Anh tọa lạc tại vị trí chiến lược, nằ', 0, NULL, '0'),
(66, 27, 8, NULL, 'KHU ĐÔ THỊ XUÂN LIÊN', 'Thôn Cường Thịnh, Xã Xuân Liên', 'Công ty TNHH xuất nhập khẩu Châu Tuấn', '2018', '125m2', '094.220.6262', 1623, '/public/upload/images/Xu%C3%A2n%20Li%C3%AAn/K%C4%90T%20Xu%C3%A2n%20Li%C3%AAn%20H%C3%A0%20T%C4%A9nh.jpg', NULL, NULL, 1, '1', NULL, '<p>\r\n	<span style=\"font-size:16px;\">&nbsp; &nbsp;Khu đ&ocirc; thị Xu&acirc;n Li&ecirc;n c&oacute; vị tr&iacute; then chốt B&aacute;m tuyến đường v&agrave;ng - nằm ngay mặt đường Ven Biển Quốc Gia, hạ tầng ho&agrave;n thiện, gi&aacute; b&aacute;n cực kỳ ưu đ&atilde;i l&agrave; sự lựa chọn tối ưu cho qu&yacute; kh&aacute;ch h&agrave;ng mua ở hoặc đầu tư.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"Cơ sở hạ tầng Xuân Liên Hà Tĩnh\" src=\"/public/upload/images/Xu%C3%A2n%20Li%C3%AAn/C%C6%A1%20s%E1%BB%9F%20H%E1%BA%A1%20t%E1%BA%A7ng.png\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">&nbsp; &nbsp;Với hạ tầng ho&agrave;n thiện, c&ugrave;ng hệ thống giao th&ocirc;ng ph&aacute;t triển khi nằm ngay mặt được Quốc gia ven biển, Đất nền tại Khu đ&ocirc; thị Xu&acirc;n Li&ecirc;n được kh&aacute;ch h&agrave;ng săn l&ugrave;ng ngay tại thời điểm mở b&aacute;n với tiềm năng sinh lời cực cao.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>TỔNG QUAN DỰ &Aacute;N</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>- T&ecirc;n dự &aacute;n: </strong>Khu đ&ocirc; thị Xu&acirc;n Li&ecirc;n.<br />\r\n	<strong>- Chủ đầu tư:</strong> C&ocirc;ng ty TNHH xuất nhập khẩu&nbsp;Ch&acirc;u Tuấn.<br />\r\n	<strong>- Vị tr&iacute;: </strong>Th&ocirc;n Cường Thịnh, x&atilde; Xu&acirc;n Li&ecirc;n, huyện Nghi Xu&acirc;n, tỉnh H&agrave; Tĩnh.<br />\r\n	<strong>- Quy m&ocirc;:</strong> 4,94 ha.<br />\r\n	<strong>- Quy hoạch:</strong> Khu đ&ocirc; thị kết hợp gồm:<br />\r\n	<em>+ 219 Nh&agrave; liền kề, biệt thự diện t&iacute;ch từ:&nbsp; 125 &ndash; 220m2.<br />\r\n	+ Khu trung t&acirc;m thương mại.<br />\r\n	+ Nh&agrave; văn h&oacute;a.<br />\r\n	+ Đường nội bộ rộng 12 &ndash; 14m.</em></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>VỊ TR&Iacute; DỰ &Aacute;N</strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:16px;\">- Khu đ&ocirc; thị Xu&acirc;n Li&ecirc;n c&oacute; vị tr&iacute; then chốt trong chiến lược ph&aacute;t triển kinh tế của huyện Nghi Xu&acirc;n n&oacute;i ri&ecirc;ng v&agrave; to&agrave;n tỉnh H&agrave; Tĩnh n&oacute;i chung.</span></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:16px;\">- Nằm tiếp gi&aacute;p tuyến Đường Quốc gia ven biển,&nbsp; từ khu đ&ocirc; thị dễ d&agrave;ng tiếp cận giao thương với 28 tỉnh, th&agrave;nh ven biển tr&ecirc;n cả nước, mang lại v&ocirc; số lợi thế kinh doanh vượt trội. Đ&acirc;y cũng được đ&aacute;nh gi&aacute; l&agrave; &ldquo;tuyến đường thịnh vượng&rdquo; n&acirc;ng tầm gi&aacute; trị bất động sản dọc hai tuyến đường.&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-size:16px;\">- Nằm c&aacute;ch bờ biển chỉ 800m, dự &aacute;n c&oacute; tiềm năng đ&oacute;n nhận lưu lượng lớn kh&aacute;ch du lịch để ph&aacute;t triển c&aacute;c loại h&igrave;nh dịch vụ phục vụ nghỉ dưỡng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Xu%C3%A2n%20Li%C3%AAn/V%E1%BB%8A%20TR%C3%8D%20XU%C3%82N%20LI%C3%8AN.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>LI&Ecirc;N KẾT V&Ugrave;NG</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">&nbsp; &nbsp;Với lợi thế giao th&ocirc;ng thuận lợi, từ Khu đ&ocirc; thị Xu&acirc;n Li&ecirc;n cư d&acirc;n dễ d&agrave;ng tiếp cận đến tất cả tiện &iacute;ch cần thiết trong v&ograve;ng b&aacute;n k&iacute;nh chỉ 1,5km.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- Tiếp gi&aacute;p Đường Quốc gia ven biển.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- Liền kề S&acirc;n b&oacute;ng nh&acirc;n tạo.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- Trường THCS Hoa Li&ecirc;n: 1,5 km.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- UBND x&atilde; Xu&acirc;n Li&ecirc;n: 1 km.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- Chợ Cương Gi&aacute;n: 1,5 km.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- Trạm Y tế Cương Gi&aacute;n: 1,5 km.</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">- B&atilde;i biển: 800m.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Xu%C3%A2n%20Li%C3%AAn/Li%C3%AAn%20k%E1%BA%BFt%20v%C3%B9ng.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>TIỆN &Iacute;CH DỰ &Aacute;N</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Xu%C3%A2n%20Li%C3%AAn/TI%E1%BB%86N%20%C3%8DCH%20D%E1%BB%B0%20%C3%81N.png\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>SƠ ĐỒ PH&Acirc;N L&Ocirc; DỰ &Aacute;N</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Xu%C3%A2n%20Li%C3%AAn/CHIA%20LO%20XUAN%20LIEN%202018-Model-01.jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>V&igrave; sao n&ecirc;n đầu tư BĐS dọc đường quốc gia ven biển???</strong></span></span><br />\r\n	- Khu đ&ocirc; thị Xu&acirc;n Li&ecirc;n c&oacute; vị tr&iacute; then chốt trong chiến lược ph&aacute;t triển kinh tế của huyện Nghi Xu&acirc;n n&oacute;i ri&ecirc;ng v&agrave; to&agrave;n tỉnh H&agrave; Tĩnh n&oacute;i chung.<br />\r\n	- Nằm tiếp gi&aacute;p tuyến Đường Quốc gia ven biển,&nbsp; từ khu đ&ocirc; thị dễ d&agrave;ng tiếp cận giao thương với 28 tỉnh, th&agrave;nh ven biển tr&ecirc;n cả nước, mang lại v&ocirc; số lợi thế kinh doanh vượt trội. Đ&acirc;y cũng được đ&aacute;nh gi&aacute; l&agrave; &ldquo;tuyến đường thịnh vượng&rdquo; n&acirc;ng tầm gi&aacute; trị bất động sản dọc hai tuyến đường.&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n	- Nằm c&aacute;ch bờ biển chỉ 800m, dự &aacute;n c&oacute; tiềm năng đ&oacute;n nhận lưu lượng lớn kh&aacute;ch du lịch để ph&aacute;t triển c&aacute;c loại h&igrave;nh dịch vụ phục vụ nghỉ dưỡng.</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">Tham khảo chi tiết: <a href=\"http://www.bdsnghetinh.net/xu-huong-dau-tu-bat-dong-san-doc-duong-quoc-gia-ven-bien/75.html\"><u>http://www.bdsnghetinh.net/xu-huong-dau-tu-bat-dong-san-doc-duong-quoc-gia-ven-bien/75.html</u></a></span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>Tiềm năng đầu tư BĐS v&agrave;o thị trường H&agrave; Tĩnh ???</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\">Ch&uacute;ng t&ocirc;i đ&atilde; thống k&ecirc; một v&agrave;i th&ocirc;ng tin tốt cho c&aacute;c nh&agrave; đầu tư v&agrave;o thị trường BĐS H&agrave; Tĩnh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><u><strong><a href=\"http://www.bdsnghetinh.net/tiem-nang-but-pha-cua-thi-truong-bat-dong-san-ha-tinh/76.html\">http://www.bdsnghetinh.net/tiem-nang-but-pha-cua-thi-truong-bat-dong-san-ha-tinh/76.html</a></strong></u></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><u><strong><a href=\"http://www.bdsnghetinh.net/3-vi-tri-dau-tu-sinh-loi-vuot-troi-tai-nghi-xuan-ha-tinh/77.html\">http://www.bdsnghetinh.net/3-vi-tri-dau-tu-sinh-loi-vuot-troi-tai-nghi-xuan-ha-tinh/77.html</a></strong></u></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><u><strong><a href=\"http://www.bdsnghetinh.net/ha-tinh-dat-lanh-cho-cac-nha-dau-tu/78.html\">http://www.bdsnghetinh.net/ha-tinh-dat-lanh-cho-cac-nha-dau-tu/78.html</a></strong></u></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><u><strong><a href=\"http://www.bdsnghetinh.net/thi-truong-bat-dong-san-ha-tinh-nha-dau-tu-thoi-lan-gio-moi/79.html\">http://www.bdsnghetinh.net/thi-truong-bat-dong-san-ha-tinh-nha-dau-tu-thoi-lan-gio-moi/79.html</a></strong></u></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><u><strong><a href=\"http://www.bdsnghetinh.net/ha-tinh-dau-tu-hon-1-400-ty-dong-lam-duong-ven-bien/80.html\">http://www.bdsnghetinh.net/ha-tinh-dau-tu-hon-1-400-ty-dong-lam-duong-ven-bien/80.html</a></strong></u></span></li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>H&Igrave;NH ẢNH THỰC TẾ</strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/6%20xu%C3%A2n%20li%C3%AAn/ecb6abe06f5c8f02d64d.jpg\" /></strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/6%20xu%C3%A2n%20li%C3%AAn/1d2d6e6caad04a8e13c1.jpg\" /></strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/6%20xu%C3%A2n%20li%C3%AAn/Khu%20%C4%91%C3%B4%20th%E1%BB%8B%20Xu%C3%A2n%20Li%C3%AAn%201.jpg\" /></strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/6%20xu%C3%A2n%20li%C3%AAn/Khu%20%C4%91%C3%B4%20th%E1%BB%8B%20Xu%C3%A2n%20Li%C3%AAn%202.jpg\" /></strong></span></span></p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/6%20xu%C3%A2n%20li%C3%AAn/Khu%20%C4%91%C3%B4%20th%E1%BB%8B%20Xu%C3%A2n%20Li%C3%AAn%204.jpg\" /></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 094.220.6262 ( Ms. Tr&acirc;m )</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">Email: tramnt.nghean@dxmb.vn</span></strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">Website: http://www.bdsnghetinh.net/</span></strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bán ngay đất đầu tư đẹp Xuân Liên - Bán đường Quốc gia ven biển', '15-10-2018', '10-5-2019', 'khu-do-thi-xuan-lien', 'Khu đô thị Xuân Liên có vị trí then chốt Bám tuyến đường vàng - nằm ngay mặt đường Ven Biển Quốc Gia, hạ tầng hoàn thiện, giá bán cực kỳ ưu đãi ...', 3, 'đất đầu tư Nghệ Tĩnh, đất nền Hà Tĩnh, đất nền Xuân Liên - Hà Tĩnh, đất đầu tư Nghệ An,', '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(67, 27, 8, NULL, 'KHU ĐÔ THỊ XUÂN AN GREEN PARK', 'Thị Trấn Xuân An', 'Công ty CP Đầu tư và Xây dựng Đông Dương Thăng Long', NULL, '120', '0962452245', 2856, '/public/upload/files/Xuan%20An-03(1).jpg', '12 triệu', NULL, 1, '1', NULL, '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>&nbsp; &nbsp;Lấy cảm hứng từ những khu đ&ocirc; thị sinh th&aacute;i đẳng cấp, Khu Đ&ocirc; Thị Xu&acirc;n An ti&ecirc;n phong kiến tạo tr&ecirc;n thị trường bất động sản Bắc Miền Trung một chuẩn mực mới của phong c&aacute;ch sống: giao h&ograve;a với tự nhi&ecirc;n giữa thi&ecirc;n đường tiện &iacute;ch. Sở hữu tới 6,4ha c&ocirc;ng vi&ecirc;n v&agrave; mặt nước, mật độ x&acirc;y dựng l&yacute; tưởng 21%, khu đ&ocirc; thị đẹp tựa vi&ecirc;n ngọc xanh b&ecirc;n thảm sinh th&aacute;i trải rộng.</em></span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong>TỔNG QUAN DỰ &Aacute;N</strong></span></span><br />\r\n	T&ecirc;n dự &aacute;n: <strong>Khu đ&ocirc; thị Xu&acirc;n An Green Park.</strong><br />\r\n	Chủ đầu tư: C&ocirc;ng ty CP Đầu tư v&agrave; X&acirc;y dựng Đ&ocirc;ng Dương Thăng Long.<br />\r\n	Vị tr&iacute;: Thị trấn Xu&acirc;n An, huyện Nghi Xu&acirc;n, H&agrave; Tĩnh.<br />\r\n	Quy m&ocirc;: 273.232m2.<br />\r\n	Mật độ x&acirc;y dựng: 21%.<br />\r\n	Quy hoạch: Khu đ&ocirc; thị kết hợp gồm: 101.569m2 đất ở bao gồm:<br />\r\n	&bull; 331 căn nh&agrave; liền kề từ 120 - 180m2.<br />\r\n	&bull; 93 căn biệt thự từ 500 - 1285m2.<br />\r\n	&bull; Khu c&acirc;y xanh rộng 10.145m2.<br />\r\n	&bull; Khu c&ocirc;ng vi&ecirc;n TDTT v&agrave; c&acirc;y xanh rộng 36.940m2.<br />\r\n	&bull; Mặt hồ rộng 16.836m2.<br />\r\n	&bull; Đất giao th&ocirc;ng rộng 104.338m2<br />\r\n	&bull; Đường nội bộ rộng 15,5 &ndash; 24m.<br />\r\n	&bull;&nbsp; Trung t&acirc;m thương mại đẳng cấp:<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; + C&agrave; ph&ecirc;, si&ecirc;u thị, khu ăn uống.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; + Bể bơi 4 m&ugrave;a.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; + Trung t&acirc;m tiệc cưới v&agrave; tổ chức sự kiện với hơn 1000 chỗ ngồi.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; + Rạp chiếu phim v&agrave; khu vui chơi giải tr&iacute;.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0002.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>VỊ TR&Iacute; DỰ &Aacute;N</strong></span></span><br />\r\n	o Dự &aacute;n tọa lạc tại trung t&acirc;m thị trấn Xu&acirc;n An &ndash; v&ugrave;ng đất &ldquo;Địa linh nh&acirc;n kiệt&rdquo;, sản sinh c&aacute;c bậc danh nh&acirc;n văn h&oacute;a như Nguyễn Du, Nguyễn C&ocirc;ng Trứ, Tả Ao,..., soi đường dẫn lối cho gia chủ chạm tới đỉnh cao cuộc sống hưng thịnh.<br />\r\n	o Địa thế lưng tựa n&uacute;i, thu trọn tầm nh&igrave;n sơn cảnh v&agrave; kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng của tự nhi&ecirc;n, mang lại nguồn năng lượng t&iacute;ch cực mỗi ng&agrave;y cho cư d&acirc;n đ&ocirc; thị.<br />\r\n	o Tiệm cận c&aacute;c trục đường rộng 42m, 36m, 18m như Quốc lộ 8B (đường Nguyễn Nghiễm), Nguyễn Khản,..., đ&aacute;p ứng nhu cầu di chuyển nhanh ch&oacute;ng tới c&aacute;c địa điểm trong tỉnh v&agrave; c&aacute;c địa phương l&acirc;n cận.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0004.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>LI&Ecirc;N KẾT V&Ugrave;NG</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0005.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>TIỀM NĂNG TỪ VỊ TR&Iacute;</strong></span></span><br />\r\n	o <strong>NGHI XU&Acirc;N: Li&ecirc;n tiếp đ&oacute;n nhận những quy hoạch mang tầm v&oacute;c chiến lược, Nghi Xu&acirc;n đang từng ng&agrave;y chuyển m&igrave;nh để trở th&agrave;nh cực tăng trưởng kinh tế ph&iacute;a Bắc của tỉnh H&agrave; Tĩnh.<br />\r\n	o Dự &aacute;n khu du lịch &ndash; đ&ocirc; thị - thương mại Nghi Xu&acirc;n - Lộc H&agrave; (nằm giữa Vinpearl Cửa Hội v&agrave; Vinpearl Cửa S&oacute;t): được quy hoạch để trở một trong những v&ugrave;ng ph&aacute;t triển động lực của to&agrave;n tỉnh H&agrave; Tĩnh.<br />\r\n	o Dự &aacute;n cầu Cửa Hội nối Cửa L&ograve; (Nghệ An) v&agrave; Nghi Xu&acirc;n (H&agrave; Tĩnh): tạo th&agrave;nh hệ thống giao th&ocirc;ng li&ecirc;n ho&agrave;n kết hợp với Đường Quốc gia ven biển, th&uacute;c đẩy giao thương li&ecirc;n tục giữa c&aacute;c tỉnh.<br />\r\n	o Dự &aacute;n Đường Quốc gia ven biển:<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + L&agrave; tuyến giao th&ocirc;ng huyết mạch v&ocirc; c&ugrave;ng quan trọng, chỉ sau Quốc lộ 1A v&agrave; đường Hồ Ch&iacute; Minh. Tuyến đường d&agrave;i 3.041 km, rộng 60m, được mệnh danh l&agrave; &ldquo;con đường tơ lụa&rdquo; chạy từ Bắc v&agrave;o Nam dọc theo bờ biển 28 tỉnh, th&agrave;nh.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Tại H&agrave; Tĩnh, tuyến đường đi qua 6 huyện v&agrave; th&agrave;nh phố H&agrave; Tĩnh, kết nối 4 khu du lịch, 2 khu c&ocirc;ng nghiệp v&agrave; 1 cảng biển, tạo nền tảng hạ tầng để ph&aacute;t huy tối đa thế mạnh khu vực.</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/files/Ban%20do%20giao%20thong%20Nghi%20Kim-02(1).jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>TIỀM NĂNG TỪ VỊ TR&Iacute;</strong></span></span><br />\r\n	&bull;<strong> Xu&acirc;n An nằm trong tam gi&aacute;c ph&aacute;t triển kinh tế x&atilde; hội của tỉnh H&agrave; Tĩnh l&agrave; Xu&acirc;n An - Nghi Xu&acirc;n - Xu&acirc;n Th&agrave;nh theo ti&ecirc;u chuẩn đ&ocirc; thị loại III.<br />\r\n	&bull; Xu&acirc;n An hội tụ vẻ đẹp tự nhi&ecirc;n, l&agrave; nơi kết tinh kh&ocirc;ng gian sống xanh &ndash; &aacute;nh s&aacute;ng &ndash; gi&oacute; trời v&agrave; tinh t&uacute;y ng&agrave;n đời của d&ograve;ng s&ocirc;ng Lam &ndash; n&uacute;i Hồng.<br />\r\n	&bull; Nằm s&aacute;t th&agrave;nh phố Vinh, Xu&acirc;n An l&agrave; địa điểm kết nối với b&atilde;i biển Cửa L&ograve; v&agrave; b&atilde;i biển Xu&acirc;n Th&agrave;nh &ndash; hai điểm nhấn du lịch h&agrave;ng đầu của Nghệ An v&agrave; H&agrave; Tĩnh, đang được ban ng&agrave;nh c&aacute;c cấp tập trung x&acirc;y dựng th&agrave;nh trung t&acirc;m du lịch &ndash; thương mại hiện đại v&agrave; đẳng cấp.<br />\r\n	&bull; Khu đ&ocirc; thị Xu&acirc;n An Green Park l&agrave; điểm nhấn rực rỡ trong bức tranh tương lai của Xu&acirc;n An v&agrave; to&agrave;n tỉnh H&agrave; Tĩnh, l&agrave; biểu tượng của niềm ki&ecirc;u h&atilde;nh v&agrave; tự h&agrave;o của c&aacute;c thế hệ cư d&acirc;n ưu t&uacute;.</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0007.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>TIỆN &Iacute;CH DỰ &Aacute;N</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0008.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0009.jpg\" style=\"text-align: center;\" /></p>\r\n<p>\r\n	<br />\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0010.jpg\" style=\"text-align: center;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0011.jpg\" /><br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0012.jpg\" /><br />\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0013.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0014.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0015.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0016.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0017.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0018.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0020.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0021.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0022.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>ĐIỂM NHẤN DỰ &Aacute;N</strong></span></span><br />\r\n	&bull; <strong>Dự &aacute;n đầu ti&ecirc;n tại khu vực Bắc Miền Trung được quy hoạch b&agrave;i bản, đồng bộ để trở th&agrave;nh h&igrave;nh mẫu l&yacute; tưởng của si&ecirc;u đ&ocirc; thị sinh th&aacute;i.<br />\r\n	&bull; Chủ đầu tư thể hiện sự t&acirc;m huyết trong từng chi tiết thiết kế, với mong muốn kh&ocirc;ng chỉ mang đến trải nghiệm thượng lưu cho những cư d&acirc;n đ&ocirc; thị m&agrave; c&ograve;n vươn tầm vị thế tr&ecirc;n to&agrave;n khu vực Bắc Miền Trung.<br />\r\n	&bull; Kh&ocirc;ng gian sinh th&aacute;i chuẩn mực với cấu tr&uacute;c ba tầng c&acirc;y: hoa cỏ, c&acirc;y tầm trung, c&acirc;y tầm cao, ngăn chặn nắng gắt v&agrave; một phần tia cực t&iacute;m g&acirc;y hại, điều h&ograve;a nhiệt độ trong KĐT xuống thấp hơn 2 &ndash; 3 độ C so với b&ecirc;n ngo&agrave;i.<br />\r\n	&bull; Hệ thống nước tinh khiết lấy trực tiếp từ nh&agrave; m&aacute;y Xu&acirc;n Hoa, đảm bảo ti&ecirc;u chuẩn an to&agrave;n sức khỏe cho chất lượng sống cao.<br />\r\n	&bull; Hệ thống b&oacute; vỉa v&agrave; m&aacute;ng nước được cắt bằng đ&aacute; Quỳ Hợp, c&oacute; tuổi thọ cao v&agrave; ngăn thấm nước tuyệt đối.<br />\r\n	&bull; Vỉa h&egrave; được đổ b&ecirc; t&ocirc;ng tươi trước l&uacute;c l&aacute;t đ&aacute;, c&oacute; độ bền rắn chắc v&agrave; bền bỉ từ c&aacute;c t&aacute;c động b&ecirc;n ngo&agrave;i, giữ g&igrave;n diện mạo tươi mới v&agrave; hiện đại cho KĐT trong thời gian d&agrave;i.<br />\r\n	&bull; Kh&ocirc;ng gian quanh hồ được thiết kế th&agrave;nh những khu vực đỗ xe rộng r&atilde;i, kết hợp với b&atilde;i đỗ của KĐT cho sức chứa l&ecirc;n đến hơn 3000 chiếc xe, tạo lập mỹ quan đ&ocirc; thị v&agrave; tối đa sự thuận tiện cho du kh&aacute;ch khi gh&eacute; v&agrave;o tham quan, dạo bộ.<br />\r\n	&bull; Nh&agrave; vệ sinh c&ocirc;ng cộng hiện đại được x&acirc;y dựng với khoảng c&aacute;ch hợp l&yacute; quanh KĐT.<br />\r\n	&bull; Phong thủy được&nbsp; t&iacute;nh to&aacute;n&nbsp; cẩn trọng qua việc bố&nbsp; tr&iacute; tam đa&nbsp; trấn&nbsp; giữ tại&nbsp; cổng v&agrave;o, ngăn chặn nguồn năng lượng xấu từ&nbsp; b&ecirc;n ngo&agrave;i.<br />\r\n	&bull; Đồng hồ Mặt Trời mang t&iacute;nh biểu tượng cho sự bền vững, trường tồn vĩnh cửu của cộng đồng cư d&acirc;n thịnh vượng.</strong></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0023.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>PH&Aacute;P L&Yacute; DỰ &Aacute;N</strong></span></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0025.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\">MẶT BẰNG PH&Acirc;N L&Ocirc; - PH&Acirc;N KHU DỰ &Aacute;N</span></span></strong></span></span><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0027.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\">TIẾN ĐỘ G&Oacute;P VỐN</span></span></strong></span></span><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0028.jpg\" /></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong>TIẾN ĐỘ DỰ &Aacute;N</strong></span></span><br />\r\n	&bull; KĐT đ&atilde; ho&agrave;n thiện c&aacute;c hạng mục cơ sở hạ tầng: đường, điện đường, điện đến từng khu nh&agrave; liền kề v&agrave; biệt thự, vỉa h&egrave; l&aacute;t đ&aacute;.<br />\r\n	&bull; C&aacute;c tiện &iacute;ch đang dần h&igrave;nh th&agrave;nh với tiến độ thực hiện nhanh ch&oacute;ng: đảo dưỡng sinh, cầu Nhật Bản, đồng hồ Mặt Trời, trung t&acirc;m thương mại đang được l&agrave;m m&oacute;ng<br />\r\n	&bull; Hệ thống sinh th&aacute;i đ&atilde; được ươm mầm trong nhiều năm, đang ph&aacute;t triển mạnh mẽ v&agrave; dự kiến ho&agrave;n thiện tầng, t&aacute;n trong v&ograve;ng 2&ndash; 3 năm tới.<br />\r\n	&bull; Chủ đầu tư đ&atilde; đầu tư thi c&ocirc;ng đoạn đường 24m tiệm cận với KĐT, kết hợp trồng c&acirc;y xanh l&agrave;m thay đổi ho&agrave;n to&agrave;n bộ mặt cảnh quan xung quanh đ&ocirc; thị.<br />\r\n	&bull; Với hệ thống cảnh quan v&agrave; cơ sở hạ tầng sẵn c&oacute;, KĐT đ&atilde; tổ chức th&agrave;nh c&ocirc;ng nhiều sự kiện c&oacute; quy m&ocirc; lớn:<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Chương tr&igrave;nh Lễ hội trăng rằm v&agrave;o dịp Trung thu 2018 với 3000 chiếc đ&egrave;n lồng rực rỡ trang tr&iacute; tr&ecirc;n diện t&iacute;ch 27,32ha. Chuỗi sự kiện được tổ chức 3 ng&agrave;y li&ecirc;n tiếp, thu h&uacute;t khoảng 30.000 lượt kh&aacute;ch từ Vinh, H&agrave; Tĩnh v&agrave; c&aacute;c khu vực l&acirc;n cận.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Lễ hội hoa xu&acirc;n dự kiến được tổ chức v&agrave;o dịp Tết nguy&ecirc;n đ&aacute;n 2019.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + C&aacute;c chương tr&igrave;nh của c&aacute;c trường học trong tỉnh.</span></span><br />\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/7%20xuan%20an/0030.jpg\" /></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp;</span></span><br />\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><strong>TỔNG KẾT</strong></span></span><br />\r\n	<span style=\"font-size:18px;\"><strong>&bull; Hội tụ tất cả tinh t&uacute;y của thi&ecirc;n nhi&ecirc;n, kết tinh vẻ đẹp hoa lệ của th&agrave;nh phố đương đại, Xu&acirc;n An Green Park l&agrave; nơi an cư lạc nghiệp v&agrave; chốn nghỉ dưỡng thời thượng, đưa chủ nh&acirc;n chạm tới tuyệt đỉnh thăng hoa của cuộc sống th&agrave;nh đạt - Sống thời thượng.<br />\r\n	&bull; Vị tr&iacute; kim cương: cửa ng&otilde; giao thoa giữa Vinh v&agrave; Nghi Xu&acirc;n &ndash; hai địa phương ph&aacute;t triển vượt bậc trong khu vực Bắc Miền Trung.<br />\r\n	&bull; Tận hưởng kh&ocirc;ng gian sinh th&aacute;i trong l&agrave;nh, thanh khiết.<br />\r\n	&bull; Phục vụ v&ocirc; số tiện nghi của một si&ecirc;u đ&ocirc; thị chuẩn cao cấp.<br />\r\n	&bull; An t&acirc;m với hệ thống an ninh chuy&ecirc;n nghiệp.</strong></span></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:18px;\"><strong><span style=\"color:#ff0000;\"><span style=\"font-size:20px;\"><span style=\"font-size:24px;\">Dẫn dắt thị trường</span></span></span><br />\r\n	<span style=\"font-size:20px;\">&bull;</span> Gia tăng gi&aacute; trị BĐS bền vững tr&ecirc;n khu đất v&agrave;ng.<br />\r\n	&bull; Tiềm năng kinh doanh c&aacute;c dịch vụ thương mại: nh&agrave; h&agrave;ng, hội nghị, chụp ảnh cưới,...<br />\r\n	&bull; Mở rộng cơ hội đầu tư v&agrave;o c&aacute;c loại h&igrave;nh nghỉ dưỡng: homestay, bugalow,...</strong></span></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<u><a href=\"http://www.bdsnghetinh.net/quy-hoach-phat-trien-nghi-xuan-tinh-ha-tinh-noi-chung-va-thi-tran-xuan-an-noi-rieng-/89.html\"><span style=\"color:#ff0000;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:18px;\"><strong>TẠI SAO N&Ecirc;N ĐẦU TƯ BĐS DỰ &Aacute;N XU&Acirc;N AN GREEN PARK?&nbsp; xem tại đ&acirc;y.</strong></span></span></span></span></a></u></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:26px;\"><strong>----------------------------------------------------------</strong></span><br />\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong><span style=\"color:#ff0000;\"><span style=\"font-size:24px;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VỀ DỰ &Aacute;N XIN VUI L&Ograve;NG LI&Ecirc;N HỆ:<br />\r\n	C&ocirc;ng ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An<br />\r\n	Hotline: 0962452245 ( Mr. L&acirc;m )<br />\r\n	Email: lamtrinhgovland@gmail.com<br />\r\n	Website: http://www.bdsnghetinh.net/</span></span></strong></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Xuân An Green Park - KĐT bậc nhất Bắc Trung Bộ', '27-11-2018', '28-1-2021', 'khu-do-thi-xuan-an-green-park', 'Xuân An: Tâm điểm cuộc sống nghỉ dưỡng với vị trí vàng bao quanh hệ thống hồ điều hòa, thiết kế tận dụng tối đa không gian mở trong lành & rộng thoáng', 0, NULL, '0'),
(68, 27, 7, NULL, 'KHU ĐÔ THỊ TRƯỜNG SƠN HOMES CỬA NAM', '30A Đường Siêu Hải, Phường Cửa Nam, TP Vinh, Nghệ An', NULL, NULL, NULL, NULL, 1241, '/public/upload/images/truongsonhomes/Picture8.jpg', NULL, NULL, 1, '1', NULL, '<p style=\"text-align: justify;\">\r\n	<em><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/dat-nen-biet-thu/khu-do-thi-truong-son-homes-cua-nam/68.html\">Khu Đ&ocirc; Thị Trường Sơn Homes</a></strong> tọa lạc tại phường Cửa Nam l&agrave; chốn an cư l&yacute; tưởng cho những t&acirc;m hồn đam m&ecirc; nghệ thuật, bởi từng chi tiết trong thiết kế, mỗi tiểu tiết của căn nh&agrave; hay s&acirc;n vườn đều được lấy cảm hứng từ những ca từ tuyệt đẹp của thơ ca, đặc biệt l&agrave; nhạc Trịnh.</span></span></em></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture2.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000cd;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N TRƯỜNG SƠN HOMES</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; T&ecirc;n dự &aacute;n: Khu đ&ocirc; thị Trường Sơn Homes</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Chủ đầu tư: C&ocirc;ng ty cổ phần Tiến Lực</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Vị tr&iacute;: 30A Đường Si&ecirc;u Hải, Phường Cửa Nam, TP Vinh, Nghệ An</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Quy m&ocirc;: 7.725,51m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Mật độ x&acirc;y dựng: 67.62%</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Quy hoạch khu đ&ocirc; thị kết hợp gồm:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; - Nh&agrave; liền kề: 57 căn, cao 3 tầng diện t&iacute;ch từ: 71,24m2 &ndash; 156,48m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; - Nh&agrave; hội qu&aacute;n</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; - Đường nội bộ l&aacute;t đ&aacute; rộng từ 11 &ndash; 18m</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>VỊ TR&Iacute; DỰ &Aacute;N</strong></span></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture3.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Phường Cửa Nam nằm ở ph&iacute;a T&acirc;y th&agrave;nh phố Vinh, c&oacute; rất nhiều lợi thế trong ph&aacute;t triển kinh tế dịch vụ - thương mại v&agrave; du lịch.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Tr&ecirc;n địa b&agrave;n phường, hệ thống giao th&ocirc;ng ph&aacute;t triển mạnh, gồm đường sắt Bắc - Nam, đường bộ, đường thủy</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hạ tầng đ&ocirc; thị được đầu tư như: cầu vượt đường sắt Bắc - Nam, k&egrave; Hồ Th&agrave;nh, cầu Cửa Tiền</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Phường c&oacute; Th&agrave;nh cổ Vinh, Nh&agrave; thờ Cầu Rầm, Ch&ugrave;a Cần Linh, Đền Hồng Sơn, khu thương mại, S&acirc;n vận động TP Vinh, Bảo t&agrave;ng X&ocirc; Viết Nghệ Tĩnh&hellip; được v&iacute; như một th&agrave;nh phố thu nhỏ, l&agrave; t&acirc;m điểm sống l&yacute; tưởng của TP Vinh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong>LI&Ecirc;N KẾT V&Ugrave;NG</strong></span></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture4.png\" /></strong></span></span></span></span><br />\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;<a href=\"http://www.bdsnghetinh.net/dat-nen-biet-thu/khu-do-thi-truong-son-homes-cua-nam/68.html\">Khu đ&ocirc; thị Trường Sơn Homes</a></strong> sở hữu vị tr&iacute; đắc địa tại trung t&acirc;m TP Vinh. Từ dự &aacute;n, cư d&acirc;n dễ d&agrave;ng kết nối đến hệ thống c&aacute;c tiện &iacute;ch sẵn c&oacute; phục vụ cuộc sống:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- UBND Cửa Nam: 1,5km</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Bệnh viện đa khoa Th&aacute;i An: 1km</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- S&acirc;n v&acirc;n động TP. Vinh: 2km</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Chợ Vinh: 3km</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Trường tiểu học, THCS Cửa Nam: 1,5km</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">- Khu c&ocirc;ng nghiệp Vsip: 2km</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:22px;\"><span style=\"font-family:times new roman,times,serif;\">CẢM HỨNG NGHỆ THUẬT</span></span></strong></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;KĐT Trường Sơn Homes l&agrave; chốn an cư l&yacute; tưởng cho những t&acirc;m hồn đam m&ecirc; nghệ thuật, bởi từng chi tiết trong thiết kế, mỗi tiểu tiết của căn nh&agrave; hay s&acirc;n vườn đều được lấy cảm hứng từ những ca từ tuyệt đẹp của thơ ca, đặc biệt l&agrave; nhạc Trịnh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture5.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><strong><span style=\"font-family:times new roman,times,serif;\">KH&Ocirc;NG GIAN SỐNG THANH B&Igrave;NH</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Nằm c&aacute;ch đường lớn một khoảng c&aacute;ch vừa đủ, cư d&acirc;n KĐT Trường Sơn Homes c&oacute; thể dễ d&agrave;ng di chuyển, nhưng vẫn kh&ocirc;ng phải hứng chịu tiếng ồn v&agrave; kh&oacute;i bụi từ mật độ giao th&ocirc;ng d&agrave;y đặc.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture6.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Tại đ&acirc;y, cư d&acirc;n được tận hưởng bầu kh&ocirc;ng kh&iacute; an y&ecirc;n, thanh b&igrave;nh v&agrave; trong l&agrave;nh mỗi ng&agrave;y nhờ hệ thống c&acirc;y xanh trải d&agrave;i trong nội khu, đ&oacute;ng vai tr&ograve; như một lớp c&aacute;ch &acirc;m v&agrave; c&aacute;ch nhiệt, mang đến cho gia chủ cuộc sống giao h&ograve;a với thi&ecirc;n nhi&ecirc;n v&agrave; tĩnh tại trong t&acirc;m hồn.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>CỘNG ĐỒNG VĂN MINH KẾT NỐI</strong></span></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Với những gi&aacute; trị cao cấp m&agrave; khu đ&ocirc; thị mang lại, Trường Sơn Homes sẽ l&agrave; nơi quy tụ những gia chủ c&oacute; tr&igrave;nh độ học vấn cao v&agrave; nền tảng vững chắc về t&agrave;i ch&iacute;nh, c&ugrave;ng với t&igrave;nh y&ecirc;u thi&ecirc;n nhi&ecirc;n, tạo n&ecirc;n một cộng đồng văn minh, chan h&ograve;a.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture7.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Nh&agrave; văn h&oacute;a KĐT được x&acirc;y dựng l&agrave;m địa điểm giao lưu, gặp gỡ, tổ chức c&aacute;c chương tr&igrave;nh kết nối c&aacute;c hộ gia đ&igrave;nh trong KĐT.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">KĐT Trường Sơn Homes l&agrave; một trong số &iacute;t dự &aacute;n tại TP Vinh b&agrave;n giao c&ocirc;ng tr&igrave;nh ho&agrave;n thiện, gia chủ c&oacute; thể nhận nh&agrave; ở ngay v&agrave; tận hưởng kh&ocirc;ng gian sống đẳng cấp, hiện đại.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:22px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>THIẾT KẾ TINH TẾ</strong></span></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;C&aacute;c căn nh&agrave; được thiết kế 3 tầng cao gi&uacute;p tối đa h&oacute;a diện t&iacute;ch sử dụng. Mặt trước căn nh&agrave; kh&eacute;o l&eacute;o tận dụng chất liệu k&iacute;nh l&agrave;m cửa sổ, lan can, vừa gi&uacute;p mở rộng kh&ocirc;ng gian nhờ hiệu ứng thị gi&aacute;c, vừa mở lối v&agrave;o cho &aacute;nh s&aacute;ng dễ chịu của thi&ecirc;n nhi&ecirc;n, đồng thời gi&uacute;p tiết kiệm điện năng sử dụng.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/truongsonhomes/Picture8.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Đặc biệt mỗi căn nh&agrave; đều c&oacute; t&iacute;nh to&aacute;n khoảng l&ugrave;i vừa phải để trồng hoa, c&acirc;y cảnh, t&ocirc; điểm cho vẻ đẹp của căn nh&agrave;. Tầng 3 với khoảnh s&acirc;n vườn rộng r&atilde;i được thiết kế để trồng c&aacute;c lo&agrave;i thực vật từ rau củ đến c&acirc;y ăn quả, mang đến một khu vườn đ&uacute;ng nghĩa c&ugrave;ng cảm gi&aacute;c an y&ecirc;n, tự tại như được trở về với nguồn cội.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Vỉa h&egrave; được l&aacute;t bằng đ&aacute; Quỳ Hợp tăng độ bền cho c&ocirc;ng tr&igrave;nh, kh&ocirc;ng được b&oacute; vỉa gi&uacute;p mở rộng kh&ocirc;ng gian đỗ xe d&agrave;nh cho gia chủ.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hệ thống c&acirc;y xanh trải d&agrave;i c&aacute;c con phố nội khu mang đến kh&ocirc;ng gian sống trong l&agrave;nh, m&aacute;t mẻ, th&iacute;ch hợp để dạo bộ v&agrave; l&agrave; s&acirc;n chơi l&yacute; tưởng cho trẻ em.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<strong new=\"\" style=\"font-size: 22px; color: rgb(0, 0, 255); font-family: \" times=\"\">CH&Iacute;NH S&Aacute;CH ƯU Đ&Atilde;I HẤP DẪN</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<strong style=\"font-size: 22px; color: rgb(0, 0, 255); font-family: &quot;times new roman&quot;, times, serif;\">LI&Ecirc;N HỆ NGAY ĐỂ NHẬN ƯU Đ&Atilde;I TỐT NHẤT</strong></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">MỌI TH&Ocirc;NG TIN CHI TIẾT VUI L&Ograve;NG LI&Ecirc;N HỆ<br />\r\n	KHU Đ&Ocirc; THỊ TRƯỜNG SƠN HOMES</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">ĐƠN VỊ PH&Acirc;N PHỐI</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\"><span style=\"font-family:times new roman,times,serif;\">C&ocirc;ng ty Cổ phần Bất động sản Bắc Trung Bộ</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:24px;\"><span font-size:=\"\" new=\"\" times=\"\">Hotline: 094.220.6262</span></span></strong></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Khu Đô Thị Trường Sơn Homes P.Cửa Nam', '26-12-2018', '6-4-2020', 'khu-do-thi-truong-son-homes-cua-nam', 'Khu Đô Thị Trường Sơn Homes tọa lạc tại phường Cửa Nam là chốn an cư lý tưởng cho những tâm hồn đam mê nghệ thuật, bởi từng chi tiết trong thiết kế, m', 0, NULL, '0'),
(72, 1, 12, NULL, 'Trung tâm thương mại kết hợp chợ truyền thống Đô Lương', 'Thị trấn Đô Lương và xã Yên Sơn, huyện Đô Lương, Nghệ An', 'Hợp tác xã đầu tư, xây dựng, quản lý và khai thác chợ Hải An – Đô Lương', 'Tháng 12/ 2020', NULL, '0942206262', 1514, '/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/pc-cho-do-luong-2.png', NULL, NULL, 1, '1', NULL, '<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000ff;\"><span style=\"font-family:times new roman,times,serif;\">TH&Ocirc;NG TIN TỔNG QUAN TTTM KẾT HỢP CHỢ TRUYỀN THỐNG Đ&Ocirc; LƯƠNG</span></span></span></h2>\r\n<p>\r\n	<em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Dự &aacute;n Chợ Đ&ocirc; Lương v&agrave; Trung T&acirc;m Thương Mại được nghi&ecirc;n cứu quy hoạch thuộc địa phận Thị trấn Đ&ocirc; Lương v&agrave; X&atilde; Y&ecirc;n Sơn. Vị tr&iacute; dự &aacute;n nằm s&aacute;t với tuyến đường Quốc lộ 7, tuyến đường n&agrave;y đang được đầu tư với quy m&ocirc; mặt cắt đường l&agrave; 36m.</span></span></em></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Dự &aacute;n: </strong>Trung t&acirc;m thương mại kết hợp chợ Đ&ocirc; Lương<br />\r\n	<strong>Vị tr&iacute;: </strong>Thị trấn Đ&ocirc; Lương v&agrave; x&atilde; Y&ecirc;n Sơn, huyện Đ&ocirc; Lương, Nghệ An<br />\r\n	<strong>Chủ đầu tư:</strong> Hợp t&aacute;c x&atilde; đầu tư, x&acirc;y dựng, quản l&yacute; v&agrave; khai th&aacute;c chợ Hải An &ndash; Đ&ocirc; Lương<br />\r\n	<strong>Quy m&ocirc;: </strong>3.44 ha<br />\r\n	<strong>Quy hoạch:</strong> Kiot chợ: 1396, kiot 3 tầng: 142</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/pc-cho-do-luong-2.png\" /></span></span></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000ff;\"><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N CHỢ MỚI Đ&Ocirc; LƯƠNG</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TTTM kết hợp chợ Đ&ocirc; Lương nằm tr&ecirc;n khu đất thịnh vượng, sở hữu vị thế kinh doanh đắc địa bậc nhất của to&agrave;n huyện Đ&ocirc; Lương, đ&oacute;ng vai tr&ograve; &ldquo;cửa ng&otilde;&rdquo; kết nối th&ocirc;ng thương mạnh mẽ với c&aacute;c khu vực l&acirc;n cận, yếu tố quan trọng h&agrave;ng đầu cho th&agrave;nh c&ocirc;ng của c&ocirc;ng việc kinh doanh.<br />\r\n	Dự &aacute;n tọa lạc tại vị tr&iacute; v&agrave;ng nơi d&acirc;n cư đ&ocirc;ng đ&uacute;c, gần c&aacute;c c&ocirc;ng tr&igrave;nh trọng điểm của huyện như UBND huyện Đ&ocirc; Lương, S&acirc;n vận động huyện Đ&ocirc; Lương, Bến xe Đ&ocirc; Lương, Khu đ&ocirc; thị Vườn Xanh.<br />\r\n	Hệ thống giao th&ocirc;ng thuận lợi kết nối trực tiếp với c&aacute;c tuyến đường huyết mạch QL 7 v&agrave; QL 15.</span></span></p>\r\n<h2 style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/vi-tri-cho-do-luong.png\" /></span></h2>\r\n<h2 style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/cho-moi-do-luong-lien-ket-v%C3%B9ng.jpg\" /></span></h2>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000ff;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N CHỢ MỚI Đ&Ocirc; LƯƠNG</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Những cung đường rộng r&atilde;i, th&ocirc;ng tho&aacute;ng v&agrave; h&agrave;ng c&acirc;y xanh trải b&oacute;ng m&aacute;t mang đến cảm nhận văn minh, mới lạ cho kh&aacute;ch mua sắm. Tạo n&ecirc;n n&eacute;t mỹ quan đ&ocirc; thị ph&ugrave; hợp với mục đ&iacute;ch kết hợp thương mại v&agrave; trải nghiệm văn h&oacute;a của khu chợ mới</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Dự &aacute;n l&agrave; sự tổng h&ograve;a của h&igrave;nh thức trung t&acirc;m thương mại kết hợp chợ truyền thống tr&ecirc;n khu phố nhộn nhịp, tạo th&agrave;nh hệ thống kinh doanh hiện đại, chuy&ecirc;n nghiệp. Với thiết kế gồm kiot chợ v&agrave; kiot 3 tầng c&oacute; diện t&iacute;ch đa dạng, ph&ugrave; hợp với nhiều mục đ&iacute;ch kinh doanh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/cho-do-luong5.jpg\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Chợ quy tụ th&agrave;nh hệ thống h&agrave;ng trăm thương hiệu v&agrave; ng&agrave;nh h&agrave;ng phong ph&uacute;, k&iacute;ch hoạt lợi thế của tổ hợp kinh doanh đồng bộ v&agrave; chuy&ecirc;n nghiệp, &ldquo;bu&ocirc;n c&oacute; bạn, b&aacute;n c&oacute; phường&rdquo;, tạo n&ecirc;n sức hấp dẫn thu h&uacute;t đ&ocirc;ng đảo kh&aacute;ch h&agrave;ng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TTTM kết hợp chợ Đ&ocirc; Lương tạo n&ecirc;n điểm nhấn đ&ocirc; thị với lối kiến tr&uacute;c t&acirc;n cổ điển mới lạ, sang trọng. Vẻ đẹp k&iacute;ch th&iacute;ch thị gi&aacute;c, t&aacute;c động đến t&acirc;m l&yacute; v&agrave; nhu cầu mua sắm của kh&aacute;ch h&agrave;ng, g&oacute;p phần gia tăng gi&aacute; trị thương hiệu v&agrave; dẫn lối đến th&agrave;nh c&ocirc;ng của doanh nghiệp. Biểu tượng của gi&agrave;u sang, sung t&uacute;c v&agrave; thịnh vượng</span></span></p>\r\n<h2>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#0000ff;\"><span style=\"font-family:times new roman,times,serif;\">TIỀM NĂNG KINH DOANH &amp; ĐẦU TƯ VƯỢT TRỘI</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Dự &aacute;n &ldquo;đi tắt đ&oacute;n đầu&rdquo; chiếm hữu lợi thế kim cương tại vị tr&iacute; giao thương ho&agrave;n hảo QL7, tại một trong những địa phương ph&aacute;t triển bậc nhất tỉnh Nghệ An đ&atilde; mở ra cơ hội lớn cho tiềm năng kinh doanh v&agrave; đầu tư d&agrave;nh cho c&aacute;c chủ sở hữu.<br />\r\n	Với h&igrave;nh thức &ldquo;2 trong 1&rdquo; kết hợp chợ v&agrave; TTTM, c&aacute;ch bố tr&iacute; hợp l&yacute; c&aacute;c d&atilde;y kiot 3 tầng bao quanh chợ gi&uacute;p gia tăng lượng kh&aacute;ch h&agrave;ng đổ v&agrave;o v&ugrave;ng l&otilde;i của chợ, tăng sức cạnh tranh của chợ đồng thời ph&aacute;t huy lợi thế của TTTM.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/pc-cho-do-luong-4.png\" /></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Với thiết kế sang trọng v&agrave; hiện đại, tập trung h&agrave;ng ngh&igrave;n ng&agrave;nh h&agrave;ng v&agrave; thương hiệu đa dạng, TTTM kết hợp chợ truyền thống Đ&ocirc; Lương hứa hẹn kh&ocirc;ng chỉ trở th&agrave;nh &ldquo;con g&agrave; đẻ trứng v&agrave;ng&rdquo;, đầu mối th&ocirc;ng thương tại Nghệ An m&agrave; c&ograve;n trở th&agrave;nh n&eacute;t đặc trưng văn h&oacute;a, thu h&uacute;t đ&ocirc;ng đảo du kh&aacute;ch tham quan, trải nghiệm.&nbsp;</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/%C4%90%C3%B4%20l%C6%B0%C6%A1ng/Ly-do-chon-cho-moi-do-luong.jpg\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>LI&Ecirc;N HỆ t&igrave;m hiểu dự &aacute;n TTTM kết hợp chợ truyền thống Đ&ocirc; Lương:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000cd;\"><strong>C&ocirc;ng Ty Cổ phần Bất động sản Bắc Trung Bộ</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hotline:</strong> <span style=\"color:#b22222;\"><strong>094.220.6262</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Website:</strong> <a href=\"http://www.bdsnghetinh.net/\">http://www.bdsnghetinh.net/</a></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'TTTM kết hợp chợ truyền thống Đô Lương', '15-6-2020', '16-6-2020', 'trung-tam-thuong-mai-ket-hop-cho-truyen-thong-do-luong', 'Dự án là sự tổng hòa của hình thức trung tâm thương mại kết hợp chợ truyền thống trên khu phố nhộn nhịp, tạo thành hệ thống kinh doanh hiện đại,...', 3, NULL, '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(69, 1, 11, NULL, 'KHU ĐÔ THỊ TNR STARS DIỄN CHÂU', 'Thôn 7, Diễn Kỷ', 'TNR Holdings Việt Nam', NULL, NULL, '0942206262', 1565, '/public/upload/images/TNR-dien-chau/TNR-Dienchau-phoi-canh.jpg', NULL, NULL, 1, '1', NULL, '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Sở hữu vị tr&iacute; tuyệt đẹp được bao bọc bởi d&ograve;ng s&ocirc;ng B&ugrave;ng xanh m&aacute;t v&agrave; thơ mộng, kết nối với Quốc lộ 1A v&agrave; khu chợ Sy nhộn nhịp, Khu đ&ocirc; thị thương mại &ndash; dịch vụ - giải tr&iacute; TNR Stars Diễn Ch&acirc;u kiến tạo n&ecirc;n thế đất &ldquo;nhất cận thị, nhị cận giang, tam cận lộ&rdquo;, tạo n&ecirc;n vị thế &ldquo;vượng lộc đắc t&agrave;i&rdquo; độc đ&aacute;o v&agrave; hiếm c&oacute; tr&ecirc;n to&agrave;n khu vực Bắc Trung Bộ.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/dat-nen-tnr-star-dien-chau.jpg\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N TNR STARS DIỄN CH&Acirc;U</span></span></strong></span></h2>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>T&ecirc;n dự &aacute;n:</strong> Khu đ&ocirc; thị TNR Stars Diễn Ch&acirc;u</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Chủ đầu tư:</strong> TNR Holdings Việt Nam</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Vị tr&iacute;: </strong>Diễn Kỷ, Diễn Ch&acirc;u, Nghệ An</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Quy m&ocirc;:</strong> 33.35 ha</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Mật độ x&acirc;y dựng:</strong> 44%</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Quy hoạch: </strong>Khu đ&ocirc; thị kết hợp gồm:</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>4 ph&acirc;n khu:</strong> AN TR&Iacute;, AN H&Ograve;A, AN LỘC, AN THỊNH</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Tổng số sản phẩm (Đất ở 44%):</strong> 1.196 L&ocirc; (Liền kề, Shopouse, Nh&agrave; vườn, Biệt thự)</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Diện t&iacute;ch giao th&ocirc;ng, c&acirc;y xanh, c&ocirc;ng vi&ecirc;n, tiện &iacute;ch : 56%</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">30 tiện &iacute;ch đẳng cấp</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>VỊ TR&Iacute; DỰ &Aacute;N TNR DIỄN CH&Acirc;U</strong></span></span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Dự &aacute;n tọa lạc tại vị tr&iacute; đắc địa ngay trung t&acirc;m Diễn Ch&acirc;u &ndash; địa phương sở hữu nội lực dồi d&agrave;o v&agrave; nhiều tiềm năng ph&aacute;t triển về kinh tế - văn h&oacute;a &ndash; x&atilde; hội với 25% d&acirc;n số đang l&agrave;m việc tại nước ngo&agrave;i. Ri&ecirc;ng x&atilde; Diễn Kỷ l&agrave; khu vực c&oacute; nhiều lao động đang l&agrave;m việc tại Ch&acirc;u &Acirc;u, c&oacute; đ&oacute;ng g&oacute;p lớn v&agrave;o lượng kiều hối cuối năm v&agrave; thường xuy&ecirc;n thực hiện c&aacute;c phong tr&agrave;o trở về x&acirc;y dựng qu&ecirc; hương.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/vi-tri-tnr-dien-chau.png\" /></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">D&ograve;ng s&ocirc;ng l&agrave; nguồn mạch của sự sống, khởi nguồn của phồn thịnh &ndash; TNR Stars Diễn Ch&acirc;u theo đ&oacute; c&agrave;ng thể hiện gi&aacute; trị vững bền của một đ&ocirc; thị đẳng cấp với cuộc sống thượng lưu, sung t&uacute;c. To&agrave;n bộ TNR Stars Diễn Ch&acirc;u được bao bọc bởi con s&ocirc;ng B&ugrave;ng quanh năm xanh m&aacute;t, tạo n&ecirc;n kh&ocirc;ng kh&iacute; thư th&aacute;i dễ chịu v&agrave; đưa nguồn sinh kh&iacute; tự nhi&ecirc;n tu&ocirc;n chảy v&agrave;o từng ng&oacute;c ng&aacute;ch của khu đ&ocirc; thị.&nbsp;</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Giao th&ocirc;ng thuận lợi kết nối với Quốc lộ 1A, c&aacute;ch TP. Vinh 42km v&agrave; H&agrave; Nội 257km, Khu đ&ocirc; thị TNR Stars Diễn Ch&acirc;u tạo n&ecirc;n thế đất &ldquo;nhất cận thị (chợ Sy), nhị cận giang (s&ocirc;ng B&ugrave;ng), tam cận lộ (QL1A)&rdquo;, đảm bảo tiềm năng tăng gi&aacute; đột ph&aacute; v&agrave; sinh lời bền vững cho c&aacute;c gia chủ.&nbsp;&nbsp;</span></span></p>\r\n<h3>\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Li&ecirc;n kết v&ugrave;ng TNR Diễn Ch&acirc;u:</span></span></strong></span></h3>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Chợ Sy.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Ga chợ Sy.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- UBND x&atilde; Diễn Kỷ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Trạm Y tế x&atilde; Diễn Kỷ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Trường Tiểu học, Trung học x&atilde; Diễn Kỷ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Trường THPT Diễn Ch&acirc;u 1, Diễn Ch&acirc;u, Diễn Ch&acirc;u 2, Diễn Ch&acirc;u 3.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- C&ocirc;ng vi&ecirc;n huyện Diễn Ch&acirc;u.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Đền Cu&ocirc;ng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Ch&ugrave;a Cổ Am.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Nh&agrave; thờ Thi&ecirc;n ch&uacute;a gi&aacute;o &ndash; Đền th&aacute;nh An T&ocirc;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- B&atilde;i biển Diễn Th&agrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N TNR DIỄN CH&Acirc;U</span></span></strong></span></h2>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n tr&ecirc;n thị trường Bắc Trung Bộ xuất hiện một khu đ&ocirc; thị với 30 tiện &iacute;ch vượt trội, đẳng cấp v&agrave; kh&eacute;p k&iacute;n, l&agrave;m h&agrave;i l&ograve;ng những gia chủ thượng lưu. Cư d&acirc;n Khu đ&ocirc; thị TNR Stars Diễn Ch&acirc;u được tận hưởng trọn vẹn nhu cầu giải tr&iacute; &ndash; mua sắm &ndash; gi&aacute;o dục giữa kh&ocirc;ng gian sống thư th&aacute;i, v&agrave; đặc biệt nhận những gi&aacute; trị gia tăng về cơ hội kinh doanh ngay trong khu&ocirc;n vi&ecirc;n sinh sống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"color:#0000cd;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>TH&Ocirc;NG TIN SẢN PHẨM TNR DIỄN CH&Acirc;U:</strong></span></span></span></h3>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">An y&ecirc;n giữa v&ugrave;ng đất hưng thịnh, Khu đ&ocirc; thị TNR Stars Diễn Ch&acirc;u ph&aacute;t triển vững bền với &ldquo;Tứ An&rdquo;: An Thịnh, An H&ograve;a, An Tr&iacute;, An Lạc, hội tụ tinh hoa cho một tương lai rực s&aacute;ng.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/phan-khu-tnr-dien-chau.jpg\" /></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>An Thịnh:</strong> TIỂU KHU VĂN H&Oacute;A THƯƠNG MẠI</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>An H&ograve;a:</strong> TIỂU KHU THƯƠNG MẠI &ndash; DỊCH VỤ TỔNG HỢP</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>An Tr&iacute;:</strong> TIỂU KHU DỊCH VỤ GI&Aacute;O DỤC</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>An Lộc:</strong> TIỂU KHU DỊCH VỤ GIẢI TR&Iacute;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">NHỮNG L&Yacute; DO N&Ecirc;N CHỌN MUA DỰ &Aacute;N TNR STARS DIỄN CH&Acirc;U</span></span></strong></h3>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">1. Được ph&aacute;t triển bởi TNR Holdings Việt Nam - đơn vị c&oacute; Uy t&iacute;n &ndash; Năng lực &ndash; Kinh nghiệm thực hiện nhiều dự &aacute;n đ&ocirc; thị tr&ecirc;n cả nước.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">2. Tọa lạc tại vị tr&iacute; thuận lợi &ldquo;nhất cận thị, nhị cận giang, tam cận lộ&rdquo;.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">3. M&ocirc;i trường sinh th&aacute;i trong l&agrave;nh, d&ograve;ng s&ocirc;ng B&ugrave;ng chảy quanh dự &aacute;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">4. Hệ thống tiện &iacute;ch đa dạng, vượt trội bậc nhất khu vực.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">5. Quy hoạch đồng bộ, tối ưu c&ocirc;ng năng, đa dạng diện t&iacute;ch.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">6. Cộng đồng d&acirc;n cư văn minh, đẳng cấp.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">7. M&ocirc;i trường ph&aacute;t triển to&agrave;n diện cho trẻ em.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">8. An ninh đa lớp, bảo vệ an to&agrave;n cho cư d&acirc;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">9. Tiềm năng tăng gi&aacute; trị trong tương lai, sinh lời bền vững.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">10. Tiến độ thanh to&aacute;n c&aacute;ch xa, kh&aacute;ch h&agrave;ng c&oacute; thời gian chuẩn bị nguồn tiền.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/%E1%BA%A2nh%20An%20Loc%20-%20TT%20cong%20vien%20the%20ky.jpg\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#ff0000;\"><strong>TH&Ocirc;NG TIN LI&Ecirc;N HỆ:</strong></span><br />\r\n	<span style=\"color:#0000ff;\"><em>C&ocirc;ng Ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An</em></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hotline:</strong> 094.220.6262</span></span></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Website:&nbsp;</span></span></strong><a href=\"http://www.bdsnghetinh.net/\"><span style=\"font-family: &quot;times new roman&quot;, times, serif; font-size: 16px;\">http://www.bdsnghetinh.net/</span></a></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'TNR Diễn Châu: Nơi an cư lý tưởng -  Chốn phú quý trường tồn', '2-4-2019', NULL, 'khu-do-thi-tnr-stars-dien-chau', 'Cư dân Khu đô thị TNR Stars Diễn Châu được tận hưởng trọn vẹn nhu cầu giải trí – mua sắm – giáo dục giữa không gian sống thư thái, và đặc biệt ...', 4, NULL, '0'),
(70, 26, 7, NULL, 'Chung cư Sai Gon Sky Tower', 'Phường Đội Cung', 'CTCP Đầu tư và Phát triển Miền Trung', NULL, NULL, '0942206262', 2152, '/public/upload/images/SGS/%E1%BA%A3nh%20b%C3%ACa%20sgs%206262%20chu%E1%BA%A9n.jpg', NULL, NULL, 1, '1', NULL, '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><a href=\"http://www.bdsnghetinh.net/chung-cu/chung-cu-sai-gon-sky-tower/70.html\"><strong><span style=\"color:#0000ff;\">S&agrave;i G&ograve;n Sky Tower</span></strong></a><span style=\"color:#0000ff;\"> </span>được định vị l&agrave; một trong số &iacute;t chung cư thuộc ph&acirc;n kh&uacute;c chung cư cao cấp tại TP Vinh. Kh&ocirc;ng chỉ sở hữu vị tr&iacute; trung t&acirc;m, <strong>chung cư S&agrave;i G&ograve;n Sky Tower</strong> l&agrave; những căn hộ th&ocirc;ng minh được chủ đầu tư chăm ch&uacute;t trong từng thiết kế nội v&agrave; ngoại thất bằng c&aacute;c vật liệu bền đẹp v&agrave; mang t&iacute;nh thẩm mỹ cao, c&ugrave;ng tiện &iacute;ch nội khu đa dạng mang lại cuộc sống đẳng cấp v&agrave; sang trọng cho gia chủ.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/SGS/55.jpg\" /><br />\r\n	<em>Phối cảnh chung cư S&agrave;i G&ograve;n Sky Tower thuộc khu đ&ocirc; thị Sai Gon Sky TP Vinh.</em></span></span><br />\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N CHUNG CƯ SAI GON SKY</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Dự &aacute;n:</strong> Chung cư cao cấp Sai Gon Sky<br />\r\n	<strong>Chủ đầu tư:</strong> C&ocirc;ng ty Cổ phần Đầu tư v&agrave; Ph&aacute;t triển Miền Trung<br />\r\n	<strong>Vị tr&iacute;: </strong>Phường Đội Cung, TP. Vinh<br />\r\n	<strong>Quy m&ocirc;:</strong> 1330m2&nbsp; &nbsp; &nbsp; &nbsp;</span></span><span font-size:=\"\" new=\"\" style=\"font-family: \" times=\"\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>QUY HOẠCH:</strong><br />\r\n	- Số tầng: 18 tầng (01 tầng b&aacute;n hầm + 01 tum)<br />\r\n	- Cao độ s&agrave;n tầng 1: + 1,5m so với cốt s&acirc;n nội bộ.<br />\r\n	- Chiều cao tầng b&aacute;n hầm: 3,0m.<br />\r\n	- Chiều cao c&aacute;c tầng 1 &ndash; 2: 3,9m.<br />\r\n	- Chiều cao c&aacute;c tầng từ tầng 3 &ndash; 15: 3,2m.<br />\r\n	- Chiều cao tầng 16: 4,0m.<br />\r\n	- Chiều cao tầng tum: 3,2m.<br />\r\n	- Tổng chiều cao c&ocirc;ng tr&igrave;nh: 58,1m.<br />\r\n	- Diện t&iacute;ch x&acirc;y dựng: 995,34 m2.<br />\r\n	- Tổng diện t&iacute;ch s&agrave;n: 15.782,5m2.<br />\r\n	- Số căn hộ: 130 căn hộ (2 &ndash; 3 PN).<br />\r\n	- Diện t&iacute;ch căn hộ từ: 60 &ndash; 82m2.</span></span><br />\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; ĐẮC ĐỊA CHUNG CƯ SAI GON SKY</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><a href=\"http://www.bdsnghetinh.net/chung-cu/chung-cu-sai-gon-sky-tower/70.html\"><span style=\"color:#0000ff;\"><strong>Chung cư Sai Gon Sky</strong></span></a><span style=\"color:#0000ff;\"> </span>Tower tọa lạc tại vị tr&iacute; đắc địa &ldquo;<em>trung t&acirc;m của trung t&acirc;m</em>&rdquo; tại Phường Đội Cung, TP Vinh, nằm giữa hai phố lớn l&agrave; Nguyễn Th&aacute;i Học v&agrave; Phan Chu Trinh, gần phố Trần Hưng Đạo v&agrave; Quang Trung, dễ d&agrave;ng tiếp cận đến những c&ocirc;ng tr&igrave;nh trọng điểm trong th&agrave;nh phố.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/SGS/Picture1.jpg\" /><br />\r\n	<em>Vị tr&iacute; dự &aacute;n chung cư S&agrave;i G&ograve;n Sky Tower TP Vinh.</em></span></span></p>\r\n<h2>\r\n	<br />\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">CHUNG CƯ SAI GON SKY LI&Ecirc;N KẾT V&Ugrave;NG THUẬN TIỆN</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Với li&ecirc;n kết v&ugrave;ng thuận tiện, chủ nh&acirc;n <a href=\"http://www.bdsnghetinh.net/chung-cu/chung-cu-sai-gon-sky-tower/70.html\"><span style=\"color:#0000ff;\">chung cư Sai Gon Sky Tower TP Vinh</span></a> c&oacute; thể tận hưởng cuộc sống &ldquo;t&acirc;m điểm&rdquo; với mọi c&ocirc;ng tr&igrave;nh gi&aacute;o dục, giải tr&iacute;, mua sắm, giao th&ocirc;ng, bệnh viện&hellip;đều tập trung xung quanh, c&aacute;ch dự &aacute;n chỉ v&agrave;i ph&uacute;t di chuyển như:</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&ndash; Trường Đại Học C&ocirc;ng Nghiệp Vinh<br />\r\n	&ndash; Trường Mầm Non ABC<br />\r\n	&ndash; Trường tiểu học, THCS Đội Cung<br />\r\n	&ndash; BigC &ndash; Chợ Vinh<br />\r\n	&ndash; TT Giải tr&iacute; VRC<br />\r\n	&ndash; Quảng Trường HCM<br />\r\n	&ndash; Ga Vinh<br />\r\n	&ndash; S&acirc;n Bay Vinh.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/SGS/Picture2.jpg\" /><br />\r\n	<em>Chung cư S&agrave;i G&ograve;n Sky Tower TP Vinh c&oacute; li&ecirc;n kết v&ugrave;ng thuận tiện.</em></span></span></p>\r\n<h2>\r\n	<br />\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">MẶT BẰNG CĂN HỘ CHUNG CƯ SAI GON SKY</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Kh&ocirc;ng gian mặt bằng căn hộ chung cư S&agrave;i G&ograve;n Sky TP Vinh được thiết kế tho&aacute;ng m&aacute;t với h&agrave;nh lang rộng r&atilde;i, xẻ khe h&uacute;t gi&oacute;, mang đến cho chủ nh&acirc;n căn hộ S&agrave;i G&ograve;n Sky một cuộc sống vi&ecirc;n m&atilde;n đ&oacute;n gi&oacute; trời tự nhi&ecirc;n, &aacute;nh s&aacute;ng tối đa v&agrave;o từng căn hộ v&agrave; tận hưởng tầm nh&igrave;n th&agrave;nh phố tuyệt đẹp.<br />\r\n	Diện t&iacute;ch mỗi căn hộ hợp l&yacute; v&agrave; tối ưu từ , mang đến nhiều lựa chọn cho kh&aacute;ch h&agrave;ng v&agrave; tối đa c&ocirc;ng năng sử dụng.</span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">THIẾT KẾ THẨM MỸ</span></strong></span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Thiết kế ngoại thất t&acirc;n cổ điển gi&agrave;u t&iacute;nh thẩm mỹ l&agrave; một trong những điểm nhấn mang lại cảm gi&aacute;c sang trọng cho tổng thể chung cư S&agrave;i Gon Sky TP Vinh.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Với mặt k&iacute;nh trong suốt bao phủ tầng 1, khối đế được ốp đ&aacute; hoa văn, đường ph&agrave;o nổi d&agrave;y dặn v&agrave; chắc chắn, sảnh chờ v&agrave; sảnh thang m&aacute;y được trang tr&iacute; đầy tinh tế, <strong>S&agrave;i G&ograve;n Sky Tower TP Vinh</strong> mang đến cho gia chủ một tổ ấm khang trang mang vẻ đẹp vượt thời gian.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/SGS/13.jpg\" /></span></span><br />\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em><strong>Kh&ocirc;ng gian y&ecirc;n b&igrave;nh!</strong></em><br />\r\n	Tọa lạc ngay trung t&acirc;m th&agrave;nh phố v&agrave; tiệm cận c&aacute;c tuyến phố lớn, nhưng Sai Gon Sky vẫn giữ lại trong m&igrave;nh n&eacute;t đẹp b&igrave;nh y&ecirc;n với kh&ocirc;ng gian ri&ecirc;ng kh&ocirc;ng qu&aacute; ồn &agrave;o. Cư d&acirc;n Sai Gon Sky c&oacute; thể một bước tận hưởng hơi thở hiện đại nơi phố thị s&ocirc;i động, đồng thời một bước trở về tổ ấm th&acirc;n thương giữa thi&ecirc;n nhi&ecirc;n dịu m&aacute;t, thanh b&igrave;nh.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Chung cư S&agrave;i G&ograve;n Sky Tower TP Vinh l&agrave; t&ograve;a nh&agrave; hiếm hoi tho&aacute;ng đ&atilde;ng cả bốn ph&iacute;a, kh&ocirc;ng bị chắn bởi c&aacute;c t&ograve;a nh&agrave; kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N CHUNG CƯ SAI GON SKY</span></span></span></h3>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Với mong muốn mang đến cuộc sống tiện nghi, thịnh vượng cho cư d&acirc;n, dự &aacute;n chung cư S&agrave;i G&ograve;n Sky TP Vinh thiết kế đa dạng c&aacute;c tiện &iacute;ch nội khu, gi&uacute;p mở rộng kh&ocirc;ng gian sinh hoạt cộng đồng v&agrave; gia đ&igrave;nh để c&acirc;n bằng cuộc sống.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Khu&ocirc;n vi&ecirc;n rộng r&atilde;i:</em> Nơi cả gia đ&igrave;nh c&ugrave;ng nhau dạo bộ, thưởng ngoạn phong cảnh tr&agrave;n ngập c&acirc;y xanh, thư gi&atilde;n tinh thần v&agrave; tăng cường sức khỏe.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khu&ocirc;n vi&ecirc;n rộng r&atilde;i v&agrave; l&yacute; tưởng cho cư d&acirc;n chung cư S&agrave;i G&ograve;n Sky TP Vinh.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Khu vui chơi cho trẻ:</em> Kh&ocirc;ng gian mở với những tr&ograve; chơi th&uacute; vị, bổ &iacute;ch v&agrave; an to&agrave;n, cho trẻ vui chơi v&agrave; ph&aacute;t triển to&agrave;n diện về thể chất lẫn tinh thần. Tại đ&acirc;y, trẻ vừa được tận hưởng thi&ecirc;n nhi&ecirc;n trong l&agrave;nh, tr&aacute;nh xa t&aacute;c hại của đồ điện tử, vừa kết nối b&egrave; bạn, tạo n&ecirc;n những kỷ niệm đẹp v&agrave; &yacute; nghĩa trong những năm th&aacute;ng tuổi thơ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Nh&agrave; trẻ: </em>X&acirc;y dựng khang trang với đội ngũ gi&aacute;o vi&ecirc;n được chọn lọc, tạo nền tảng vững chắc cho tương lai của trẻ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Si&ecirc;u thị:</em> Tiện nghi kh&ocirc;ng thể thiếu cho cuộc sống hiện đại v&agrave; kh&eacute;p k&iacute;n. Sẽ được mở tại tầng thương mại của t&ograve;a nh&agrave; chung cư S&agrave;i G&ograve;n Sky TP Vinh.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Chung cư S&agrave;i G&ograve;n Sky TP Vinh sẽ c&oacute; si&ecirc;u thị tại c&aacute;c tầng thương mại.<br />\r\n	<em>Ph&ograve;ng sinh hoạt cộng đồng: </em>Tổ chức c&aacute;c sự kiện nhằm kết nối c&aacute;c hộ gia đ&igrave;nh, tạo n&ecirc;n một cộng đồng văn minh v&agrave; gắn kết.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>B&atilde;i đỗ xe: </em>Với đội ngũ an ninh bảo vệ an to&agrave;n tuyệt đối. B&atilde;i đậu v&agrave; tầng hầm để xe của chung cư S&agrave;i G&ograve;n Sky TP Vinh sẽ kh&ocirc;ng c&oacute; cảnh chen ch&uacute;c chật chội bởi S&agrave;i G&ograve;n Sky Tower c&oacute; số lượng căn hộ kh&ocirc;ng cao, xung quanh c&oacute; nhiều diện t&iacute;ch c&ocirc;ng cộng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Dự &aacute;n sở hữu mật độ thang m&aacute;y l&yacute; tưởng:</em> 33 căn/thang, với chỉ 16 tầng v&agrave; 130 căn hộ, gi&uacute;p giao th&ocirc;ng diễn ra nhanh ch&oacute;ng giữa c&aacute;c tầng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Thang m&aacute;y chung cư S&agrave;i G&ograve;n Sky Tower c&oacute; mật độ thấp nhất TP Vinh hiện nay.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Kh&ocirc;ng gian sống y&ecirc;n b&igrave;nh:</em> Tọa lạc ngay trung t&acirc;m th&agrave;nh phố v&agrave; tiệm cận c&aacute;c tuyến phố lớn, nhưng Sai Gon Sky vẫn giữ lại trong m&igrave;nh n&eacute;t đẹp b&igrave;nh y&ecirc;n với kh&ocirc;ng gian ri&ecirc;ng kh&ocirc;ng qu&aacute; ồn &agrave;o. Cư d&acirc;n Sai Gon Sky c&oacute; thể một bước tận hưởng hơi thở hiện đại nơi phố thị s&ocirc;i động, đồng thời một bước trở về tổ ấm th&acirc;n thương giữa thi&ecirc;n nhi&ecirc;n dịu m&aacute;t, thanh b&igrave;nh.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Cộng đồng cư d&acirc;n văn minh: </em>Một m&ocirc;i trường l&yacute; tưởng để an cư lạc nghiệp, một cộng đồng cư d&acirc;n văn minh với phong c&aacute;ch sống hiện đại l&agrave; những điều Sai Gon Sky sẽ mang lại cho c&aacute;c chủ nh&acirc;n tương lai. Tại đ&acirc;y, c&aacute;c gia đ&igrave;nh sẽ c&ugrave;ng nhau tham c&aacute;c hoạt động gắn kết cư d&acirc;n, c&ugrave;ng nhau x&acirc;y dựng cầu nối tạo n&ecirc;n một cộng đồng chia sẻ, hạnh ph&uacute;c v&agrave; thịnh vượng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/SGS/tien-%C3%ADch.jpg\" /></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:18px;\"><strong><span style=\"font-family:times new roman,times,serif;\">CĂN HỘ TH&Ocirc;NG MINH ĐẦU TI&Ecirc;N TẠI NGHỆ AN</span></strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Lần đầu ti&ecirc;n tại TP Vinh, Sai Gon Sky mang đến hệ thống<strong> SMART HOME</strong> &ndash; căn hộ th&ocirc;ng minh đạt chuẩn cao cấp với c&ocirc;ng nghệ vượt trội, gi&uacute;p c&aacute;c chủ nh&acirc;n dễ d&agrave;ng điều khiển căn hộ chỉ với một thiết bị di động.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Chỉ một n&uacute;t bấm để điều khiển tất cả thiết bị trong nh&agrave;</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Chạm để điều khiển: bật bản nhạc y&ecirc;u th&iacute;ch, tăng giảm điều h&ograve;a, mở cửa sổ, k&iacute;ch hoạt hệ thống an ninh...</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Ra lệnh bằng giọng n&oacute;i: c&ocirc;ng nghệ trợ l&yacute; ảo mang đến trải nghiệm tuyệt vời khi điều khiển bằng giọng n&oacute;i</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Kết nối kh&ocirc;ng giới hạn: điều khiển căn hộ từ oto, văn ph&ograve;ng hoặc trong chuyến c&ocirc;ng t&aacute;c, du lịch xa</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">SMART HOME ch&iacute;nh l&agrave; ng&ocirc;i nh&agrave; tương lai, với những c&ocirc;ng nghệ ti&ecirc;n tiến được ứng dụng rộng r&atilde;i tr&ecirc;n thế giới nhằm kiến tạo một cộng đồng th&ocirc;ng minh, mang đến một cuộc sống hiện đại, tiện nghi, bắt kịp với thời đại.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/SGS/smart%20home.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><span style=\"font-size:18px;\"><strong><span style=\"font-family:times new roman,times,serif;\">NỘI THẤT TINH TẾ</span></strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Chung cư Sai Gon Sky b&agrave;n giao nội thất cơ bản với c&aacute;c trang thiết bị cao cấp, hiện đại</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; S&agrave;n nhựa giả gỗ mang cảm gi&aacute;c sang trọng</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Sơn tường: Sử dụng sơn nước cao cấp theo c&ocirc;ng nghệ ti&ecirc;n tiến bền đẹp v&agrave; chống b&aacute;m bẩn</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Thiết bị vệ sinh cao cấp : Bồn rửa, gương, gi&aacute; để đồ, v&ograve;i sen, v&ograve;i xịt, bồn cầu</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Đ&egrave;n chiếu s&aacute;ng</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; Chu&ocirc;ng b&aacute;o ch&aacute;y</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/files/noi-that-saigonsky.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TH&Ocirc;NG TIN LI&Ecirc;N HỆ:</span></span></strong></span></p>\r\n<p>\r\n	<span style=\"color:#0000ff;\"><em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">C&ocirc;ng Ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An</span></span></em></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hotline: 094.220.6262</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Website:</strong> <a href=\"http://www.bdsnghetinh.net/chung-cu/chung-cu-sai-gon-sky-tower/70.html\">http://www.bdsnghetinh.net/</a></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Chung cư cao cấp Sai Gon Sky: Tổ ấm thông minh, Yên bình giữa phố', '25-6-2019', '7-2-2020', 'chung-cu-sai-gon-sky-tower', 'Sài Gòn Sky Tower được định vị thuộc phân khúc chung cư cao cấp tại TP Vinh. Sở hữu vị trí trung tâm, chăm chút trong từng thiết kế nội và ngoại thất', 0, 'chung cư Nguyễn Thái Học, chung cư cao cấp Sai Gon Sky, Chung cư Phường Đội Cung,', '0'),
(71, 1, 11, NULL, 'SHOPHOUSE DỰ ÁN TNR STARS DIỄN CHÂU', 'Thôn 7 Diễn Kỷ', 'TNR Holdings Việt Nam', '2020', '140m2', '0942206262', 1179, '/public/upload/images/TNR-dien-chau/Shophouse%20tnr%20stars%20dien%20chau.jpg', NULL, NULL, 1, '1', NULL, '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Sở hữu vị tr&iacute; tuyệt đẹp được bao bọc bởi d&ograve;ng s&ocirc;ng B&ugrave;ng xanh m&aacute;t v&agrave; thơ mộng, kết nối với Quốc lộ 1A v&agrave; khu chợ Sy nhộn nhịp, Khu đ&ocirc; thị thương mại &ndash; dịch vụ - giải tr&iacute; TNR Stars Diễn Ch&acirc;u kiến tạo n&ecirc;n thế đất &ldquo;nhất cận thị, nhị cận giang, tam cận lộ&rdquo;, tạo n&ecirc;n vị thế &ldquo;vượng lộc đắc t&agrave;i&rdquo; độc đ&aacute;o v&agrave; hiếm c&oacute; tr&ecirc;n to&agrave;n khu vực Bắc Trung Bộ.&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">V&agrave; điểm nhấn đặc biệt của dự &aacute;n ch&iacute;nh l&agrave; <a href=\"http://www.bdsnghetinh.net/du-an-bds/shophouse-du-an-tnr-stars-dien-chau/71.html\"><span style=\"color:#ff0000;\"><strong>Shophouse TNR Stars Diễn Ch&acirc;u</strong></span></a>, với tiềm năng kinh doanh bu&ocirc;n b&aacute;n v&agrave; đầu tư sinh lời cực kỳ hấp dẫn. Thuộc quần thể dự &aacute;n KĐT kiểu mẫu, sớm khẳng định l&agrave; điểm n&oacute;ng thu h&uacute;t nhiều nh&agrave; đầu tư v&igrave; nằm trong khu đ&ocirc;ng d&acirc;n cư v&agrave; t&iacute;nh ưu việt trong thiết kế, cũng như an to&agrave;n trong ph&aacute;p l&yacute;.</span></span></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/Shophouse%20tnr%20stars%20dien%20chau.jpg\" /></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khả năng sinh lời cao v&agrave; ổn định, &iacute;t chịu biến động của thị trường bất động sản, shophouse hứa hẹn l&agrave; sản phẩm đ&aacute;ng đầu tư của năm nay.<br />\r\n	Shophouse sở hữu thiết kế th&ocirc;ng minh 2in1, gi&uacute;p chủ nh&acirc;n dễ d&agrave;ng kết hợp vừa shop (kinh doanh) vừa house (an cư). So với đất nền v&agrave; chung cư, loại h&igrave;nh n&agrave;y vừa c&oacute; thể đưa v&agrave;o khai th&aacute;c sử dụng ngay, vừa chiếm ưu thế tọa lạc t&agrave;i vị tr&iacute; trung t&acirc;m nhất dự &aacute;n mức độ tăng trưởng gi&aacute; vượt trội.</span></span></p>\r\n<h2>\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TH&Ocirc;NG TIN TỔNG QUAN DỰ &Aacute;N</span></span></strong></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>T&ecirc;n dự &aacute;n: </strong>Khu đ&ocirc; thị TNR Stars Diễn Ch&acirc;u</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Chủ đầu tư: </strong>TNR Holdings Việt Nam</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Vị tr&iacute;: </strong>Diễn Kỷ, Diễn Ch&acirc;u, Nghệ An</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Quy m&ocirc;:</strong> 33.35 ha</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Mật độ x&acirc;y dựng:</strong> 44%</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Quy hoạch:</strong> Khu đ&ocirc; thị kết hợp gồm:</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>4 ph&acirc;n khu: </strong>AN TR&Iacute;, AN H&Ograve;A, AN LỘC, AN THỊNH</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Tổng số sản phẩm</strong> (Đất ở 44%): 1.196 L&ocirc; (Liền kề, Shopouse, Nh&agrave; vườn, Biệt thự)</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Diện t&iacute;ch giao th&ocirc;ng, c&acirc;y xanh, c&ocirc;ng vi&ecirc;n, tiện &iacute;ch : 56%</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">30 tiện &iacute;ch đẳng cấp</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/TNR%20%C4%90%C3%AAm%206262.jpg\" /></span></span></p>\r\n<h2>\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">TIỀM NĂNG CỦA SHOPHOUSE TNR STARS DIỄN CH&Acirc;U:</span></span></strong></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>1. Vị tr&iacute; đắc địa, trung t&acirc;m của trung t&acirc;m</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">C&aacute;c căn shophouse nằm ngay mặt tiền đường ch&iacute;nh, đ&ocirc;ng người lưu th&ocirc;ng qua lại, tận dụng nguồn kh&aacute;ch tiềm năng từ ch&iacute;nh trong khu đ&ocirc; thị <em>(Cư d&acirc;n dự kiến 3000 - 4000 người)</em>. Đ&acirc;y l&agrave; một trong những yếu tố đảm bảo cho việc kinh doanh hoặc cho thu&ecirc; shophouse tốt. Hơn nữa, dự &aacute;n quy hoạch Kiểu mẫu tổ hợp Thương mại, dịch vụ, giải tr&iacute; sẽ c&ograve;n thu h&uacute;t số đ&ocirc;ng hơn lượng lớn d&acirc;n cư trong huyện v&agrave; l&acirc;n cận t&igrave;m đến tham quan, mua sắm, vui chơi giải tr&iacute;. L&agrave; một lợi thế cực lớn để l&agrave;m kinh doanh dịch vụ.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>2. Số lượng hạn chế</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Hiện khu vực Diễn Ch&acirc;u chưa c&oacute; dự &aacute;n quy hoạch đồng bộ quy m&ocirc; như TNR, v&agrave; đến 5 -10 năm nữa cũng kh&ocirc;ng thể c&oacute;. TNR vị tr&iacute; đẹp cộng số lượng tung ra thị trường kh&ocirc;ng nhiều, kh&ocirc;ng thể đ&aacute;p ứng hết nhu cầu của thị trường v&igrave; vậy c&aacute;c căn shophouse lại c&agrave;ng trở n&ecirc;n khan hiếm.</span></span></p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">3. Thiết kế th&ocirc;ng minh, tiện lợi</span></span></strong></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Với lợi th&ecirc;́ vị trí cũng như thi&ecirc;́t k&ecirc;́ đẹp th&ocirc;ng minh, <a href=\"http://www.bdsnghetinh.net/du-an-bds/shophouse-du-an-tnr-stars-dien-chau/71.html\"><strong>shophouse Dự &aacute;n TNR Stars Diễn Ch&acirc;u</strong></a> thích hợp vừa ở vừa mở cửa hàng kinh doanh, thuận tiện trong việc quản l&yacute;. Hoặc c&oacute; thể cho thu&ecirc; l&agrave;m c&aacute;c văn ph&ograve;ng đại diện c&ocirc;ng ty lớn hay như ng&acirc;n h&agrave;ng,...</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>4. Trung t&acirc;m tiện &iacute;ch thuận tiện di chuyển</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Di chuyển cũng l&agrave; một vấn đề quan trọng đối với cửa h&agrave;ng, c&aacute;c căn shophouse thường được x&acirc;y dựng tại vị tr&iacute; trung t&acirc;m, đường rộng r&atilde;i dễ d&agrave;ng đỗ xe b&ecirc;n đường để tiện mua sắm. Ngo&agrave;i ra kh&aacute;ch h&agrave;ng kh&ocirc;ng phải trong khu đ&ocirc; thị cũng dễ d&agrave;ng tiếp cận khu nh&agrave; phố thương mại n&agrave;y hơn cũng bởi vị tr&iacute; trung t&acirc;m thuận tiện di chuyển khi dạo bước v&agrave;o KĐT.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>5. Thanh khoản tốt</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Một trong c&aacute;c yếu tố hấp dẫn nữa của shophouse đ&oacute; ch&iacute;nh l&agrave; t&iacute;nh thanh khoản cao, với c&aacute;c yếu tố từ vị tr&iacute; trung t&acirc;m nhất, thiết kế đẹp thuận tiện c&ugrave;ng số lượng hạn chế, c&aacute;c nh&agrave; đầu tư c&oacute; thể y&ecirc;n t&acirc;m về t&iacute;nh thanh khoản do c&oacute; thể dễ d&agrave;ng mua b&aacute;n, cho thu&ecirc;, sử dụng ngay.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>6. Cơ hội gia tăng l&atilde;i vốn ( Gi&aacute; trị t&agrave;i sản)</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Cơ hội kinh doanh tuyệt vời d&agrave;nh ri&ecirc;ng cho gia chủ ngay ch&iacute;nh căn shophouse gi&aacute; trị của m&igrave;nh. Cả một d&atilde;y phố đều tăm tắp kinh doanh đa dạng v&agrave; nhộn nhịp, tạo cơ hội lớn cho ch&iacute;nh c&aacute;c chủ nh&acirc;n nơi đ&acirc;y khai th&aacute;c tiềm năng kinh doanh lớn nhất. B&ecirc;n cạnh đ&oacute;, bạn cũng kh&ocirc;ng phải lo chi ph&iacute; thu&ecirc; mặt bằng với gi&aacute; cao h&agrave;ng th&aacute;ng tại một vị tr&iacute; đắc địa, gi&aacute; trị t&agrave;i sản của bạn cũng kh&ocirc;ng ngừng tăng l&ecirc;n nhanh ch&oacute;ng.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>7. Sở hữu l&acirc;u d&agrave;i:</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Shophouse TNR Stars Diễn Ch&acirc;u sở hữu vĩnh viễn, đảm bảo cho sự gia tăng bền vững gi&aacute; trị BĐS.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/TNR-dien-chau/Minh%20hoa%20shophouse.png\" /></p>\r\n<h2>\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><strong>NHỮNG L&Yacute; DO N&Ecirc;N CHỌN MUA SHOPHOUSE DỰ &Aacute;N TNR STARS DIỄN CH&Acirc;U</strong></span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">1. Được ph&aacute;t triển bởi TNR Holdings Việt Nam - đơn vị c&oacute; Uy t&iacute;n &ndash; Năng lực &ndash; Kinh nghiệm thực hiện nhiều dự &aacute;n đ&ocirc; thị tr&ecirc;n cả nước.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">2. Tọa lạc tại vị tr&iacute; thuận lợi &ldquo;nhất cận thị, nhị cận giang, tam cận lộ&rdquo;.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">3. M&ocirc;i trường sinh th&aacute;i trong l&agrave;nh, d&ograve;ng s&ocirc;ng B&ugrave;ng chảy quanh dự &aacute;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">4. Hệ thống tiện &iacute;ch đa dạng, vượt trội bậc nhất khu vực.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">5. Quy hoạch đồng bộ, tối ưu c&ocirc;ng năng, đa dạng diện t&iacute;ch.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">6. Cộng đồng d&acirc;n cư văn minh, đẳng cấp.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">7. M&ocirc;i trường ph&aacute;t triển to&agrave;n diện cho trẻ em.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">8. An ninh đa lớp, bảo vệ an to&agrave;n cho cư d&acirc;n.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">9. Tiềm năng tăng gi&aacute; trị trong tương lai, sinh lời bền vững.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">10. Tiến độ thanh to&aacute;n c&aacute;ch xa, kh&aacute;ch h&agrave;ng c&oacute; thời gian chuẩn bị nguồn tiền.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#ff0000;\"><strong>TH&Ocirc;NG TIN LI&Ecirc;N HỆ:</strong></span><br />\r\n	<span style=\"color:#0000ff;\">C&ocirc;ng Ty CPDV &amp; Địa Ốc Đất Xanh Nghệ An</span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hotline:<span style=\"color:#0000cd;\"> 094.220.6262</span></strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Website:</strong><a href=\"http://www.bdsnghetinh.net/\"> http://www.bdsnghetinh.net/</a></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Shophouse dự án TNR Diễn Châu - Tiềm năng sinh lời vượt trội', '7-11-2019', '7-11-2019', 'shophouse-du-an-tnr-stars-dien-chau', 'Shophouse TNR Stars Diễn Châu, với tiềm năng kinh doanh buôn bán và đầu tư sinh lời cực kỳ hấp dẫn. Thuộc quần thể dự án KĐT kiểu mẫu, sớm khẳng định', 0, 'Shophouse Nghệ An, Shophouse tnr diễn châu, shophouse tnr dien chau, tnr dien chau,', '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(73, 1, 7, NULL, 'CHUNG CƯ CAO CẤP TRUNG ĐỨC TOWER', 'Số 2 Lê Lợi, TP Vinh, Nghệ An', 'Công ty CP xây dựng Trung Đức', NULL, NULL, '0962452245', 743, '/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/f85895f34cf0b1aee8e1.jpg', NULL, NULL, 1, '1', NULL, '<h2 style=\"text-align: center;\">\r\n	<span style=\"font-size:22px;\"><span style=\"color:#0000ff;\"><strong><span style=\"font-family:times new roman,times,serif;\">TH&Ocirc;NG TIN TỔNG QUAN CHUNG CƯ TRUNG ĐỨC</span></strong></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;Chung cư Trung Đức Tower</strong> được định vị l&agrave; một trong những chung cư chuẩn cao cấp h&agrave;ng đầu khu vực, mang đến lối sống thời thượng, chạm tới đỉnh cao của những chủ nh&acirc;n tinh hoa của th&agrave;nh phố. Trung Đức mang đến những trải nghiệm sống kh&aacute;c biệt, l&agrave; nơi đủ tầm để thể hiện đẳng cấp, t&ocirc;n vinh vị thế tinh hoa của một cộng đồng danh gi&aacute;, l&agrave; điểm nhấn trong phong c&aacute;ch sống hiện đại tại trung t&acirc;m khu vực Bắc Trung Bộ.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><strong>T&ecirc;n dự &aacute;n: </strong>Trung t&acirc;m thương mại v&agrave; chung cư cao cấp Trung Đức Tower<br />\r\n	<strong>Vị tr&iacute;:</strong> Đường L&ecirc; Lợi, Phường Hưng B&igrave;nh, Th&agrave;nh Phố Vinh, Tỉnh Nghệ An<br />\r\n	<strong>Chủ đầu tư:</strong> C&ocirc;ng ty CP x&acirc;y dựng Trung Đức<br />\r\n	<strong>Đơn vị tư vấn thiết kế:</strong> Li&ecirc;n doanh tư vấn thiết kế Kiến tr&uacute;c Việt Nam &ndash; Singapore<br />\r\n	<strong>Diện t&iacute;ch khu đất: </strong>4.886 m2<br />\r\n	<em>+&nbsp; Diện t&iacute;ch đất x&acirc;y dựng: </em>2.438 m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><em>+&nbsp; Diện t&iacute;ch đất khu&ocirc;n vi&ecirc;n c&ocirc;ng cộng: </em>2.448 m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><strong>Quy m&ocirc;: </strong>21 tầng nổi v&agrave; 1 tầng hầm rộng 3.422 m2, khu dịch vụ thương mại rộng 7.920m2 được bố tr&iacute; từ tầng 1 &ndash; tầng 4, khu căn hộ cao cấp từ tầng 5 &ndash; tầng 19 v&agrave; tầng căn hộ Penhouse.<br />\r\n	Hiện dự &aacute;n đ&atilde; ho&agrave;n thiện v&agrave; b&agrave;n giao nh&agrave;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N TRUNG ĐỨC TOWER</span></strong></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">Dự &aacute;n tọa lạc tr&ecirc;n giao lộ của hai tuyến đường đẹp v&agrave; sầm uất bậc nhất TP Vinh</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">Ph&iacute;a nam gi&aacute;p Hồ điều h&ograve;a Thủy Tạ diện t&iacute;ch 3400 m2<br />\r\n	Ph&iacute;a T&acirc;y gi&aacute;p đường L&ecirc; Lợi &ndash; Quốc lộ 1A;<br />\r\n	Ph&iacute;a đ&ocirc;ng gi&aacute;p đường quy hoạch rộng 5 m&eacute;t;<br />\r\n	Ph&iacute;a Bắc gi&aacute;p đường L&yacute; Thường Kiệt k&eacute;o d&agrave;i;<br />\r\n	Với lợi thế trung t&acirc;m th&agrave;nh phố, ngay gần c&aacute;c khu kinh tế, h&agrave;nh ch&iacute;nh quan trọng, Trung Đức Tower c&oacute; kết nối giao th&ocirc;ng thuận tiện qua c&aacute;c tuyến đường huyết mạch như đường L&yacute; Thường Kiệt, Phan Bội Ch&acirc;u, Nguyễn Sỹ S&aacute;ch, đường L&ecirc; Hồng Phong, Nguyễn Thị Minh Khai... v&agrave; hưởng lợi trực tiếp từ hệ thống tiện &iacute;ch c&ocirc;ng cộng đa dạng gồm trường học, bệnh viện, c&ocirc;ng vi&ecirc;n tam gi&aacute;c, s&acirc;n bay, chợ Quang Trung, si&ecirc;u thị Maximax, BigC, c&aacute;c khu vui chơi giải tr&iacute;&hellip; gi&uacute;p gia chủ chỉ cần bước ch&acirc;n ra cửa l&agrave; được hưởng trọn vẹn tất cả tiện &iacute;ch sống.</span></span></p>\r\n<h2 style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/vi-tri-chung-cu-trung-duc.jpg\" />&nbsp;<img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/lien-ket-vung-chung-cu-trung-duc.jpg\" /></h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỰ &Aacute;N CHUNG CƯ TRUNG ĐỨC</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">Trung Đức Tower l&agrave; dự &aacute;n căn hộ cao cấp c&oacute; tổng mức đầu tư gần 340 tỷ đồng, c&oacute; diện t&iacute;ch v&agrave; c&ocirc;ng năng sử dụng đa dạng.<br />\r\n	Được mệnh danh l&agrave; một &ldquo;th&agrave;nh phố thu nhỏ&rdquo; với đầy đủ tiện &iacute;ch kh&eacute;p k&iacute;n như trung t&acirc;m thương mại, si&ecirc;u thị, nh&agrave; trẻ, ph&ograve;ng tập gym, spa, khu vui chơi trẻ em, khu sinh hoạt cộng đồng được bố tr&iacute; từ tầng 1 đến tầng 4.<br />\r\n	Ngo&agrave;i ra dự &aacute;n được sử dụng kh&ocirc;ng gian hai vỉa h&egrave; tr&ecirc;n hai tuyến đường ch&iacute;nh với h&agrave;ng c&acirc;y cổ thụ, ghế đ&aacute; đan xen bồn hoa tạo n&ecirc;n kh&ocirc;ng gian m&aacute;t mẻ v&agrave; l&agrave; điểm dừng ch&acirc;n cho kh&aacute;ch h&agrave;ng tham quan mua sắm tại khu thương mại.<br />\r\n	Ph&iacute;a mặt hồ Thủy Tạ được cải tạo v&agrave; lắp đ&agrave;i phun nước kết hợp đ&egrave;n m&agrave;u tạo điểm nhấn cho t&ograve;a nh&agrave; v&agrave; kh&ocirc;ng gian của chung cư.<br />\r\n	C&aacute;c tiện &iacute;ch kh&ocirc;ng chỉ mang lại sự thuận tiện trong sinh hoạt v&igrave; kh&ocirc;ng cần bước ch&acirc;n ra khỏi cửa m&agrave; vẫn c&oacute; thể sử dụng tất cả c&aacute;c dịch vụ thiết yếu, m&agrave; c&ograve;n t&ocirc;n vinh đẳng cấp của những chủ nh&acirc;n căn hộ với cuộc sống sung t&uacute;c, đủ đầy, đạt chuẩn thượng lưu.<br />\r\n	Chung cư Trung Đức l&agrave; dự án đ&acirc;̀u ti&ecirc;n và duy nh&acirc;́t tại TP Vinh đ&ecirc;́n thời đi&ecirc;̉m hi&ecirc;̣n tại có h&ocirc;̀ đi&ecirc;̀u hoà r&ocirc;̣ng với đài phun nước trung t&acirc;m, tạo n&ecirc;n sự hài hoà v&ecirc;̀ y&ecirc;́u t&ocirc;́ phong thuỷ, mang lại tài l&ocirc;̣c, ti&ecirc;̀n bạc, sức khoẻ, sinh khí, an lành, thịnh vượng cho cư d&acirc;n nơi đ&acirc;y. Hồ điều h&ograve;a g&oacute;p phần cải thiện chất lượng kh&ocirc;ng kh&iacute;, đưa những l&agrave;n gi&oacute; tươi v&agrave;o từng căn hộ. Ngay ven h&ocirc;̀ là khu vực tản b&ocirc;̣, giúp cư d&acirc;n thư giãn, t&acirc;̣p th&ecirc;̉ dục m&ocirc;̃i ngày. Từ tầng cao, gia chủ có th&ecirc;̉ thu v&agrave;o tầm mắt v&agrave; mở rộng mọi gi&aacute;c quan để thưởng thức cảnh quan thi&ecirc;n nhi&ecirc;n thoáng đãng trong sắc xanh của mặt hồ, của c&acirc;y c&ocirc;́i, bầu trời v&agrave; th&agrave;nh phố c&agrave;ng trở n&ecirc;n lung linh khi về đ&ecirc;m.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/Anh-thuc-te-chung-cu-trung-duc-2.jpg\" /></span></span><br />\r\n	<br />\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#0000ff;\"><strong>KIẾN TR&Uacute;C ĐẬM CHẤT SINGAPORE</strong></span></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">Dự &aacute;n được thiết kế bởi đơn vị li&ecirc;n doanh Việt &ndash; Sing, đi theo phong c&aacute;ch Singapore hiện đại, ph&oacute;ng kho&aacute;ng<br />\r\n	Đ&acirc;y l&agrave; dự &aacute;n duy nhất ở TP Vinh kh&ocirc;ng đi theo chuẩn mực h&igrave;nh hộp của những chung cư th&ocirc;ng thường m&agrave; được thiết kế ph&aacute; c&aacute;ch h&igrave;nh con thuyền mang &yacute; nghĩa ph&uacute; qu&yacute;, thịnh vượng cho gia chủ<br />\r\n	Lối kiến tr&uacute;c n&agrave;y gi&uacute;p mở rộng kh&ocirc;ng gian sử dụng của mặt bằng tầng 1: ot&ocirc; hoặc người đi tập thể dục c&oacute; thể đi hết một v&ograve;ng t&ograve;a nh&agrave; thay v&igrave; chỉ sử dụng được mặt trước hoặc sau như những t&ograve;a nh&agrave; kh&aacute;c<br />\r\n	Cả t&ograve;a nh&agrave; được x&acirc;y theo h&igrave;nh con thuyền chạy từ T&acirc;y sang Đ&ocirc;ng, hạn chế mạn tiếp x&uacute;c với hướng T&acirc;y n&ecirc;n tr&aacute;nh được nắng n&oacute;ng v&agrave; gi&oacute; L&agrave;o v&agrave;o m&ugrave;a h&egrave;. Điều n&agrave;y vừa mang lại kh&ocirc;ng gian sống m&aacute;t mẻ vừa gi&uacute;p tăng tuổi thọ của c&aacute;c vật liệu, thiết bị của t&ograve;a nh&agrave;.<br />\r\n	Hướng tới mục ti&ecirc;u x&acirc;y dựng một dự &aacute;n chuẩn cao cấp, ph&ugrave; hợp với những kh&aacute;ch h&agrave;ng tinh hoa, dự &aacute;n được t&iacute;nh to&aacute;n đến từng tiểu tiết, tiệm cận đến sự ho&agrave;n hảo d&agrave;nh cho một tổ ấm an cư l&yacute; tưởng cho mọi gia đ&igrave;nh thượng lưu</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/Anh-thuc-te-chung-cu-trung-duc-4.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000cd;\"><strong><span new=\"\" times=\"\">CĂN HỘ ĐẲNG CẤP</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">Diện t&iacute;ch căn hộ nhỏ nhất: 85m2, tầm t&agrave;i ch&iacute;nh tỷ đồng trở l&ecirc;n tạo n&ecirc;n một sự ph&acirc;n kh&uacute;c r&otilde; r&agrave;ng cho c&aacute;c gia chủ của Trung Đức Tower: đ&oacute; l&agrave; những người c&oacute; tầm v&agrave; tiền, tầng lớp ngang h&agrave;ng về tr&igrave;nh độ d&acirc;n tr&iacute; v&agrave; t&agrave;i ch&iacute;nh.<br />\r\n	C&aacute;c căn hộ được thiết kế theo ti&ecirc;u chuẩn Singapore, tối ưu về thẩm mỹ lẫn c&ocirc;ng năng sử dụng:<br />\r\n	Tất cả c&aacute;c căn hộ đều đ&oacute;n được &aacute;nh s&aacute;ng v&agrave; gi&oacute; tươi tự nhi&ecirc;n.<br />\r\n	H&acirc;̀u h&ecirc;́t c&aacute;c căn h&ocirc;̣ có bếp đều tiếp x&uacute;c với l&ocirc; gia kỹ thuật, c&oacute; thể kh&eacute;p k&iacute;n hoặc thiết kế mở ph&ugrave; hợp nhu cầu gia chủ:<br />\r\n	- CĐT bố tr&iacute; vị tr&iacute; lắp điều h&ograve;a sẵn: ống dẫn nước PVC, chỉ cần lắp điều h&ograve;a khi về ở m&agrave; kh&ocirc;ng phải đục tường<br />\r\n	- Thiết kế ph&ograve;ng vệ sinh lớn, sang trọng: người Singapore quan niệm ph&ograve;ng vệ sinh l&agrave; quan trọng nhất trong 1 căn hộ v&igrave; l&agrave; nơi thư gi&atilde;n, hưởng thụ (b&ecirc;n cạnh bếp, ph&ograve;ng ăn, ph&ograve;ng kh&aacute;ch)</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/Anh-thuc-te-chung-cu-trung-duc-6.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/Anh-thuc-te-chung-cu-trung-duc-5.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#0000ff;\"><strong><span style=\"font-size:20px;\"><span new=\"\" times=\"\">MẶT BẰNG CĂN HỘ CHUNG CƯ TRUNG ĐỨC</span></span></strong></span></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/public/upload/images/Trung%20%C4%90%E1%BB%A9c%202020/mat-bang-chung-cu-trung-duc.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#0000ff;\"><strong><span style=\"font-family:times new roman,times,serif;\">L&Yacute; DO CHỌN MUA CĂN HỘ TRUNG ĐỨC TOWER</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">1. Vị tr&iacute; đắc địa, mặt đường L&ecirc; Lợi huyết mạch giao th&ocirc;ng TP Vinh</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">2. Một bước xuống phố, hạ tầng đồng bộ dễ d&agrave;ng kết nối với khu vực l&acirc;n cận</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">3. Tiện &iacute;ch đầy đủ cho gia chủ một cuộc sống vi&ecirc;n m&atilde;n</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span new=\"\" style=\"font-size: 16px; font-family: \" times=\"\">4. </span><span new=\"\" style=\"font-size: 16px; font-family: \" times=\"\">Nhận nh&agrave; v&agrave;o ở ngay, kh&ocirc;ng c&ograve;n nỗi lo về tiến độ dự &aacute;n</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family:times new roman,times,serif;\"><span style=\"font-size:16px;\">5. Phong thủy độc t&ocirc;n, vượng lộc đắc t&agrave;i</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span new=\"\" style=\"font-size: 16px; font-family: \" times=\"\">6. Căn hộ cao cấp với kiến tr&uacute;c độc đ&aacute;o đậm chất Singapore</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span new=\"\" style=\"font-size: 16px; font-family: \" times=\"\">==============================================</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span font-size:=\"\" new=\"\" style=\"font-family: \" times=\"\">LI&Ecirc;N HỆ t&igrave;m hiểu ngay Chung cư Cao cấp Trung Đức Tower:</span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"color:#0000ff;\"><span new=\"\" style=\"font-size: 16px; font-family: \" times=\"\">C&ocirc;ng Ty Cổ phần Bất động sản Bắc Trung Bộ</span></span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:28px;\"><span new=\"\" times=\"\"><strong>Hotline: <span style=\"color:#ff0000;\">096.245.2.245</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span new=\"\" style=\"font-size: 16px; font-family: \" times=\"\"><strong>Website:</strong> <a href=\"http://www.bdsnghetinh.net/\"><span style=\"color:#0000ff;\">http://www.bdsnghetinh.net/</span></a></span></p>', NULL, NULL, NULL, NULL, NULL, '30.000.000', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Chung cư cao cấp Trung Đức Tower', '19-6-2020', '27-1-2021', 'chung-cu-cao-cap-trung-duc-tower', 'Chung cư cao cấp Trung Đức tower đi theo phong cách Singapore hiện đại, phóng khoáng Đây là dự án duy nhất ở TP Vinh không theo chuẩn mực thông thường', 0, NULL, '0'),
(74, 26, 13, NULL, 'VINCOM SHOPHOUSE THÁI HÒA', 'Khối 8, Long Sơn', NULL, NULL, NULL, '0962452245', 1010, '/public/upload/images/Vincom%20Th%C3%A1i%20H%C3%B2a/vinthaihoa1.jpg', NULL, NULL, 1, '1', NULL, '<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Tọa lạc tại mảnh đất ph&iacute;a T&acirc;y xứ Nghệ, dự &aacute;n Trung T&acirc;m Thương Mại v&agrave; nh&agrave; phố thương mại Vincom Shophouse Th&aacute;i H&ograve;a được x&acirc;y dựng hiện đại mang đến l&agrave;n gi&oacute; mới kết nối mọi gi&aacute; trị thịnh vượng tại mảnh đất xứ Nghệ đầy tiềm năng.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>T&ecirc;n dự &aacute;n: </strong>Vincom Shophouse Th&aacute;i H&ograve;a<br />\r\n	<strong>Vị tr&iacute; dự &aacute;n:</strong> Khối 8, Long Sơn, Th&aacute;i H&ograve;a, Nghệ An<br />\r\n	<strong>Tổng diện t&iacute;ch: </strong>1.9ha<br />\r\n	<strong>Loại h&igrave;nh sản phẩm:</strong> Nh&agrave; phố shophouse<br />\r\n	<strong>Diện t&iacute;ch đất:</strong> 75m2-135.2m2<br />\r\n	<strong>Tổng diện t&iacute;ch x&acirc;y dựng:</strong> 257.7 đến 362.1m2<br />\r\n	Thiết kế 4 tầng hiện đại, mặt tiền 5-8m</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"tttm vincom thai hoa \" src=\"/public/upload/images/Vincom%20Th%C3%A1i%20H%C3%B2a/pasted%20image%200.png\" /><br />\r\n	Ảnh minh họa</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Vị tr&iacute; dự &aacute;n Vincom Shophouse Th&aacute;i H&ograve;a tọa lạc tại khối 8, phường Long Sơn, Thị x&atilde; Th&aacute;i H&ograve;a, tỉnh Nghệ An. Đ&acirc;y được đ&aacute;nh gi&aacute; l&agrave; khu vực ph&aacute;t triển, một trong những điểm &ldquo;v&agrave;ng&rdquo; kết nối thuận tiện của thị x&atilde; mang đến tiềm năng ph&aacute;t triển vượt trội.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;L&agrave; trung t&acirc;m s&ocirc;i động với cơ sở hạ tầng đồng bộ, hiện đại</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Vị tr&iacute; dự &aacute;n nằm tr&ecirc;n con phố ch&iacute;nh, kết nối thuận tiện với c&aacute;c trục đường huyết mạch của th&agrave;nh phố, đảm bảo li&ecirc;n kết thuận tiện tới c&aacute;c hạng mục văn h&oacute;a, h&agrave;nh ch&iacute;nh, du lịch của th&agrave;nh phố.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Mặt kh&aacute;c dự &aacute;n c&ograve;n sở hữu vị tr&iacute; c&oacute; gi&aacute; trị cao về phong thủy v&agrave; t&agrave;i lộc, mang đến tiềm năng tăng gi&aacute; vượt trội cho sản phẩm.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">MẶT BẰNG THIẾT KẾ DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"mat bang thiet ke vincom thai hoa\" src=\"/public/upload/images/Vincom%20Th%C3%A1i%20H%C3%B2a/Mat-bang-vin-thai-hoa.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Ảnh minh họa</span></span></em></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dựa tr&ecirc;n quy hoạch của UBND tỉnh Nghệ An, dự &aacute;n Vincom Shophouse Th&aacute;i H&ograve;a được ph&aacute;t triển theo 3 hạng mục l&agrave; khu thương mại dịch vụ, si&ecirc;u thị tiện &iacute;ch v&agrave; khu nh&agrave; ở cao 4 tầng với 75 l&ocirc; đất.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Theo đ&oacute;, dự &aacute;n Vincom Shophouse Th&aacute;i H&ograve;a sẽ mang đến hệ thống trung t&acirc;m thương mại Plaza cao 1,5-3 tầng v&agrave; 76 l&ocirc; nh&agrave; phố shophouse hiện đại, thiết kế th&ocirc;ng minh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><strong><span style=\"font-family:times new roman,times,serif;\">Sản phẩm shophouse Vincom Th&aacute;i H&ograve;a</span></strong></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<img alt=\"\" src=\"/public/upload/images/Vincom%20Th%C3%A1i%20H%C3%B2a/vinthaihoa1.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;L&agrave; một trong những d&ograve;ng sản phẩm đẳng cấp mang thương hiệu Vincom, ph&aacute;t triển tr&ecirc;n khắp c&aacute;c tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc, sản phẩm shophouse g&acirc;y ấn tượng kh&ocirc;ng chỉ bởi thiết kế độc đ&aacute;o m&agrave; c&ograve;n mang đến cơ hội đầu tư vượt trội với gi&aacute; trị 2 trong 1 bền vững, vừa để ở vừa c&oacute; thể kinh doanh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Kh&aacute;c với nh&agrave; phố truyền thống, hệ thống nh&agrave; phố thương mại Vincom đ&atilde; được cải tiến v&agrave; n&acirc;ng cấp to&agrave;n diện vừa đảm bảo t&iacute;nh ri&ecirc;ng tư, nhưng đồng thời cũng mở ra cơ hội giao thương sinh lời vượt trội.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hệ thống nh&agrave; phố tại Vincom Shophouse Th&aacute;i H&ograve;a sở hữu diện t&iacute;ch x&acirc;y dựng từ 75-135m2, x&acirc;y dựng 4 tầng, diện t&iacute;ch từ 257.7 - 362.1,m, mặt tiền rộng tho&aacute;ng từ 5-8m, đảm bảo phục vụ tốt nhất nhu cầu kinh doanh tại tầng 1.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự &aacute;n được x&acirc;y th&ocirc;, b&agrave;n giao ho&agrave;n thiện mặt ngo&agrave;i.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Ti&ecirc;u chuẩn b&agrave;n giao: B&agrave;n giao th&ocirc;, ho&agrave;n thiện mặt ngo&agrave;i.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">HỆ THỐNG TIỆN &Iacute;CH TẠI VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp; Sinh sống tại Vincom Shophouse Th&aacute;i H&ograve;a, cư d&acirc;n sẽ được tận hưởng cuộc sống vi&ecirc;n m&atilde;n với hệ thống cảnh quan thi&ecirc;n nhi&ecirc;n xanh m&aacute;t c&ugrave;ng c&aacute;c khu thể thao, vui chơi giải tr&iacute; đa dạng. Hệ thống tiện &iacute;ch mang thương hiệu Vin như sau:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">- Trung t&acirc;m thương mại Vincom, đ&aacute;p ứng nhu cầu mua sắm, vui chơi giải tr&iacute;.<br />\r\n	- Si&ecirc;u thị tiện &iacute;ch Vinmart, Vinpro.<br />\r\n	- Ph&acirc;n khu ẩm thực, cafe, rạp chiếu phim hiện đại.<br />\r\n	- Ph&acirc;n khu tr&ograve; chơi, b&atilde;i đỗ xe,...<br />\r\n	- Hệ thống vườn hoa, cảnh quan xanh m&aacute;t.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<font face=\"times new roman, times, serif\"><span style=\"font-size: 16px;\">&nbsp;</span></font></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">CHỦ ĐẦU TƯ DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Thương hiệu Vincom l&agrave; thương hiệu bất động sản thuộc tập đo&agrave;n Vingroup. Kỳ vọng kiến tạo n&ecirc;n kh&ocirc;ng gian mua sắm, vui chơi, giải tr&iacute;, ẩm thực hiện đại v&agrave; tiện nghi đẳng cấp ti&ecirc;u chuẩn quốc tế, thương hiệu Vincom đang dần trở th&agrave;nh biểu tượng cho cộng đồng cư d&acirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Tập đo&agrave;n Vingroup l&agrave; doanh nghiệp c&oacute; gi&aacute; trị vốn h&oacute;a lớn nhất thị trường chứng kho&aacute;n Việt Nam. Bằng kh&aacute;t vọng ti&ecirc;n phong c&ugrave;ng chiến lược đầu tư ph&aacute;t triển bền vững của Vingroup phấn đấu trở th&agrave;nh tập đo&agrave;n kinh tế h&agrave;ng đầu Việt Nam, c&oacute; uy t&iacute;n v&agrave; vị thế tr&ecirc;n bản đồ kinh tế thế giới x&acirc;y dựng th&agrave;nh c&ocirc;ng chuỗi sản phẩm v&agrave; dịch vụ đẳng cấp g&oacute;p phần n&acirc;ng cao chất lượng cuộc sống của người Việt v&agrave; n&acirc;ng tầm vị thế của người Việt Tr&ecirc;n trường quốc tế.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Được đầu tư x&acirc;y dựng bởi đơn vị uy t&iacute;n, tiềm năng như Vingroup, Vincom shophouse Th&aacute;i H&ograve;a&nbsp; hứa hẹn mang tới sự t&iacute;n nhiệm, an t&acirc;m tuyệt đối cho kh&aacute;ch h&agrave;ng về chất lượng sản phẩm cũng như cơ hội đầu tư hấp dẫn kh&ocirc;ng thể bỏ qua.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp;</h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">CH&Iacute;NH S&Aacute;CH B&Aacute;N H&Agrave;NG DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hiện tại chủ đầu tư đang gấp r&uacute;t ho&agrave;n thiện ch&iacute;nh s&aacute;ch b&aacute;n h&agrave;ng v&agrave; sẽ sớm th&ocirc;ng tin đến qu&yacute; kh&aacute;ch h&agrave;ng trong thời gian tới.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">BẢNG GI&Aacute; DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Gi&aacute; b&aacute;n Shophouse: Đang cập nhật&hellip;.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">TIỀM NĂNG ĐẦU TƯ TẠI SHOPHOUSE VINCOM TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hệ thống sản phẩm Vincom Shophouse Th&aacute;i H&ograve;a sở hữu 2 gi&aacute; trị nổi bật vừa để ở, vừa để kinh doanh với thiết kế đa năng. Dự &aacute;n sở hữu 76 căn thiết kế linh hoạt từ 76.9-231.6m2 đảm bảo phục vụ nhu cầu về kh&ocirc;ng gian sống cũng như tiềm lực t&agrave;i ch&iacute;nh của gia đ&igrave;nh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Về Vị tr&iacute;, dự &aacute;n nằm trong khu vực c&oacute; vị tr&iacute; &ldquo;địa lợi&rdquo; liền kề trung t&acirc;m thương mại Vincom c&ugrave;ng hệ thống c&aacute;c trung t&acirc;m dịch vụ như HONDA, TOYOTA Thống nhất, trung t&acirc;m thương mại Trường Thắng thu h&uacute;t đ&ocirc;ng đảo lượng kh&aacute;ch h&agrave;ng cũng như hưởng lợi từ quy hoạch cơ sở hạ tầng xung quanh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Đặc biệt, mặt tiền của c&aacute;c căn shophouse được thiết kế hợp l&yacute; với bề ngang từ 6.5-10m đảm bảo kh&ocirc;ng gian phục vụ nhu cầu thương mại của gia chủ.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Sở hữu&nbsp; gi&aacute; trị thương mại to lớn, Vincom Shophouse Th&aacute;i H&ograve;a mang đến cơ hội đầu tư&nbsp; cho những gia chủ đam m&ecirc; kinh doanh. Kết hợp ho&agrave;n hảo 2 yếu tố &ldquo;an cư&rdquo; v&agrave; lạc nghiệp&rdquo;, qu&yacute; vị c&oacute; thể mở một nh&agrave; h&agrave;ng sang trọng, một shop thời trang s&agrave;nh điệu, văn ph&ograve;ng giao dịch thương mại hay đầu tư cho thu&ecirc; lại mặt bằng&hellip;, những cơ hội sinh lời trọn đời đ&oacute; khởi tạo n&ecirc;n một tương lai vững chắc d&agrave;nh cho tất cả c&aacute;c chủ nh&acirc;n đẳng cấp của Vincom Shophouse Th&aacute;i H&ograve;a</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"font-family:times new roman,times,serif;\">TIẾN ĐỘ DỰ &Aacute;N VINCOM SHOPHOUSE TH&Aacute;I H&Ograve;A</span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Li&ecirc;n tục cập nhật h&igrave;nh ảnh tiến độ dự &aacute;n Vincom Shophouse Th&aacute;i H&ograve;a, th&ocirc;ng tin ch&iacute;nh x&aacute;c nhất đến với qu&yacute; kh&aacute;ch h&agrave;ng</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/Vincom%20Th%C3%A1i%20H%C3%B2a/vinthaihoa1.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/Vincom%20Th%C3%A1i%20H%C3%B2a/vinthaihoa2.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color:#ff0000;\"><strong><span style=\"font-size:28px;\"><font face=\"times new roman, times, serif\">Li&ecirc;n hệ PKD CĐT : 096.245.2.245 ( Mr. L&acirc;m)</font></span></strong></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '20-7-2020', '27-1-2021', 'vincom-shophouse-thai-hoa', NULL, 150, NULL, '0'),
(75, 27, 7, NULL, 'Khu Đô Thị Vinh Heritage - Mipec Tràng An Vinh', 'Đường Lê Mao kéo dài, P. Vinh Tân', 'Công Ty Cổ Phần Hóa Dầu Quân Đội (Mipec) và Công Ty Cổ Phần Đầu Tư Và Xây Dựng Tràng An', NULL, NULL, '0962452245', 947, '/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/mipec-vinh.png', NULL, NULL, 1, '1', NULL, '<h2 style=\"text-align: center;\">\r\n	<span style=\"font-size:26px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N VINH HERITAGE</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" style=\"color: rgb(0, 0, 0); font-family: \" times=\"\"><a href=\"http://www.bdsnghetinh.net/dat-nen-biet-thu/khu-do-thi-mipec-trang-an-mipec-vinh/75.html\"><strong>Vinh Heritage (Mipec Tr&agrave;ng An)</strong></a>&nbsp; l&agrave; khu đ&ocirc; thị phức hợp kiểu mẫu đ</span><span font-size:=\"\" new=\"\" style=\"color: rgb(0, 0, 0); font-family: \" times=\"\">ược định hướng ph&aacute;t triển th&agrave;nh một KĐT kiểu mẫu đầu ti&ecirc;n tại TP. Vinh. Một KĐT đẳng cấp, hiện đại d&agrave;nh cho giới tinh hoa tại th&agrave;nh Vinh, tương tự m&ocirc; h&igrave;nh c&aacute;c KĐT lớn ở c&aacute;c tỉnh th&agrave;nh như KĐT Ciputra hay Vinhomes Riverside ở H&agrave; Nội hay Vinhomes Star City Thanh H&oacute;a.</span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Chủ Đầu Tư:</strong> C&ocirc;ng Ty Cổ Phần H&oacute;a Dầu Qu&acirc;n Đội (Mipec) v&agrave; C&ocirc;ng Ty Cổ Phần Đầu Tư V&agrave; X&acirc;y Dựng Tr&agrave;ng An li&ecirc;n kết hợp t&aacute;c.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>T&ecirc;n dự &aacute;n: </strong>Vinh Heritage</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Quy m&ocirc; dự &aacute;n:</strong> Tổng diện t&iacute;ch 40,47 ha,&nbsp;Tổng diện t&iacute;ch c&acirc;y xanh v&agrave; mặt nước: khoảng 8.4ha</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Vị tr&iacute; dự &aacute;n: </strong>Tọa lạc tại trục đường L&ecirc; Mao k&eacute;o d&agrave;i &ndash; Phường Vinh T&acirc;n &ndash; TP. Vinh, tỉnh Nghệ An.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Đơn vị QLDA- TVGS: </strong>C&ocirc;ng ty TNHH tư vấn đại học x&acirc;y dựng.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Đơn vị thiết kế:</strong> C&ocirc;ng ty cổ phần kiến tr&uacute;c KC.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Đơn vị thi c&ocirc;ng: </strong>C&ocirc;ng ty x&acirc;y dựng 384 &ndash; Binh đo&agrave;n 12.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Ph&aacute;p l&yacute;: </strong>Sổ hồng l&acirc;u d&agrave;i.</span></span></li>\r\n	<li>\r\n		<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Loại h&igrave;nh sản phẩm:</strong>&nbsp;bao gồm khu Thấp tầng, Cao tầng v&agrave; khu thương mại dịch vụ</span></span></li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/Vinh-heritage-365-ngay-tinh-khiet.jpg\" /></span></p>\r\n<h2 style=\"text-align: center;\">\r\n	<br />\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:26px;\"><span style=\"font-family:times new roman,times,serif;\"><span style=\"color:#ff0000;\">VINH HERITAGE (MIPEC TR&Agrave;NG AN) SỞ HỮU VỊ TR&Iacute; KIM CƯƠNG</span></span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự &aacute;n Vinh Heritage tọa lạc tại vị tr&iacute; đắc địa nhất trung t&acirc;m Khu đ&ocirc; thị mới phường Vinh T&acirc;n, Th&agrave;nh Phố Vinh, Nghệ An. Dự &aacute;n sở hữu cơ sở hạ tầng đồng bộ v&agrave; kh&eacute;p k&iacute;n. Đặc biệt ph&iacute;a Đ&ocirc;ng dự &aacute;n gi&aacute;p với tuyến đường L&ecirc; Mao k&eacute;o d&agrave;i, kết nối Tp.Vinh với đường tr&aacute;nh Vinh, đ&acirc;y l&agrave; trục đường huyết mạch mở rộng địa giới h&agrave;nh ch&iacute;nh theo định hướng ph&aacute;t triển của th&agrave;nh phố.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/mipec-vinh.png\" /></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;KĐT Vinh Heritage - Mipec Tr&agrave;ng An hứa hẹn l&agrave; một nơi tr&agrave;n đầy sinh kh&iacute;, hướng tới một kh&ocirc;ng gian sống xanh th&acirc;n thiện với m&ocirc;i trường đồng thời nhờ sở hữu vị tr&iacute; v&agrave;ng ở trung t&acirc;m TP.Vinh n&ecirc;n dự &aacute;n đang nhận được rất nhiều sự quan t&acirc;m của giới đầu tư cũng như kh&aacute;ch h&agrave;ng c&oacute; nhu cầu mua nh&agrave; ở tại địa phương.</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" times=\"\">&nbsp; &nbsp;Vinh Heritage&nbsp;</span><span style=\"font-family:times new roman,times,serif;\">được x&acirc;y dựng theo phong c&aacute;ch ch&acirc;u &Acirc;u với nhiều ph&acirc;n khu nh&agrave; vườn liền kề nhau. Trong đ&oacute; c&oacute; Tiểu khu Cảnh vi&ecirc;n, tiểu khu Hưng Th&aacute;i v&agrave; tiểu khu An Ph&uacute;, mỗi tiểu khu được thiết kế với một vẻ độc đ&aacute;o, phong ph&uacute; ri&ecirc;ng, h&agrave;i h&ograve;a, thống nhất trong tổng thể kiến tr&uacute;c.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/vi-tri.png\" /></span></span></p>\r\n<ul>\r\n	<li>\r\n		<strong><span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">C&aacute;ch C&ocirc;ng vi&ecirc;n v&agrave; Quảng trường trung t&acirc;m Th&agrave;nh phố 800m</span></em></span></strong></li>\r\n	<li>\r\n		<strong><span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">C&aacute;ch trường tiểu học v&agrave; THCS Vinh T&acirc;n: 500m</span></em></span></strong></li>\r\n	<li>\r\n		<strong><span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">C&aacute;ch C&ocirc;ng vi&ecirc;n trung t&acirc;m: 700m</span></em></span></strong></li>\r\n	<li>\r\n		<strong><span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">C&aacute;ch bệnh viện Th&agrave;nh phố: 800m</span></em></span></strong></li>\r\n	<li>\r\n		<strong><span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">C&aacute;ch chợ Vinh: 1km</span></em></span></strong></li>\r\n	<li>\r\n		<strong><span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">C&aacute;ch BigC Vinh: 1km</span></em></span></strong></li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong><em><span style=\"font-family:times new roman,times,serif;\">Sản phẩm Mipec Vinh</span></em></strong></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Vinh Heritage (người d&acirc;n địa phương c&ograve;n gọi l&agrave; Mipec Tr&agrave;ng An - Mipec Vinh) dự kiến mở b&aacute;n v&agrave;o qu&yacute; 4/2020 gồm c&aacute;c d&ograve;ng sản phẩm về nh&agrave; ở thấp tầng tr&ecirc;n tổng diện t&iacute;ch đất 40,47ha thuộc phường Vinh T&acirc;n, th&agrave;nh phố Vinh tỉnh Nghệ An. Chủ đầu tư dự kiến ph&acirc;n chia th&agrave;nh 2 giai đoạn ch&iacute;nh: Giai đoạn 1 mở b&aacute;n c&aacute;c sản phẩm Biệt thự đơn lập, biệt thự song lập. Giai đoạn 2 mở b&aacute;n nh&agrave; Liền kề v&agrave; Shophouse.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">BIỆT THỰ ĐƠN LẬP MIPEC TR&Agrave;NG AN</span></span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/biet-thu-don-lap-mipec-vinh.png\" /></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">Ảnh minh họa- biệt thự đơn lập</span></em></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch Đất:</strong> Từ 350m2 đến 511m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" times=\"\"><strong>Diện t&iacute;ch XD: </strong>488m2 đến 550m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Số lượng căn:</strong> 78 căn</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hướng ch&iacute;nh:</strong> Đ&ocirc;ng bắc, T&acirc;y nam, đ&ocirc;ng nam</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span font-size:=\"\" new=\"\" times=\"\">BIỆT THỰ SONG LẬP MIPEC TR&Agrave;NG AN</span></span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" times=\"\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/biet-thu-song-lap-mipec-vinh.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">Ảnh minh họa - Biệt thự song lập</span></em></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch Đất:</strong> Từ 180m2 đến 271m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch XD: </strong>337m2-416m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Số lượng căn:</strong> c&ograve;n 140 căn</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hướng ch&iacute;nh:</strong> Đ&ocirc;ng bắc, T&acirc;y nam, ch&iacute;nh nam</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span font-size:=\"\" new=\"\" times=\"\">NH&Agrave; LIỀN KỀ MIPEC TR&Agrave;NG AN</span></span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" times=\"\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/nha-lien-ke-mipec-vinh.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">Ảnh minh họa - Nh&agrave; liền kề</span></em></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch Đất:</strong> Từ 90m2 đến 150m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch XD:</strong> 250-360m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hướng: </strong>T&acirc;y Bắc, Ch&iacute;nh t&acirc;y, ch&iacute;nh đ&ocirc;ng, đ&ocirc;ng nam</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Ti&ecirc;u chuẩn b&agrave;n giao: </strong>B&agrave;n giao th&ocirc;, ho&agrave;n thiện mặt ngo&agrave;i</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span font-size:=\"\" new=\"\" times=\"\">SHOPHOUSE - NH&Agrave; PHỐ THƯƠNG MẠI MIPEC VINH</span></span></span></h3>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" times=\"\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/shophouse-mipec-vinh.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">Ảnh minh họa - Nh&agrave; phố (Shophouse)</span></em></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch</strong> từ 90m2 - 220m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Diện t&iacute;ch x&acirc;y dựng:</strong> 350m2 - 480m2</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Số lượng:</strong> 68 căn</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><em><span style=\"font-family:times new roman,times,serif;\">Cơ hội đầu tư sinh lời tốt nhất năm 2020</span></em></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH HO&Agrave;N HẢO MIPEC VINH</span></span></span></h2>\r\n<p>\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/public/upload/images/Mipec%20Vinh/B%C3%A0i%20t%E1%BB%95ng%20quan/Mipec-vinh-tien-ich-du-an.jpg\" /></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;T&acirc;n Long Land</strong>&nbsp; li&ecirc;n tục cập nhật th&ocirc;ng tin dự &aacute;n, qu&yacute; kh&aacute;ch h&agrave;ng quan t&acirc;m vui l&ograve;ng đăng k&yacute; th&ocirc;ng tin li&ecirc;n hệ Chuy&ecirc;n vi&ecirc;n tư vấn sẽ li&ecirc;n hệ chi tiết đến Anh chị!</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span font-size:=\"\" new=\"\" times=\"\">==============================================</span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">C&ocirc;ng Ty Cổ phần Dịch vụ v&agrave; đầu tư T&acirc;n Long</span></span></strong></span></p>\r\n<p>\r\n	<span style=\"font-size:28px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Hotline:</strong> <span style=\"color:#ff0000;\"><strong>096.245.2.245</strong></span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>Website:</strong> <a href=\"http://www.bdsnghetinh.net/\"><span style=\"color:#0000ff;\"><em>http://www.bdsnghetinh.net/</em></span></a></span></span></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Khu Đô Thị Vinh Heritage - Mipec Tràng An Vinh', '30-7-2020', '27-1-2021', 'khu-do-thi-vinh-heritage-mipec-trang-an-vinh', 'KĐT Mipec Tràng An là một nơi tràn đầy sinh khí hướng tới một không gian sống xanh thân thiện với môi trường đồng thời nhờ sở hữu vị trí vàng nên dự á', 0, NULL, '0');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `investor`, `complete`, `acreage`, `hotline`, `hits`, `img`, `price`, `old_price`, `hot`, `status`, `detail`, `content`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `heading`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `title`, `date`, `date_up`, `slug`, `description`, `desc_size`, `keywords`, `robot`) VALUES
(76, 1, 7, NULL, 'HƯNG LỘC HOME 1', 'Đường Lê Viết Thuật - 35m, Hưng Lộc', 'CTCP Đầu tư và Thương mại Dầu khí Nghệ An', NULL, NULL, '0962452245', 2213, '/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/hung-loc-homes-1.jpg', NULL, NULL, 1, '1', NULL, '<h2 style=\"text-align: center;\">\r\n	<span style=\"font-size:26px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">TỔNG QUAN DỰ &Aacute;N HƯNG LỘC HOMES</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&bull; T&ecirc;n dự &aacute;n: </strong>Hưng L&ocirc;̣c Homes</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&bull; Chủ đầu tư:</strong> C&ocirc;ng ty Cổ phần Đầu tư v&agrave; Thương mại Dầu kh&iacute; Nghệ An</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; <strong>Đơn vị ph&aacute;t triển dự &aacute;n:</strong> C&ocirc;ng ty Cổ phần Đầu tư Landcom</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&bull; Vị tr&iacute;:</strong> Ngã tư Đường L&ecirc; Vi&ecirc;́t Thu&acirc;̣t, x&atilde; Hưng Lộc, th&agrave;nh phố Vinh, Nghệ An</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; <strong>Tổng diện t&iacute;ch quy hoạch: </strong>2.5ha</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&bull; Sản phẩm:</strong> nh&agrave; liền kề, Shophouse, Biệt thự</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull;<strong> H&igrave;nh thức sở hữu:</strong> L&acirc;u d&agrave;i</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; <strong>Ti&ecirc;u chuẩn b&agrave;n giao: </strong>X&acirc;y th&ocirc;, ho&agrave;n thiện mặt ngo&agrave;i theo thiết kế</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&bull; <strong>Tiến độ x&acirc;y dựng: </strong>Đang triển khai thi c&ocirc;ng hạ tầng kỹ thuật</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">VỊ TR&Iacute; HƯNG LỘC HOMES - T&Acirc;M ĐIỂM KẾT NỐI</span></span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/pasted%20image%200.png\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Vị tr&iacute; dự &aacute;n Hưng Lộc Homes</span></span></em></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>&nbsp; &nbsp;Dự &aacute;n Hưng Lộc Homes</strong> tọa lạc tại ng&atilde; tư đường L&ecirc; Viết Thuật, x&atilde; Hưng Lộc, Tp Vinh, Nghệ An. Đ&acirc;y được đ&aacute;nh gi&aacute; l&agrave; tuyến đường giao th&ocirc;ng huyết mạch kết nối trung t&acirc;m th&agrave;nh phố Vinh v&agrave; thị x&atilde; Cửa L&ograve;.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Sở hữu hệ thống giao th&ocirc;ng thuận tiện với 3 dự &aacute;n giao th&ocirc;ng lớn, bao gồm: Đường Vinh - Cửa Hội ( đường tỉnh lộ 535), tuyến đường quy hoạch 35m v&agrave; đường quy hoạch rộng 24m, Hưng Lộc Homes mang đến kết nối thuận tiện với&nbsp; c&aacute;c trục đường ch&iacute;nh v&agrave; c&aacute;c khu d&acirc;n cư , tạo lập cho to&agrave;n bộ dự &aacute;n lợi thế về cơ sở hạ tầng đồng bộ.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;B&ecirc;n cạnh đ&oacute;, tuyến đường huyết mạch đi qua x&atilde; Hưng Lộc l&agrave; nơi tập trung c&aacute;c trường Đại học, Cao đẳng đ&oacute;ng tr&ecirc;n địa b&agrave;n th&agrave;nh phố, tạo n&ecirc;n mật độ lưu th&ocirc;ng đ&ocirc;ng đ&uacute;c, gia tăng tiềm năng kinh doanh, đầu tư sinh lời.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Trong những năm qua, thị trường bất động sản tại x&atilde; Hưng Lộc đang dần trở n&ecirc;n s&ocirc;i động. Đặc biệt, th&aacute;ng 9/2019 Thủ tướng Ch&iacute;nh phủ ch&iacute;nh thức ph&ecirc; duyệt Đề &aacute;n ph&aacute;t triển th&agrave;nh phố Vinh- Cửa L&ograve; trở th&agrave;nh trung t&acirc;m kinh tế, văn h&oacute;a v&ugrave;ng Bắc Trung Bộ đ&atilde; th&uacute;c đẩy thị trường bất động sản nơi đ&acirc;y dần tăng nhiệt tạo điều kiện thuận lợi thu h&uacute;t đ&ocirc;ng đảo nh&agrave; đầu tư th&ocirc;ng th&aacute;i tại Th&agrave;nh phố Vinh.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">MẶT BẰNG THIẾT KẾ HƯNG LỘC HOMES</span></span></span></h2>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/Phoi-canh-mat-bang-hung-loc-home.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Mặt bằng dự &aacute;n Hưng Lộc Homes</em></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Cảm hứng về một chốn an cư l&yacute; tưởng đồng thời kiến tạo n&ecirc;n những gi&aacute; trị thương mại vượt trội cho cư d&acirc;n th&agrave;nh phố Vinh, C&ocirc;ng ty Cổ phần Đầu tư v&agrave; Thương mại Dầu kh&iacute; Nghệ An ( PVIT) mang đến dự &aacute;n khu nh&agrave; ở Hưng Lộc Homes, hứa hẹn tạo lập một cuộc sống đẳng cấp, tiện nghi v&agrave; hiện đại nhưng vẫn đảm bảo gần gũi với thi&ecirc;n nhi&ecirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Được quy hoạch tr&ecirc;n tổng diện t&iacute;ch 2.6ha, dự &aacute;n Hưng Lộc Homes mang đến cho thị trường bất động sản 3 loại h&igrave;nh sản phẩm, bao gồm: Nh&agrave; phố thương mại shophouse, nh&agrave; liền kề v&agrave; biệt thự, trong đ&oacute; thiết kế cụ thể như sau:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<u><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>BIỆT THỰ:</strong></span></span></u></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Số lượng: 07 căn</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Tổng diện t&iacute;ch đất :1.053,9m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Diện t&iacute;ch đất trung b&igrave;nh: 150,5m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Thiết kế : 3.5 tầng</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/Phoi-canh-hung-loc-vinh-5.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Biệt thự Hưng Lộc Homes</em></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><strong>NH&Agrave; PHỐ THƯƠNG MẠI SHOPHOUSE:</strong></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Số lượng: 42 căn</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Tổng diện t&iacute;ch đất: 5.275,4m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Diện t&iacute;ch đất trung b&igrave;nh: 125,6m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Thiết kế: 3.5 tầng</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/Phoi-canh-hung-loc-vinh-1.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Nh&agrave; phố shophouse Hưng Lộc Homes</em></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<strong><span style=\"font-size:14px;\"><span style=\"font-family:times new roman,times,serif;\">NH&Agrave; LIỀN KỀ:</span></span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Số lượng: 60 căn</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Tổng diện t&iacute;ch đất: 7.540,3m2</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Diện t&iacute;ch đất trung b&igrave;nh: 112,5m2</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/Phoi-canh-hung-loc-vinh-2.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><em>Liền kề Hưng Lộc Homes</em></span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">TIỆN &Iacute;CH DỊCH VỤ HO&Agrave;N HẢO</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Được quy hoạch tr&ecirc;n diện t&iacute;ch 2.6ha, dự &aacute;n Hưng Lộc Homes sở hữu hệ thống nh&agrave; phố thương mại, nh&agrave; liền kề v&agrave; biệt thự đẳng cấp, thiết kế h&agrave;i h&ograve;a giữa thi&ecirc;n nhi&ecirc;n v&agrave; hoa cỏ mang đến trải nghiệm ho&agrave;n to&agrave;n mới mẻ về phong c&aacute;ch sống kh&aacute;c biệt.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hiểu r&otilde; gi&aacute; trị cần c&oacute; của cuộc sống cư d&acirc;n thời đại mới, chủ đầu tư đ&atilde; mang đến hệ thống sản phẩm với&nbsp; những m&agrave;u sắc rất ri&ecirc;ng, hội tụ tinh hoa của nghệ thuật thiết kế, thể hiện tầm v&oacute;c của gia chủ thượng lưu.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hướng đến những chuẩn mực sống cao cấp của cư d&acirc;n th&agrave;nh phố Vinh, tại Hưng Lộc Homes, chủ đầu tư cung cấp một kh&ocirc;ng gian xanh tho&aacute;ng m&aacute;t c&ugrave;ng hệ thống tiện &iacute;ch dịch vụ đa dạng, kh&ocirc;ng chỉ đẳng cấp về chất lượng m&agrave; c&ograve;n thỏa m&atilde;n tầm mắt với thiết kế kiến tr&uacute;c hiện đại.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Hệ thống tiện &iacute;ch dịch vụ bao gồm:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Quảng trường</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">S&acirc;n cầu l&ocirc;ng</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khu vui chơi trẻ em</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khu tập thể dục cho người cao tuổi</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khu vườn thư gi&atilde;n</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">S&acirc;n b&oacute;ng rổ mini</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">X&iacute;ch đu</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Khu để xe</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Nh&agrave; văn h&oacute;a</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Tiện &iacute;ch cảnh quan</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Đường dạo bộ</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/Phoi-canh-hung-loc-vinh-3.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/Phoi-canh-hung-loc-vinh-4.jpg\" /></span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp;</h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">CHỦ ĐẦU TƯ DỰ &Aacute;N HƯNG LỘC HOMES</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Dự &aacute;n Hưng Lộc Homes được đầu tư x&acirc;y dựng bởi C&ocirc;ng ty Cổ phần Đầu tư v&agrave; Thương mại Dầu kh&iacute; Nghệ An ( PVIT). PVIT được th&agrave;nh lập từ th&aacute;ng 2/2008 trong kế hoạch thỏa thuận hợp t&aacute;c to&agrave;n diện giữa Tập đo&agrave;n Dầu kh&iacute; quốc gia Việt Nam v&agrave; Ủy ban nh&acirc;n d&acirc;n tỉnh Nghệ An.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;C&ugrave;ng với xu thế hội nhập v&agrave; ph&aacute;t triển, PVID x&aacute;c định r&otilde; mục ti&ecirc;u v&agrave; chiến lược ph&aacute;t triển l&agrave; trở th&agrave;nh doanh nghiệp mạnh theo hướng đa ng&agrave;nh nghề, trong đ&oacute; tập trung ưu ti&ecirc;n v&agrave;o lĩnh vực: đầu tư kinh doanh bất động sản, x&acirc;y dựng c&ocirc;ng tr&igrave;nh, hạ tầng kỹ thuật, giao th&ocirc;ng vận tải v&agrave; giao th&ocirc;ng thương mại.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Trải qua 12 năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, với sứ mệnh cung cấp sản phẩm đa dạng, độc đ&aacute;o v&igrave; lợi &iacute;ch của kh&aacute;ch h&agrave;ng, lu&ocirc;n h&agrave;i h&ograve;a giữa lợi doanh nghiệp với lợi &iacute;ch x&atilde; hội, đồng h&agrave;nh c&ugrave;ng c&aacute;c hoạt động v&igrave; cộng đồng, đến nay, c&aacute;c dự &aacute;n đầu tư kinh doanh bất động sản trọng điểm của ng&agrave;nh dầu kh&iacute; mang thương hiệu PVIT đ&atilde; v&agrave; đang nối tiếp nhau tr&ecirc;n địa b&agrave;n tỉnh Nghệ An v&agrave; khu vực Bắc Trung Bộ, ti&ecirc;u biểu như:</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">-T&ograve;a nh&agrave; dầu kh&iacute; Nghệ An</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">-Khu đ&ocirc; thị Nghi Ph&uacute;</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">-Khu đ&ocirc; thị dầu kh&iacute; Nghệ An.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Với định hướng đ&uacute;ng đắn của Hội đồng quản trị, Ban gi&aacute;m đốc v&agrave; sự nỗ lực kh&ocirc;ng ngừng nghỉ của đội ngũ C&aacute;n bộ nh&acirc;n vi&ecirc;n, PVIT đ&atilde; v&agrave; đang khẳng định được thương hiệu, vị thế của m&igrave;nh trong lĩnh vực đầu tư kinh doanh Bất động sản .</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Sở hữu tiềm lực t&agrave;i ch&iacute;nh vững mạnh, tầm nh&igrave;n vượt trội v&agrave; kinh nghiệm l&acirc;u năm trong lĩnh vực bất động sản, C&ocirc;ng ty Cổ phần Đầu tư v&agrave; Thương mại Dầu kh&iacute; Nghệ An tiếp tục mang đến thị trường sản phẩm Hưng Lộc Homes- một điểm đến l&yacute; tưởng, k&ecirc;nh đầu tư hấp dẫn cho cư d&acirc;n tại Th&agrave;nh phố Vinh.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	&nbsp;</h2>\r\n<h2 style=\"text-align: justify;\">\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">CH&Iacute;NH S&Aacute;CH B&Aacute;N H&Agrave;NG DỰ &Aacute;N HƯNG LỘC HOMES</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Đang cập nhật...</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"color:#ff0000;\"><span style=\"font-family:times new roman,times,serif;\">TIẾN ĐỘ DỰ &Aacute;N HƯNG LỘC HOMES</span></span></span></h2>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">&nbsp; &nbsp;Hiện tại Chủ đầu tư đang cho tiến h&agrave;nh triển khai thi c&ocirc;ng hạ tầng kỹ thuật, đảm bảo x&acirc;y dựng theo đ&uacute;ng tiến độ đề ra.</span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/tien-do-du-an-hung-loc-home.jpg\" /></span></span></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\"><img alt=\"\" src=\"/public/upload/images/H%C6%B0ng%20L%E1%BB%99c/tien-do-DA-hung-loc-home-2.jpg\" /></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong><span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\"><em>&nbsp; &nbsp;Q&uacute;y kh&aacute;ch h&agrave;ng lưu &yacute;:&nbsp;</em> Mọi th&ocirc;ng tin ch&uacute;ng t&ocirc;i cập nhật về dự &aacute;n Hưng Lộc Homes chỉ mang t&iacute;nh chất minh họa v&agrave; tham khảo. Hiện tại dự &aacute;n đang được chủ đầu tư l&ecirc;n kế hoạch triển khai v&agrave; sẽ sớm th&ocirc;ng tin ch&iacute;nh thức đến với kh&aacute;ch h&agrave;ng. Khi c&oacute; th&ocirc;ng tin ch&iacute;nh thức, ch&uacute;ng t&ocirc;i sẽ cập nhật tới qu&yacute; kh&aacute;ch h&agrave;ng sớm nhất.</span></span></strong></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><font face=\"times new roman, times, serif\"><strong>HOTLINE: 0962452245 ( Mr. L&acirc;m)</strong></font></span></span></p>\r\n<p style=\"text-align: justify;\">\r\n	<em><span style=\"font-size:16px;\"><span style=\"font-family:times new roman,times,serif;\">Tr&acirc;n trọng!</span></span></em></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Hưng Lộc Home - Tân Long Nghệ An Độc quyền phân phối chính thức', '29-8-2020', '27-1-2021', 'hung-loc-home-1', 'Dự án Hưng Lộc Homes tọa lạc tại ngã tư đường Lê Viết Thuật, xã Hưng Lộc, Tp Vinh, Nghệ An. Đây được đánh giá là tuyến đường giao thông huyết mạch ...', 0, NULL, '0');

